INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Cay đắng khi chứng kiến bạn gái vào nhà nghỉ với người khác', 'cay-dang-khi-chung-kien-ban-gai-vao-nha-nghi-voi-nguoi-khac', 'https://i1-vnexpress.vnecdn.net/2025/10/14/Google-AI-Studio-2025-10-14T01-5808-8415-1760406714.png?w=680&h=0&q=100&dpr=1&fit=crop&s=P8XmzQXbWTkN5SnAyhOjnQ', 'Tôi 28 tuổi, làm trong ngành xây dựng. Cách đây gần hai năm, tôi quen bạn gái qua một người bạn chung. Lúc đó, tôi thật sự ấn tượng với cô ấy từ nói chuyện nhẹ nhàng, biết quan tâm, có công việc ổn định. Cô ấy không quá xinh nhưng rất duyên, đặc biệt là tạo cho người đối diện cảm giác muốn che chở. Sau vài tháng tìm hiểu, chúng tôi chính thức yêu nhau. Tình yêu của tôi dành cho bạn gái khá nghiêm túc. Tôi đưa cô ấy về ra mắt gia đình, có ý định tiến xa. Mọi người đều quý cô ấy vì hiền và cư xử khéo. Tôi cũng tự nhủ, sau nhiều năm phấn đấu, đây có lẽ là người con gái mình nên gắn bó.\nNhưng gần đây, tôi bắt đầu thấy bạn gái khác khác, không phải thay đổi lớn, chỉ rất nhỏ thôi nhưng vì yêu nên tôi hay để ý tới cô ấy và nhận ra, chẳng hạn bạn gái hay xem điện thoại khi đi ăn, hay mỉm cười khi đọc tin nhắn. Có lần, tôi vô tình thấy cô ấy lưu một số điện thoại trong danh bạ, chỉ để ký hiệu là "@". Linh cảm khiến tôi tò mò và ghi lại số ấy. Sau đó, tôi thử tra trên Zalo, thấy ra một người đàn ông, nhìn ảnh đại diện, có lẽ hơn tôi vài tuổi.\nTôi vẫn cố bình tĩnh, không muốn ghen tuông mù quáng. Nhưng rồi, trong một lần đi chơi phải về sớm vì bạn gái có việc gấp, cô ấy quên điện thoại trong xe, có tin nhắn đến: "Nhớ em quá, anh tới đón luôn được không?". Tôi đọc mà tim lạnh đi. Bạn gái nói dối tôi hôm đó về nhà có việc gia đình. Tôi im lặng, giả vờ như không biết, để xem cô ấy giấu đến đâu. Tôi quay xe lại để trả điện thoại cho cô ấy rồi tạm biệt về. Nhưng tôi không về ngay mà đứng ở một góc cách nhà bạn gái một đoạn và chờ.\nKhoảng 30 phút sau, tôi thấy một người đàn ông lái xe tới, bạn gái chạy ra và lên xe người đó luôn, hành động như đã quen thuộc lâu rồi. Tôi lặng lẽ bám theo thì thấy họ ghé siêu thị nhỏ mua ít đồ rồi ra và đi thẳng tới một nhà nghỉ trong ngõ vắng, nơi mà nếu tôi không theo dõi chắc chắn sẽ không bao giờ đi tới. Tôi thử gọi bạn gái thì không thấy nghe máy, gọi thêm lần nữa thì tắt máy. Tôi cay đắng vô cùng. Lúc đó tôi không còn đủ tỉnh táo để chụp ảnh làm bằng chứng. Sau một hồi suy nghĩ, tôi quyết định quay xe về nhà chứ không vào bắt tại trận hoặc chờ họ xong việc đi ra để làm um lên. Tôi không muốn làm việc mất mặt đó.\nNgày hôm sau, bạn gái gọi lại cho tôi nói rằng hôm qua mệt nên ngủ sớm, không để ý điện thoại hết pin. Mọi lời giải thích quá hợp lý. Từ hôm tới giờ đã một tuần trôi qua, tôi viện lý do tăng ca để không gặp cô ấy, cũng không chủ động liên lạc trước nữa. Đầu óc tôi mông lung, chẳng biết nên đối diện thế nào. Cứ nghĩ tới việc bị lừa dối, tôi cay đắng vô cùng vì đã tin tưởng bạn gái quá. Nhưng nghĩ tới chia tay, tôi lại thấy đau lòng. Thấy tôi dạo này không quan tâm như trước, bạn gái cũng chủ động hỏi han, qua nhà tôi nhưng sau đó tỏ vẻ giận hờn. Tôi phải làm sao đây? Mong mọi người ở ngoài sáng suốt, cho tôi xin lời khuyên.', 'Tôi lặng lẽ bám theo thì thấy họ ghé siêu thị nhỏ mua ít đồ rồi ra và đi thẳng tới một nhà nghỉ trong ngõ vắng.', @cat_id_moi_nhat, @user_id_haianh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Trailer phim tài liệu của Taylor Swift gây sốt', 'trailer-phim-tai-lieu-cua-taylor-swift-gay-sot', 'https://i1-giaitri.vnecdn.net/2025/10/14/240613-liverpool-taylor-swift-6617-3049-1760412912.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=0iOAIR8w0mWAx6YmhE1mJg', 'TheoVariety, phim tài liệuThe End of an Eracùng dự án hòa nhạcThe Final Showsẽ phát hành trên nền tảngDisney+vào ngày 12/12, một ngày trước sinh nhật ca sĩ.\n"Êkíp chúng tôi muốn lưu giữ từng khoảnh khắc trước khi khép lại chương quan trọng và mãnh liệt nhất trong cuộc đời. Đoàn làm phim đã ghi lại mọi câu chuyện trong chuyến lưu diễn và đêm concert cuối", Taylor nói.\nSau khi đăng tải, trailer đạt hơn 3,6 triệu lượt thích, hơn 50 nghìn lượt chia sẻ trên các nền tảng mạng xã hội. Một người dùng bình luận: "Taylor thật nhiều năng lượng, tôi có cảm giác lúc nào cô ấy cũng làm việc và mang đến cho chúng ta nhiều bất ngờ". Người khác viết: "Là fan của bạn, chúng tôi chưa sẵn sàng để nói lời tạm biệtThe Eras Tour".\nThe End of an Erado Don Argott thực hiện, mang đến cái nhìn về cuộc sống của Taylor Swift trong suốt quá trình lưu diễn kéo dài gần hai năm. Bộ phim ghi lại câu chuyện hậu trường, những lần ca sĩ tập luyện cùng ban nhạc, quy mô sân khấu. Ba mẹ Taylor, bạn trai Travis Kelce cùng các ca sĩ Gracie Abrams, Sabrina Carpenter, Ed Sheeran và Florence Welch cũng góp mặt trong tác phẩm.\nPhim hòa nhạcThe Final Showdo đạo diễn Glenn Weiss chỉ đạo, đưa người xem trở lại đêm diễn cuối tại sân vận động BC Place (Vancouver). Lần đầu tiên người hâm mộ có thể xem trọn vẹn phần biểu diễn mở rộng sau khi Taylor phát hành albumThe Tortured Poets Department, bao gồm đoạn trình diễnFemale Rage: The Musical Segment.\nGiới chuyên môn nhận định Taylor công bố hai dự án mới trong lúc cô đang nối dài chuỗi thành công trong sự nghiệp. Album thứ 12The Life of a Showgirlgiữ vị trí số một trên bảng xếp hạng Billboard 200, phim ca nhạcThe Release Party of a Showgirl- được mô tả như "bữa tiệc ra mắt" (release party) nhạc mới của cô - thu 33 triệu USD sau ba ngày công chiếu.\nTaylor Swift, 36 tuổi, được mệnh danh "công chúa nhạc đồng quê" của nền âm nhạc Mỹ. Cô ra mắt năm 2006, sớm gặt hái nhiều thành tích. Gần 20 năm trong nghề, cô nhận 14 tượng vàng Grammy, 30 cúp VMAs, tổ chức nhiều chuyến lưu diễn lớn. Năm 2023, tạp chíTIMEvinh danh cô là "Nhân vật của năm". Tháng 11/2024, cô xếp thứ hai trong top 25 Ngôi sao pop vĩ đại nhất thế kỷ 21 của tạp chíBillboard. Hiện khối tài sản của cô là 1,6 tỷ USD, theoForbes. Hồi tháng 8, ca sĩđính hônbạn trai - cầu thủ bóng bầu dục Travis Kelce, bằng tuổi.\nThe Eras Tourđược xem là hiện tượng văn hóa toàn cầu, thu về hơn hai tỷ USD và trở thành tour diễn có doanh thu cao nhất lịch sử. Tour nhạc bắt đầu hồi tháng 3/2023 và kết thúc cuối năm 2024, tổng cộng 151 show. Tính trung bình, mỗi buổi diễn hút hơn 70 nghìn khán giả, thu về 17 triệu USD, mỗi vé có giá khoảng 238 USD. Cuối năm 2023, ca sĩ ra mắt dự ánTaylor Swift: The Eras Tour, đạt hơn 261 triệu USD tiền vé, trở thành phim tài liệu hòa nhạc có doanh thu cao nhất mọi thời.\nCát Tiên(theoVariety)', 'Ca sĩ Taylor Swift gây "bão" mạng xã hội khi công bố series tài liệu và phim hòa nhạc về chuyến lưu diễn The Eras Tour.', @cat_id_moi_nhat, @user_id_cattien, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("Nữ thần điền kinh Anh: 'Luôn coi trọng huy chương hơn đàn ông'", 'nu-than-dien-kinh-anh-luon-coi-trong-huy-chuong-hon-dan-ong', 'https://i1-thethao.vnecdn.net/2025/10/14/amy-PNG-1760412578-3549-1760412924.png?w=680&h=0&q=100&dpr=1&fit=crop&s=T0ToevmH66HFQActVLVtOg', 'Ở tuổi 23, Hunt đang trở thànhgương mặt biểu tượng mới của thể thao Anh- vừa mạnh mẽ, thông minh, vừa có vẻ đẹp cổ điển gợi cảm. Cô giành HC bạc 200m tại Giải vô địch thế giới ở Tokyo, đồng thời là cựu sinh viên ngành Văn học Anh của Đại học Cambridge, nơi Hunt từng suýt bỏ học vì kiệt sức khi phải vừa học, vừa tập luyện ở cường độ cao.\nKhi nhập học tại Cambridge, Hunt đã là nhà vô địch trẻ từng lập kỷ lục U18 thế giới ở cự ly 200m. Nhưng ngôi trường danh giá ấy lại khiến cô choáng ngợp. "Tôi từng muốn bỏ học mỗi năm", Hunt nói với tờTimes. "Các giáo sư chỉ quan tâm đến luận văn, không hiểu tại sao tôi cần rời trường để tập luyện ở Loughborough. Tôi thấy mình bị kẹt giữa hai thế giới".\nCăng thẳng, chấn thương và cô đơn từng khiến Hunt rơi vào khủng hoảng. Nhưng cô vẫn quyết tâm chạm đích ở cả hai đường đua - học thuật và thể thao. "Tôi học được ở Cambridge cách chịu đựng áp lực. Trả lời báo chí sau khi về nhì thế giới còn dễ hơn đối mặt với bài kiểm tra về thơ Tudor", cô dí dỏm nói.\nSau khi tốt nghiệp, Hunt chuyển đến Padua, Italy để theo đuổi sự nghiệp chuyên nghiệp dưới sự hướng dẫn của HLV Marco Airale. "Giờ tôi sống trong căn hộ nhìn ra nhà thờ cổ, chỉ mất 20 phút tàu là đến Venice. Có những buổi sáng tôi vừa ghi chép về nghệ thuật Phục Hưng, chiều đã tập nâng 90 kg trong phòng gym. Đó là hai thế giới khác biệt, nhưng đều làm tôi hạnh phúc", cô cho biết.\nKhi được hỏi về chuyện tình cảm, Hunt không ngần ngại: "Tôi luôn ưu tiên huy chương. Tôi còn quá trẻ, còn nhiều thứ để làm. Mẹ tôi thường bảo: \'Đàn ông thì lúc nào chẳng có, còn cơ hội vô địch thế giới chỉ đến vài lần trong đời\'. Tôi thấy bà nói đúng".\nCâu nói "Huy chương trước đàn ông" nhanh chóng trở thành khẩu hiệu mà người hâm mộ gắn với Hunt - một tuyên ngôn vừa hài hước, vừa phản ánh tinh thần độc lập của thế hệ VĐV nữ trẻ. "Dĩ nhiên tôi cũng mơ về tình yêu", cô nói. "Nhưng tôi muốn trở thành phiên bản tốt nhất của chính mình trước đã".\nHunt cho rằng việc lập gia đình hay sinh con là điều cô sẽ cân nhắc khi thời điểm đến. "Với nữ VĐV, bạn phải tính cả chuyện làm mẹ vào kế hoạch thi đấu. Nhưng tôi đang hạnh phúc với hiện tại. Cả thế giới rộng mở, và tôi muốn tận hưởng mọi thứ đúng lúc của nó", chân chạy 23 tuổi bày tỏ.\nNgoài đường chạy, Hunt đam mê nghệ thuật và văn học. Cô từng viết luận văn về "siêu thị hiện đại trong văn học thập niên 1980" và học chuyên đề về thời Phục Hưng trong năm cuối đại học. "Giờ tôi sống ngay giữa trung tâm của Phục Hưng, thật điên rồ. Tôi thường đến Guggenheim Venice mỗi tháng, xem các bức tranh mà trước đây tôi chỉ thấy trong sách", VĐV người Anh nói.\nGiữa các giải đấu, Hunt viết nhật ký, vẽ ký họa, hoặc ngồi ở quán cà phê bên kênh đào để ghi lại cảm xúc sau buổi chạy. Cô nói: "Đôi khi tôi viết lại cảm nhận về một bức tượng, rồi nghĩ: Ồ, năm 21 tuổi mình từng đứng ở đây và thấy như thế này. Đó là cách tôi lưu giữ khoảnh khắc, giống như sưu tầm huy chương tinh thần".\nCô cũng chơi cello, dù đành để cây đàn ở Anh. "Mang nó sang Italy chắc phải mua thêm vé máy bay", cô nói rồi bật cười. "Nhưng tôi vẫn nghe opera ở Verona hay Florence. Tôi thích sự kết nối giữa âm nhạc, thể thao và nghệ thuật, tất cả đều cần nhịp điệu và cảm xúc".\nHunt từng học A-level (chương trình dự bị đại học của Anh) ba môn: Hóa học, Anh văn và Âm nhạc. "Tôi định học kỹ sư hóa, nhưng rồi nghĩ: làm sao vừa tính phản ứng hóa học vừa chạy 200m được chứ?", cô nhớ lại. Sau đó, cô chọn văn chương vì "vừa đủ thử thách trí tuệ, vừa cho phép tôi mơ mộng".\nSự kết hợp đó tạo nên một Hunt khác biệt: một VĐV có khả năng phân tích bằng dữ liệu khoa học, nhưng cũng biết rung động trước tranh của Duchamp hay nhạc của Charli XCX. "Tôi thích vừa là học bá, vừa là nữ thần tốc độ", cô nói, nhắc lại lời khẳng định sau khi giành HC bạc thế giới hồi tháng 9. "Không cần chọn một trong hai".\nTại Tokyo 2025, Hunt vượt qua Sherica Jackson (Jamaica) để cán đích với thành tích 22 giây 14, chỉ kém nhà vô địch Melissa Jefferson-Wooden (21 giây 68). Đó là dấu mốc lớn sau nhiều năm vật lộn với chấn thương, trong đó có ca phẫu thuật gân đùi năm 2022. "Tôi đã mất gần hai năm để lấy lại cảm giác chạy. Nhưng tôi biết mình vẫn còn rất nhiều thứ để cải thiện", cô tự hào.\nVới chiều cao 1m78 và bước chạy dài, Hunt được xem là mẫu VĐV lý tưởng cho nội dung 200m. "Tôi biết mình có thể nhanh hơn. HLV của tôi luôn nói tôi là ‘sản phẩm còn đang chín dở’. Chúng tôi làm việc tỉ mỉ, đo từng chỉ số bằng laser và phần mềm. Đó không chỉ là thể lực, mà còn là khoa học", cô cho biết.\nKhi kết thúc buổi trò chuyện, Hunt mỉm cười nói: "Tôi không chỉ là cô gái Cambridge biết chạy nhanh. Tôi là người yêu nghệ thuật, mê nhạc pop và vẫn đang học cách tận hưởng mọi khoảnh khắc".\nRồi cô đùa, lần nữa, bằng câu nói quen thuộc: "Huy chương trước đàn ông, nhưng biết đâu một ngày, tôi sẽ tìm thấy ai đó chạy nhanh hơn mình".\nHồng Duytổng hợp', 'Amy Hunt, VĐV tự nhận là nữ thần điền kinh, khẳng định luôn ưu tiên cạnh tranh huy chương hơn tìm kiếm tình yêu.', @cat_id_moi_nhat, @user_id_hongduy, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Khám phá Hà Giang bằng xe máy vào top trải nghiệm mùa thu', 'kham-pha-ha-giang-bang-xe-may-vao-top-trai-nghiem-mua-thu', 'https://i1-dulich.vnecdn.net/2025/10/14/HG-1760413112-2603-1760413916.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=4MbeTxhuQki0lvofCDdUDg', 'Châu Á được tạp chí du lịch Lonely Planet đánh giá là khu vực có đa dạng địa hình, từ những bãi biển nhiệt đới và các siêu đô thị nhộn nhịp đến thảo nguyên mênh mông và những dãy núi cao nhất thế giới. Mùa thu là thời điểm đặc biệt để du lịch châu Á nhờ thời tiết ôn hòa và thiên nhiên tươi đẹp.\nTrong số 12 trải nghiệm đặc biệt nhất được Lonely Planet lựa chọn, Hà Giang được đánh giá là "một trong những điểm đến có cảnh quan ngoạn mục nhất Đông Nam Á". Vào mùa thu, từ tháng 9 đến tháng 11, nơi này có thời tiết thuận lợi, mùa lúa chín vàng và lễ hội tam giác mạch.\nHành trình vòng lặp Hà Giang (Ha Giang Loop) bằng xe máy nổi tiếng cộng đồng du lịch là gợi ý của Lonely Planet. Với hành trình này, du khách được chiêm ngưỡng núi non hùng vĩ, những cung đường đèo ngoạn mục, trải nghiệm văn hóa dân tộc thiểu số. Đây là hành trình vòng khép kín dài khoảng 300-350 km, thường mất 3-5 ngày để hoàn thành, đi qua các điểm gồm phường Hà Giang 1 và 2 (TP Hà Giang cũ), cột cờ Lũng Cũ (điểm Cực Bắc), xã Đồng Văn (phố cổ, chợ), đèo Mã Pì Lèng, làng văn hóa Lũng Tám, Yên Minh, Quản Bạ.\nCùng với Hà Giang, trong danh sách 12 hành trình và điểm đến lý tưởng cho mùa thu châu Á còn có trekking ở dãy Himalaya (Nepal), trải nghiệm mùa mưa (Thái Lan), đi bộ đường dài giữa rừng lá đỏ (Nhật Bản), tham dự lễ hội Đại bàng (Mông Cổ), khám phá Kerala (Ấn Độ), thăm quần đảo Banda hẻo lánh (Indonesia), trải nghiệm Ladakh huyền bí (Ấn Độ), đạp xe từ Incheon đến Busan (Hàn Quốc), thám hiểm thung lũng Hunza (Pakistan), đi tàu dọc hành lang Cam Túc (Trung Quốc), lễ hội ma đói ở Penang (Malaysia).\nLonely Planet (Australia) là một trong những tạp chí du lịch hàng đầu thế giới, hoạt động từ năm 1972. Lonely Planet chuyên về các hướng dẫn du lịch, xuất bản sách du lịch và sản xuất nội dung du lịch trực tuyến. Các gợi ý của tạp chí đều dựa trên trải nghiệm thực tế của các phóng viên, biên tập viên, cộng tác viên và du khách trên khắp thế giới.\nTâm Anh(theoLonely Planet)', 'Lonely Planet xếp Hà Giang vào danh sách 12 điểm đến và trải nghiệm lý tưởng cho mùa thu, với lễ hội tam giác mạch và những chuyến du ngoạn bằng xe máy.', @cat_id_moi_nhat, @user_id_tamanh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("Xử lý chồng chéo giữa các quy hoạch 'tốn kém, mất thời gian'", 'xu-ly-chong-cheo-giua-cac-quy-hoach-ton-kem-mat-thoi-gian', 'https://i1-kinhdoanh.vnecdn.net/2025/10/14/68edc4ad08dcb36fd59d5963-17604-7634-3589-1760416440.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=Jhpzq4oR7EgOMN5OR94Jxg', 'Sáng 14/10, Ủy ban Thường vụ Quốc hội cho ý kiến dự án luật Quy hoạch (sửa đổi). Thừa ủy quyền Chính phủ trình bày tờ trình dự án luật, Thứ trưởng Tài chính Trần Quốc Phương cho biết dự thảo bổ sung quy định để xử lý các mâu thuẫn, chồng chéo giữa quy hoạch do cấp khác nhau phê duyệt, chẳng hạn quy hoạch vùng, tỉnh với các ngành.\n"Việc xử lý mâu thuẫn nhằm bảo đảm các dự án đầu tư có thể thực hiện trong thời gian ngắn nhất sau khi đã xác định quy hoạch được áp dụng", ông Phương nói.Nêu quan điểm về dự luật, Chủ nhiệm Ủy ban Quốc phòng, An ninh và Đối ngoại Quốc hội Lê Tấn Tới băn khoăn về việc xử lý mâu thuẫn, vướng mắc giữa quy hoạch đất quốc phòng, an ninh với phát triển kinh tế - xã hội và đầu tư.\n"Vấn đề này vướng mắc và khi giải quyết cũng mất rất nhiều thời gian", ông nói, đồng thời dẫn chứng thực tế giải quyết vụ việc liên quan "mấyụ bê tôngở sân bay Tân Sơn Nhất cũng mất vài năm, khi Thủ tướng vào xem xét, giải quyết mới xong".\nDo đó, ông đề nghị cơ quan soạn thảo nghiên cứu kỹ để quy định khi được bổ sung sẽ là cơ sở giúp Chính phủ xử lý hài hòa giữa phát triển kinh tế - xã hội và đảm bảo quốc phòng, an ninh.\nỞ khía cạnh này, Phó chủ tịch Quốc hội Trần Quang Phương cho rằng vấn đề khó nhất là giải quyết mối quan hệ giữa quy hoạch với chiến lược, kế hoạch. Trong khi đó, mối quan hệ giữa các quy hoạch hiện phân theo thứ bậc hành chính dẫn đến vướng mắc. Do đó, ông đề nghị làm rõ mối quan hệ giữa các loại quy hoạch, thứ bậc và tính chất của chúng.\nNgoài xử lý chồng chéo giữa các quy hoạch, dự luật cũng bổ sung quy định tăng phân cấp, phân quyền trong quyết định, duyệt quy hoạch so với hiện nay. Theo đó, Thủ tướng sẽ phê duyệt quy hoạch không gian biển, sử dụng đất quốc gia và vùng, thay vì thẩm quyền Quốc hội như luật hiện hành. Quy hoạch ngành sẽ do Bộ trưởng các bộ phê duyệt, còn quy hoạch tỉnh là Chủ tịch UBND cấp tỉnh.\nÔng Lê Tấn Tới lưu ý việc phân cấp thẩm quyền cần đảm bảo bảo mật truy cập dữ liệu quy hoạch có yếu tố quốc phòng, an ninh trên hệ thống thông tin quốc gia. "Vấn đề này cần giải quyết hài hòa, tránh lộ lọt bí mật về quốc phòng, an ninh", ông Tới nói.\nTrong khi đó, theo Phó chủ tịch Quốc hội Trần Quang Phương, có những vấn đề không thể phân cấp, phân quyền. Ông ví dụ việc phân quyền cho Chủ tịch UBND tỉnh duyệt quy hoạch tỉnh sẽ dễ mâu thuẫn với quy hoạch quốc gia.\nCùng quan điểm, Phó chủ tịch Quốc hội Nguyễn Khắc Định cho rằng quy hoạch quốc gia phải do Quốc hội quyết định, còn quy hoạch ngành thẩm quyền thuộc thẩm quyền của Chính phủ. "Đây là những quy hoạch quan trọng, sự sống của nền kinh tế nên không thể phân cấp. Có những thứ dứt khoát không phân cấp", ông Định nói.\nGiải trình sau đó, Phó thủ tướng Nguyễn Chí Dũng nói Luật Quy hoạch "khó, phức tạp", do đó phương pháp tiếp cận là "vừa làm, vừa sửa để hoàn chỉnh". Theo ông, Chính phủ đề xuất sửa Luật Quy hoạch toàn diện để xử lý dứt điểm các mâu thuẫn và điểm nghẽn hiện nay. Dự luật sửa đổi cũng bảo đảm thống nhất trong hệ thống pháp luật và có điều khoản chuyển tiếp để kế thừa tối đa quy hoạch đã lập trước đó.Kết luận phiên họp, Phó chủ tịch Quốc hội Vũ Hồng Thanh đề nghị phân biệt rõ giữa quy hoạch chiến lược, dài hạn phục vụ hoạch định chính sách và quy hoạch mang tính công cụ quản lý hành chính để làm dự án đầu tư.\nÔng cũng lưu ý cơ quan soạn thảo giải trình rõ tính hợp lý khi đưa quy hoạch ngành vào hệ thống quy hoạch quốc gia, tránh hình thức và chồng chéo.\nDự án Luật Quy hoạch (sửa đổi) dự kiến trình Quốc hội xem xét, thông qua tại kỳ họp tháng 10.', 'Ông Lê Tấn Tới - Chủ nhiệm Ủy ban Quốc phòng, An ninh và Đối ngoại cho rằng việc xử lý mâu thuẫn giữa quy hoạch đất quốc phòng, an ninh với phát triển kinh tế, đầu tư "tốn kém, mất thời gian".', @cat_id_moi_nhat, @user_id_phuongdung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("'Miền Bắc cần thay đổi tư duy từ chống lũ sang sống chung với lũ'", 'mien-bac-can-thay-doi-tu-duy-tu-chong-lu-sang-song-chung-voi-lu', 'https://i1-vnexpress.vnecdn.net/2025/10/13/550572306962309104-1760335144-1421-6380-1760335283.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=5_yGqt55kxs_zMz012NeOQ', 'TS Lương Hữu Dũng, Giám đốc Trung tâm Nghiên cứu Thủy văn và Hải văn (Viện Khoa học Khí tượng Thủy văn và Biến đổi khí hậu) trả lời VnExpress về những bất thường của đợt mưa lũ do bão Matmo và đề xuất giải pháp ứng phó.\n- Ông đánh giá thế nào về đợt mưa lũ do bão Matmo?\n- Mưa do bão Matmo tập trung từ ngày 6 đến 8/10, khá bất thường. Thông thường mưa lũ lớn ở miền Bắc chủ yếu xuất hiện vào tháng 8 hoặc đầu tháng 9, năm nay lại muộn tới 10. Trong đợt này cơ quan khí tượng đã ghi nhận 20 kỷ lục về mưa, tập trung ở Tuyên Quang, Thái Nguyên, Lạng Sơn và Bắc Ninh.\nTrong đó, Bắc Kạn (Thái Nguyên) mưa ngày 7/10 tới 201 mm, gần gấp đôi quá khứ, phá vỡ kỷ lục mưa tồn tại 61 năm ở địa bàn này. Cùng ngày 7/10, Thái Nguyên mưa đến 491 mm, cao hơn nhiều mức 201 mm năm 1978, thậm chí cao hơn cả tổng lượng mưa tháng 10/1964. Tại Bắc Giang (Bắc Ninh), mưa ngày 7/10 tới 365 mm, gấp ba lần kỷ lục 120 mm vào năm 2016.\nMưa lớn diện rộng khiến lũ lên rất nhanh, phá vỡ nhiều kỷ lục được xác lập trước đó. Lũ sông Cầu tại Gia Bảy (Thái Nguyên) ngày 8/10 đã vượt lũ lịch sử 1,09 m; sông Thương tại Hữu Lũng (Lạng Sơn) vượt lịch sử 1,77 m. Lũ sông Bằng tại Bằng Giang (Cao Bằng) cũng vượt lịch sử 0,58 m.\nHai năm liên tiếp 2024-2025 miền Bắc xảy ra mưa lũ lớn, nhiều nơi đạt và vượt mức lịch sử. Đây là điều rất hiếm gặp, cho thấy tính cực đoan của thời tiết ngày càng rõ rệt và cần được quan tâm đúng mức.\n- Vì sao lũ chỉ tập trung ở bốn tỉnh Thái Nguyên, Lạng Sơn, Cao Bằng và Bắc Ninh?\n- Trận mưa lũ lịch sử tháng 10/2025 là kết quả của hình thái thời tiết cực đoan hiếm thấy. Sau khi bão Matmo suy yếu thành vùng áp thấp trên đất liền, hoàn lưu vẫn còn rất mạnh và đã tương tác với một vùng hội tụ gió hình thành ở độ cao khoảng 1.500 đến 5.000 m.\nSự kết hợp này tạo nên tổ hợp đặc biệt nguy hiểm gồm hoàn lưu bão mang lượng hơi ẩm khổng lồ từ biển vào, trong khi vùng hội tụ gió đóng vai trò như "cỗ máy" nâng toàn bộ khối ẩm đó lên cao, gây ngưng tụ mạnh và hình thành những trận mưa cực lớn.\nĐiều không may là tâm mưa chủ yếu ở các tỉnh Thái Nguyên, Lạng Sơn, Bắc Ninh và Cao Bằng - nơi địa hình trung du và đồng bằng xen kẽ, hệ thống sông suối dày đặc nhưng khả năng thoát lũ hạn chế. Chính sự trùng hợp này đã tạo ra ngập lụt nghiêm trọng.\n- Đã dừng mưa 6 ngày nhưng hôm nay lũ sông Cầu, sông Thương ở Bắc Ninh vẫn còn ở báo động 1-2, nhiều xã ven sông ở Bắc Ninh, Hà Nội vẫn còn ngập. Theo ông, vì sao ngập lụt kéo dài như vậy?\n- Đầu tiên phải nói Bắc Ninh, Hà Nội nằm ở hạ lưu, nhận nước từ các tỉnh thượng nguồn như Thái Nguyên, Bắc Giang, Lạng Sơn đổ về qua hệ thống sông Cầu, sông Thương. Mưa đã ngừng, nhưng lượng nước khổng lồ vẫn tiếp tục đổ xuống vùng hạ du khiến mực nước duy trì ở mức cao.\nTrước đó, Bắc Ninh và Hà Nội đã no nước vì mưa lớn kéo dài, khiến đất bão hòa, hệ thống tiêu thoát bị quá tải. Lượng nước tràn vào sông, vùng trũng thấp và khu dân cư không kịp thoát ra ngoài.\nNgoài ra, địa hình vùng này bằng phẳng, sông ngòi quanh co uốn khúc, không gian thoát lũ nhiều nơi bị lấn chiếm nên dòng chảy bị cản trở. Độ dốc nhỏ khiến khả năng thoát lũ yếu, tốc độ truyền lũ chậm hơn bình thường. Tất cả những yếu tố này khiến nước rút rất chậm dù trời đã tạnh mưa nhiều ngày.\n- Lũ sông Cầu, sông Thương rút sẽ tác động thế nào đến đồng bằng Bắc Bộ, trong đó có Hà Nội?\n-Sông Cầu, sông Thương và sông Lục Nam là ba sông chính hợp thành hệ thống sông Thái Bình. Hệ thống này lại kết hợp với sông Đuống, vốn có nhiệm vụ phân nước từ sông Hồng nên khi các sông cùng có lũ lớn, sẽ tác động đáng kể đến đồng bằng Bắc Bộ, đặc biệt là các tỉnh thành vùng hạ lưu như Bắc Ninh, Hải Dương cũ, Hưng Yên và Hải Phòng.\nTuy nhiên, nếu xét riêng thì đóng góp của lũ sông Cầu cho toàn vùng đồng bằng Bắc Bộ không lớn. Đợt lũ vừa qua cho thấy phạm vi ảnh hưởng trực tiếp chủ yếu là các tỉnh Thái Nguyên, Bắc Ninh, Lạng Sơn và một phần nhỏ lưu vực sông Cà Lồ thuộc Hà Nội.\n- Theo ông, phải làm gì trong bối cảnh mưa lũ liên tiếp, lần sau cao hơn lần trước, diện ngập lụt mở rộng tác động tới cả chục triệu người ở miền Bắc?\n-Tôi cho rằng việc phòng chống và quản lý lũ lụt, tức công tác trị thủy, cần thay đổi cách nghĩ và cách làm. Bởi vì lượng mưa và mức độ lũ những năm gần đây đã vượt xa khả năng chống đỡ của con người cũng như các công trình hiện có.\nThay vì chỉ cố chống lũ, chúng ta cần chuyển sang tư duy chủ động phòng tránh, thích ứng và sống chung với lũ, đặc biệt ở những nơi không có hồ chứa lớn hoặc hệ thống đê điều kiên cố đủ cao.\nTrước đây, khái niệm "sống chung với lũ" thường chỉ nói đến Đồng bằng sông Cửu Long hay miền Trung là phòng tránh chủ động. Nhưng với những gì đang diễn ra ở miền Bắc trong những năm gần đây, rõ ràng cách nghĩ đó cần thay đổi.\n-Vì sao cần thay đổi theo hướng sống chung với lũ trong khi có thể đầu tư kiên cố, nâng chiều cao của đê?\n- Hệ thống đê điều ở miền Bắc đã được xây dựng từ hàng chục đến hàng trăm năm trước. Mục đích chính là chống lại các trận lũ thường xảy ra với tần suất tương đối cao, khoảng từ 1% đến dưới 10% (tức là trung bình 10-100 năm mới có một trận lũ lớn). Riêng một số tuyến đê đặc biệt có khả năng chống được cả những trận lũ rất hiếm gặp, chỉ xảy ra dưới 1%.\nTuy nhiên, hiện nay hệ thống đê không còn đủ sức bảo vệ đối với các trận mưa lũ đặc biệt lớn. Việc gia cố, nâng cấp là cần thiết, nhưng ta cũng không thể cứ xây đê cao mãi hay tôn cao, hay thay đổi hoàn toàn quy hoạch các khu đô thị đã có. Vì vậy, sống chung chủ động với lũ trong điều kiện mới là hướng đi cần xem xét.\nVà vấn đề là giải pháp ứng phó theo sự kiện cực đoan và thích ứng lâu dài như thế nào? Tôi chia làm hai nhóm giải pháp, đầu tiên là thường xuyên tu sửa, củng cố hệ thống đê điều; nạo vét sông ngòi; mở rộng hệ thống thoát nước đô thị; ngăn chặn việc lấn chiếm lòng sông, hành lang thoát lũ. Ngoài ra, cần quy hoạch thêm các khu vực có thể chứa, trữ hoặc phân lũ theo đặc thù từng lưu vực sông để giảm áp lực cho hệ thống chính.\nVới giải pháp phi công trình thì nên trồng thêm rừng ở đầu nguồn; quy hoạch sử dụng đất hợp lý; giữ gìn các vùng thoát lũ tự nhiên; và điều chỉnh hay xây dựng các tiêu chuẩn thiết kế dựa trên những thay đổi mới giữa lượng mưa lũ cực lớn và sức chịu đựng của hệ thống đê, hồ, trạm bơm.\nSong song với đó, cần tăng cường dự báo, cảnh báo mưa lũ, ngập lụt, chuẩn bị sẵn kịch bản ứng phó khẩn cấp như sơ tán dân, cứu hộ, cứu trợ. Trong đó, ứng dụng công nghệ hiện đại và làm chủ công nghệ trong dự báo, cảnh báo mưa lũ và quản lý lũ là quan trọng, không thể thiếu. Bởi trong bối cảnh biến đổi khí hậu, chỉ khi kết hợp khoa học, công nghệ, quản lý đồng bộ, chúng ta mới có thể giảm thiểu thiệt hại cho người dân và nền kinh tế.', 'Trong bối cảnh mưa lũ cực đoan, TS Lương Hữu Dũng, chuyên gia thủy văn, cho rằng miền Bắc cần chuyển từ chống lũ sang thích ứng, sống chung với lũ.', @cat_id_thoi_su, @user_id_giachinh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Hải Phòng ngập diện rộng sau cơn mưa 4 tiếng', 'hai-phong-ngap-dien-rong-sau-con-mua-4-tieng', 'https://i1-vnexpress.vnecdn.net/2025/10/14/tranhungdao-1760410109-7908-1760410334.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=MJTw3o9wjmuy7J2wOzOqsg', 'Từ 5h, TP Hải Phòng bắt đầu mưa lớn. Nước không kịp tiêu thoát gây ngập hàng loạt tuyến đường như: Cầu Đất, Trần Hưng Đạo, Lương Khánh Thiện, Đà Nẵng, Hoàng Diệu, Hàng Kênh, Văn Cao, Tô Hiệu...\nNgập sâu nhất 0,5 m là đoạn đường Tam Bạc (dưới gầm cầu Lạc Long), ngã tư Nguyễn Tri Phương - Hoàng Văn Thụ (đoạn trước cửa Bưu điện trung tâm thành phố), đường Cầu Đất. Một số phương tiện bị chết máy trên đường ngập.\nDo thủy triều đang lên cao (đạt đỉnh lúc 10h) nên nước không thể rút ra biển, ngược lại còn tràn vào nhà dân, cửa hàng. Cảnh sát giao thông và đơn vị thoát nước cử người ra các điểm ngập để điều tiết giao thông, đặt biển cảnh báo.\nLý giải tình trạng ngập, Sở Xây dựng TP Hải Phòng cho biết hệ thống thoát nước mưa đô thị được xây dựng từ thời Pháp, trải qua nhiều thời kỳ đã được nâng cấp nhưng còn chắp vá, phục vụ chủ yếu cho các quận trung tâm.\nHệ thống thoát nước ngoại thành chưa được đầu tư hoàn chỉnh và còn chưa được quy hoạch. Vì vậy khi mưa trên 50 mm/24 giờ, thành phố sẽ ngập diện rộng, trong đó có 16 điểm đen ngập sâu, nước rút chậm.\nTheo Trung tâm Dự báo Khí tượng Thủy văn quốc gia, ảnh hưởng của không khí lạnh yếu kết hợp với gió đông từ hoàn lưu áp cao cận nhiệt đới lấn sang, từ ngày 13 đến 15/10, miền Bắc, đặc biệt là Đông Bắc Bộ, có mưa.\nLượng mưa ghi nhận từ 19h hôm qua đến 7h sáng nay ở Đông Bắc Bộ phổ biến 20-40 mm, riêng Hưng Yên mưa rất to. Một số trạm mưa lớn hơn như: Phình Hồ (Quảng Ninh) 123 mm; Thái Thụy (Hưng Yên) 169 mm; Kiến Xương (Hưng Yên) 159 mm; Thái Ninh (Hưng Yên) 157 mm.\nDự báo từ nay đến hết ngày mai, đồng bằng Bắc Bộ mưa 30-70 mm, cục bộ có nơi mưa to trên 130 mm; trung du và vùng núi phía Đông Bắc Bộ có mưa rào và giông với lượng phổ biến 20-40 mm, cục bộ trên 80 mm.', 'Cơn mưa lớn kéo dài 4 tiếng chưa dứt cùng với triều cường khiến nhiều tuyến phố, khu đô thị ở TP Hải Phòng ngập từ 0,3 đến 0,5 m, sáng 14/10.', @cat_id_thoi_su_chinh_tri, @user_id_letan, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Ông Lê Quốc Phong làm Phó bí thư thường trực Thành ủy TP HCM', 'ng-le-quoc-phong-lam-pho-bi-thu-thuong-truc-thanh-uy-tp-hcm', 'https://i1-vnexpress.vnecdn.net/2025/10/14/z7114455297962-f6b6720ce972a29-9350-7381-1760412655.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=uejIdZ341NIH3yTP6QnCYA', 'Quyết định nhân sự của Bộ Chính trị được ông Nguyễn Quang Dương, Phó ban Tổ chức Trung ương công bố sáng 14/10, tại Đại hội Đại biểu Đảng bộ TP HCM lần thứ nhất. Dự đại hội có 546 đại biểu đại diện hơn 366.000 đảng viên.\nTân Phó bí thư thường trực Thành ủy TP HCM quê Hà Nội, là người trưởng thành từ hoạt động Đoàn khi lần lượt giữ chức Phó bí thư Thành đoàn, Chủ tịch Hội Sinh viên TP HCM, Phó chủ tịch, Chủ tịch Hội Sinh viên Việt Nam. Tháng 1/2016, ông Phong được bầu làm Ủy viên dự khuyết Trung ương Đảng. Ba tháng sau, ông giữ chức Bí thư thứ nhất Trung ương Đoàn.\nĐầu tháng 10/2020, ông Phong, khi đó 42 tuổi, được Bộ Chính trị điều động và giới thiệu để bầu Bí thư Tỉnh ủy Đồng Tháp nhiệm kỳ 2020-2025, thay người tiền nhiệm Lê Minh Hoan. Thời điểm đó, ông là Bí thư tỉnh ủy trẻ nhất nước. Ngày 1/7, khi tỉnh này sáp nhập với Tiền Giang thành Đồng Tháp mới, ông tiếp tục giữ vị trí này đến ngày 1/10.\n5 năm làm Bí thư Đồng Tháp, ông Phong để lại dấu ấn là lãnh đạo trẻ, năng động, sâu sát với cơ sở và thúc đẩy nhiều chương trình khởi nghiệp. Đồng Tháp là tỉnh có số sản phẩm OCOP nhiều nhất cả nước. Chỉ số năng lực cạnh tranh cấp tỉnh (PCI) được duy trì ở mức cao.\nKinh tế tư nhân từng bước trở thành động lực quan trọng trong phát triển kinh tế của tỉnh, có 7.670 doanh nghiệp thành lập mới, tăng 1.637 doanh nghiệp (tăng 27,1%) so với giai đoạn 2015 - 2020. Hoạt động khởi nghiệp của tỉnh tiếp tục phát triển với nhiều hoạt động hỗ trợ, duy trìDiễn đàn Mekong Startup, được Chương trình khởi nghiệp quốc gia chứng nhận danh hiệuĐịa phương tiêu biểu xây dựng hệ sinh thái khởi nghiệp năm 2024...\nNhư vậy, thường trực Thành ủy TP HCM hiện có 7 người: Bí thư Trần Lưu Quang; 6 Phó bí thư là các ông: Lê Quốc Phong (thường trực), Nguyễn Văn Được (Chủ tịch UBND thành phố), Võ Văn Minh (Chủ tịch HĐND thành phố), Nguyễn Phước Lộc (Chủ tịch Ủy ban MTTQ Việt Nam thành phố), Đặng Minh Thông và bà Văn Thị Bạch Tuyết.', 'Ông Lê Quốc Phong, 47 tuổi, nguyên Bí thư Tỉnh ủy Đồng Tháp được Bộ Chính trị chỉ định làm Phó bí thư thường trực Thành ủy TP HCM, nhiệm kỳ 2025-2030.', @cat_id_thoi_su_giao_thong, @user_id_letuyet, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Đường phố Hà Nội tắc cứng trong mưa', 'uong-pho-ha-noi-tac-cung-trong-mua', 'https://i1-vnexpress.vnecdn.net/2025/10/14/trungkinh-jpeg-1760412974-4767-1760413053.jpg?w=1200&h=0&q=100&dpr=1&fit=crop&s=ikLK0BwnGuusEuXnPagd3Q', 'Trung tâm Dự báo khí tượng thủy văn Quốc gia, ghi nhận đêm qua và sáng nay một số nơi ở Bắc Bộ có mưa lớn như Mỹ Lộc (Hưng Yên) 191 mm; Vĩnh Bảo (Hải Phòng) 139 mm, Phình Hồ (Quảng Ninh) 128 mm, Văn Lý (Ninh Bình) 114 mm.\nRiêng Hà Nội dự báo từ sáng nay đến hết ngày 15/10 có mưa, mưa vừa và giông với lượng mưa 30-60 mm, cục bộ có nơi mưa to trên 90 mm. Trong mưa giông có khả năng xảy ra lốc, sét, mưa đá và gió giật mạnh.\nNút giao Mai Dịch đoạn qua cầu vượt gần 10h sáng một số đoạn ùn ứ do phương tiện đổ về đông, xe cộ di chuyển chậm trên đường Hồ Tùng Mậu. Nút giao kết nối đường Hồ Tùng Mậu - Phạm Văn Đồng - Xuân Thủy - Phạm Hùng ngày thường ghi nhận lưu lượng xe lớn, ngày mưa thường xảy ra ùn tắc. Khu vực này tập trung nhiều công sở trên các phố Duy Tân, Cầu Giấy, cùng hàng loạt trường đại học như Thương Mại, Sư phạm Hà Nội, Đại học Quốc gia Hà Nội và Học viện Báo chí và Tuyên Truyền.\nRời nhà từ sớm, nhiều người vẫn chưa thể tới cơ quan khi ùn tắc khắp mọi ngã ba, ngã tư đường phố. Chị Đỗ Thuỳ Linh rời nhà trên phố Nguyễn Khánh Toàn từ 7h30, đến 8h34 mới tới cơ quan trên phố Khâm Thiên, chậm 4 phút so với giờ chấm công. "Thế là đánh rơi một ngày công trên đường vì mưa với tắc", chị ngao ngán.\nĐường Lê Văn Lương hướng vào nội đô ùn tắc cục bộ từ hầm chui đến các nút giao, ngã tư đèn đỏ. Cảnh sát giao thông túc trực phân làn, hướng dẫn các phương tiện di chuyển trong mưa lớn.\nDi chuyển từ Vĩnh Tuy sang Cầu Giấy, chị Thu Hằng cho biết hiện tại "đứng im" trên phố Nguyễn Ngọc Vũ, không thể quay xe hướng khác vì khắp nơi đều tắc. "Giao thông rất hỗn loạn", chị cho hay.\nTương tự trên phố Kim Mã, dòng người nhích từng chút một, "40 phút đi được một km" do đường hẹp lại qua đoạn rào chắn xây dựng ga metro.\nTrên đường 70 qua Bệnh viện K Tân Triều, các phương tiện ùn ứ từ 7h.\nĐường Võ Chí Công hướng về Vành đai 2.5, Hoàng Quốc Việt, Bưởi, ùn ứ cả hai chiều. Các phương tiện di chuyển chậm chạp.\nMưa tắc giờ đi làm buổi sáng khiến hàng nghìn người chật vật tìm lối thoát.', 'Sáng 14/10, mưa rải rác 10-20 mm tại các phường nội đô khiến nhiều tuyến đường Hà Nội ùn tắc nghiêm trọng dù không xảy ra ngập úng.', @cat_id_thoi_su_phap_luat, @user_id_vnexpress, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Hàng trăm người tìm kiếm ba bố con nghi mất tích ở sông Lam', 'hang-tram-nguoi-tim-kiem-ba-bo-con-nghi-mat-tich-o-song-lam', 'https://i1-vnexpress.vnecdn.net/2025/10/14/TIM-Kiem-3-nan-nhan-1760408058-3101-1760408251.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=wfe6tbvRC-HO4gElcQDJbw', 'Khoảng 22h ngày 13/10, người dân đi qua cầu Bến Thủy, nối hai tỉnh Nghệ An và Hà Tĩnh, phát hiện xe máy biển Nghệ An cùng hai cặp sách học sinh để gần lan can cầu nên trình báo cơ quan chức năng.\nNhà chức trách bước đầu xác định người để lại xe là anh Võ Văn Dũng, 32 tuổi, trú xã Kim Liên. Hai con anh Dũng 4-5 tuổi, hiện gia đình không liên lạc được.\nTừ khuya 13/10 đến sáng 14/10, hàng trăm người cứu hộ của Công an tỉnh Nghệ An cùng chính quyền địa phương và người dân, chia thành nhiều tổ tìm kiếm dọc sông Lam và mở rộng về khu vực hạ lưu, song chưa có kết quả.\nĐoạn sông Lam chảy qua cầu Bến Thủy rộng hơn 300 m, sâu trung bình 8-10 m, có nơi tới 15 m. Sáng nay khu vực này mưa nhỏ.', 'Lực lượng cứu hộ của Công an Nghệ An cùng chính quyền địa phương và người dân chia thành nhiều tổ đi dọc sông Lam tìm kiếm ba bố con nghi mất tích.', @cat_id_thoi_su, @user_id_uchung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Khánh thành loạt công trình vệ sinh cho học sinh Quảng Trị', 'khanh-thanh-loat-cong-trinh-ve-sinh-cho-hoc-sinh-quang-tri', 'https://i1-vnexpress.vnecdn.net/2025/10/13/IMG-4900-1760357523-8301-1760358655.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=aEg81b5v8ZdVSgTGi_7c4A', 'Công trình vệ sinh phục vụ hơn 450 học sinh, với 12 lớp, thay thế nhà vệ sinh xây dựng hơn 10 năm trước đã hư hỏng.\nThầy Trần Thanh Linh, hiệu trưởng nhà trường cho biết, trường có số lượng học sinh lớn, nhiều lớp nhưng quá trình đầu tư xây dựng không có nhà vệ sinh. Công trình cũ trước đây được một tổ chức tài trợ nhưng xuống cấp, không thể sử dụng.\nCông trình mới đã giúp học sinh có chỗ vệ sinh sạch sẽ. Nhà vệ sinh chia phân khu nam - nữ với lối đi riêng, trang bị đầy đủ bệ xí, bồn rửa tay, hệ thống nước cấp - xả, hệ thống tự hủy, các biển bảng hướng dẫn sử dụng nhà vệ sinh và rửa tay đúng cách.\nNhân dịp này, Opella Việt Nam trao tặng sách tô màu lồng ghép các nội dung hướng dẫn giữ gìn vệ sinh, sức khỏe cho các học trò Trường THCS số 2 Hưng Trạch.\nCông trình tại Trường THCS số 2 Hưng Trạch, là một trong 21 công trình vệ sinh, nhà tắm khởi công xây dựng vào tháng 5 (huyện Bố Trạch cũ) và hoàn thiện trong tháng 9 - 10, thuộc dự án Vệ sinh học đường do quỹ Hy vọng triển khai tại Quảng Trị với nguồn tài trợ của Opella Việt Nam, nhãn hàng Enterogermina.\nDự án cũng nhận được sự đồng hành của FPT Long Châu và tư vấn xây dựng, thiết kế từ Tổng Công ty Tư vấn Xây dựng Việt Nam cùng sự chung tay của độc giả báoVnExpress. Tổng kinh phí dự án hơn 4,95 tỷ đồng, trong đó các đơn vị tài trợ chung tay 4,2 tỷ đồng, còn lại do địa phương đối ứng.\nBên cạnh xây dựng các công trình vệ sinh theo đúng chuẩn của Bộ Giáo dục và Đào tạo, dự án còn thực hiện các hoạt động nâng cao ý thức giữ gìn, bảo quản vệ sinh chung; hướng dẫn thực hành vệ sinh học đường thông qua các bộ tài liệu dành cho học sinh, giáo viên; tổ chức Ngày hội vệ sinh học đường. Ước tính, 7.500 học sinh, giáo viên được thụ hưởng từ công trình vệ sinh, nhà tắm sạch, đẹp, an toàn của dự án.\nNhững công trình vệ sinh đạt chuẩn, hệ thống nước sạch và các bộ tài liệu thực hành vệ sinh của dự án sau khi được chuyển giao, địa phương có thể nhân rộng, đồng bộ tại các điểm trường khác trong vùng. Từ nhà trường, dự án mong muốn tác động thay đổi thói quen vệ sinh đến gia đình và cộng đồng xung quanh.\nĐây là năm thứ 4, Opella Việt Nam đồng hành cùng dự án Vệ sinh học đường, nhằm cải thiện chất lượng sống và xây dựng môi trường học tập tốt đẹp hơn cho các em nhỏ. Tổng cộng 80 công trình vệ sinh đã được đơn vị này tài trợ tại Sơn La, Hà Giang, Yên Bái, Lai Châu và Quảng Trị. Dự kiến hết 2025, Quỹ Hy vọng xây mới được 270 công trình vệ sinh, góp phần nâng cao sức khỏe cho gần 65.000 học sinh trên cả nước.\nTrong quá trình triển khai, Quỹ Hy vọng còn thực hiện đánh giá về kết quả dự án Vệ sinh học đường tại các nơi đã bàn giao. Theo kết quả khảo sát 2024, 94% giáo viên chia sẻ nhà vệ sinh mới giúp giảm số lần đi khám vì tiêu chảy, đau dạ dày của học sinh. 91% giáo viên cho biết nhà vệ sinh mới và những thay đổi về thói quen vệ sinh giúp học sinh thích đến trường hơn. Hệ thống nhà vệ sinh đạt chuẩn cùng các tài liệu tập huấn đã trở thành mô hình để địa phương nhân rộng. Ghi nhận từ Phòng Giáo dục và Đào tạo các huyện Vân Hồ, Đồng Văn, Mù Cang Chải cũ, tỷ lệ nhà vệ sinh đạt chuẩn tại địa phương tăng 10-19% sau khi dự án được thực hiện.', 'Nhà vệ sinh có hai phân khu nam nữ, với 12 khoang được bàn giao cho Trường THCS số 2 Hưng Trạch, xã Bố Trạch, chiều 13/10.', @cat_id_thoi_su_chinh_tri, @user_id_acthanh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Thủ tướng đồng ý nghỉ Tết Bính Ngọ 9 ngày', 'thu-tuong-dong-y-nghi-tet-binh-ngo-9-ngay', 'https://i1-vnexpress.vnecdn.net/2025/10/14/te-t-a-m-li-ch-1760409533-1760-7228-9577-1760409560.png?w=680&h=0&q=100&dpr=1&fit=crop&s=ObuaNmUaeD6Ea8H6SGbt4g', 'Văn phòng Chính phủ hôm nay cho biết Thủ tướng Phạm Minh Chính đồng ý với đề xuất của Bộ Nội vụ nghỉ Tết Âm lịch 9 ngày.\nTheo đó, công chức, người lao động sẽ nghỉ 5 ngày theo quy định (một ngày trước và bốn ngày sau Tết), cộng thêm bốn ngày cuối tuần trước và sau, tổng cộng 9 ngày. Công chức, người lao động trở lại làm việc ngày 23/2, tức mùng 7 Tết.\nThủ tướng giao Bộ trưởng Nội vụ thông báo cho các cơ quan, doanh nghiệp, người lao động. Các đơn vị sắp xếp bộ phận ứng trực, làm việc hợp lý để giải quyết công việc liên tục, phục vụ người dân. Bộ ngành, địa phương đảm bảo cung cầu hàng hóa, ổn định giá cả, thị trường, thúc đẩy tăng trưởng kinh tế, thực hành tiết kiệm, chống lãng phí trong thời gian nghỉ Tết.\nĐây là năm thứ hai liên tiếp kỳ nghỉ Tết kéo dài 9 ngày nhờ liền kề cuối tuần. Kỳ nghỉ dài tạo thuận lợi cho người dân bố trí đi lại, mua sắm và giảm tải giao thông cuối năm.\nViệt Nam có 11 ngày nghỉ lễ chính thức mỗi năm, ít hơn mức trung bình của các nước Đông Nam Á. Lịch nghỉ gồm: Tết Dương lịch một ngày, Tết Âm lịch 5 ngày, Giỗ Tổ Hùng Vương một ngày, dịp 30/4 và 1/5 hai ngày, Quốc khánh hai ngày. Từ năm 2021, Quốc khánh được bổ sung một ngày nghỉ và Chính phủ quyết định nghỉ 1/9 hoặc 3/9 tùy năm. Người nước ngoài làm việc tại Việt Nam được nghỉ thêm ngày Tết cổ truyền và Quốc khánh của nước họ.', 'Tết Âm lịch 2026 sẽ kéo dài 9 ngày từ 14/2 đến hết 22/2, tức 27 tháng chạp năm Ất Tỵ đến hết mùng 6 tháng giêng năm Bính Ngọ.', @cat_id_thoi_su_giao_thong, @user_id_hongchieu, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Bắc Ninh cần 500 tỷ đồng khắc phục đê điều', 'bac-ninh-can-500-ty-dong-khac-phuc-de-dieu', 'https://i1-vnexpress.vnecdn.net/2025/10/12/cambao-jpeg-1760273620-4993-1760274031.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=cHgo6OPyw5IQZBzqnM5q1g', 'Ngày 12/10, Sở Nông nghiệp và Môi trường tỉnh Bắc Ninh gửi báo cáo kiến nghị Trung ương hỗ trợ 755 tỷ đồng khắc phục hậu quả mưa lũ do bão Matmo, trong đó 500 tỷ đồng dành cho đê điều, thủy lợi và 255 tỷ hỗ trợ sản xuất nông nghiệp.\nHệ thống đê điều, công trình thủy lợi của Bắc Ninh đã chống chọi với lũ đặc biệt lớn trên sông Cầu, sông Thương khiến tuyến đê cấp III trở lên xuất hiện hàng trăm sự cố. Trong đó có 12 sự cố sạt trượt, xói lở; 52 sự cố về mạch đùn mạch sủi; 64 đoạn bị thẩm lậu, rò rỉ nước; 35 đoạn đê phải đắp trạch chống tràn; 11 sự cố về cống. Các tuyến đê dưới cấp III xuất hiện 14 sự cố.\nTổng chiều dài bị sạt trượt, rò rỉ khoảng 29 km; tràn mặt đê khoảng 20 km (70% tuyến đê bối thuộc Bắc Giang cũ bị tràn và vỡ). Đê bối, đê bao là đê nhỏ, thấp hơn và nằm ngoài tuyến đê sông chính, nhiệm vụ bảo vệ bãi bồi, bãi nổi, vùng dân sinh hoặc đất nông nghiệp ven sông.\n"Hệ thống đê điều về cơ bản vẫn ổn định song một số vị trí sạt trượt sẽ phải đầu tư nâng cấp. Khắc phục toàn tuyến mất khoảng 2 năm, riêng đắp lại mất một năm", ông Đặng Công Hưởng, Phó giám đốc Sở Nông nghiệp và Môi trường Bắc Ninh, cho hay.\nXã Tiên Lục (huyện Lạng Giang cũ) nằm ven bờ sông Thương - nơi có 60 thôn với gần 60.000 dân cư, ghi nhận hàng loạt vị trí sụt lở đê tại các thôn Ngoài, Đụn, Sỏi với chiều dài 10-30 m. Nước tràn đê tại các thôn Tây, Dinh, Trung Phố, Bờ Lở với chiều dài 30-450 m khiến hàng trăm ngôi nhà bị ngập. Dự kiến 2-4 ngày tới nước mới rút hết.\nXã Mỹ Thái rạng sáng 12/10 xảy ra sự cố vỡ 5 m đê bối cấp V thuộc thôn Hồng Giang. Ngoài ra, hàng loạt vị trí bị sạt trượt tại thôn Cổng Phên, Thuận, Đầm Nùng. Chính quyền và người dân phải đắp chống tràn đê Đò Mom ở xóm Lẻ khoảng 20 m, đê cấp III đoạn Dương Quan Thượng khoảng 800 m, đê Chi Lễ khoảng 1.200 m. Hiện lũ đã rút xuống báo động hai, nhưng vẫn còn hơn 2.000 hộ thuộc 10 thôn ngập 0,5-1,5 m, dự kiến 2-4 ngày tới nước mới rút hết.\nĐợt lũ đặc biệt lớn trên sông Thương, sông Cầu khiến 153 thôn xóm, tổ dân phố thuộc 20 xã, phường với 15.000 hộ dân phải di dời và bị cô lập. Trên 300 tuyến quốc lộ, tỉnh lộ bị ách tắc, sạt lở. Mưa lũ làm 5 người chết, trong đó Mỹ Thái và Xuân Lương mỗi xã 2 người, Đồng Kỳ 1 người; 240 điểm trường, 60 trạm y tế bị ngập sâu. Sở Nông nghiệp và Môi trường ước tính tổng thiệt hại 1.670 tỷ đồng.', 'Mưa lũ gây ra hàng trăm sự cố trên các tuyến đê cấp III trở lên, dự kiến kinh phí khắc phục khoảng 500 tỷ đồng, theo Sở Nông nghiệp và Môi trường tỉnh Bắc Ninh.', @cat_id_thoi_su_phap_luat, @user_id_hongchieu, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Khánh thành nhà máy điện rác lớn nhất Việt Nam', 'khanh-thanh-nha-may-dien-rac-lon-nhat-viet-nam', 'https://i1-vnexpress.vnecdn.net/2025/10/13/nha-may-1760356756-1760356802-8708-1760356810.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=t_DSWLzH-iZ_hep8J9joow', 'Ngày 13/10, tại xã Trung Giã, UBND TP Hà Nội tổ chức khánh thành Nhà máy Điện rác Sóc Sơn. Công trình được khởi công từ tháng 8/2019, sử dụng công nghệ lò đốt ghi cơ học kiểu Waterleau của Bỉ, đảm bảo tiêu chuẩn môi trường châu Âu và Việt Nam, giúp xử lý triệt để rác thải sinh hoạt mà không cần phân loại đầu vào.\nPhát biểu tại lễ khánh thành, Chủ tịch UBND TP Hà Nội Trần Sỹ Thanh khẳng định việc đưa Nhà máy Điện rác Sóc Sơn vào vận hành có ý nghĩa đặc biệt quan trọng không chỉ trong công tác bảo vệ môi trường mà còn trong chiến lược phát triển bền vững của Thủ đô. Nhiều năm qua phần lớn rác thải của Hà Nội được xử lý bằng chôn lấp, không còn phù hợp với yêu cầu phát triển xanh.\nChủ tịch thành phố Hà Nội đề nghị chủ đầu tư Nhà máy Điện rác Sóc Sơn (doanh nghiệp Trung Quốc) khẩn trương triển khai giai đoạn hai của dự án, với công suất 1.600 tấn/ngày đêm, nhằm xử lý toàn bộ lượng rác moi từ các bãi chôn lấp cũ trong Khu liên hợp xử lý chất thải Sóc Sơn.\n"Đây là việc làm cần thiết, cấp bách để xử lý tận gốc, triệt để vấn đề ô nhiễm tồn lưu nhiều năm. Dự kiến quá trình này sẽ kéo dài từ 5 đến 7 năm, nhưng chúng ta cần bắt tay ngay, để từng ngày từng bước mang lại không khí trong lành, môi trường sống an toàn cho người dân Thủ đô", Chủ tịch Trần Sỹ Thanh nói.\nHiện mỗi ngày Hà Nội phát sinh hơn 7.600 tấn rác thải sinh hoạt, chủ yếu được đưa về xử lý tại hai nhà máy điện rác Sóc Sơn (Trung Giã) và Seraphin (Tùng Thiện), số ít còn lại xử lý tại các nhà máy nhỏ lẻ hoặc chôn lấp hợp vệ sinh. Nhà máy Điện rác Sóc Sơn có công suất tiếp nhận, xử lý 5.000 tấn rác sinh hoạt mỗi ngày, phát điện 90 MW. Nhà máy Điện rác Seraphin công suất xử lý 2.250 tấn/ngày, phát điện 37 MW điện sạch.', 'Hà NộiNhà máy điện rác Sóc Sơn có công suất tiếp nhận, xử lý 5.000 tấn rác sinh hoạt mỗi ngày, phát điện 90 MW, là nhà máy điện rác lớn nhất Việt Nam.', @cat_id_thoi_su, @user_id_vohai, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Bắt được cá sấu 80 kg nghi tấn công người ở Khánh Hòa', 'bat-duoc-ca-sau-80-kg-nghi-tan-cong-nguoi-o-khanh-hoa', 'https://i1-vnexpress.vnecdn.net/2025/10/13/z7112108156742-d2d44d94baca5de-3257-8950-1760347908.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=IVw2k8F0zutKsrULT6lbYA', 'Chiều 13/10, lãnh đạo UBND xã Diên Lâm cho biết, cá sấu được phát hiện và bắt giữ lúc 14h30 tại khu vực sông Chò. Vị trí này cách điểm người dân bị cá cắn khoảng một km. Con vật sẽ được bàn giao cho kiểm lâm, cơ quan chức năng đang xác minh nguồn gốc.\nNăm ngày trước, nam thanh niên đến khu vực gần cầu Suối Sâu B (thôn Xuân Trung) câu cá. Khi lưỡi câu mắc vào cành cây dưới sông, người này lội xuống gỡ thì bị cá sấu lao tới tấn công, cắn vào tay phải và chân trái. Sau khi giằng co, anh thoát được và lên bờ đến trạm y tế Khánh Bình sơ cứu.\nChính quyền xã Diên Lâm sau đó huy động lực lượng phối hợp truy bắt cá sấu. Công tác tìm kiếm gặp khó do mưa lớn, nước sông đục. Công an xã phát cảnh báo về "sự xuất hiện của động vật nguy hiểm" và khuyến cáo người dân không đến gần sông Chò, hạn chế câu cá, tắm rửa hay chăn thả gia súc khu vực này.\nSông Chò dài 74 km, sâu hơn 2 m, bắt nguồn từ xã Ea Trang (Đăk Lăk), chảy qua xã Trung Khánh Vĩnh, xã Xuân Đồng và đổ ra sông Cái ở Nha Trang.', 'Cá sấu nặng 80 kg, dài hơn 2,5 m được cho từng tấn công người ở sông Chò, xã Diên Lâm, đã bị người dân bắt sau 4 ngày truy tìm.', @cat_id_thoi_su_chinh_tri, @user_id_buitoan, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Lật xe khách, ba người chết', 'lat-xe-khach-ba-nguoi-chet', 'https://i1-vnexpress.vnecdn.net/2025/10/13/capture-jpg-1760343973-7284-17-5640-7019-1760347109.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=x8_vC-umUieyRWUTf56_3Q', 'Khoảng 12h, xe chạy trên tỉnh lộ 172 hướng đi Hà Nội, khi tới xã Việt Hồng thì mất lái, lao vào taluy dương. Người dân phá kính lái đưa hành khách ra ngoài.\nVụ tai nạn khiến ba người chết. Cả ba nạn nhân đều trú tại Lào Cai, gồm một phụ nữ và hai nam giới. 5 người bị thương đang được điều trị tại Bệnh viện Đa khoa số 1 tỉnh Lào Cai, sức khỏe ổn định.\nTại hiện trường, xe khách hư hỏng nặng, phần kính trước và sau bị vỡ, xăng, dầu chảy tràn ra mặt đường. Công an đang điều tra nguyên nhân tai nạn. Kết quả kiểm tra ban đầu cho thấy tài xế không có nồng độ cồn, ma túy. Một số nhân chứng nói xe có dấu hiệu mất phanh khi đổ đèo.\nTỉnh lộ 172 là tuyến đường chính kết nối khu vực phía tây Lào Cai với các địa phương lân cận.', 'Lào CaiXe khách chở 26 người lao từ đỉnh đèo xuống thì bất ngờ đâm vào taluy dương bên đường rồi lật nghiêng làm ba người chết và 5 người bị thương, trưa 13/10.', @cat_id_thoi_su_giao_thong, @user_id_vietan, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Thiệt hại do mưa bão được thống kê như thế nào', 'thiet-hai-do-mua-bao-duoc-thong-ke-nhu-the-nao', 'https://i1-vnexpress.vnecdn.net/2025/10/12/ThaiNguyen-5-7776-1760241301.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=FosWkY-v1qpuD_E6fPpEdg', 'Tại xã Quảng Điền (TP Huế), sau trận lốc xoáy cuối tháng 9 khiến 75 căn nhà bị tốc mái, trưởng thôn An Xuân dùng điện thoại ghi hình từng hộ dân và gửi lên nhóm làm việc của xã để thống kê thiệt hại. "Chúng tôi vừa giúp dân lợp lại mái, vừa ghi nhận cụ thể từng thiệt hại để báo cáo cấp trên", ông Nguyễn Ánh Cầu, Chủ tịch xã, nói.\nTheo ông Cầu, phương châm "4 tại chỗ" - gồm chỉ huy, lực lượng, phương tiện và hậu cần tại chỗ - giúp địa phương chủ động thống kê ngay trong những giờ đầu. Trưởng thôn lập danh sách hộ bị ảnh hưởng, chuyển lên UBND xã. "Ngay khi nghe báo cáo tốc mái hàng chục nhà, xã lập tức gửi thông tin lên Ban Chỉ huy phòng chống thiên tai và lãnh đạo thành phố", ông nói.\nTại Hải Phòng, quy trình được phân cấp đến từng tổ dân phố. Ông Phạm Hoàng Tuấn, Phó chủ tịch thường trực UBND phường Đồ Sơn, cho biết mỗi tổ phụ trách rà soát công trình công cộng, nhà dân, cây xanh. "Thông tin nhanh được chuyển qua nhóm làm việc của Ban chỉ đạo phòng thủ dân sự, sau đó cán bộ xuống hiện trường chụp ảnh, xác minh hư hỏng", ông nói.\nỞ Thanh Hóa, ông Nguyễn Văn Chính, Chi cục trưởng Chi cục Thủy lợi, cho biết báo cáo nhanh hàng ngày chủ yếu để nắm tình hình, chưa mang tính pháp lý cao do có thể bị gián đoạn bởi mất điện, mất sóng hoặc chưa kiểm đếm đủ. "Khoảng 15 ngày sau khi thiên tai xảy ra, tỉnh sẽ có báo cáo chi tiết do Chủ tịch UBND ký - đây là căn cứ pháp lý cuối cùng, người ký phải chịu trách nhiệm về số liệu", ông Chính nói.\nTại Quảng Trị, ông Trần Quốc Tuấn, Giám đốc Sở Nông nghiệp và Môi trường, cho biết việc thống kê thiệt hại được thực hiện theo mẫu biểu thống nhất của Ban chỉ huy phòng thủ dân sự tỉnh, gồm các hạng mục nhà cửa, hoa màu, vật nuôi, người chết, bị thương để xã điền thông tin. Dữ liệu từ thôn, xã được tổng hợp, nhập lên hệ thống điện tử của Ban chỉ huy tỉnh.\n"Toàn tỉnh Quảng Trị có 79 xã, phường, đặc khu thực hiện quy trình báo cáo hàng ngày: 5h30 sáng gửi báo cáo sơ bộ, cuối ngày nộp báo cáo chính thức", ông Tuấn nói, nhấn mạnh địa phương nào cũng phải chịu trách nhiệm với số liệu đã gửi. Xã phải ban hành văn bản đóng dấu để làm căn cứ hỗ trợ thiệt hại sau này; xã nào báo cáo sai sẽ chịu trách nhiệm.\nThống kê nhiều tầng\nTheo Luật Phòng chống thiên tai 2023, tổ chức và cá nhân phải báo cáo thiệt hại trong phạm vi quản lý cho Ban chỉ huy phòng thủ dân sự cấp xã hoặc cơ quan chủ quản. Ban chỉ huy các cấp tổng hợp, gửi UBND cùng cấp và Ban chỉ đạo phòng thủ dân sự quốc gia. UBND các cấp chịu trách nhiệm thống kê, kiểm tra, đánh giá trước khi chuyển báo cáo lên cấp trên.\nSau khi thẩm định, UBND tỉnh gửi báo cáo Thủ tướng và Bộ Nông nghiệp và Môi trường - cơ quan đầu mối tổng hợp, công bố số liệu toàn quốc. Bộ Công an, Tổng cục Thống kê và các bộ, ngành liên quan cũng tổng hợp thiệt hại trong lĩnh vực mình phụ trách để đối chiếu, bảo đảm tính thống nhất.\nThông tư liên tịch giữa Bộ Nông nghiệp và Môi trường với Bộ Tài chính quy định thống kê thiệt hại phải khách quan, minh bạch, phản ánh đúng thực tế. Báo cáo nhanh được lập ngay sau khi thiên tai xảy ra và gửi hằng ngày cho đến khi kết thúc đợt; báo cáo tổng hợp hoàn thiện sau khi rà soát, xác minh đầy đủ.\nCác hạng mục thống kê bao gồm người chết, mất tích, bị thương; nhà ở, trường học, bệnh viện; diện tích lúa, hoa màu, vật nuôi; cùng các công trình giao thông, thủy lợi, đê điều, hồ đập, khu neo đậu tránh trú bão. Mỗi nội dung có biểu mẫu riêng, yêu cầu mô tả cụ thể vị trí, quy mô, mức độ hư hỏng và ước tính giá trị thiệt hại.\nBáo cáo nhanh được gửi trước 19h mỗi ngày qua điện thoại, tin nhắn, email hoặc văn bản. Số liệu được cộng dồn, điều chỉnh hoặc bổ sung đến thời điểm báo cáo cuối cùng.\nXử lý sai phạm trong thống kê\nKhi phát hiện sai lệch hoặc kê khai chưa đúng, Ban chỉ huy phòng thủ dân sự hoặc UBND cấp trên sẽ thông báo để địa phương rà soát lại. Đơn vị thống kê phải đối chiếu hồ sơ, biên bản kiểm đếm, hình ảnh hiện trường hoặc xác nhận của người dân. Với sai sót nhỏ, có thể điều chỉnh trực tiếp trong báo cáo tiếp theo; còn trường hợp nghi ngờ gian lận, cấp tỉnh sẽ tổ chức đoàn kiểm tra chéo, lập biên bản xác minh.\nSau khi thống nhất, địa phương ban hành văn bản điều chỉnh, nêu rõ nguyên nhân và thời điểm hiệu chỉnh; cấp trên thẩm định, phê duyệt lại. Nếu phát hiện cố ý khai sai để trục lợi, cá nhân, tổ chức sẽ bị xử lý hành chính hoặc hình sự theo Luật Phòng chống thiên tai và Nghị định 66/2021.\nTheo Bộ Nông nghiệp và Môi trường, quy trình thống kê thiệt hại được thực hiện liên tục từ khi thiên tai bắt đầu đến khi có báo cáo tổng hợp toàn quốc. Cục Quản lý đê điều và Phòng chống thiên tai cho biết thống kê được thực hiện bằng hai phương pháp: điều tra trực tiếp tại hiện trường và tổng hợp qua báo cáo nhanh từ cơ sở, sau đó đối chiếu ngẫu nhiên để bảo đảm trung thực.\nCục Thống kê duy trì hệ thống "Số vụ thiên tai và mức độ thiệt hại" kết nối dữ liệu từ Bộ Nông nghiệp và các địa phương, giúp kiểm tra, đối chiếu tự động trước khi trình Chính phủ. Bộ Công an - cơ quan thường trực phòng thủ dân sự trong lĩnh vực an ninh, trật tự - cũng phối hợp thống kê, cứu hộ và vận chuyển hàng cứu trợ.\nTừ đầu năm 2025 đến nay, thiên tai làm 238 người chết và mất tích, 367 người bị thương; hơn 258.000 ngôi nhà hư hỏng, tốc mái; 555.000 ha lúa và hoa màu ngập úng, tổng thiệt hại hơn 33.500 tỷ đồng. Riêng bão Bualoi khiến 15 người chết và mất tích, 7 người bị thương, gần 17.000 nhà bị ngập - chủ yếu ở Cao Bằng, Thái Nguyên, Lạng Sơn và Hà Nội.\nBộ Nông nghiệp và Môi trường cho biết nhờ hệ thống thống kê nhiều tầng và quy định rõ trách nhiệm, mọi thiệt hại sau thiên tai đều được ghi nhận đầy đủ, giúp Chính phủ có căn cứ hỗ trợ, tái thiết và hoạch định chính sách phòng chống hiệu quả hơn.', 'Sau mỗi đợt thiên tai, công tác thống kê thiệt hại được triển khai từ cấp xã đến trung ương theo quy trình nhiều tầng, nhằm bảo đảm số liệu chính xác và hỗ trợ người dân kịp thời.', @cat_id_thoi_su_phap_luat, @user_id_nhomphongvien, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Chủ tịch Hà Nội: Không nạo vét sông hồ do không có chỗ đổ bùn đúng quy định', 'chu-tich-ha-noi-khong-nao-vet-song-ho-do-khong-co-cho-do-bun-dung-quy-dinh', 'https://i1-vnexpress.vnecdn.net/2025/10/13/ong-thanh-1760346268-5248-1760346361.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=pOLLvviPn4Yfs-_Vo1na6Q', 'Sáng 13/10, tại lễ khánh thành Nhà máy Điện rác Sóc Sơn, xã Trung Giã, Chủ tịch thành phố Trần Sỹ Thanh tiếp tục nhắc đến vấn đề ngập lụt ở Hà Nội.\nTừ ngày 26/8 đến nay, sau ba cơn bão Kajiki (đổ bộ Nghệ An - Hà Tĩnh ngày 25/8), bão Bualoi (vào Nghệ An - bắc Quảng Trị ngày 29/9) và bão Matmo (vào Quảng Tây, Trung Quốc ngày 6/10), Hà Nội cũng như khắp miền Bắc trải qua ba đợt mưa lớn, ngập diện rộng, gây rối loạn giao thông, đảo lộn cuộc sống người dân.\nChủ tịch thành phố chỉ ra một trong những nguyên nhân là hầu như tất cả sông, hồ những năm qua không được nạo vét. "Không phải chúng ta không biết cách làm hay không có tiền mà vì không có chỗ chôn lấp bùn thải theo quy định", ông giải thích.\nViệc nạo vét bùn ở sông, hồ nhằm khơi thông dòng chảy, tăng dung tích chứa, giúp tiêu thoát nước nhanh không đơn giản là "đào bùn lên mang đi chỗ khác đổ mà phải có chỗ đổ và xử lý". Ông Thanh dẫn chứng Hồ Tây đã có dự án nạo vét, nhưng phải tạm dừng do không có chỗ đổ bùn thải.\nNgười đứng đầu chính quyền thành phố chỉ đạo các đơn vị liên quan sớm tìm quỹ đất, giao nhà đầu tư để khởi công nhà máy xử lý bùn thải, sau đó thành phố sẽ triển khai ngay dự án cấp bách nạo vét sông, hồ điều hòa.\nTrước đó ngày 11/10, tại buổi kiểm tra hai xã bị ngập úng nặng nhất là Trung Giã và Đa Phúc, ông Trần Sỹ Thanh cho biết Hà Nội chưa bao giờ đối mặt "thiên tai kép" - bão chồng bão, lượng mưa đặc biệt lớn. Sau bão Bualoi "có chỗ này, chỗ kia còn bị động", nhưng quy mô quá lớn, diễn biến quá nhanh khiến các lực lượng gần như không thể tiếp cận nhau trong thời điểm đó.\n"Chúng ta bị động từ khâu dự báo đến tổ chức ứng phó", Chủ tịch Hà Nội nói.\nCơ quan khí tượng dự báo lượng mưa hai ngày 6-7/10 ở Hà Nội do ảnh hưởng của bão Matmo là 50-100 mm, có nơi trên 150 mm, nhưng thực tế phổ biến 100-200 mm, một số nơi như Mễ Trì 255 mm trong 13 tiếng.\nTừ thực tế trên, ông Thanh yêu cầu các đơn vị liên quan lập danh mục công trình tiêu thoát nước khẩn cấp trình UBND thành phố phê duyệt trong tuần tới; rà soát tổng thể hệ thống thoát nước, nhất là ngoại thành và các điểm thường xuyên ngập như Yên Nghĩa, Láng - Hòa Lạc, Ciputra.\nThống kê từ Cục Quản lý đê điều và Phòng chống thiên tai, đến sáng nay Hà Nội còn hơn 2.000 nhà ngập tại 8 xã Trung Giã, Đa Phúc, Sóc Sơn, Thư Lâm, Phúc Thịnh, Quang Minh, Tiến Thắng, Nội Bài. Các xã này chịu tác động của lũ sông Cầu từ Thái Nguyên, Bắc Ninh tràn về, dự báo còn ngập tới ngày mai.', 'Chủ tịch TP Hà Nội Trần Sỹ Thanh cho rằng một trong những nguyên nhân gây ngập trên địa bàn là các sông hồ không được nạo vét, do không có chỗ đổ bùn thải.', @cat_id_thoi_su, @user_id_vohai, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("'Chủ tịch tỉnh, xã phải tiếp công dân, không được ủy quyền'", 'chu-tich-tinh-xa-phai-tiep-cong-dan-khong-duoc-uy-quyen', 'https://i1-vnexpress.vnecdn.net/2025/10/13/202510090853403652-z7096772435-8026-8536-1760339375.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=7_siL0TxhavlyeO800dgEQ', 'Sáng 13/10, Ủy ban Thường vụ Quốc hội cho ý kiến về dự thảo Luật sửa đổi, bổ sung một số điều của Luật Tiếp công dân, Luật Khiếu nại và Luật Tố cáo. Theo dự thảo, Chủ tịch UBND cấp xã phải trực tiếp phụ trách công tác tiếp công dân ít nhất hai ngày mỗi tháng và thực hiện tiếp đột xuất khi có vụ việc. Chủ tịch UBND cấp xã có thẩm quyền giải quyết khiếu nại lần đầu đối với các quyết định, hành vi hành chính của mình và của cơ quan, công chức thuộc quyền quản lý trực tiếp.\nCơ quan soạn thảo cho biết các điều chỉnh trong dự thảo nhằm thể chế hóa mô hình chính quyền địa phương hai cấp (tỉnh và xã), bỏ cấp huyện trong công tác tiếp công dân, giải quyết khiếu nại, tố cáo; đồng thời phân cấp, cắt giảm thủ tục hành chính và điều chỉnh thẩm quyền cho phù hợp với bộ máy tổ chức mới.\nPhát biểu tại phiên thảo luận, Chủ nhiệm Ủy ban Công tác đại biểu Nguyễn Thanh Hải cho rằng chất lượng tiếp công dân phụ thuộc lớn vào thẩm quyền và trách nhiệm của người tiếp dân. Qua giám sát tại nhiều địa phương, bà nhận thấy tình trạng ủy quyền phổ biến, khiến người dân phải đi lại nhiều, không gặp được người đứng đầu, làm vụ việc kéo dài. "Nhiều trường hợp, nếu người dân được gặp trực tiếp chủ tịch, chỉ cần 15-20 phút là giải quyết xong", bà Hải nói và đề nghị dự luật phải ghi rõ quy định "người tiếp công dân không được ủy quyền".\nTheo bà Hải, Chủ tịch UBND tỉnh, xã đã được quy định phải tiếp công dân thì phải trực tiếp lắng nghe, chỉ đạo giải quyết. Việc ủy quyền cho phó chủ tịch hay lãnh đạo sở là không phù hợp vì những người này chỉ có vai trò tham mưu, không có thẩm quyền quyết định. "Phải là người đứng đầu mới có trách nhiệm và thẩm quyền giải quyết; cần cương quyết luật hóa yêu cầu không được ủy quyền", bà nhấn mạnh.\nPhó chủ tịch Quốc hội Nguyễn Thị Thanh cùng quan điểm, cho rằng nhiều vụ việc khiếu nại, tố cáo kéo dài là do người có trách nhiệm không thực hiện trách nhiệm, thẩm quyền của mình. Cán bộ thường ủy quyền cho cấp phó, thậm chí người được ủy quyền lại tiếp tục ủy quyền cho người khác, khiến vụ việc không được giải quyết dứt điểm.\nTrong khi đó, một số ý kiến cho rằng quy định "không được ủy quyền" cần cân nhắc để bảo đảm tính khả thi. Phó chủ tịch HĐND TP Hà Nội Phạm Thị Thanh Mai đề nghị nên quy định tối thiểu số buổi chủ tịch tỉnh, xã phải tiếp dân và có thể ủy quyền trong những trường hợp cụ thể để bảo đảm linh hoạt. Phó Viện trưởng VKSND Tối cao Nguyễn Duy Giảng cho rằng áp lực tiếp dân rất lớn, nếu người đứng đầu phải tiếp toàn bộ thì "chủ tịch suốt ngày tiếp công dân, không thể làm việc khác".\nPhó tổng Thanh tra Chính phủ Lê Tiến Đạt khẳng định luật hiện hành đã quy định người đứng đầu phải trực tiếp tiếp công dân, không cho phép ủy quyền. Tuy nhiên, thực tế một số nơi chưa thực hiện nghiêm. Dự thảo lần này chỉ bổ sung trường hợp Thủ tướng được ủy quyền cho Tổng Thanh tra Chính phủ trong việc thụ lý tố cáo - do tính chất đặc thù công việc và nhằm đơn giản thủ tục hành chính.\nKết luận phiên thảo luận, Phó chủ tịch Quốc hội Trần Quang Phương cho rằng với công tác tiếp công dân, người đứng đầu như Chủ tịch UBND tỉnh, xã phải trực tiếp thực hiện, không được ủy quyền; còn trong việc giải quyết các vụ việc cụ thể, có thể cho phép ủy quyền theo quy định.\nDự án luật sẽ được trình Quốc hội xem xét, thông qua tại kỳ họp thứ 10 khai mạc ngày 20/10.', 'Một số đại biểu đề nghị luật hóa quy định người đứng đầu không được ủy quyền tiếp công dân, song Phó chủ tịch Quốc hội Trần Quang Phương cho biết luật đã quy định nên Chủ tịch tỉnh, xã phải thực hiện trách nhiệm này.', @cat_id_thoi_su_chinh_tri, @user_id_sonha, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('TP HCM có thêm công viên 17.000 m2', 'tp-hcm-co-them-cong-vien-17000-m2', 'https://i1-vnexpress.vnecdn.net/2025/10/13/z7111561192950-065d994cc68dea4-2481-6829-1760341140.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=eWESA_AhNxKzjRkfzLk67g', 'Dự án nằm ở khu ao cá Tân Chánh Hiệp, giáp đường Nguyễn Thị Búp, phường Trung Mỹ Tây. Bên trong được thiết kế nhiều hạng mục, như: sân trung tâm, khu vui chơi thiếu nhi, sân đa năng.\nCác hạng mục tiện ích công cộng cũng được xây dựng gồm: chòi nghỉ, giàn hoa, mái che, nhà vệ sinh, thảm cỏ, hệ thống đường đi nội bộ. Ngoài ra, công trình còn bao gồm lắp đặt hệ thống tưới nước, chiếu sáng cùng các thiết bị thể thao.\nTheo Ban Quản lý dự án đầu tư xây dựng hạ tầng đô thị TP HCM (chủ đầu tư), dự án công viên được triển khai với tổng vốn hơn 27 tỷ đồng. Theo kế hoạch, công trình hoàn thành vào tháng 4/2026, nhưng đơn vị đặt mục tiêu đẩy nhanh, đưa vào khai thác vào dịp Tết Nguyên đán sắp tới.\nÔng Phan Phương Bình, Phó chủ tịch UBND phường Trung Mỹ Tây, cho biết dự án công viên giúp địa phương có thêm không gian xanh, kỳ vọng là một trong những điểm đến cho người dân giải trí, thể dục thể thao, sinh hoạt cộng đồng. Địa phương sẽ phối hợp với chủ đầu tư đảm bảo công trình hoàn thành đúng tiến độ.\nTP HCM trước khi sáp nhập Bình Dương và Bà Rịa - Vũng Tàu có khoảng 400 công viên, bao gồm công viên công cộng và trong khuôn viên khu ở, với hơn 235.000 cây xanh. Trong đó, các quận nội thành có diện tích công viên lớn hơn khu vực ngoại thành. Tổng diện tích đất quy hoạch cho công viên cây xanh ở thành phố hơn 11.400 ha, tương ứng 7 m2/người nhưng thực tế đangrất thấp, chỉ khoảng 500 ha. Với dân cư thường trực ở thành phố khoảng 10 triệu người, tỷ lệ trên chỉ đạt bình quân 0,55 m2/người.\nCác năm gần đây, thành phố xây thêm nhiều công viên giúp tăng mảng xanh ở một số khu vực, như công viên ở huyện Hóc Môn rộng 6 ha, công viên dọc sông Sài Gòn phía Khu đô thị Thủ Thiêm... Mới đây,dự áncông viên rộng gần 20.000 m2 ở huyện Củ Chi cũ cũng được khởi công, góp phần tăng mảng xanh, không gian công cộng cho khu vực vùng ven.', 'Công viên Tân Chánh Hiệp (quận 12 cũ) rộng gần 17.000 m2 thiết kế theo hình ảnh hạt gạo khởi công ngày 13/10 góp phần tạo thêm mảng xanh, nơi vui chơi cho người dân.', @cat_id_thoi_su_giao_thong, @user_id_gianganh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Nhiều tổ chức quốc tế hỗ trợ Việt Nam khắc phục hậu quả thiên tai', 'nhieu-to-chuc-quoc-te-ho-tro-viet-nam-khac-phuc-hau-qua-thien-tai', 'https://i1-vnexpress.vnecdn.net/2025/10/13/B497099C-33F0-4AE7-BF72-E73284-8830-2900-1760333582.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=tb6DXwUJrkZXa_5NBQ-76Q', 'Sáng 13/10, Cục Quản lý đê điều và Phòng chống thiên tai (Bộ Nông nghiệp và Môi trường) cho biết sau 5 ngày kêu gọi, nhiều tổ chức quốc tế đã chuyển hàng cứu trợ cũng như cam kết hỗ trợ Việt Nam khắc phục hậu quả thiên tai.\nTổ chức Samaritan’s Purse đã gửi đồ dùng gia đình, vật dụng bếp, đồ vệ sinh cá nhân cho 1.500 hộ dân ở Hà Tĩnh và 800 hộ ở Tuyên Quang; trợ giúp 4.000 thùng mì, 4.000 thùng nước và 4.000 hộp xúc xích cho người dân vùng ngập ở Lạng Sơn. UNICEF dự kiến gửi 4.500 thùng nước uống đóng chai và 600 bồn nhựa chứa nước cho Lạng Sơn hoặc Cao Bằng.\nTổ chức Save the Children tặng 500 triệu đồng cho hộ có trẻ em ở Hà Tĩnh. IOM hỗ trợ Hà Tĩnh 4.000 chai nước, 350 bộ vật dụng thiết yếu. Australia tặng 756 Bộ dụng cụ vệ sinh cá nhân, 320 bộ dụng cụ bếp, 30 chăn, 756 bộ dụng cụ sửa chữa nhà cửa cho tỉnh Bắc Ninh. Số hàng này dự kiến đến Nội Bài ngày 14/10.\nTrung tâm AHA cứu trợ Cao Bằng 2.800 bộ dụng cụ gia đình, 2.000 bộ vệ sinh cá nhân, 3.176 bộ nhà bếp trị giá 216.000 USD, dự kiến tuần sau đến Nội Bài. JICA (Nhật Bản) giúp bà con xã Thịnh Hợp, tỉnh Bắc Ninh 40 thiết bị lọc nước, 5.100 chăn, 1.000 can nhựa, 50 tấm plastic.\nTổ chức ActionAid đang làm thủ tục hỗ trợ 480.000 triệu đồng cho Tuyên Quang, trong đó 330 triệu tiền mặt cho 110 hộ gia đình; 50 triệu đồ dùng sinh hoạt và học tập cho học sinh Trường PTDT bán trú Tiểu học Cán Tỷ, xã Cán Tỷ; 100 triệu trợ giúp đồ dùng sinh hoạt và học tập cho học sinh tại xã Phú Linh.\nCông ty CP AceCook hỗ trợ 3.700 thùng mì cho các tỉnh, trong đó Hà Tĩnh 700 thùng, Tuyên Quang 1.000, Lạng Sơn 2.000. Tổ chức Tzu Chi (Đài Loan, Trung Quốc) dự kiến gửi 1.000 suất, mỗi suất 20 kg gạo và hai lít dầu ăn cho phường Gia Sàng ở tỉnh Thái Nguyên.\nBên cạnh đó, hàng cứu trợ của Nga đang trên đường tới Nội Bài; Canada hỗ trợ 50.000 USD qua tổ chức phi chính phủ. Đại sứ quán Mỹ, Liên minh Châu Âu, Meta (Facebook) cũng dự kiến thông qua tổ chức phi chính phủ để trợ giúp đồng bào chịu ảnh hưởng của thiên tai.\nTừ đầu năm đến nay, Việt Nam trải qua 11 cơn bão (6 cơn ảnh hưởng trực tiếp đất liền), 4 áp thấp nhiệt đới, làm 238 người chết, gần 400 người bị thương. Hàng trăm nghìn ngôi nhà sập đổ. Hàng trăm nghìn ha hoa màu bị cuốn trôi, giao thông sạt lở, chia cắt. Thống kê của Bộ Nông nghiệp và Môi trường, thiên tai đã gây thiệt hại kinh tế khoảng 35.000 tỷ đồng (1,3 tỷ USD), làm giảm 0,2% GDP.\nTại cuộc họp với các đối tác giảm nhẹ rủi ro thiên tai ngày 9/10, Thứ trưởng Nông nghiệp và Môi trường Nguyễn Hoàng Hiệp chia sẻ phải đối phó với thiên tai triền miên, Chính phủ, người dân Việt Nam đã rất nỗ lực nhưng một số vấn đề đã vượt qua sức chịu đựng. Ông mong muốn đối tác có phương án hỗ trợ, chuyển hàng hóa đến người dân nhanh nhất, chính xác nhất, hiệu quả nhất.', 'Ngoài tiền mặt, nhiều tổ chức như Samaritan’s Purse, JICA, AHA hỗ trợ đồng bào vùng lũ đồ dùng gia đình, thiết bị lọc nước, chăn màn, tấm lợp đa năng.', @cat_id_thoi_su_phap_luat, @user_id_giachinh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("Tổng Bí thư: Đất nước đã 'hàng thẳng, lối thông' để bước đều vào kỷ nguyên mới", 'tong-bi-thu-at-nuoc-da-hang-thang-loi-thong-de-buoc-deu-vao-ky-nguyen-moi', 'https://i1-vnexpress.vnecdn.net/2025/10/13/dabb81b059ccd4928ddd-176033234-9588-5293-1760332519.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=2amCNchIAnWrsV201Mgc5A', 'Phát biểu chỉ đạo tại Đại hội đại biểu Đảng bộ Chính phủ lần thứ nhất, nhiệm kỳ 2025-2030 sáng 13/10, Tổng Bí thư cho biết sự kiện diễn ra trong không khí cả nước thi đua chào mừng Đại hội đại biểu toàn quốc lần thứ 14 của Đảng, đúng dịp kỷ niệm 80 năm Ngày thành lập Chính phủ. Đại hội có 453 đại biểu đại diện cho hơn 209.000 đảng viên của 2.211 tổ chức cơ sở đảng, "thể hiện trí tuệ, ý chí thống nhất và khát vọng phát triển của toàn Đảng bộ Chính phủ".\nTổng Bí thư cho biết Đảng bộ Chính phủ là đảng bộ lớn, trực thuộc Trung ương, giữ vai trò trung tâm trong lãnh đạo, chỉ đạo Chính phủ, Thủ tướng và các tổ chức đảng trực thuộc trong quản trị nền hành chính quốc gia, phát triển kinh tế - xã hội, bảo đảm quốc phòng, an ninh và mở rộng đối ngoại, hội nhập quốc tế. Bộ Chính trị và Ban Bí thư đã trực tiếp cho ý kiến định hướng về văn kiện, nhân sự, thể hiện sự quan tâm đặc biệt đối với Đại hội Đảng bộ Chính phủ lần thứ nhất.\nNhìn lại nhiệm kỳ 2020-2025, Tổng Bí thư đánh giá Chính phủ đã đối mặt với nhiều thách thức chưa có tiền lệ - đại dịch Covid-19, thiên tai, xung đột, cạnh tranh chiến lược giữa các nước lớn - nhưng Ban Cán sự Đảng Chính phủ và nay là Đảng bộ Chính phủ "đã thể hiện bản lĩnh, đoàn kết, sáng tạo, quyết liệt trong điều hành, đạt được kết quả toàn diện trên mọi lĩnh vực".\nKinh tế vĩ mô ổn định, tăng trưởng GDP bình quân đạt 6,3%/năm, thuộc nhóm cao của khu vực. Quy mô nền kinh tế tăng từ 346 tỷ USD năm 2020 lên khoảng 510 tỷ USD năm 2025, đưa Việt Nam lên vị trí thứ 32 thế giới, thứ 4 ASEAN; GDP bình quân đầu người đạt 5.000 USD, gấp 1,4 lần so với năm 2021. Lạm phát được kiểm soát quanh mức 4%/năm, các cân đối lớn được bảo đảm. Cùng với đó, chương trình phục hồi và phát triển kinh tế - xã hội quy mô hàng trăm nghìn tỷ đồng đã hỗ trợ hơn 68 triệu lượt người lao động và 1,4 triệu doanh nghiệp vượt qua khó khăn.\nHạ tầng quốc gia được đầu tư mạnh mẽ với hơn 3.200 km đường cao tốc và 1.700 km đường ven biển dự kiến hoàn thành trong năm nay, vượt xa mục tiêu Đại hội 13. Cảng hàng không quốc tế Long Thành giai đoạn 1 cơ bản hoàn thiện, tạo diện mạo mới cho đất nước. Tiềm lực quốc phòng, an ninh được tăng cường; đối ngoại, hội nhập quốc tế đi vào chiều sâu; văn hóa, xã hội, môi trường có chuyển biến rõ nét; đời sống nhân dân ngày càng được cải thiện.\nTổng Bí thư biểu dương những kết quả quan trọng, khẳng định đây là nền tảng vững chắc để Đảng bộ Chính phủ bước vào nhiệm kỳ mới. Tuy nhiên, ông thẳng thắn chỉ ra những hạn chế còn tồn tại như kinh tế vẫn tiềm ẩn rủi ro; cơ cấu lại nền kinh tế, nâng cao năng suất, chất lượng tăng trưởng còn chậm; thể chế, hạ tầng, giáo dục, y tế và hạ tầng số chưa đồng bộ; phân cấp, phân quyền và cải cách hành chính chưa thật mạnh mẽ. Một bộ phận cán bộ, đảng viên còn sợ sai, né tránh trách nhiệm; kỷ luật, kỷ cương hành chính chưa nghiêm; công tác kiểm tra, giám sát, phòng ngừa tham nhũng còn hình thức.\n"Chúng ta có hàng thẳng, lối thông thì bây giờ phải bước đều, bước vững chắc vào kỷ nguyên mới", Tổng Bí thư nói, nhấn mạnh Đảng bộ Chính phủ cần phát huy bản lĩnh, trí tuệ, đoàn kết, hành động quyết liệt hơn để đáp ứng yêu cầu ngày càng cao trong giai đoạn phát triển mới.\nTổng Bí thư cho rằng đất nước đang ở giai đoạn chuyển mình mang tính lịch sử, khi mô hình chính quyền địa phương hai cấp bắt đầu vận hành, không gian phát triển được quy hoạch lại và nhiều chủ trương chiến lược đang được triển khai. "Đây là lúc chúng ta phải nắm bắt thời cơ, đi tắt đón đầu bằng trí tuệ Việt Nam, kết hợp với tri thức tiến bộ của nhân loại, sớm tạo lập trạng thái tự chủ chiến lược, duy trì tốc độ phát triển cao và bền vững", ông nói.\nTheo Tổng Bí thư, Đảng bộ Chính phủ cần thống nhất nhận thức, xác định rõ mục tiêu đưa đất nước vững mạnh, tiến vào kỷ nguyên phát triển giàu mạnh, phồn vinh, văn minh, hạnh phúc. Chính phủ phải nỗ lực lớn hơn, khát vọng mạnh mẽ hơn, nâng cao năng lực dự báo, lãnh đạo, đổi mới sáng tạo trong quản lý nhà nước. "Phải phá bỏ lối mòn tư duy cũ để đề ra giải pháp thực chất, tất cả vì phát triển kinh tế, văn hóa, xã hội và hạnh phúc của nhân dân", Tổng Bí thư nhấn mạnh.\nÔng yêu cầu xây dựng đội ngũ cán bộ, công chức có tài, có tầm, có tâm, bản lĩnh chính trị vững vàng, đạo đức trong sáng, dám nghĩ, dám làm, dám chịu trách nhiệm, dám đương đầu với khó khăn, thử thách. Mỗi cơ quan phải có cơ chế, chính sách bảo vệ người dám đổi mới vì lợi ích chung. "Không để cơ quan nhà nước trở thành nơi trú ẩn an toàn cho người yếu kém, ngại va chạm", Tổng Bí thư quán triệt.\nTập trung giải quyết vấn đề đô thị, ứng phó thiên tai\nTổng Bí thư yêu cầu Chính phủ tập trung giải quyết ngay trong nhiệm kỳ này các vấn đề bức xúc của người dân, nhất là ở các đô thị lớn. "Phải xử lý dứt điểm ùn tắc giao thông, ô nhiễm môi trường, ngập lụt tại Hà Nội, TP HCM và nhiều địa phương khác; những tồn tại kéo dài nhiều năm, nay phải quyết tâm khắc phục", ông nói.\nÔng đề nghị Ban Chỉ đạo Phòng chống Lụt bão Trung ương cùng các cơ quan liên quan đánh giá lại hiệu quả công tác phòng chống thiên tai, đổi mới toàn diện hoạt động dự báo, điều hành. "Bão không thể tránh được, nhưng chúng ta phải chủ động. Không thể chống chọi bằng bao cát, hết bão lại làm lại từ đầu; cần kiên cố hóa hệ thống đê sông, đê biển, nhất là ở những vùng thường xuyên chịu thiên tai", ông chỉ rõ.\nTổng Bí thư nhấn mạnh Đảng bộ Chính phủ có vị trí đặc biệt trong hệ thống chính trị - là nơi quy tụ đội ngũ cán bộ lãnh đạo cấp chiến lược, trực tiếp thể chế hóa và tổ chức thực hiện các chủ trương, đường lối của Đảng bằng pháp luật, chính sách của Nhà nước. Vì vậy, Đảng bộ phải là tấm gương mẫu mực về bản lĩnh chính trị, trí tuệ, đạo đức công vụ; là trung tâm đoàn kết thống nhất ý chí và hành động trong toàn bộ bộ máy hành chính.\nÔng yêu cầu tập trung thực hiện ba đột phá chiến lược, gồm hoàn thiện thể chế, phát triển hạ tầng và nâng cao chất lượng nguồn nhân lực. Thể chế phải trở thành lợi thế cạnh tranh, khơi thông mọi nguồn lực phát triển; hạ tầng phải được đầu tư đồng bộ, hiện đại, trong đó có giao thông, cảng biển, đường sắt tốc độ cao, không gian biển, không gian ngầm, không gian vũ trụ.\nTổng Bí thư cũng đề nghị đầu tư xây dựng một số công trình văn hóa, thể thao, giáo dục, y tế trọng điểm quốc gia mang tầm khu vực và quốc tế, hướng tới việc Việt Nam có thể đăng cai các sự kiện thể thao lớn như ASIAD, Olympic. Ông nhấn mạnh giáo dục phải thực sự là quốc sách hàng đầu; khoa học công nghệ, đổi mới sáng tạo và chuyển đổi số phải trở thành động lực then chốt cho phát triển bền vững.\nCùng với đó là phát triển văn hóa hài hòa với kinh tế - xã hội, chăm lo đời sống nhân dân, không để ai bị bỏ lại phía sau; quản lý, sử dụng hiệu quả tài nguyên, bảo vệ môi trường, chủ động ứng phó với biến đổi khí hậu; tăng cường tiềm lực quốc phòng, an ninh, nâng cao hiệu quả đối ngoại và hội nhập quốc tế, giữ vững độc lập, chủ quyền, môi trường hòa bình để phát triển đất nước.\n"Tôi tin tưởng với bản lĩnh chính trị, trí tuệ, tinh thần đoàn kết và khát vọng vươn lên, Đảng bộ Chính phủ sẽ phát huy truyền thống, hoàn thành xuất sắc mọi nhiệm vụ được giao, góp phần thực hiện thắng lợi Nghị quyết Đại hội 14, đưa đất nước bước vào kỷ nguyên phát triển mới - hòa bình, độc lập, dân chủ, giàu mạnh, phồn vinh, văn minh, hạnh phúc", Tổng Bí thư Tô Lâm nhấn mạnh.', 'Tổng Bí thư Tô Lâm nhấn mạnh đất nước đang ở giai đoạn chuyển mình mang tính lịch sử, đã đến lúc "bước đều, bước vững chắc" vào kỷ nguyên phát triển mới, với tinh thần đổi mới, sáng tạo và quyết tâm cao hơn nữa.', @cat_id_thoi_su, @user_id_sonha, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Lũ rút nhanh, miền Bắc còn 22 xã ngập', 'lu-rut-nhanh-mien-bac-con-22-xa-ngap', 'https://i1-vnexpress.vnecdn.net/2025/10/13/ngap1-1760327643-1760327738-2661-1760327743.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=3G_GV6VycKXPtOludEnZxQ', 'Trung tâm Dự báo Khí tượng Thủy văn quốc gia cho biết lúc 7h hôm nay, lũ sông Cầu tại Phúc Lộc Phương, Đáp Cầu, Lương Phúc, tỉnh Bắc Ninh đều dưới báo động hai, giảm khoảng một mét so với sáng qua.\nTại Hà Nội, lũ sông Cà Lồ tại Mạnh Tân là 7,37 m, trên báo động hai 0,37 m, giảm 0,85 m so với sáng qua. Lũ sông Thương rút 0,9 m, sáng nay tại Phủ Lạng Thương là 5,84 m, dưới báo động ba 0,46 m.\nDự báo trong 12-24 giờ tới, lũ sông Cầu tại trạm Đáp Cầu tiếp tục xuống báo động một, lũ sông Thương tại trạm Phủ Lạng Thương ở báo động hai (nguy hiểm nhất là báo động ba).\nCục Quản lý đê điều và Phòng chống thiên tai cho biết đến sáng nay còn khoảng 12.200 nhà bị ngập, so với sáng qua tăng gần 4.600 nhà do các địa phương rà soát, bổ sung số liệu. Số xã bị ngập đã giảm từ 25 hôm qua xuống còn 22.\nBắc Ninh còn khoảng 1.000 nhà ngập tại 10 xã gồm: Tiên Lục, Mỹ Thái, Đa Mai, Phúc Hòa, Ngọc Thiện, Vân Hà, Việt Yên, Tân Yên, Yên Thế, Bố Hạ. Hà Nội còn hơn 2.000 nhà ngập tại 8 xã Trung Giã, Đa Phúc, Sóc Sơn, Thư Lâm, Phúc Thịnh, Quang Minh, Tiến Thắng, Nội Bài.\nThái Nguyên còn 4 xã ngập là Phú Bình, Điềm Thụy, Vạn Xuân, Trung Thành. So với hôm qua, mức độ ngập giảm nhanh, còn khoảng 0,3-1 m tùy vị trí.\nCác tuyến quốc lộ qua vùng ngập đã thông tuyến; đường sắt Hà Nội - Thái Nguyên thông lúc 18h30 hôm qua. Miền Bắc còn 48.169 khách hàng mất điện, trong đó Thái Nguyên 8.640; Bắc Ninh 29.440; Lạng Sơn 8.890; Cao Bằng 1.197.\nLúc cao điểm, miền Bắc có hơn 234.000 nhà bị ngập, phần lớn ở Thái Nguyên với 200.000, Bắc Ninh hơn 15.000, Hà Nội hơn 8.100, Cao Bằng 7.500, Lạng Sơn 3.000 và Sơn La 120 nhà.\nMưa lũ sau bão Matmo khiến 17 người chết, một người mất tích, 15 người bị thương; gây ra 59 sự cố đê điều, chủ yếu tại Bắc Ninh 42, Thái Nguyên 8, Hà Nội 9. Các địa phương đã xử lý giờ đầu các sự cố và vẫn đang theo dõi chặt chẽ. Thiệt hại kinh tế do mưa lũ ước tính là 8.720 tỷ đồng (Lạng Sơn 1.050 tỷ đồng, Cao Bằng 2.000 tỷ đồng, Thái Nguyên 4.000 tỷ đồng, Bắc Ninh 1.670 tỷ đồng).\nNgày 12/10, Thủ tướng quyết định hỗ trợ 400 tỷ đồng khắc phục hậu quả mưa lũ sau bão Matmo, trong đó Thái Nguyên 250, Cao Bằng 50, Lạng Sơn 50, Bắc Ninh 50 tỷ. Ban Vận động cứu trợ Trung ương đã huy động được 1.011 tỷ đồng ủng hộ khắc phục thiệt hại do bão Bualoi và Matmo.', 'Sáng nay, lũ sông Cầu, Thương, Cà Lồ đã xuống báo động hai, Bắc Ninh, Thái Nguyên và Hà Nội còn 22 xã bị ngập, độ sâu 0,3-0,1 m.', @cat_id_thoi_su_chinh_tri, @user_id_giachinh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Thủ tướng: Đảng bộ Chính phủ tiên phong kiến tạo kỷ nguyên phát triển mới', 'thu-tuong-ang-bo-chinh-phu-tien-phong-kien-tao-ky-nguyen-phat-trien-moi', 'https://i1-vnexpress.vnecdn.net/2025/10/13/ttg1-16674742319591431933173-1-5119-5259-1760312793.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=dByE_i4uKK2u8_6pis8dAw', 'Trong bài viết nhân dịp Đại hội đại biểu Đảng bộ Chính phủ lần thứ nhất, nhiệm kỳ 2025-2030 với tiêu đề:Khát vọng, bản lĩnh, trí tuệ - Đảng bộ Chính phủ tiên phong, vững bước cùng dân tộc trong kỷ nguyên mới, Thủ tướng Phạm Minh Chính, Ủy viên Bộ Chính trị, Bí thư Đảng ủy Chính phủ, khẳng định Đại hội là dấu mốc chính trị đặc biệt, mở ra hành trình hiện thực hóa khát vọng phát triển quốc gia, đưa Việt Nam tiến nhanh, bền vững.\nThủ tướng cho biết Đảng bộ Chính phủ được thành lập theo Quyết định 243 ngày 24/1/2025 của Bộ Chính trị, với hơn 209.000 đảng viên sinh hoạt tại 2.211 tổ chức cơ sở đảng. Việc thành lập Đảng bộ Chính phủ là bước phát triển mang tính chiến lược, bảo đảm sự lãnh đạo trực tiếp, toàn diện của Đảng đối với Chính phủ - cơ quan hành chính nhà nước cao nhất, thực hiện quyền hành pháp và quản trị quốc gia; vừa kế thừa vai trò Ban Cán sự đảng Chính phủ trước đây, vừa đặt nền móng cho phương thức lãnh đạo hiện đại, thống nhất, khoa học.\nNhìn lại nhiệm kỳ 2020-2025, trong bối cảnh thế giới và khu vực biến động nhanh, phức tạp, nhiều vấn đề chưa có tiền lệ, Đảng bộ Chính phủ đã lãnh đạo, chỉ đạo thực hiện đồng bộ ba đột phá chiến lược, sáu nhiệm vụ trọng tâm, 12 nhóm giải pháp chủ yếu mà Đại hội 13 đề ra, đạt kết quả toàn diện. Kinh tế duy trì tăng trưởng thuộc nhóm cao, quy mô GDP tăng từ 346 tỷ USD năm 2020 lên khoảng 510 tỷ USD năm 2025, xếp thứ 32 thế giới; GDP bình quân đầu người khoảng 5.000 USD; lạm phát khoảng 4%, các cân đối lớn được bảo đảm, xuất siêu duy trì nhiều năm. Thu ngân sách giai đoạn 2021-2025 ước đạt 9,6 triệu tỷ đồng; nợ công khoảng 35% GDP.\nTheo người đứng đầu Chính phủ, đầu tư và hạ tầng bứt phá, tổng vốn đầu tư toàn xã hội đạt 17,3 triệu tỷ đồng (khoảng 33% GDP); vốn đầu tư công giai đoạn 2021-2025 khoảng 3,4 triệu tỷ đồng, tăng 55% so với nhiệm kỳ trước. Đến cuối năm 2025, dự kiến hoàn thành 3.245 km đường bộ cao tốc và 1.711 km đường ven biển; mở rộng các cảng hàng không lớn; cơ bản hoàn thành giai đoạn 1 Cảng hàng không quốc tế Long Thành; đồng thời thúc đẩy vành đai đô thị, một số tuyến đường sắt đô thị, hạ tầng số và năng lượng.\nCông tác xây dựng và hoàn thiện thể chế có đổi mới mạnh mẽ. Chính phủ trình Quốc hội số lượng dự án luật, nghị quyết lớn nhất trong một nhiệm kỳ; ban hành 820 nghị định và gần 1.400 nghị quyết, tháo gỡ vướng mắc cho hàng nghìn dự án, khơi thông nguồn lực phát triển. Cải cách bộ máy được triển khai quyết liệt: giảm 8 bộ, cơ quan ngang bộ (còn 14 bộ, 3 cơ quan ngang bộ), tinh giản 145.000 biên chế, giảm chi thường xuyên khoảng 39.000 tỷ đồng/năm; mô hình chính quyền địa phương hai cấp vận hành nền nếp, hiệu quả.\nAn sinh xã hội được ưu tiên, tỷ lệ hộ nghèo đa chiều giảm từ 4,1% năm 2021 xuống 1,3% năm 2025; thu nhập bình quân người lao động tăng từ 5,5 lên 8,4 triệu đồng/tháng; hoàn thành mục tiêu xóa nhà tạm, dột nát trước thời hạn hơn 5 năm với trên 334.000 căn; triển khai 633.000 căn nhà ở xã hội. Tỷ lệ bao phủ bảo hiểm y tế đạt 95,2%; chỉ số hạnh phúc của Việt Nam tăng 37 bậc, lên thứ 46 thế giới. Giáo dục và phát triển nhân lực được coi là đột phá dài hạn khi miễn, hỗ trợ học phí mầm non và phổ thông từ năm học 2025-2026; xây dựng trường liên cấp tại các xã biên giới; đào tạo 100.000 kỹ sư lĩnh vực chip bán dẫn và trí tuệ nhân tạo.\nTrong phòng chống dịch Covid-19, Chính phủ tổ chức chiến dịch tiêm chủng miễn phí lớn nhất lịch sử, đẩy mạnh ngoại giao vaccine, hỗ trợ khoảng 119.000 tỷ đồng cho người lao động và doanh nghiệp, giúp Việt Nam phục hồi nhanh. Công tác phòng chống tham nhũng, tiêu cực, lợi ích nhóm được tăng cường; nhiều vụ việc được xử lý nghiêm, củng cố niềm tin nhân dân. Đối ngoại và quốc phòng - an ninh đạt nhiều kết quả quan trọng; Việt Nam thiết lập quan hệ với 195 nước, có 38 đối tác chiến lược, chiến lược toàn diện; tăng cường tiềm lực quốc phòng, an ninh, giữ vững môi trường hòa bình, ổn định để phát triển.\nBứt tốc bằng khoa học, đổi mới và chuyển đổi số\nBước vào nhiệm kỳ 2025-2030, Thủ tướng nhận định thế giới tiếp tục biến động nhanh, cạnh tranh chiến lược gay gắt; biến đổi khí hậu, an ninh phi truyền thống, rủi ro hệ thống gia tăng. Trong nước, quá trình tái cơ cấu và chuyển đổi mô hình tăng trưởng đòi hỏi tư duy, phương thức lãnh đạo và hành động mạnh mẽ hơn. Vì vậy, Đảng bộ Chính phủ "phải nêu gương tiên phong, hành động quyết liệt, là trung tâm đoàn kết, sáng tạo, dám nghĩ, dám làm, dám chịu trách nhiệm vì lợi ích quốc gia, dân tộc".\nPhương châm hành động được xác định là "Đoàn kết kỷ cương - Dân chủ đổi mới - Đột phá phát triển - Gần dân vì dân"; trong đó đoàn kết, kỷ cương là nền tảng; dân chủ, đổi mới là phương pháp; đột phá, phát triển là mục tiêu; gần dân, vì dân là động lực bền vững.\nTheo người đứng đầu Chính phủ, nhiệm kỳ mới sẽ tập trung ba đột phá chiến lược, gồm hoàn thiện thể chế phát triển đồng bộ, hiện đại; phát triển khoa học - công nghệ, đổi mới sáng tạo và chuyển đổi số quốc gia như động lực then chốt, đồng thời thúc đẩy kinh tế xanh, kinh tế số, kinh tế tuần hoàn; xây dựng kết cấu hạ tầng đồng bộ, hiện đại, trọng tâm là giao thông, năng lượng, hạ tầng số và xã hội, phấn đấu đến năm 2030 hoàn thành 5.000 km cao tốc, phát triển các dự án đường sắt tốc độ cao, cảng biển, sân bay đạt chuẩn khu vực.\nMục tiêu đến năm 2030 là tăng trưởng GDP bình quân 10%/năm trở lên, GDP bình quân đầu người khoảng 8.500 USD; Việt Nam trở thành nước đang phát triển có công nghiệp hiện đại, thu nhập trung bình cao, thuộc nhóm 30 nền kinh tế có quy mô GDP hàng đầu thế giới.\nĐảng ủy Chính phủ sẽ xây dựng Chính phủ liêm chính, hành động, kiến tạo phát triển; nâng cao năng lực quản trị quốc gia, hiệu lực hành chính, kỷ luật công vụ; đẩy mạnh chuyển đổi số toàn diện trong quản lý nhà nước, phát triển hạ tầng dữ liệu quốc gia, chính phủ số, kinh tế số và công dân số; gắn kết chặt chẽ phát triển kinh tế - xã hội với quốc phòng, an ninh và đối ngoại.\n"Đảng bộ Chính phủ phải là lực lượng tiên phong, gương mẫu đi đầu trong triển khai Nghị quyết Đại hội 14 của Đảng, đặc biệt ở các lĩnh vực khoa học - công nghệ, đổi mới sáng tạo và chuyển đổi số; biến khát vọng thành hành động, biến quyết tâm thành kết quả cụ thể, mang lại ấm no, hạnh phúc cho nhân dân", Thủ tướng nhấn mạnh.\n>> Nội dung bài viết của Thủ tướng Phạm Minh Chính', 'Thủ tướng Phạm Minh Chính nhấn mạnh Đảng bộ Chính phủ tiên phong; phát huy bản lĩnh, trí tuệ, khát vọng đổi mới; góp phần đưa đất nước vào kỷ nguyên phát triển phồn vinh.', @cat_id_thoi_su_giao_thong, @user_id_vnexpress, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Đôi hoa tai cưới mở lối cho nông dân làm giàu', 'oi-hoa-tai-cuoi-mo-loi-cho-nong-dan-lam-giau', 'https://i1-vnexpress.vnecdn.net/2025/10/12/ong-tran-xuan-son-3-1760283693-4340-1760284742.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=sDE9M47XQCtp6EwN8M8jDw', 'Năm 2000, sau khi cưới vợ, ông Trần Xuân Sơn, lúc này 28 tuổi, theo người dân ở Nghĩa Hưng (trước đây thuộc huyện Nghĩa Đàn), trồng 4-5 sào mía bán cho Nhà máy đường Nghệ An. Vào vụ thu hoạch, tiền bán mía đủ nuôi gia đình, song ông phân tích ruộng mình ít, trong khi nhiều hộ trong xã sở hữu 2-3 ha, nếu theo nghề lâu dài thì nguy cơ sẽ tụt lại phía sau.\n"Tôi muốn bứt phá, tìm hướng đi khác biệt để tạo lập cơ nghiệp", ông Sơn kể.\nNăm 2001, trong một lần xuống thị xã Thái Hòa cũ, ông Sơn ghé hiệu sách, tình cờ thấy cuốnKỹ thuật chăn nuôi gà. Đọc vài phút, ông nghĩ thầm "cũng được đấy". Một lần xuống nhà em họ ở TP Vinh chơi, nghe giới thiệu gần đó có trại gia cầm lớn làm ăn khấm khá, ông đến xem và mua luôn 100 gà giống (gà cỏ) về nuôi.\nKhông có vốn lẫn kinh nghiệm, lứa đầu tiên gà chết gần một nửa. Hàng xóm bảo "gà công nghiệp thế này ai ăn", thương lái thì chê khó bán. Bà Nguyễn Thị Lương, vợ ông Sơn, buồn rầu sau mỗi lần dọn chuồng, khuyên chồng nên chuyển sang nuôi lợn, bởi nuôi gà toàn thấy chết, nguy cơ nợ nần chồng chất.\nNhưng ông Sơn ôm bà Lương động viên, bảo lứa đầu chưa có kinh nghiệm, nhưng lứa tiếp theo chắc chắn sẽ khác. Đợt tái đầu tư lần hai, ông nói dối sẽ mua 50 con gà về nuôi vì sợ vợ lo lắng, nhưng đến trại ông mua luôn 200 con, trả một ít tiền, còn lại xin khất nợ.\nNhờ rút kinh nghiệm từ lần trước, 200 con gà sau 4 tháng chỉ hao hụt 10 con, ông bán lãi khoảng 7 triệu đồng - số tiền được xem là cả một gia tài đối với vợ chồng trẻ. Người dân và thương lái trong vùng bắt đầu biết đến trại gà của ông Sơn, họ khen thịt gà ngon, không còn chê nữa.\nThấy có lãi, ông bắt đầu có ý định làm lớn, vài tháng sau cải tạo chuồng trâu thành chuồng gà, tăng số lượng từ 200 lên 500 con. Tuy nhiên, càng làm càng thiếu vốn. Tài sản giá trị nhất trong nhà chỉ là chiếc xe đạp Thống Nhất, ông nhờ người họ hàng làm cán bộ ngân hàng vay hộ 5 triệu đồng mua cám cho gà ăn.\nTrong lúc bế tắc, nhìn thấy đôi hoa tai vợ đang đeo - kỷ vật của bà ngoại tặng khi cưới - ông động viên vợ "cho mượn", đem ra tiệm vàng bán lấy tiền mua thức ăn cho gà, hứa sau một đến hai vụ sẽ mua lại. "Khi vợ đồng ý tôi rất mừng, nhưng lại thương bà ấy vì phải bán kỷ vật thiêng liêng", ông kể.\nGiá vàng thời điểm đó là 460.000 đồng một chỉ, đôi hoa tai hơn hai chỉ, bán được hơn một triệu đồng, đủ để ông mua cám cho gà ăn trong 1-2 tháng. Đôi hoa tay giúp trại gà cầm cự trong giai đoạn thiếu thốn, bởi ông không biết bấu víu vào đâu. Ba năm đầu, trại gà phát triển, nuôi đến 800 con gà mái đẻ trứng, bắt đầu cho lãi cao.\nDù hứa với vợ sẽ chuộc lại trang sức trong vài tháng, nhưng phải đến năm thứ ba ông mới thực hiện. Lần này bà Lương đổi ý, không mua vàng ta nữa mà mua vàng tây cho rẻ. Khi thấy chồng đưa đôi hoa tai mới bằng vàng tây về, bà nói đùa: "Từ nay về sau nếu có thua lỗ cũng không bán nữa".\nĐang trên đà phát triển, ông Sơn chịu thiệt hại lớn khi dịch cúm gia cầm bùng phát cuối năm 2004. Theo quy định của chính quyền, trong bán kính 3 km xung quanh ổ dịch, gà bị buộc tiêu hủy. Sợ mất trắng, ông đưa đàn gà gần 1.000 con lên gửi trong trang trại giữa rừng, cách khu dân cư hơn 5 km.\n"Chạy dịch" thành công, nhưng trong suốt hai tháng, trứng và gà thịt không bán được, ông lại thiếu vốn mua thức ăn. Thấy trứng quá nhiều nhưng không thể bán, ông cùng vợ bám trại, hàng ngày luộc trứng rồi trộn đều với sắn, ngô, khoai... làm thức ăn cho gà, nhờ vậy tiết kiệm được chi phí.\nĐầu năm 2005, khi hết dịch, ông gặp may khi giá trứng tăng vọt lên 3.000 đồng một quả. Nhờ lượng trứng lớn từ đàn gà gần 1.000 con, trong khoảng nửa năm ông thu lời lớn, gỡ gạc được vốn, có thêm kinh phí đầu tư.\nGiai đoạn 2005-2013, quy mô trang trại phát triển nhanh, thường xuyên nuôi 3.000 gà lai Ai Cập. Ông Sơn không chỉ bán trứng mà còn nhân giống, mở rộng thị trường khắp tỉnh. Đến năm 2014, khi chính quyền có chủ trương dồn điền đổi thửa, gia đình có vài thửa ruộng nằm rải rác nên ông đi thương lượng với người dân để đổi, nhằm tập trung đất sản xuất. Cuối cùng, ông có thêm 0,6 ha để xây trang trại tập trung.\nCó thêm đất, ông đầu tư hơn 3 tỷ đồng xây chuồng trại khép kín, nuôi 8.000 con, rồi lắp đặt hệ thống điều hòa, chuồng lạnh. Những năm sau, ông chi thêm 4 tỷ đồng xây trại 9.000 con, đầu tư 300 triệu đồng lắp điện mặt trời để chống rủi ro mất điện.\nNhưng thị trường không ít lần thử thách. Đầu năm 2024, giá trứng rẻ, mỗi vạn trứng lỗ 3,5 triệu đồng, có tháng trang trại lỗ 300 triệu đồng. Song ông Sơn vẫn bình thản bởi đã có vốn xoay vòng. Ông cho rằng tâm lý nông dân thường chán nản, khi gặp khó sẽ bỏ nghề, ai kiên trì bám trụ, đến lúc thị trường hồi phục thì thắng.\nHiện ông Sơn sở hữu trang trại diện tích hơn 7.000 m2, trong đó có 2.300 m2 chuồng trại, quy hoạch cách khu dân cư 300 m. Tổng đàn gà 23.000 con, gồm 17.000 con gà mái đẻ và 6.000 con gà hậu bị. Sản lượng trứng mỗi ngày 12.000-13.000 quả, đạt tiêu chuẩn VietGAP, chất lượng OCOP 3 sao.\nTổng doanh thu từ chăn nuôi gà đẻ lấy trứng và gà thịt đạt 11 tỷ đồng mỗi năm, lợi nhuận sau chi phí khoảng 1,2 tỷ đồng. Cơ sở tạo việc làm cho 4 lao động thường xuyên, lương 5,5 triệu đồng/tháng, và 10 lao động thời vụ.\nÔng Sơn chia sẻ luôn giữ triết lý "làm ăn không được gian dối, thất đức". "Bán đắt hơn một đồng thì được, nhưng không bao giờ được phép cân điêu, đếm sai. Trứng thiếu hôm nay thì mai phải bù. Giữ chữ tín thì thị trường mới mở rộng", ông nói. Hiện mỗi năm trang trại bán khoảng 25 tấn thịt gà, song ông chủ động nhường bớt thị phần cho bà con trong vùng, tập trung vào trứng thương phẩm.\nNgoài hệ thống trang trại hiện đại, ông cũng xây được nhà hai tầng kiên cố ở xã Nghĩa Hưng, sắm ôtô đi lại, kinh tế gia đình khấm khá. Hai con gái đầu đã lấy chồng, có công việc ổn định, con trai út đang học lớp 10.\nBà Nguyễn Thị Lương cho hay nhiều lần thua lỗ nhưng luôn thấy sự lạc quan, kiên cường hiếm có ở chồng. "Ông ấy bảo kinh doanh khi lời khi lỗ là bình thường. Nó giống như sóng biển, vỗ vào bờ rồi rút ra, nhưng lại có đợt sóng khác quay lại cao hơn. Tôi thấy rất đúng", bà kể.\nHiện nay ông Sơn chuẩn bị hồ sơ nâng hạng sản phẩm trứng gà OCOP từ 3 sao lên 4 sao, hướng tới mở rộng thị trường ở nhiều tỉnh. Đầu tháng 9, khi được Trung ương Hội Nông dân Việt Nam công nhận danh hiệuNông dân Việt Nam xuất sắc 2025, ông Sơn khá bất ngờ bởi "giữa hàng triệu nông dân, mình chỉ là một hạt cát nhỏ bé". Điều ông nhớ nhất trong hành trình lập nghiệp là việc bán đôi hoa tai cưới đã đổi lấy một cơ nghiệp bền vững.\nLãnh đạo Hội Nông dân xã Nghĩa Hưng đánh giá ông Trần Xuân Sơn là tấm gương nông dân vượt khó, dám nghĩ dám làm. "Từ hai bàn tay trắng, ông đã gây dựng trang trại hàng chục nghìn con gà với doanh thu tiền tỷ, tạo việc làm cho nhiều lao động và chia sẻ kinh nghiệm giúp các hộ khác vươn lên", vị này nói.', 'Nghệ AnTrước khi trở thành nông dân xuất sắc với trang trại gà 23.000 con, ông Trần Xuân Sơn suýt phá sản, phải bán đôi hoa tai cưới của vợ để lấy tiền mua cám.', @cat_id_thoi_su_phap_luat, @user_id_uchung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('UAV Lancet tập kích tổ hợp chống drone Mỹ chuyển cho Ukraine', 'uav-lancet-tap-kich-to-hop-chong-drone-my-chuyen-cho-ukraine', 'https://i1-vnexpress.vnecdn.net/2025/10/14/vampire-JPG-1760417410-7382-1760417480.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=rPB39Y4TKXimsJhvsksGDA', 'Truyền hình quốc phòng NgaZvezdahôm nay công bố video từ máy bay không người lái (UAV) trinh sát, cho thấy tổ hợp phòng không Vampire của quân đội Ukraine triển khai ở cánh đồng trống trải tại tỉnh Sumy, cách tiền tuyến khoảng 47 km.\nTrong hình ảnh tiếp theo, UAV tự sát Lancet khóa mục tiêu, lao vào tổ hợp Vampire và phát nổ, khiến khí tài này bốc cháy. Ngọn lửa lan đến cụm bệ phóng, làm quả đạn nạp sẵn tự động bắn ra ngoài. Một binh sĩ Ukraine rời khoang lái và chạy ra xa, trong khi một người nằm im dưới đất và không rõ tình trạng.\nBộ Quốc phòng Nga cho biết đòn tấn công được tiến hành bởi Trung tâm Rubikon, đơn vị vận hành thiết bị bay không người lái (drone) tinh nhuệ hàng đầu nước này.\nQuân đội Ukraine chưa bình luận về video.\nVampire là tổ hợp phòng không tầm ngắn do hãng L3Harris của Mỹ chế tạo, bắt đầu thực chiến từ năm 2023. Nhà sản xuất khẳng định đây là "vũ khí nhỏ gọn, chuyên chống UAV và drone, có giá phải chăng, lắp được trên nhiều loại xe cơ giới hoặc tàu thuyền".\nL3Harris tuyên bố biến thể Vampire mới nhất được tích hợp AI, giúp hệ thống có khả năng phát hiện và bắn hạ mục tiêu nhỏ, khó bám bắt như drone góc nhìn thứ nhất.\nTổ hợp Vampire sử dụng Hệ thống Sát thương Chính xác Tiên tiến thế hệ 2 (APKWS II) được BAE Systems phát triển.\nAPKWS là hình thức cải tiến rocket thông thường Hydra cỡ 70 mm thành vũ khí dẫn đường bằng cách lắp thêm đầu dò laser bán chủ động, cánh lái và ngòi nổ cận đích. Phiên bản APKWS II bổ sung đầu dò hồng ngoại, cho phép rocket tự bám bắt mục tiêu trong quá trình tiếp cận, không cần đài điều khiển liên tục chiếu xạ laser như biến thể đời đầu.\nMỗi quả đạn APKWS II có giá khoảng 20.000-30.000 USD. Một tổ hợp Vampire hoàn chỉnh thường có chi phí khoảng 1,4 triệu USD, song con số thực tế có thể thay đổi tùy vào hợp đồng và cấu hình.\nUkraine chủ yếu dùng Vampire để đánh chặn UAV tự sát tầm xa Geran-2, do chi phí rocket rẻ hơn tên lửa phòng không truyền thống, trong khi tầm bắn và năng lực chiến đấu của APKWS II vượt trội so với tên lửa phòng không vác vai, drone đánh chặn và súng máy.\nThống kê nguồn mở, dựa trên hình ảnh đã được xác thực, cho thấy UAV Lancet đã tấn công hơn 3.800 mục tiêu của Ukraine kể từ khi thực chiến lần đầu hồi tháng 7/2022. Con số thực tế có thể cao hơn do nhiều video không được quân đội Nga công bố để bảo đảm bí mật.\nTrong số này, những chiếc Lancet phá hủy hoàn toàn 900 mục tiêu, làm hư hại 1.769 mục tiêu và số còn lại chưa rõ kết quả. Mục tiêu hàng đầu của Lancet là pháo kéo và pháo tự hành, với lần lượt 1.049 và 660 tổ hợp đã hứng đòn tập kích, số lượng tổ hợp phòng không tự hành Ukraine trúng đòn là 103.\nNguyễn Tiến(TheoZvezda, AP, AFP)', 'Nga công bố video UAV Lancet tấn công tổ hợp phòng không Vampire của Ukraine ở tỉnh Sumy, khiến nó bốc cháy và tự phóng đạn.', @cat_id_the_gioi, @user_id_nguyentien, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Ông Macron khẳng định sẽ không từ chức', 'ng-macron-khang-dinh-se-khong-tu-chuc', 'https://i1-vnexpress.vnecdn.net/2025/10/14/Macron-Ai-Ca-p-6059-1760412342.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=AcpRab7qVhZtVlhDVm1VhA', '"Tôi được nhân dân ủy nhiệm phục vụ đất nước, tìm kiếm giải pháp cho những trăn trở của người dân và làm mọi việc trong khả năng cho nền độc lập. Đó là điều duy nhất có ý nghĩa với tôi vào thời điểm này. Tôi sẽ đảm bảo ổn định cho đất nước. Những vấn đề còn lại là công việc của chính phủ", Tổng thống Emmanuel Macron bình luận ngày 13/10, bên lề hội nghị thượng đỉnh tại Ai Cập về lộ trình hòa bình cho Dải Gaza.\nChính trường Pháp đang rơi vào khủng hoảng trầm trọng nhất trong vài thập kỷ qua, với 6 lần bổ nhiệm thủ tướng trong chưa đầy hai năm. Ngày 10/10, ông Macron tuyên bố tái bổ nhiệm ông Sebastien Lecornu làm tân thủ tướng, 4 ngày sau khi ông Lecornu xin từ chức vì danh sách nội các bị phản đối gay gắt.\nCác đảng đối lập cực hữu và cánh tả, lẫn một số cựu đồng minh của Tổng thống Pháp, những ngày qua liên tục kêu gọi ông từ chức, dù nhiệm kỳ hiện tại còn kéo dài đến năm 2027.\nPhát biểu tại Ai Cập, ông Macron tiếp tục bác bỏ khả năng giải tán quốc hội và tổ chức tổng tuyển cử sớm, khẳng định "không muốn đánh cược" và muốn đất nước tiến bộ. Chính trị gia 47 tuổi đồng thời chỉ trích các đảng đối lập đang "kích động hỗn loạn" thay vì hợp tác ổn định đất nước.\n"Trong các phe phái đang gieo rắc chia rẽ và hoài nghi, nhiều người không thật sự nỗ lực khi đất nước cần họ. Trách nhiệm của mỗi chúng ta là đảm bảo ổn định", ông Macron nói. "Lực lượng chính trị kích động bất ổn đối với chính phủ của Sebastian Lecornu mới là những người phải chịu trách nhiệm cho tình trạng hỗn loạn hiện nay".\nTrong khi đó, giới chuyên gia cho rằng cuộc khủng hoảng hiện nay bắt nguồn từ quyết định đầy rủi ro của ông Macron hồi giữa năm 2024, khi ông giải tán quốc hội và tổ chức bầu cử sớm với hy vọng củng cố ưu thế đa số của các đảng trung dung.\nKết quả lại trái ngược với kỳ vọng, khi phe cực hữu và cánh tả đều giành thêm ghế, đẩy chính trường Pháp vào tình trạng "quốc hội treo" với ba khối lớn gần như không ai chịu thỏa hiệp.\nNội các mới dự kiến trình dự thảo ngân sách vào ngày 15/10, hướng tới mức thâm hụt dưới 5% GDP, theo người phát ngôn chính phủ Maud Bregeon. Quốc hội có 70 ngày để thẩm định dự luật trước khi năm tài khóa kết thúc.\nTuy nhiên, chính phủ lần hai của ông Lecornu đang đối mặt rủi ro bỏ phiếu bất tín nhiệm ngay khi trình bày cương lĩnh trước quốc hội. Đảng cánh tả Nước Pháp Bất khuất (LFI) và cực hữu Mặt trận Quốc gia (RN) đã tuyên bố sẽ nộp kiến nghị hạ bệ nội các mới.\nTổng thống Pháp là nguyên thủ quốc gia, được người dân bầu 5 năm một lần, chịu trách nhiệm về chính sách đối ngoại và quốc phòng. Thủ tướng Pháp được tổng thống bổ nhiệm sau các kỳ bầu cử nghị viện, thường thuộc đảng kiểm soát quốc hội, chịu trách nhiệm về các chính sách trong nước và đảm nhiệm công việc hàng ngày của chính phủ. Thành viên trong nội các do thủ tướng đề cử và cần được tổng thống chấp thuận.\nThanh Danh(TheoAFP, Guardian)', 'Tổng thống Emmanuel Macron tuyên bố sẽ không từ chức dù Pháp đang đối mặt với khủng hoảng chính trị nghiêm trọng nhất trong nhiều năm.', @cat_id_the_gioi_tu_lieu, @user_id_thanhdanh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Sức ép khiến Hamas đổi ý về kế hoạch hòa bình của ông Trump', 'suc-ep-khien-hamas-doi-y-ve-ke-hoach-hoa-binh-cua-ong-trump', 'https://i1-vnexpress.vnecdn.net/2025/10/13/AFP-20211130-AA-30112021-53484-4124-3828-1760370434.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=TZhE9V8FdSOfZcCCP2d_Bw', 'Khi đến khu nghỉ dưỡng Sharm el-Sheikh, Ai Cập để tham gia vòng đàm phán gián tiếp với Israel ngày 6/10, phó thủ lĩnh Hamas Khalil al-Hayya được các nhà đàm phán nước chủ nhà và Qatar cung cấp nội dung dự thảo kế hoạch hòa bình 20 điểm mà Tổng thống Mỹ Donald Trump thúc đẩy. Sau khi đọc dự thảo, ông al-Hayya lập tức phản hồi ngắn gọn: "Không chấp nhận".\nCác quan chức thạo tin cho biết nội dung kế hoạch hoàn toàn không giống với những gì ông al-Hayya được thông báo trước đó, và dường như đã được Israel can thiệp chỉnh sửa đáng kể. Tài liệu có điều khoản yêu cầu Hamas phải giải giáp vũ khí, nhưng lại quy định các bước cụ thể để đảm bảo Israel chấm dứt chiến sự.\nAl-Hayya tuyên bố Hamas sẽ tiếp tục giữ 20 con tin còn sống ở Gaza cho đến khi có được cam kết mang tính ràng buộc rằng xung đột sẽ chấm dứt. Điều này khiến nỗ lực đàm phán hòa bình ở Gaza tiếp tục lâm vào bế tắc.\nNhưng hai ngày sau, al-Hayya trở lại Ai Cập và nói "đồng ý". Nội dung kế hoạch vẫn vậy, song áp lực mà Hamas phải chịu trong thời gian ngắn này đã tăng đáng kể từ mọi phía.\nĐể Hamas miễn cưỡng chấp nhận một thỏa thuận mà họ vốn không muốn, đội ngũ của Tổng thống Trump đã nỗ lực suốt nhiều tuần để tập hợp các cường quốc Trung Đông như Ai Cập, Qatar và Thổ Nhĩ Kỳ, cùng phối hợp gây sức ép lên ban lãnh đạo của nhóm.\n"Hamas đã phải chịu rất nhiều áp lực từ các trung gian đàm phán khu vực", Tahani Mustafa, nhà nghiên cứu tại Hội đồng châu Âu về Quan hệ Đối ngoại, nói vớiWSJ. "Hamas cũng hiểu họ khó có thể tồn tại về mặt chính trị nếu tình hình tiếp tục theo hướng này, nhất là khi sự ủng hộ dành cho họ đang suy giảm".\nChiến sự Israel - Hamas bùng phát tháng 10/2023 sau khi nhóm vũ trang tấn công Israel, khiến hơn 1.200 người chết và bắt cóc 251 con tin. Israel đã mở chiến dịch vào Gaza với mục tiêu loại bỏ Hamas. Giao tranh khiến hơn 67.800 người Palestine thiệt mạng, hơn 170.000 người bị thương, chủ yếu là dân thường.\nCác cuộc đàm phán trước đó không mang lại kết quả đáng kể, ngoài thỏa thuận ngừng bắn ngắn ngủi cuối tháng 1, giúp giải cứu một phần con tin. Tiến trình có bước ngoặt sau vụ Israel ngày 9/9 không kích thủ đô Doha, Qatar, nhắm mục tiêu nhóm quan chức Hamas tham gia hòa đàm. Đòn tập kích khiến Mỹ và các nước Arab phẫn nộ, quyết tâm chấm dứt cuộc xung đột.\nKhi ông Trump buộc Thủ tướng Israel Benjamin Netanyahu phải công khai chấp nhận kế hoạch 20 điểm vào ngày 29/9, Hamas trở thành trở ngại duy nhất còn lại trong nỗ lực hòa bình. Phe chỉ trích từ lâu đã kêu gọi các nước Arab gây áp lực mạnh mẽ hơn với Hamas, và lần này, điều kiện đã chín muồi.\n"Tôi nghĩ ai cũng chỉ muốn mọi chuyện sớm kết thúc", ông Michael Ratney, cựu đại sứ Mỹ tại Arab Saudi, nhận định.\nViệc ông al-Hayya, người suýt bị Israel hạ sát trong cuộc tập kích ở Doha, dẫn đầu phái đoàn Hamas đến Sharm el-Sheikh đàm phán được xem là biểu tượng cho sức chống trả ngoan cường của nhóm. Nhưng trên thực tế, Hamas không còn nhiều thời gian, khi các cường quốc khu vực dần thống nhất lập trường về kế hoạch hòa bình.\nNgay trong ngày 6/10, ngày đầu tiên của đàm phán, các bên trung gian đã đưa ra tối hậu thư với Hamas: tin tưởng và chấp nhận kế hoạch của ông Trump, hoặc phải tiếp tục một cuộc chiến vô tận. Nhóm có thời hạn 5 ngày để quyết định.\nTrưởng đoàn đàm phán của Israel là Bộ trưởng Các vấn đề chiến lược Ron Dermer, đến Sharm el-Sheikh cùng ngày để thương lượng về trao đổi con tin - tù nhân, một nội dung trong kế hoạch 20 điểm.\nĐến ngày 7/10, các bên vẫn bế tắc về các nội dung như Israel sẽ rút quân đến khu vực nào ở Gaza để Hamas có thể tập hợp rồi trao trả các con tin, một nguồn thạo tin nói vớiReuters.\nĐể tạo đột phá, Thủ tướng Qatar Mohammed bin Abdulrahman Al Thani cùng phái đoàn Thổ Nhĩ Kỳ, do giám đốc tình báo Ibrahim Kalin dẫn đầu, cũng tham gia quá trình đàm phán. Sáng 8/10, đặc phái viên Mỹ về Trung Đông Steve Witkoff và con rể ông Trump là Jared Kushner đến Sharm el-Sheikh.\nSự hiện diện của phái đoàn Thổ Nhĩ Kỳ có vai trò quan trọng, vì Ankara cũng có quan hệ mật thiết với Hamas, trong khi Tổng thống Tayyip Erdogan nói ông chủ Nhà Trắng đã nhờ ông giúp thuyết phục Hamas chấp thuận kế hoạch.\nCác trung gian đàm phán lưu ý với Hamas việc tiếp tục giữ con tin đang trở thành gánh nặng chiến lược với nhóm, vì điều này tạo ra cơ sở chính đáng để Israel tiếp tục chiến dịch.\nHọ cảnh báo al-Hayya rằng nếu ông không chấp thuận, Hamas sẽ mất toàn bộ sự bảo trợ chính trị và ngoại giao. Qatar và Thổ Nhĩ Kỳ sẽ ngừng cho phép lãnh đạo chính trị của Hamas hoạt động trên lãnh thổ của họ, Ai Cập không tiếp tục vận động để Hamas có tiếng nói trong quản trị Gaza hậu chiến.\nTrên thực địa ở Gaza, Hamas cũng lâm vào tình thế vô cùng khó khăn. Nhóm cạn kiệt nguồn lực tài chính, không thể thực hiện chiến thuật du kích chống lại lực lượng Israel đã kiểm soát phần lớn Gaza, người dân Palestine kiệt quệ vì đói khát, chỉ mong chiến tranh sớm chấm dứt.\nHamas nhận ra việc tiếp tục giữ con tin đã làm suy yếu sự ủng hộ toàn cầu dành cho người Palestine và nếu trao trả hết, Israel không còn cớ để nối lại giao tranh, một quan chức Palestine nói.\nNhững yếu tố này đủ để thúc đẩy Hamas ngày 8/10 thông báo đạt thỏa thuận trao trả toàn bộ con tin đang giữ ở Gaza và đồng ý với thỏa thuận giai đoạn một ngày 9/10.\nTổng thống Trump, Quốc vương Qatar Tamim bin Hamad Al Thani, Tổng thống Thổ Nhĩ Kỳ Recep Tayyip Erdogan và Tổng thống Ai Cập Abdel Fattah al-Sisi ngày 13/10 ký tuyên bố chung Gaza, trong đó các bên cam kết theo đuổi tầm nhìn toàn diện về hòa bình, an ninh và thịnh vượng chung khu vực, đồng thời hoan nghênh những tiến bộ đã đạt được về thiết lập thỏa thuận hòa bình "toàn diện và bền vững" tại Dải Gaza.\n"Đây là một ngày tuyệt vời với thế giới, một ngày tuyệt vời với Trung Đông", ông Trump phát biểu, tuyên bố các lãnh đạo đã đạt được điều mà mọi người từng nói là "bất khả thi". "Cuối cùng chúng ta đã có hòa bình ở Trung Đông", ông nói.\nTuy nhiên, Hamas không nhận được bất kỳ cam kết bằng văn bản nào đảm bảo giai đoạn ngừng bắn đầu tiên sẽ tiến triển thành thỏa thuận toàn diện để chấm dứt chiến sự, theo hai quan chức Hamas. Nhóm chấp nhận đảm bảo bằng lời từ các trung gian Mỹ, Qatar, Ai Cập và Thổ Nhĩ Kỳ rằng Tổng thống Trump sẽ giám sát thỏa thuận và không cho phép Israel nối lại chiến dịch nếu các con tin đã được thả.\nMỹ, Qatar, Ai Cập, Thổ Nhĩ Kỳ chưa bình luận về thông tin.\nKhi được hỏi về áp lực phải chịu khi ký thỏa thuận, Khaled al-Qaddoumi, đại diện Hamas ở Iran, nói nhóm ủng hộ các đề xuất từ Tổng thống Trump vì kế hoạch đảm bảo người Palestine không bị ép trục xuất khỏi Gaza, mở đường cho hoạt động nhân đạo và tái thiết dải đất.\nHamas giờ đây trông cậy vào ông Trump trong việc đảm bảo Israel không nối lại giao tranh trên Dải Gaza, Qaddoumi nói.\nNhư Tâm(TheoWSJ, Reuters, AFP)', 'Hamas ban đầu bác bỏ kế hoạch hòa bình của ông Trump, nhưng sức ép từ các nước Arab cùng tình thế bất lợi ở Gaza đã khiến nhóm phải đổi ý.', @cat_id_the_gioi_phan_tich, @user_id_nhutam, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("Đàn ông Nhật 'vượt ngại' để cầm ô che nắng", 'an-ong-nhat-vuot-ngai-de-cam-o-che-nang', 'https://i1-vnexpress.vnecdn.net/2025/10/14/f6096a4f-30a8-4dc4-bcb0-bdfe2b-8625-3111-1760411908.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=9-eDmAs-eWVBj0DeAEb_GA', 'Phụ nữ Nhật Bản từ lâu chuộng che ô mùa hè để bảo vệ làn da khỏi tia cực tím, trong khi đàn ông nước này chủ yếu che ô lúc trời mưa, hiếm khi sử dụng lúc trời nắng do định kiến "giống phụ nữ".\nTuy nhiên, khảo sát 8.400 người hồi tháng 9 của giới chức Tokyo cho thấy 44% nam giới được hỏi đã sử dụng ô để che nắng trong mùa hè nóng kỷ lục năm nay, so với 91% nữ giới.\nTrong số nhóm nam dùng ô, 44% cho biết đây là lần đầu tiên họ sử dụng ô che nắng, 23% nói đã dùng từ năm ngoái, 13% bắt đầu dùng từ hai năm trước.\nỞ nhóm 20-30 tuổi, hơn 1/2 nam giới được hỏi xác nhận bắt đầu dùng ô từ năm nay. Tỷ lệ sử dụng thấp nhất được ghi nhận ở nhóm trên 50 tuổi, với 39%.\nKhi được hỏi có để ý ánh nhìn của những người xung quanh không, 61% nam giới nói điều này không làm họ khó chịu, trong khi 34% thấy ít ngại ngùng hơn so với tưởng tượng ban đầu.\nGiới chức Tokyo cũng khảo sát 25 công ty tham gia lĩnh vực sản xuất ô che nắng, ghi nhận 4 công ty báo cáo lượng ô che nắng cho nam giới tăng gấp đôi hoặc hơn so với năm ngoái.\nNhật Bản ghi nhận nhiệt độ cao nhất mọi thời đại ở mức 41,8 độ C tại Isesaki, tỉnh Gunma trong hè năm nay. Nhiệt độ trung bình tháng 6 đến tháng 8 cao hơn bình thường 2,36 độ C, mức cao nhất kể từ khi nước này bắt đầu thu thập số liệu vào năm 1898.\nĐức Trung(TheoKyodo, Mainichi)', 'Khảo sát cho thấy gần một nửa nam giới ở Tokyo dùng ô để che nắng trong mùa hè 2025, cho thấy định kiến che ô trời nắng "giống phụ nữ" đang phai dần.', @cat_id_the_gioi_quan_su, @user_id_uctrung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("Trung Quốc sẵn sàng 'đấu đến cùng' với Mỹ trong thương chiến", 'trung-quoc-san-sang-dau-den-cung-voi-my-trong-thuong-chien', 'https://i1-vnexpress.vnecdn.net/2025/10/14/AFP-20250417-42LH27U-v1-HighRe-3269-9558-1760413109.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=B77LLuJMX4fWib3csLI8Ow', '"Lập trường của Trung Quốc về cuộc chiến thuế quan và thương mại vẫn nhất quán. Nếu các ngài muốn đấu, chúng tôi sẽ đấu đến cùng. Nếu các ngài muốn đàm phán, cánh cửa của chúng tôi luôn để mở", một phát ngôn viên không nêu tên của Bộ Thương mại Trung Quốc hôm nay ra tuyên bố cho hay.\nNgười phát ngôn này cũng cáo buộc Mỹ lạm dụng các biện pháp kiểm soát xuất khẩu và "phân biệt đối xử" với Trung Quốc.\nTuyên bố được đưa ra sau khi Tổng thống Mỹ Donald Trump hôm 10/10 thông báo sẽ áp thuế bổ sung 100% với hàng hóa Trung Quốc, đồng thời áp đặt kiểm soát xuất khẩu với toàn bộ phần mềm thiết yếu do Mỹ sản xuất từ ngày 1/11 hoặc sớm hơn, nhằm đáp trả "những động thái cực kỳ gây hấn" của Bắc Kinh liên quan đến chính sách hạn chế xuất khẩu đất hiếm.\nHai ngày sau, ông Trump thể hiện giọng điệu hòa hoãn khi nói Mỹ muốn "giúp Trung Quốc", không phải gây tổn hại cho nước này. Bộ trưởng Tài chính Mỹ Scott Bessent ngày 13/10 cho biết hai nước đã có nhiều cuộc thảo luận cuối tuần qua và "giảm đáng kể căng thẳng". Ông Bessent nhấn mạnh mức thuế 100% với Trung Quốc "không nhất thiết phải thực thi".\nNgười phát ngôn Bộ Thương mại Trung Quốc cho hay Bắc Kinh đã thông báo với Mỹ về biện pháp kiểm soát xuất khẩu đất hiếm trước khi công bố. Theo bộ này, các biện pháp như vậy là "hợp pháp" và nhằm bảo vệ an ninh quốc gia.\n"Mỹ không thể vừa theo đuổi đối thoại vừa đe dọa áp đặt biện pháp hạn chế mới. Đây không phải cách phù hợp để hợp tác với Trung Quốc", phát ngôn viên Bộ Thương mại Trung Quốc nhấn mạnh, thêm rằng các cuộc đàm phán cấp làm việc giữa hai bên đã diễn ra hôm 13/10.\nDữ liệu chính thức được công bố tháng 9 cho thấy xuất khẩu của Trung Quốc vẫn duy trì sức bật đáng kể, tăng vọt 8,3% so với cùng kỳ năm ngoái, bất chấp sức ép thương mại từ Mỹ. Đây là mức tăng trưởng nhanh nhất kể từ tháng 3 và nhanh hơn nhiều so với dự báo. Xuất khẩu tới Mỹ, thị trường tiêu dùng lớn nhất thế giới, tăng lên mức 34,3 tỷ USD.\nHàng hóa Trung Quốc hiện chịu mức thuế ít nhất 30% theo các sắc lệnh mà ông Trump đã ký. Trung Quốc áp thuế trả đũa Mỹ ở mức 10%.\nCác nguyên tố đất hiếm được xem rất quan trọng với ngành công nghệ cao, từ điện thoại thông minh, xe điện, thiết bị quân sự tới công nghệ năng lượng tái tạo. Trung Quốc hiện nắm giữ khoảng 70% nguồn cung đất hiếm trên toàn thế giới.\nBộ trưởng Tài chính Mỹ nói việc Trung Quốc hạn chế xuất khẩu đất hiếm là động thái khiêu khích và Washington đã phản đối mạnh mẽ. Ông khẳng định Mỹ sẽ bác bỏ các yêu cầu cấp phép của Trung Quốc liên quan tới xuất khẩu đất hiếm hoặc các sản phẩm từ vật liệu này.\nTrung Quốc chỉ trích Mỹ tiêu chuẩn kép, khẳng định chính Mỹ từ lâu cũng áp đặt các biện pháp kiểm soát như vậy và thực thi các hành vi phân biệt đối xử chống lại Trung Quốc dưới danh nghĩa an ninh quốc gia.\nHuyền Lê(TheoAFP, Reuters, Investing)', 'Bắc Kinh tuyên bố sẵn sàng "đấu đến cùng" trong cuộc chiến thương mại với Washington, sau khi ông Trump dọa áp thêm thuế 100% hàng hóa từ Trung Quốc.', @cat_id_the_gioi, @user_id_huyenle, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Quốc hội Mỹ điều tra công ty Starlink của Elon Musk', 'quoc-hoi-my-dieu-tra-cong-ty-starlink-cua-elon-musk', 'https://i1-vnexpress.vnecdn.net/2025/10/14/afp-20251014-74wj6qr-v1-highre-6933-1689-1760410835.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=fnFQNvwA6rfwtb1Th04pSw', 'Ủy ban Kinh tế Liên hợp Quốc hội Mỹ, cơ quan quyền lực gồm cả thành viên Hạ viện và Thượng viện, hôm 13/10 xác nhận cuộc điều tra về mối liên quan của công ty vệ tinh Starlink với các trung tâm lừa đảo ở Myanmar đã bắt đầu vào tháng 7. Ủy ban lưỡng đảng này có thẩm quyền yêu cầu tỷ phú Elon Musk, chủ sở hữu Starlink, ra điều trần.\nThượng nghị sĩ Maggie Hassan, thành viên hàng đầu của ủy ban, trước đó đã kêu gọi Musk hành động, sau khi nhận được cảnh báo từ nhiều chính trị gia Mỹ và Thái Lan, quốc gia có chung biên giới với Myanmar.\nCác công tố viên bang California hồi tháng 7 năm ngoái từng công khai cảnh báo Starlink rằng hệ thống vệ tinh của họ đang được những kẻ lừa đảo ở Myanmar sử dụng, nhưng không nhận được phản hồi.\nSpaceX, công ty sở hữu Starlink, hiện chưa lên tiếng về thông tin này.\nMyanmar và Campuchia là nơi xuất hiện nhiều trung tâm lừa đảo trực tuyến, với các tổ hợp được điều hành chủ yếu bởi tội phạm có tổ chức ở Trung Quốc, thu lợi bất chính hàng tỷ USD mỗi năm. Thứ trưởng Tài chính Mỹ John K. Hurley cho biết người dân nước này đã mất hơn 10 tỷ USD trong năm 2024 vì các hoạt động lừa đảo trực tuyến từ khu vực Đông Nam Á, tăng 66% so với năm trước.\nTrung Quốc, Thái Lan và Myanmar hồi tháng 2 buộc các nhóm vũ trang ở Myanmar, vốn bảo kê cho trung tâm lừa đảo, phải cam kết "xóa bỏ" những ổ lừa đảo trực tuyến ở thị trấn Myawaddy, sát biên giới Thái Lan - Myanmar. Khi đó, khoảng 7.000 người, phần lớn là công dân Trung Quốc, được giải cứu khỏi hệ thống "tổng đài lừa đảo" mà Liên Hợp Quốc mô tả là hoạt động dựa trên lao động cưỡng bức và buôn người.\nNhiều người làm việc tại trung tâm kể rằng họ bị các ông trùm điều hành đường dây lừa đảo đánh đập và bị buộc phải làm việc nhiều giờ liên tục.\nTuy nhiên, sau khi chiến dịch trấn áp kết thúc, những tòa nhà mới lại mọc lên với tốc độ chóng mặt trong các khu phức hợp được canh phòng nghiêm ngặt ở Myawaddy, dọc sông Moei ngăn cách Myanmar và Thái Lan.\nẢnh vệ tinh từ Planet Labs PBC cho thấy hàng chục tòa nhà mới được xây dựng hoặc cải tạo trong khu phức hợp lớn nhất KK Park từ tháng 3 đến tháng 9. Một số tòa nhà khác còn được gắn ăng-ten dịch vụ Starlink, theo hình ảnh vệ tinh và video quay bằng flycam của AFP.\nDữ liệu từ APNIC, cơ quan đăng ký Internet khu vực châu Á, cho thấy Starlink đã trở thành nhà cung cấp Internet lớn nhất tại Myanmar mỗi ngày từ 3/7 đến 1/10. Dịch vụ của Starlink không xuất hiện ở Myawaddy vào thời điểm chiến dịch trấn áp trung tâm lừa đảo diễn ra.\nTại Myawaddy, nhiều con đường mới được xây thêm, chốt an ninh ở lối vào được mở rộng đáng kể. Hoạt động xây dựng quy mô lớn đang diễn ra, trong đó có các tòa nhà văn phòng lớn.\nÍt nhất 5 bến phà mới băng qua sông Moei cũng đã xuất hiện để vận chuyển vật tư và hàng hóa từ phía Thái Lan sang.\nPhân tích của AFP còn cho thấy công trình xây dựng đang diễn ra tại nhiều nơi trong số 27 địa điểm nghi là trung tâm lừa đảo ở Myawaddy, trong đó có Shwe Kokko, nơi Bộ Tài chính Mỹ gọi là trung tâm lừa đảo "khét tiếng".\nTháng trước, Mỹ trừng phạt gần 20 cá nhân, tổ chức ở Myanmar và Campuchia, trong đó có 9 người có liên hệ với Shwe Kokko và trùm tội phạm Trung Quốc She Zhijiang, với cáo buộc tổ chức, bảo kê ngành công nghiệp lừa đảo trực tuyến. She Zhijiang là người thành lập khu phức hợp Yatai New City gồm nhiều tòa nhà cao tầng Shwe Kokko.\nHuyền Lê(TheoAFP)', 'Ủy ban quốc hội Mỹ đang điều tra nghi vấn công ty Starlink của Elon Musk cung cấp dịch vụ Internet vệ tinh cho các trung tâm lừa đảo ở Myanmar.', @cat_id_the_gioi_tu_lieu, @user_id_huyenle, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("Ông Trump khen Thủ tướng Italy 'xinh đẹp'", 'ng-trump-khen-thu-tuong-italy-xinh-dep', 'https://i1-vnexpress.vnecdn.net/2025/10/14/AP25286576111712-3487-1760400793.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=Rg7WiDzJTER_kW8Wy1Jvew', '"Tôi đáng lẽ không được phép nói điều này, bởi bình thường nếu nói ra, sự nghiệp chính trị của các vị sẽ chấm dứt. Nhưng bà ấy là một phụ nữ trẻ xinh đẹp", Tổng thống Mỹ Donald Trump đề cập tới Thủ tướng Italy Giorgia Meloni khi đang phát biểu về nỗ lực thúc đẩy hòa bình ở Trung Đông.\nÔng chủ Nhà Trắng giải thích thêm rằng "nếu bạn ở Mỹ và dùng từ \'xinh đẹp\' để nói về một phụ nữ, đó sẽ là dấu chấm hết cho sự nghiệp chính trị của bạn. Nhưng tôi sẽ thử xem sao".\nÔng sau đó quay lại phía bà Meloni, nữ lãnh đạo 48 tuổi, và nói rằng "bà không ngại nếu được khen như vậy chứ? Bởi vì bà thật sự xinh đẹp". Không rõ phản ứng lúc đó của Thủ tướng Italy như thế nào, do ông Trump đứng chắn giữa bà Meloni và máy quay.\n"Họ thực sự rất tôn trọng bà ấy ở Italy. Bà ấy là chính trị gia rất thành công", Tổng thống Mỹ nói thêm.\nThủ tướng Italy là người phụ nữ duy nhất trong số khoảng 30 lãnh đạo có mặt tại hội nghị thượng đỉnh tại Ai Cập, nơi họ cùng ký tuyên bố chung để cam kết thúc đẩy hòa bình Gaza.\nBà Giorgia Meloni đảm nhận vai trò thủ tướng từ năm 2022 và là người phụ nữ đầu tiên giữ vị trí này ở Italy.\nThanh Tâm(TheoAFP)', 'Tổng thống Trump khen ngợi Thủ tướng Italy "xinh đẹp", khi bà là nữ lãnh đạo duy nhất có mặt tại hội nghị thượng đỉnh Gaza ở Ai Cập.', @cat_id_the_gioi_phan_tich, @user_id_thanhtam, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Người Hàn thấp thỏm với nỗi sợ bị bắt cóc ở Campuchia', 'nguoi-han-thap-thom-voi-noi-so-bi-bat-coc-o-campuchia', 'https://i1-vnexpress.vnecdn.net/2025/10/14/co-so-lu-a-da-o-jpeg-176040747-8108-1406-1760408050.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=Q_doBksY-eVn24GlNclH4Q', '"Các nhà hàng, công ty du lịch ở đây đang mất nhiều hợp đồng đặt chỗ, nhiều người cắt liên lạc với đối tác Hàn Quốc. Mọi người khuyên nhau khi đến đây: Hãy cúi đầu im lặng, đừng gây chú ý", Chủ tịch Hiệp hội người Hàn Quốc tại Campuchia Chung Myung-kyu ngày 13/10 nói vớiKorean Times.\nTrong vài tháng qua, giới chức Hàn Quốc ghi nhận nhiều vụ công dân mất tích ở Campuchia, hầu hết bị bắt cóc tống tiền hoặc phải làm việc trong trung tâm lừa đảo, gây ảnh hưởng không nhỏ đến quan hệ Seoul - Phnom Penh. Những trung tâm này phần lớn do các băng nhóm có liên hệ với mạng lưới tội phạm gốc Hoa điều hành.\nVụ án gây rúng động nhất là trường hợp sinh viên 22 tuổi, quê Bắc Gyeongsang, bị bắt cóc tống tiền sau khi đến Campuchia vào ngày 17/7, rồi được phát hiện tử vong vào đầu tháng 8 sau khi bị tra tấn.\nTổng thống Lee Jae-myung hôm 11/10 yêu cầu chính phủ "dốc toàn bộ nguồn lực ngoại giao để bảo vệ công dân khỏi tội phạm tại Campuchia", trong khi cảnh sát Hàn Quốc đề nghị thành lập đơn vị chuyên trách ở Phnom Penh để hỗ trợ công dân và điều tra những vụ án liên quan.\nCác chủ doanh nghiệp du lịch địa phương cho biết nhiều hợp đồng bị hủy và lượng khách giảm mạnh kể từ khi các vụ bắt cóc, tra tấn, lừa đảo được giới chức Hàn Quốc công bố, cộng hưởng với những thông tin cảnh báo được lan truyền chóng mặt trên mạng xã hội.\nPark, một nhân viên ngân hàng 48 tuổi ở Seoul, từng sang Campuchia hồi đầu năm để triển khai dự án hợp tác với một ngân hàng sở tại và dự kiến quay lại vào cuối năm nay hoặc đầu năm sau. Công ty còn lên kế hoạch bố trí nhân sự làm việc ở Campuchia trong ít nhất 6 tháng để giám sát dự án.\nTuy nhiên, sau các vụ án nghiêm trọng, Park và gia đình cảm thấy bất an, đến mức anh đang cân nhắc chuyển bộ phận làm việc nếu bị yêu cầu sang Campuchia công tác dài hạn.\n"Du lịch an toàn" đã trở thành thông điệp trọng tâm của các chương trình xúc tiến du lịch nước ngoài ở Hàn Quốc. Các công ty lữ hành lớn cho biết số khách hủy tour chưa nhiều, nhưng họ phải gấp rút củng cố quy trình đảm bảo an ninh cho du khách và tăng cường phối hợp với các cơ quan bảo hộ ngoại giao của Hàn Quốc.\nTheo Bộ Ngoại giao Hàn Quốc, số trường hợp công dân bị bắt cóc hoặc mất tích khi đến Campuchia đã tăng vọt trong hai năm qua, từ chưa đến 20 vụ năm 2022 lên hơn 220 vụ năm 2024 và vượt 330 vụ chỉ trong 8 tháng đầu năm nay. Nạn nhân chủ yếu bị dụ dỗ đến Phnom Penh, Sihanoukville và Bokor.\n"Họ bị giam, bị đánh và bị buộc tham gia hoạt động phi pháp. Khi trốn thoát, họ không có hộ chiếu hay hành lý, chỉ bắt taxi thẳng đến đại sứ quán hoặc văn phòng của chúng tôi", ông Chung Myung-kyu kể trong cuộc phỏng vấn với đàiCBS Radio. "Đã có trường hợp tội phạm đến tận sân bay tìm cách bắt lại người bỏ trốn. Chúng tôi cố gắng hỗ trợ đến cùng, nhưng số đơn cầu cứu ngày càng vượt quá khả năng".\nMỗi tuần có 5-10 người liên hệ hiệp hội xin giải cứu hoặc hỗ trợ rời khỏi các trung tâm lừa đảo. Riêng trong năm nay, hiệp hội đã hỗ trợ hồi hương hơn 300 công dân Hàn Quốc.\n"Có người bị ép buộc dụ dỗ thêm nạn nhân mới thì sẽ được thả, hoặc được trả tiền để săn thêm người cho tổ chức. Chúng tôi gần đây phát hiện một số người Hàn Quốc thậm chí đã trở thành quản lý tầm trung trong các nhóm tội phạm", ông nói.\nCampuchia vài năm qua đã mở hàng loạt chiến dịch truy quét tội phạm lừa đảo trực tuyến. Phần lớn nghi phạm bị bắt trong các chiến dịch này là người nước ngoài, theo Ban Thư ký Ủy ban Quốc gia Phòng chống Lừa đảo Công nghệ Campuchia.\nTuy nhiên, ông Chung cho rằng loại hình tội phạm này vẫn là thách thức rất lớn đối với chính quyền sở tại vì các băng nhóm hoạt động tinh vi, giữa các chân rết không có sự liên kết chặt chẽ và phân tán khắp nơi.\nTheo Liên Hợp Quốc, hàng trăm nghìn người đã bị các băng nhóm tội phạm lừa vào làm việc tại những trung tâm lừa đảo trực tuyến hoạt động bất hợp pháp trên khắp Đông Nam Á. Báo cáo của Liên Hợp Quốc năm 2023 ước tính các mạng lưới lừa đảo này mang về nguồn lợi nhuận phi pháp hàng tỷ USD mỗi năm.\nThanh Danh(TheoKorean Times, Hanbook Ilbo, Khmer Times)', 'Nhiều vụ bắt cóc tống tiền nhắm vào công dân Hàn Quốc thời gian qua khiến du khách, doanh nhân nước này dần e ngại đến Campuchia.', @cat_id_the_gioi_quan_su, @user_id_thanhdanh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Nhà hàng ở Mỹ chật vật với đòn thuế của ông Trump', 'nha-hang-o-my-chat-vat-voi-don-thue-cua-ong-trump', 'https://i1-vnexpress.vnecdn.net/2025/10/14/RedHookTavern-Burger20932-1760-7642-1497-1760405763.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=Y9PgbaAeECjBk6HUuwvtCw', 'Theo khảo sát mới từ công ty phần mềm quản lý nhà hàng Toast, gần một nửa chủ nhà hàng cho biết họ dự định tăng giá thực đơn nếu lạm phát, thuế và chi phí lao động tiếp tục leo thang.\nHiệp hội Nhà hàng Quốc gia Mỹ ước tính để duy trì biên lợi nhuận khiêm tốn 5%, một nhà hàng trung bình ở nước này sẽ cần tăng giá đồ ăn, thức uống khoảng 30,3%, điều có thể khiến họ mất khách.\nĐây là tin đáng lo ngại đối với thực khách Mỹ, những người cảm thấy những món ăn yêu thích như burger, burrito, cà phê vốn đã rất đắt. Theo hệ thống theo dõi giá thực đơn tháng 8 của Toast, chỉ có hai mặt hàng là cánh gà và bia có giá tăng chậm hơn lạm phát toàn quốc 2,9%.\nGiá thực phẩm tại Mỹ đã tăng mạnh trong những tháng gần đây, đạt mức cao nhất kể từ tháng 8/2022. Lạm phát hiện vượt mục tiêu giữ ở mức 2% do Cục Dự trữ Liên bang (Fed) đề ra.\nTheoBusiness Insider, đợt tăng giá này gắn liền với chiến lược thuế quan của Tổng thống Donald Trump. Theo đánh giá của Ernie Tedesch, chuyên gia kinh tế tại Đại học Yale, đòn thuế mà ông Trump áp với hàng hóa các nước sẽ khiến giá cả các mặt hàng tổng thể ở Mỹ tăng 2,3%, khiến mỗi hộ gia đình mất thêm 3.800 USD trong năm nay.\nCuối tuần trước, ông Trump tuyên bố sẽ áp thuế bổ sung 100% với tất cả hàng hóa Trung Quốc. Nếu lời đe dọa này trở thành hiện thực, mức tăng giá các mặt hàng ở Mỹ có thể sẽ cao hơn.\nDù thuế quan của ông Trump với Trung Quốc chủ yếu ảnh hưởng đến các mặt hàng như đồ may mặc, thiết bị điện tử, chúng cũng gồm nhiều loại cá, đồ ăn nhẹ, gia vị.\n"Thuế quan có thể được thực hiện chỉ bằng một chữ ký, song chúng ta phải mất nhiều năm mới thiết lập lại được chuỗi cung ứng toàn cầu", John Lash, lãnh đạo cấp cao của nền tảng chuỗi cung ứng kết nối e2open, nói. "Quá trình này là một công thức phức tạp với nhiều yếu tố bất ngờ, nhưng nhìn chung sẽ dẫn đến giá tiêu dùng tăng cao".\nĐối với ngành nhà hàng, vốn hoạt động với biên lợi nhuận mỏng, chi phí nguyên liệu tăng cao có thể đẩy họ tiến gần điểm khủng hoảng. Không chỉ chi phí thực phẩm, nhân công tăng cao, nhà hàng cũng bị ảnh hưởng khi người tiêu dùng thắt chặt chi tiêu, ít ăn ngoài và thường tìm nơi có khuyến mãi.\nTrong tuyên bố tháng 8, lãnh đạo Hiệp hội Nhà hàng Quốc gia Michelle Korsmo cho biết các nhà hàng không muốn tăng giá vì có thể khiến người tiêu dùng ít ăn ngoài hơn, gây ảnh hưởng toàn ngành, nhưng họ "có thể không còn lựa chọn nào khác" nếu muốn duy trì hoạt động.\nBình luận viên Katherine Tangalakis-Lippert của Business Insider cho rằng thực khách Mỹ nên thưởng thức nốt những chiếc burger 14 USD, bởi đó có thể là mức giá rẻ nhất còn lại, hoặc chọn cánh gà và bia như những món hợp túi tiền cuối cùng trong thực đơn.\nĐức Trung(TheoBusiness Insider, Forbes)', 'Đòn thuế của ông Trump khiến lạm phát và chi phí lao động tăng, buộc nhiều nhà hàng Mỹ xem xét tăng giá tới 30% mới có lợi nhuận.', @cat_id_the_gioi, @user_id_uctrung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Nhiều công dân Hàn Quốc mất tích ở Campuchia', 'nhieu-cong-dan-han-quoc-mat-tich-o-campuchia', 'https://i1-vnexpress.vnecdn.net/2025/10/14/trung-tam-lua-dao-jpeg-1759219-3476-5267-1760405947.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=z4QDPAkIrzF0iXUmEpVjJA', 'Cảnh sát tỉnh Gyeongbuk của Hàn Quốc hôm 13/10 cho biết đã nhận được thông tin rằng A, người đàn ông ngoài 30 tuổi ở thành phố Sangju, tới Campuchia tháng 8 năm ngoái và mất tích. A sau đó liên lạc với gia đình qua cuộc gọi video trên Telegram và nói sẽ được thả nếu nộp 20 triệu won (14.000 USD).\nTại tỉnh Chungcheongbuk, một gia đình báo cảnh sát rằng con trai họ, được gọi là B, dường như đang bị giam tại Campuchia.\n"Con trai liên lạc với chúng tôi qua KakaoTalk, nói rằng đã tới Campuchia cùng hai người quen và bị giữ trong một tòa nhà ở Phnom Penh. Con tôi nói tài khoản ngân hàng của nó đang được dùng để rửa tiền và nó sẽ gặp nguy hiểm nếu tài khoản bị đóng", bố mẹ của B cho hay.\nCảnh sát xác nhận tài khoản ngân hàng được đề cập đã bị sử dụng để lừa đảo qua điện thoại tại Hàn Quốc.\nTại quận Gyeyang ở thành phố Incheon, cảnh sát đã bắt một người đàn ông ngoài 30 tuổi vì nghi ngờ vi phạm Đạo luật Giao dịch Tài chính Điện tử. Người này sang Campuchia vào tháng 8/2024 để làm ăn, nhưng lại dính líu đến tổ chức điều hành web cờ bạc bất hợp pháp và cho họ mượn tài khoản ngân hàng.\nKhoảng 1 tỷ won (700.000 USD) tiền cờ bạc bất hợp pháp đã được chuyển vào tài khoản của anh này. "Tôi bị giam ở Campuchia suốt một tuần và sống sót nhờ 10 chai nước khoáng. Những người Hàn Quốc khác cũng bị giam cùng với tôi và bị tổ chức này đe dọa", người đàn ông khai.\nỞ thành phố Gwangju thuộc tỉnh Nam Jeolla, nam thanh niên hơn 20 tuổi, được giới chức gọi là C, cũng mất tích sau khi sang Campuchia và cảnh sát đã vào cuộc điều tra. Trước khi lên đường, C nói với gia đình rằng sẽ làm nhân viên giám sát an toàn tại hồ bơi.\nCảnh sát xác nhận C đã tới Thái Lan rồi sang Campuchia vào tháng 6/2024 và đang tìm tung tích người này. Gia đình C khai rằng họ nhận được cuộc gọi từ số máy lạ, người đó nói "cứu tôi với" rồi cúp máy.\nCũng ở Gwangju, hai nam thanh niên ngoài 20 tuổi tới Campuchia lần lượt vào tháng 11/2024 và tháng 4 năm nay cũng đã mất tích. Cảnh sát đang điều tra sự việc.\nCảnh sát tỉnh Jeonbuk hồi tháng 3 nhận được trình báo với nội dung "tôi nhận được ảnh từ em gái đang ở Campuchia, cho thấy ngón tay của nó bị cắt đứt". Cảnh sát sau đó tìm thấy D, ngoài 20 tuổi, nhưng không phát hiện bằng chứng liên quan tội phạm. D khai rằng bị đứt ngón tay do tai nạn trong lúc đốt pháo hoa.\nTuy nhiên, cảnh sát vẫn tiếp tục điều tra vì D đến nay vẫn chưa trở về nhà, dù gia đình đã yêu cầu.\nHồi tháng 6, công dân E ngoài 20 tuổi ở Jeju đã sang Campuchia và được thả sau khi gia đình trả khoản tiền điện tử tương đương 35 triệu won (24.500 USD). E khi đó nói với gia đình rằng đang làm việc ở Campuchia để trả nợ do bị lừa. Người này không bị giam, nhưng cũng không được ra ngoài.\nVào tháng 7, hai người đàn ông và một phụ nữ ngoài 20 tuổi ở tỉnh Gyeongnam tới Campuchia sau khi người môi giới hứa hẹn sắp xếp công việc bán thời gian thu nhập cao. Họ bị giam một tuần và được về sau khi gia đình nộp khoản tiền điện tử trị giá 16 triệu won (11.200 USD).\nTheo Bộ Ngoại giao Hàn Quốc, số vụ công dân bị giam giữ tại Campuchia đã lên tới 330 trong năm nay, tăng đáng kể so với 220 vụ trong năm ngoái và 17 vụ năm 2023.\nĐáng chú ý là trường hợp nam sinh viên 22 tuổi từ tỉnh Bắc Gyeongsang đến Campuchia vào ngày 17/7 để nghỉ hè, sau đó bị bắt cóc và tử vong hồi tháng 8. Khám nghiệm pháp y bước đầu của giới chức Campuchia cho thấy nam sinh viên tử vong vì đột quỵ, hậu quả từ những đòn tra tấn và chấn thương nặng.\nGia đình anh cho biết họ nhận được cuộc gọi đòi khoản tiền chuộc 38.500 USD "để giải quyết rắc rối" khoảng một tuần sau khi con trai đến Campuchia. Vài ngày sau, nhóm bắt cóc cắt liên lạc với gia đình khi người thân của anh liên hệ với cảnh sát.\nHàn Quốc đã ban hành khuyến cáo đi lại đặc biệt cho công dân khi đến Phnom Penh, đồng thời đề nghị người dân cẩn thận trước quảng cáo việc làm ở Campuchia nếu đối tác hứa hẹn mức lương cao bất thường. Bộ Ngoại giao Hàn Quốc cho hay nhiều nạn nhân đã bị lừa, bắt cóc tống tiền hoặc ép tham gia các đường dây lừa đảo trực tuyến ở Campuchia.\nHuyền Lê(TheoChosun,Korea Herald)', 'Hàn Quốc ghi nhận nhiều vụ công dân mất tích ở Campuchia, hầu hết bị bắt cóc tống tiền hoặc phải làm việc trong trung tâm lừa đảo.', @cat_id_the_gioi_tu_lieu, @user_id_huyenle, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Tổng thống Ukraine sắp tới Mỹ gặp ông Trump', 'tong-thong-ukraine-sap-toi-my-gap-ong-trump', 'https://i1-vnexpress.vnecdn.net/2025/10/14/AP25266638531627-7065-1760401249.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=UIIrfqcFQVcAeJPQFd9jgQ', 'Tổng thống Volodymyr Zelensky ngày 13/10 thông báo ông sẽ tham gia cùng phái đoàn Ukraine đến Washington trong tuần này để đàm phán với các chính trị gia, doanh nghiệp Mỹ. Ông Zelensky không nêu cụ thể ngày ông khởi hành, nhưng cho biết có thể gặp Tổng thống Mỹ Donald Trump vào ngày 17/10.\nÔng Zelensky cho biết cuối tuần qua đã điện đàm với người đồng cấp Mỹ hai lần, trao đổi về những thách thức mà Ukraine phải đối mặt khi bị Nga không kích vào các cơ sở hạ tầng năng lượng. Hai lãnh đạo cũng thảo luận về tình hình chiến trường.\n"Chúng tôi đã nói về một số vấn đề nhạy cảm. Dù rất quan trọng, những cuộc trò chuyện đó vẫn chưa đủ", lãnh đạo Ukraine nhận xét.\nTheo ông Zelensky, phái đoàn Ukraine đã lên đường tới Mỹ gồm Thủ tướng Ukraine Yulia Svyrydenko, các quan chức cấp cao của văn phòng Tổng thống, Hội đồng An ninh Quốc gia và các đại diện ngoại giao. Khi Tổng thống Ukraine tới Washington tham gia cùng phái đoàn này, ông dự định có thêm nhiều cuộc gặp với các nghị sĩ, các công ty quân sự Mỹ.\n"Các chủ đề chính là phòng không và khả năng chúng ta gây áp lực lên Nga", Tổng thống Ukraine cho biết.\nTổng thống Mỹ cuối tuần trước nói với người đồng cấp Ukraine về khả năng cung cấp vũ khí cho nước này, trong đó có tên lửa hành trình Tomahawk. Ông Trump tuyên bố Washington sẽ không chuyển trực tiếp Tomahawk cho Kiev, nhưng đề cập phương án bán tên lửa cho NATO để liên minh viện trợ Ukraine.\nNga tuyên bố kịch bản Mỹ chuyển Tomahawk hay tên lửa tương tự cho Ukraine là "giai đoạn leo thang mới", đồng thời khẳng định sẽ tăng cường hệ thống phòng không nếu Mỹ quyết định hành động.\nNgọc Ánh(TheoAFP)', 'Tổng thống Ukraine Zelensky sẽ đến Mỹ tuần này để gặp người đồng cấp Trump, thảo luận về vấn đề phòng không và cách gia tăng áp lực lên Nga.', @cat_id_the_gioi_phan_tich, @user_id_ngocnh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Venezuela đóng cửa sứ quán ở Na Uy', 'venezuela-dong-cua-su-quan-o-na-uy', 'https://i1-vnexpress.vnecdn.net/2025/10/14/AFP-20251013-78MW829-v1-HighRe-6428-7963-1760399081.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=kN-mf9sxo5hzisFf86eLfw', 'Chính phủ Venezuela ngày 13/10 tuyên bố đóng cửa đại sứ quán ở Oslo, Na Uy trong quá trình tái cấu trúc ngành ngoại giao. Nước này cũng đóng cửa đại sứ quán ở Australia, đồng thời mở các cơ quan đại diện ngoại giao ở Zimbabwe và Burkina Faso, những quốc gia được coi là "đối tác chiến lược trong cuộc chiến chống lại áp lực bá quyền".\nBộ Ngoại giao Na Uy sau đó xác nhận Venezuela đã đóng cửa đại sứ quán tại Oslo mà không nêu lý do.\n"Đây là điều đáng tiếc. Bất chấp những bất đồng về một số vấn đề, Na Uy vẫn muốn duy trì đối thoại cởi mở với Venezuela và sẽ tiếp tục hợp tác theo hướng này", người phát ngôn Bộ Ngoại giao Na Uy Cecilie Roang nói, nhấn mạnh thêm rằng giải Nobel Hòa bình độc lập với chính phủ.\nĐộng thái của Venezuela diễn ra vài ngày sau khi thủ lĩnh phe đối lập Maria Corina Machado đoạt giải Nobel Hòa bình "vì những nỗ lực không biết mệt mỏi trong việc thúc đẩy quyền dân chủ cho người dân Venezuela và đấu tranh để chuyển đổi một cách công bằng, hòa bình sang nền dân chủ".\nChính phủ Venezuela chưa bình luận về việc bà Machado đoạt giải Nobel Hòa bình. Tuy nhiên, Tổng thống Venezuela Nicolas Maduro hôm 12/10 gọi bà là "phù thủy ma quái".\nNgọc Ánh(TheoAFP)', 'Venezuela tuyên bố đóng cửa đại sứ quán ở Oslo, vài ngày sau khi lãnh đạo phe đối lập Machado được trao giải Nobel Hòa bình.', @cat_id_the_gioi_quan_su, @user_id_ngocnh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Mỹ và các nước ký tuyên bố chung Gaza', 'my-va-cac-nuoc-ky-tuyen-bo-chung-gaza', 'https://i1-vnexpress.vnecdn.net/2025/10/14/AFP-20251013-78MN3UN-v1-HighRe-7263-3620-1760408882.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=eL3pG19JJCoYQJ0DVIxtJA', '"Đây là một ngày tuyệt vời với thế giới, một ngày tuyệt vời với Trung Đông", Tổng thống Mỹ Donald Trump phát biểu khi hơn 20 lãnh đạo thế giới ngồi vào bàn đàm phán tại hội nghị thượng đỉnh về Gaza ở khu nghỉ dưỡng Sharm el-Sheikh, Ai Cập ngày 13/10.\nÔng sau đó tuyên bố các lãnh đạo đã đạt được điều mà mọi người từng nói là "bất khả thi". "Cuối cùng chúng ta đã có hòa bình ở Trung Đông", ông nói.\nTuyên bố chung sau hội nghị được ký bởi ông Trump, Quốc vương Qatar Tamim bin Hamad Al Thani, Tổng thống Thổ Nhĩ Kỳ Recep Tayyip Erdogan và Tổng thống Ai Cập Abdel Fattah al-Sisi.\nTheo tuyên bố, các bên cam kết theo đuổi tầm nhìn toàn diện về hòa bình, an ninh và thịnh vượng chung khu vực, đồng thời hoan nghênh những tiến bộ đã đạt được về thiết lập thỏa thuận hòa bình "toàn diện và bền vững" tại Dải Gaza.\nTổng thống Ai Cập Abdel Fattah al-Sisi nói thỏa thuận Gaza đã "khép lại một chương đau thương trong lịch sử nhân loại" và mở đường cho giải pháp hai nhà nước.\nHội nghị thượng đỉnh về chấm dứt xung đột diễn ra sau khi Hamas ngày 13/10 trả tự do cho 20 con tin còn sống cuối cùng mà họ giữ ở Gaza. Đổi lại, Israel đã trả tự do cho 1.986 tù nhân, hầu hết là người Palestine, đang bị giam trong các nhà tù nước này. Đây là một phần trong kế hoạch hòa bình 20 điểm về Gaza mà ông Trump công bố hồi tháng 9.\n"Cơn ác mộng dài đằng đẵng và đau đớn cuối cùng đã kết thúc không chỉ đối với người Israel, mà còn đối với người Palestine và nhiều người khác", ông Trump phát biểu trước quốc hội Israel trước khi tới Ai Cập.\nTheo lộ trình hòa bình do Tổng thống Mỹ đề xuất, sau khi Hamas - Israel bàn giao con tin - tù nhân, Tel Aviv sẽ bắt đầu rút lực lượng khỏi Dải Gaza để chuẩn bị cho các bước đàm phán tiếp theo. Giới quan sát cho rằng những điều có thể gây trở ngại cho thỏa thuận hòa bình cuối cùng là Hamas từ chối giải giáp và Israel không cam kết rút quân hoàn toàn khỏi Gaza.\nPhát ngôn viên Hazem Qassem của Hamas ngày 13/10 thúc giục ông Trump và các bên trung gian của thỏa thuận Gaza "tiếp tục theo dõi hành động của Israel và đảm bảo nước này không tiếp tục hành động gây hấn chống lại người dân của chúng tôi".\nTuy nhiên, ông chủ Nhà Trắng nhiều lần bày tỏ tin tưởng lệnh ngừng bắn sẽ được duy trì. Khi xuất hiện bên cạnh Tổng thống Sisi ở Sharm el-Sheikh, ông Trump nói cuộc đàm phán về các bước tiếp theo của kế hoạch "đã bắt đầu".\nThanh Tâm(TheoAFP)', 'Ông Trump ca ngợi "ngày tuyệt vời cho Trung Đông" khi cùng lãnh đạo các nước ký tuyên bố nhằm củng cố lệnh ngừng bắn và hòa bình ở Gaza.', @cat_id_the_gioi, @user_id_thanhtam, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Lời dụ dỗ đến Campuchia khiến sinh viên Hàn bị tra tấn đến chết', 'loi-du-do-den-campuchia-khien-sinh-vien-han-bi-tra-tan-den-chet', 'https://i1-vnexpress.vnecdn.net/2025/10/13/trung-ta-m-lu-a-da-o-Campuchia-2032-8149-1760348639.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=6fu7vNXnmdSHmmiam5IgFg', 'Dư luận Hàn Quốc những ngày qua rúng động với vụ án sinh viên 22 tuổi, quê ở huyện Yecheon, tỉnh Bắc Gyeongsang, thiệt mạng ở Campuchia hồi tháng 8, sau nhiều tuần mất liên lạc với gia đình. Giới chức Hàn Quốc và Campuchia cho rằng sinh viên này là nạn nhân của một vụ bắt cóc chuyên nghiệp, liên quan một trung tâm lừa đảo xuyên quốc gia tại tỉnh Kampot ở Campuchia.\nDanh tính nạn nhân vẫn chưa được công bố, chỉ được truyền thông và giới chức Hàn Quốc gọi tắt là "A". Trong khi đó, hồ sơ của cơ quan công tố địa phương cho biết nạn nhân là công dân Hàn Quốc, mang họ Park. Kết quả khám nghiệm pháp y sơ bộ cho thấy nạn nhân bị tra tấn đến chết.\nCảnh sát tỉnh Gyeongbuk ngày 11/10 thông báo bắt một số nghi phạm trong đường dây môi giới "thồ tiền" xuyên quốc gia có thể liên quan đến vụ án. "Thồ tiền" là thuật ngữ chỉ mô hình dụ dỗ nạn nhân cho các tổ chức tội phạm thuê tài khoản ngân hàng để thực hiện giao dịch bất chính. Người cho thuê tài khoản ngân hàng thường được đặt biệt danh là "ngựa thồ".\nCác điều tra viên tin rằng A đã liên hệ với những người môi giới của băng đảng tội phạm từ trước, nhưng chưa rõ phương thức. Sau khi giành được niềm tin của A, nhóm tội phạm lừa anh đến Campuchia làm "việc nhẹ lương cao", thuyết phục rằng đó là nơi anh có cơ hội kiếm tiền nhiều hơn trong ngành công nghệ thông tin, thực chất là cơ sở lừa đảo chuyên nghiệp.\nNgày 17/7, A thông báo với gia đình rằng anh bay tới Campuchia để nghỉ hè, kết hợp tham dự một buổi triển lãm. Thế nhưng, khoảng một tuần sau, người nhà nhận được cuộc gọi nặc danh từ một người đàn ông nói tiếng Hàn pha giọng Trung Quốc.\n"Cậu ta đã gây rắc rối ở đây và bị bắt rồi. Gửi tôi 50 triệu won (khoảng 35.000 USD) thì chúng tôi sẽ thả cậu ta", người này nói.\nGia đình lập tức báo cảnh sát và liên hệ với đại sứ quán Hàn Quốc tại Phnom Penh để bắt đầu chiến dịch tìm kiếm. Giới chức Hàn Quốc vào cuộc điều tra, nhưng không thể xác định được vị trí của A.\n4 ngày sau, do chưa nhận được tiền chuộc, nhóm bắt cóc tống tiền cắt liên lạc với gia đình. Ngày 8/8, khoảng hai tuần sau cuộc gọi cuối cùng từ nhóm tống tiền, cảnh sát Campuchia phát hiện thi thể nam sinh trong một chiếc xe bán tải gần núi Bokor, khu vực khét tiếng nhiều băng nhóm tội phạm mạng và lừa đảo việc làm. Giới chức địa phương xác định nguyên nhân tử vong là ngừng tim do bị tra tấn nghiêm trọng.\nHai tháng sau khi được phát hiện, thi thể của nam sinh Hàn Quốc vẫn chưa được đưa về nước do quy trình khám nghiệm và xác minh pháp y giữa hai nước chưa hoàn tất. Cảnh sát tỉnh Gyeongbuk cho biết sẽ phối hợp cùng Phòng Khoa học Pháp y Quốc gia và cảnh sát Campuchia tiến hành khám nghiệm chung để xác định nguyên nhân cái chết và hỗ trợ hồi hương thi thể.\nÍt nhất ba nghi phạm quốc tịch Trung Quốc đã bị bắt tại tỉnh Campuchia liên quan cái chết của nam sinh Hàn Quốc. Những người này được xác định là thành viên trung tâm lừa đảo ở vùng núi Bokor, nơi từng giam giữ A cùng 14 công dân Hàn Quốc. Cảnh sát thu giữ nhiều thiết bị điện tử, điện thoại và tài liệu chứng minh cơ sở này chuyên về hai mô hình gọi điện lấy thông tin cá nhân và lừa tình chiếm tiền.\n"A bị đánh đập tàn bạo đến mức anh ta không thể thở được. Tôi nghe bảo rằng anh ta chết trên ôtô trên đường đến bệnh viện", một nhân chứng từng bị giam cùng A ở núi Bokor, nằm trong nhóm 14 công dân Hàn Quốc được giải cứu, tường trình với văn phòng của nghị sĩ Park Chan-dae thuộc đảng Dân chủ.\nGia đình của nam sinh Hàn Quốc gửi thư cầu cứu cho nghị sĩ Park từ những ngày đầu tiên nhận tin con trai họ bị bắt cóc.\nBộ Ngoại giao Hàn Quốc cũng xác nhận với văn phòng nghị sĩ Park rằng số vụ công dân Hàn Quốc bị bắt cóc hoặc giam giữ trái phép ở Campuchia trong 8 tháng đầu năm nay đã lên đến 330 vụ, tăng mạnh so với con số 221 vụ vào năm ngoái và 36 vụ trong giai đoạn 2021-2023. Nạn nhân chủ yếu bị dụ dỗ đến Phnom Penh, Sihanoukville và Bokor.\nTrước tình trạng tội phạm lừa đảo ngày càng tập trung nhắm vào công dân Hàn Quốc, Tổng thống Lee Jae-myung đã yêu cầu chính phủ "dốc toàn bộ nguồn lực ngoại giao để bảo vệ công dân khỏi tội phạm tại Campuchia". Ngoại trưởng Cho Hyun cũng triệu tập Đại sứ Campuchia Khuon Phon Rattanak để yêu cầu hợp tác phòng chống tội phạm xuyên quốc gia.\nCảnh sát Hàn Quốc đồng thời đề nghị thành lập "Phòng Hàn Quốc" tại Campuchia, tương tự mô hình đã triển khai ở Philippines và Thái Lan, để hỗ trợ công dân và điều tra các vụ án liên quan.\nBộ Nội vụ Campuchia ngày 12/10 tuyên bố sẽ tiếp tục phối hợp cùng đại sứ quán Hàn Quốc trong vụ án ở Kampot, "tiếp tục điều tra và bắt giữ mọi nghi phạm cũng như đồng phạm còn lại, đảm bảo công lý được thực thi".\nThanh Danh(TheoKorean Times, Chosun, Independent, Khmer Times)', 'Nam sinh Hàn dường như đã bị lừa đến Campuchia làm "việc nhẹ lương cao", để rồi bị bắt cóc và tra tấn đến chết khi gia đình báo cảnh sát.', @cat_id_the_gioi_tu_lieu, @user_id_thanhdanh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Loạt thi thể trôi sông ở Houston gây lo ngại về kẻ giết người hàng loạt', 'loat-thi-the-troi-song-o-houston-gay-lo-ngai-ve-ke-giet-nguoi-hang-loat', 'https://i1-vnexpress.vnecdn.net/2025/10/13/White-Oak-Bayou-texas-2025-100-6306-9173-1760359537.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=bsR_RV_cPG4FHrJTga_wvQ', 'Đội thợ lặn ở Houston, bang Texas ngày 8/10 vớt được một thi thể nam giới từ nhánh sông White Oak ở đường Marie Street. Viện Khoa học Pháp y Hạt Harris đang tìm nguyên nhân tử vong của người này.\nĐây là thi thể trôi sông thứ 16 được tìm thấy tại Houston trong năm nay. Hồi tháng 9, giới chức thành phố phát hiện 5 thi thể chỉ trong 5 ngày.\nCảnh sát Houston cho biết các nạn nhân gồm cả nam và nữ, từ 20 đến 60 tuổi. Trong số những người tử vong có Jade McKissic, sinh viên 20 tuổi của Đại học Houston. Thi thể cô được tìm thấy ngày 15/9 sau khi mất tích vào ngày 11/9.\nViệc giới chức liên tục tìm thấy thi thể trôi sông trong thời gian ngắn khiến người dân Houston lo ngại về khả năng có một sát thủ hàng loạt trong thành phố. Kevin Gannon, cựu điều tra viên của Sở Cảnh sát New York (NYPD), tin rằng nhóm Sát thủ Mặt cười (Smiley Face) có thể liên quan đến những cái chết này.\nÔng Gannon từ lâu ủng hộ giả thuyết gây tranh cãi cho rằng có một nhóm gọi là Sát thủ Mặt cười gây ra hàng trăm ca tử vong do đuối nước trên toàn quốc. Những sát thủ này được cho là nhắm vào những thanh niên trẻ và vứt xác xuống nước.\nTuy nhiên, cảnh sát trưởng Houston Noe Diaz cho biết không có bằng chứng xác thực về thông tin này. "Tin đồn gây ra nỗi sợ hãi và lo lắng trong cộng đồng. Điều quan trọng là phải dựa vào thông tin điều tra đã được xác minh", ông Diaz nói.\nKể từ đó, giới chức đã phát hiện thêm ít nhất hai thi thể trôi nổi. Trong họp báo ngày 23/9, Thị trưởng Houston John Whitmire khẳng định không có sát nhân hàng loạt trong thành phố, dù thừa nhận tình hình "đáng báo động".\nÔng lưu ý Houston có tổng cộng 4.000 km sông ngòi với lượng đáng kể người vô gia cư sinh sống, trong đó có nhiều người lạm dụng chất kích thích. Khi người vô gia cư qua đời, bạn bè họ không đưa họ đến nhà tang lễ, ông giải thích, ám chỉ việc ném thi thể xuống sông.\nSở Cảnh sát Houston và Văn phòng Thị trưởng Whitmire chưa bình luận về thi thể thứ 16 được tìm thấy ngày 8/10.\nJoseph Giacalone, cựu điều tra viên NYPD, giáo sư tư pháp hình sự tại Đại học Bang Pennsylvania Lehigh Valley, bày tỏ hoài nghi về thuyết âm mưu về nhóm Sát thủ Mặt cười, nhưng cũng cho rằng phát biểu của Thị trưởng Whitmire là quá sớm.\n"Có 16 thi thể, không thể chỉ nói rằng tất cả đều là người vô gia cư bị ném xác xuống sông sau khi chết," ông nói. "Thậm chí còn không biết họ có phải là người trong thành phố hay không".\nÔng khuyến cáo giới chức Houston điều tra kỹ lưỡng mỗi trường hợp tử vong, khám nghiệm tử thi và xét nghiệm độc chất đầy đủ cho từng thi thể, nhân viên pháp y cũng có thể xác định xem họ chết trước khi rơi xuống nước hay chết đuối, ông nói.\nĐức Trung(TheoPeople, FOX News, Click2Houston)', 'Giới chức Houston, Texas tìm thấy số thi thể trôi sông nhiều kỷ lục trong năm nay, khiến dư luận lo ngại về sát nhân hàng loạt, dù chưa có bằng chứng xác thực.', @cat_id_the_gioi_phan_tich, @user_id_uctrung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Người Mỹ bắt đầu thấm nỗi đau khi chính phủ đóng cửa', 'nguoi-my-bat-dau-tham-noi-dau-khi-chinh-phu-dong-cua', 'https://i1-vnexpress.vnecdn.net/2025/10/13/download1-jpeg-1760325570-9611-1760325756.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=W0xstn3hhn0j8BGv7pyQUA', 'Tổng thống Donald Trump và các nghị sĩ quốc hội vẫn bế tắc khi chính phủ Mỹ bước vào tuần đóng cửa thứ ba. Ông Trump và phe Cộng hòa rất muốn mở cửa chính phủ trở lại, nhưng không hội đủ 60 phiếu tại Thượng viện để thông qua dự luật ngân sách cho các hoạt động chính phủ.\nPhe Dân chủ nêu điều kiện rằng ông Trump và các đồng minh phải duy trì các khoản trợ cấp bảo hiểm y tế như một phần của thỏa thuận nhằm mở cửa lại chính phủ. Phe Cộng hòa quyết không nhượng bộ, khiến các nỗ lực đàm phán đều rơi vào bế tắc.\nTình trạng chính phủ đóng cửa kéo dài được dự đoán sẽ tác động mạnh mẽ tới nền kinh tế Mỹ, khi việc chậm trễ chi trả tiền lương và thiếu hụt hàng tỷ USD cho các dịch vụ công không chỉ ảnh hưởng đến công chức liên bang mà còn gây khó khăn cho người dân bình thường nói chung.\nCác nhân viên chính phủ trong những lĩnh vực thiết yếu vẫn tiếp tục làm việc trong thời kỳ chính phủ đóng cửa, nhưng chưa chắc chắn được truy lĩnh lương, khiến nhiều người cáo bệnh hoặc xin nghỉ ốm, gây ra tình trạng thiếu hụt nhân sự tại một số cơ quan như kiểm soát không lưu, thuế vụ.\nĐiều này đã gây ra tình trạng chậm trễ nhiều chuyến bay trên toàn quốc, khiến đường dây hỗ trợ người nộp thuế của Sở Thuế vụ ngừng hoạt động, làm tắc nghẽn quá trình phê duyệt giấy phép tại Cơ quan Bảo vệ Môi trường và Bộ Giao thông Vận tải, đồng thời buộc các công viên quốc gia phải đóng cửa.\nViệc đóng cửa cũng làm gia tăng căng thẳng đối với những nhà thầu, nhân viên hợp đồng chính phủ khi họ không được trả lương. Lãnh đạo các doanh nghiệp phải mò mẫm lập kế hoạch kinh doanh mà không có dữ liệu kinh tế từ chính phủ. Doanh nghiệp nhỏ không thể vay ngân hàng và người dân không thể hoàn tất thủ tục vay thế chấp bất động sản do thiếu bảo hiểm lũ lụt liên bang.\nKhoảng 750.000 công chức đã bị cho nghỉ không lương trong thời gian chính phủ đóng cửa. Chính quyền Trump hôm 10/10 sa thải khoảng 4.000 nhân viên trong số đó.\nHôm 12/10, Tổng thống ra lệnh cho Lầu Năm Góc sử dụng lại quỹ nghiên cứu và phát triển để chi trả lương cho các quân nhân, những người đáng lẽ sẽ bị lỡ kỳ lương đầu tiên vào ngày 15/10. Nhưng các nhân viên dân sự sẽ không được hưởng chính sách tương tự và trong một dự thảo kiến nghị hồi tuần trước, văn phòng ngân sách chính quyền Trump cũng lập luận rằng các nhân viên đang bị cho nghỉ tạm thời không mặc nhiên được truy lĩnh lương khi chính phủ mở cửa trở lại.\nGiới chuyên gia nhận định đợt không được lĩnh lương đầu tiên của các công chức có thể sẽ gây tổn hại đến nền kinh tế ở nhiều cộng đồng dân cư và các dịch vụ bị ngừng càng lâu, tác động của việc đóng cửa chính phủ sẽ càng lan rộng.\nNgười tiêu dùng đang phải đối mặt với bất ổn kinh tế ngày càng tăng. Theo Văn phòng Ngân sách Quốc hội, đợt chính phủ đóng cửa trước, kéo dài 34 ngày trong nhiệm kỳ đầu tiên của Tổng thống Trump, đã làm giảm 11 tỷ USD sản lượng kinh tế quốc gia.\n"Các gia đình trên toàn quốc bắt đầu nếm trải tác động và mọi chuyện sắp trở nên tồi tệ hơn rất nhiều", lãnh đạo phe đa số tại Thượng viện Mỹ John Thune phát biểu hôm 10/10.\nGiới quan sát cho hay chính phủ đóng cửa đang gây thêm khó khăn cho các ngân hàng thực phẩm Mỹ vốn phụ thuộc vào nguồn trợ cấp liên bang để cung cấp các bữa ăn hỗ trợ người nghèo.\nGeorge Matysik, giám đốc điều hành tổ chức phi lợi nhuận Share Food Program, cho biết tại Philadelphia, trước khi chính phủ đóng cửa, họ thậm chí đã không thể đáp ứng đủ nhu cầu cho những người cần hỗ trợ, do bị mất nguồn trợ cấp liên bang trị giá 8,5 triệu USD vì các đợt cắt giảm chi tiêu mà Tổng thống Trump ban hành. Việc chính phủ đóng cửa càng làm tình hình trở nên tồi tệ hơn.\n"Tôi chưa từng thấy kho dự trữ thực phẩm của mình trống rỗng đến vậy trong ba tháng qua", Matysik nói. "Giờ chúng tôi lại phải chịu một đợt đóng cửa chính phủ nữa".\nTheo lời Matysik, tổ chức của ông đang thực hiện một chương trình chuyển các hộp thực phẩm đến tay 7.000 người cao tuổi mỗi tháng. Họ có nguồn dự trữ để tiếp tục hoạt động một tháng nữa, nhưng đang phải chật vật tìm phương án duy trì chương trình trong trường hợp nguồn hỗ trợ liên bang vẫn bị cắt.\nTại Colorado, Kate Hatten, giám đốc điều hành Homefront Military Network, tổ chức phi lợi nhuận hỗ trợ các gia đình quân nhân, cho hay đường dây nóng của họ gần đây liên tục quá tải.\n"Rất nhiều người gọi bày tỏ lo lắng về việc không nhận được lương vào ngày 15/10", bà nói. Hầu hết quân nhân, cựu chiến binh lo lắng về nguy cơ thiếu thực phẩm, nhưng cũng không ít người sợ không thể thanh toán hóa đơn điện nước hay trả tiền vay thế chấp mua nhà, mua xe hàng tháng.\n"Việc lỡ một kỳ lương trong khi phải gánh trên vai những khoản chi phí khổng lồ là rắc rối lớn với rất nhiều người", Hatten cho biết. "Tôi nghĩ thách thức chung hiện nay là chi phí sinh hoạt đã vượt quá mức tăng lương, phụ cấp nhà ở, chi trả phúc lợi hay tất cả những thứ tương tự".\nTại nhiều cơ quan liên bang, trong đó có Cục An sinh Xã hội (SSA), một số dịch vụ đã bị dừng trong thời gian chính phủ đóng cửa. Theo các nhân viên giấu tên từ SSA, họ đã phải dừng cấp thư xác minh trợ cấp, vốn được dùng như bằng chứng đủ điều kiện để người dân có thể nhận các phúc lợi cấp bang, như tem phiếu thực phẩm, trợ cấp nhà ở. Mặt khác, những tương tác giữa SSA với "bên thứ ba", như luật sư hay nhóm vận động cho người khuyết tật, cũng bị ngừng.\nTất cả các dịch vụ trên đều thiết yếu đối với những người phụ thuộc vào chúng.\n"Người dân gọi điện tới bày tỏ thất vọng về những hạn chế trong công việc của chúng tôi, đặc biệt khi họ yêu cầu hoàn thành việc mà chúng tôi đã phải ngừng lại do thiếu ngân sách", một nhân viên SSA nói.\nJonathan Stewart, nhân viên kiểm soát không lưu tại sân bay quốc tế Newark Liberty ở New Jersey, cho biết hệ thống đảm bảo an toàn hàng không đã bộc lộ vấn đề chỉ sau vài ngày chính phủ đóng cửa.\n"Khi bạn tạo thêm áp lực lên một lĩnh vực nghề nghiệp vốn đã căng thẳng, các kiểm soát viên không lưu sẽ không còn có khả năng điều phối máy bay", Stewart nói. "Đây đơn giản là một lĩnh vực đã cực kỳ căng thẳng. Những gì đang diễn ra khá dễ đoán với những người có chút hiểu biết về nó".\nKỳ nhận lương gần nhất của các nhân viên kiểm soát không lưu là vào ngày 14/10 và việc chính phủ đóng cửa đang khiến bất ổn ngày một gia tăng, đe dọa làm trầm trọng thêm tình trạng chậm trễ chuyến bay.\n"Càng đến gần kỳ lương, mọi người càng dễ bị xao lãng", một kiểm soát viên không lưu giấu tên ở Bờ Tây nói. "Chắc chắn không ai cố ý, nhưng tất cả chúng tôi đều sẽ bị phân tâm. Đó là chủ đề được bàn tán ngay cả khi chúng tôi đang làm việc. Bản thân việc nhân viên bị phân tâm đã là một rủi ro về an toàn".\nTuần trước, tình trạng thiếu nhân viên tại các sân bay trên khắp đất nước, từ Denver, Nashville đến Sân bay Quốc gia Reagan tại Washington, đã khiến hàng nghìn chuyến bay bị trễ.\nTrong một cuộc phỏng vấn trênFox Business, Bộ trưởng Giao thông Vận tải Sean P. Duffy cho biết thông thường trung bình chỉ khoảng 5% số chuyến bay chậm trễ liên quan đến thiếu nhân viên, song những ngày gần đây, tỷ lệ này tăng vọt lên mức 53%.\nGreg Daco, chuyên gia kinh tế tại công ty tư vấn quản lý EY-Parthenon, cho hay các giám đốc điều hành doanh nghiệp lo ngại nhất về việc thiếu dữ liệu kinh tế của chính phủ, trong đó có dữ liệu về việc làm, cũng như những thay đổi đột ngột về chính sách. Kết quả là họ trở nên thận trọng hơn khi quyết định đầu tư vào mở rộng hay đổi mới hoạt động kinh doanh.\nNancy Vanden Houten, chuyên gia kinh tế tại công ty tư vấn và dự báo thị trường toàn cầu Oxford Economics, đánh giá việc chính phủ đóng cửa kéo dài còn có khả năng ảnh hưởng đến chi tiêu tiêu dùng, vốn là động lực của nền kinh tế Mỹ.\nTheo bà, mọi người có xu hướng giảm chi tiêu nhanh chóng khi họ tin rằng thu nhập của mình sẽ giảm, dù chỉ là tạm thời. Điều này có thể trở nên tồi tệ hơn do những cảnh báo từ Nhà Trắng rằng các công chức có thể không được nhận lương truy lĩnh sau thời gian chính phủ đóng cửa.\nGiữa cơn hỗn loạn, nhiều công chức liên bang đã đối mặt với những hệ lụy tài chính từ việc mất lương. Một nhân viên SSA cho hay vợ anh đã phải nhận thêm ca làm để bù đắp cho việc anh không được trả lương tháng này.\nMột nhân viên đang bị cho nghỉ tạm thời thuộc Viện Y tế Quốc gia cho biết anh đã đến ngân hàng thực phẩm vào tuần trước để tích trữ đồ, chuẩn bị cho những tuần không lương.\n"Vợ tôi lo lắng cực độ", anh nói. "Tôi thực sự giận dữ với chính quyền".\nVũ Hoàng(TheoWSJ, Washington Post, AFP, Reuters)', 'Việc chính phủ đóng cửa sang tuần thứ ba bắt đầu tạo ra hệ lụy sâu rộng và ngày càng trầm trọng với đời sống người dân bình thường tại Mỹ.', @cat_id_the_gioi_quan_su, @user_id_vuhoang, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("'Thỏa thuận hòa bình Gaza gieo hy vọng cho Ukraine'", 'thoa-thuan-hoa-binh-gaza-gieo-hy-vong-cho-ukraine', 'https://i1-vnexpress.vnecdn.net/2025/10/13/AP25283625866193-4888-1760368352.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=pL72twkunp3-93jpOA_21A', '"Khi hòa bình đạt được ở một khu vực trên thế giới, nó sẽ gieo nhiều hy vọng về hòa bình cho các khu vực khác", Tổng thống Ukraine Volodymyr Zelensky nói hôm nay.\nÔng Zelensky tin rằng sau lệnh ngừng bắn và hòa bình ở Trung Đông, sự lãnh đạo cùng quyết tâm của các bên trung gian đàm phán "cũng có thể mang lại lợi ích cho chúng tôi".\nNhóm Hamas ở Dải Gaza hôm nay trao trả toàn bộ 20 con tin còn sống cho Tel Aviv như một phần của thỏa thuận hòa bình do Tổng thống Mỹ Donald Trump thúc đẩy. Ông Trump đã tới Trung Đông và dự kiến chủ trì hội nghị thượng đỉnh nhằm chấm dứt chiến sự ở Dải Gaza và thúc đẩy hòa bình khu vực, tổ chức tại Ai Cập với sự tham gia của khoảng 20 lãnh đạo thế giới.\nKể từ khi tranh cử tổng thống, ông Trump đã nhiều lần tuyên bố sẽ chấm dứt xung đột Ukraine. Ông đã tiến hành hội nghị thượng đỉnh với Tổng thống Nga Vladimir Putin, song chưa thể thúc đẩy Moskva - Kiev đạt được thỏa thuận hòa bình.\nThủ tướng Đức Friedrich Merz hôm nay cũng thúc giục ông Trump tận dụng động lực từ lệnh ngừng bắn giữa Hamas và Israel để thúc đẩy nỗ lực chấm dứt xung đột ở Ukraine. Ông cho biết sẽ thảo luận về vấn đề này với ông Trump bên lề hội nghị thượng đỉnh ở Ai Cập.\n"Chúng tôi cũng hy vọng Tổng thống Mỹ sẽ sử dụng ảnh hưởng mà ông tạo ra với các bên liên quan ở Trung Đông để hợp tác với chúng tôi làm việc với chính phủ Nga", ông Merz phát biểu tại Ai Cập.\nTrong bài đăng trên X cùng ngày, ông ca ngợi việc Hamas thả 20 con tin còn sống là "khởi đầu của quá trình hàn gắn" và là bước tiến lớn tới hòa bình Trung Đông. "Hai năm sợ hãi, đau đớn và hy vọng đã qua. Hôm nay các gia đình cuối cùng đã có thể ôm người thân yêu của mình lần nữa", ông viết.\nTổng thư ký Liên Hợp Quốc Antonio Guterres cũng hoan nghênh việc Hamas trao trả toàn bộ con tin còn sống, đồng thời kêu gọi các bên tiếp tục phát huy đà tích cực và "chấm dứt cơn ác mộng" ở Gaza.\n"Tôi vô cùng nhẹ nhõm khi họ đã được tự do và sớm đoàn tụ với những người thân yêu sau nhiều đau khổ phải chịu đựng. Tôi tiếp tục kêu gọi sớm trả lại hài cốt của những con tin đã chết", ông nói.\nTheo lộ trình hòa bình do Tổng thống Mỹ đề xuất, sau khi Hamas trao trả các con tin còn sống, Israel sẽ bắt đầu thả khoảng 2.000 tù nhân Palestine, cũng như rút lực lượng khỏi Dải Gaza để chuẩn bị cho các bước đàm phán tiếp theo.\nThanh Tâm(TheoAFP, Times of Israel)', 'Tổng thống Zelensky ca ngợi lệnh ngừng bắn ở Gaza, cho rằng nó mang lại hy vọng ông Trump có thể giúp chấm dứt xung đột ở Ukraine.', @cat_id_the_gioi, @user_id_thanhtam, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Bộ trưởng Mỹ: Thuế 100% với Trung Quốc không nhất thiết thực thi', 'bo-truong-my-thue-100-voi-trung-quoc-khong-nhat-thiet-thuc-thi', 'https://i1-vnexpress.vnecdn.net/2025/10/13/Scott-Bessent-jpeg-1557-1760363596.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=0FPwEFMp75Z3H6Iq5XHJLQ', '"Tổng thống Donald Trump nói thuế quan sẽ chưa có hiệu lực trước ngày 1/11. Ông ấy sẽ gặp Chủ tịch Trung Quốc Tập Cận Bình tại Hàn Quốc. Tôi tin cuộc gặp đó vẫn sẽ diễn ra", Bộ trưởng Tài chính Mỹ Scott Bessent nói trong cuộc phỏng vấn ngày 13/10.\nÔng Trump và ông Tập đã lên kế hoạch gặp nhau tại hội nghị thượng đỉnh Diễn đàn Hợp tác Kinh tế châu Á - Thái Bình Dương (APEC) diễn ra tại Hàn Quốc cuối tháng này. Tuy nhiên, khi công bố áp thuế bổ sung 100% với tất cả hàng hóa Trung Quốc hôm 10/10, ông chủ Nhà Trắng dọa hủy cuộc gặp, nói rằng "giờ dường như không còn lý do gì để làm như vậy".\nÔng Bessent cho biết sau tuyên bố của ông Trump, hai nước đã có nhiều cuộc thảo luận cuối tuần qua và "đã giảm đáng kể căng thẳng". Ông thêm rằng sẽ có các cuộc họp cấp chuyên viên giữa hai bên tại Washington tuần này, bên lề hội nghị thường niên của Ngân hàng Thế giới và Quỹ Tiền tệ Quốc tế.\n"Mức thuế 100% không nhất thiết phải thực thi. Dù mức thuế này được thông báo tuần trước, mối quan hệ vẫn tốt đẹp. Các kênh liên lạc đã được mở lại và chúng ta hãy chờ xem mọi chuyện sẽ đi đến đâu", ông nói.\nTổng thống Trump ngày 12/10 cũng thể hiện giọng điệu hòa giải sau khi đe dọa áp thêm thuế với Bắc Kinh, nói Mỹ muốn "giúp Trung Quốc" chứ không phải gây tổn hại cho nước này.\nThông báo áp thuế mới của Mỹ đưa ra một ngày sau khi Trung Quốc ngày 9/10 ban hành các biện pháp kiểm soát sâu rộng đối với khoáng sản đất hiếm. Đây là vật liệu quan trọng được sử dụng trong ngành sản xuất chất bán dẫn, máy bay chiến đấu và nhiều công nghệ tiên tiến khác.\nTrung Quốc ngày 11/10 chỉ trích Mỹ tiêu chuẩn kép, khẳng định việc kiểm soát đất hiếm của Bắc Kinh là động thái bình thường. Họ nói chính Mỹ từ lâu cũng áp đặt các biện pháp kiểm soát như vậy và thực thi các hành vi phân biệt đối xử chống lại Trung Quốc dưới danh nghĩa an ninh quốc gia.\nTuy cho biết hai bên đang thúc đẩy giảm căng thẳng, Bộ trưởng Tài chính Mỹ ngày 13/10 vẫn chỉ trích động thái của Trung Quốc là khiêu khích, thêm rằng Washington đã phản đối mạnh mẽ. Ông khẳng định Mỹ sẽ bác bỏ các yêu cầu cấp phép của Trung Quốc liên quan tới xuất khẩu đất hiếm hoặc các sản phẩm từ vật liệu này.\nThanh Tâm(TheoReuters)', 'Bộ trưởng Tài chính Bessent nói căng thẳng Mỹ - Trung đã "giảm đáng kể" sau lời đe dọa áp thuế 100% của ông Trump và lãnh đạo hai nước sẽ gặp nhau tại APEC ở Hàn Quốc.', @cat_id_the_gioi_tu_lieu, @user_id_thanhtam, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Ông Trump: Ác mộng đau đớn ở Gaza đã qua', 'ng-trump-c-mong-dau-don-o-gaza-da-qua', 'https://i1-vnexpress.vnecdn.net/2025/10/13/AP25286444824209-2063-1760360608.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=OpngrTJtdlFDh-Z874plfg', '"Ít nhất thì giờ đây, cơn ác mộng dài đằng đẵng và đau đớn cuối cùng cũng đã qua, không chỉ với người Israel mà còn đối với người Palestine và nhiều người khác", Tổng thống Mỹ Donald Trump phát biểu trước quốc hội Israel hôm nay, đề cập đến thỏa thuận ngừng bắn ở Dải Gaza.\nÔng chủ Nhà Trắng nói khi những đống đổ nát được dọn dẹp và bình minh ló rạng ở Gaza, "một tương lai tươi sáng hơn nhiều sẽ xuất hiện trước mắt các bạn". Ông thêm rằng "đây là thời điểm rất đáng mong đợi với Israel".\nTổng thống Mỹ mô tả cuộc tấn công của Hamas vào miền nam Israel hồi tháng 10/2023 là hành động tàn bạo "đánh thẳng vào giá trị cốt lõi của con người". Ông nhấn mạnh Mỹ và Israel sẽ "không bao giờ quên" và "không bao giờ để chuyện này lặp lại".\n"Tôi cũng xin bày tỏ lòng biết ơn sâu sắc tới tất cả các quốc gia thuộc thế giới Arab và Hồi giáo, khi đã cùng nhau gia tăng sức ép để Hamas trả tự do cho con tin và đưa họ về nước", ông nói. "Việc tất cả các nước cùng hợp tác vì hòa bình là chiến thắng đáng kinh ngạc cho Israel và thế giới".\nNgoài xung đột ở Gaza, ông Trump cũng đề cập tới căng thẳng với Iran. Ông cho biết muốn có thỏa thuận hòa bình với Tehran, sau khi Washington và Tel Aviv từng cùng tấn công các cơ sở hạt nhân của Iran hồi tháng 6.\n"Sẽ thật tuyệt nếu chúng ta đạt được một thỏa thuận hòa bình với họ. Các bạn có muốn điều đó không? Tôi nghĩ đó chắc hẳn là chuyện tốt. Bởi tôi nghĩ họ muốn như vậy?", ông nói.\nÔng thêm rằng cơ hội đạt thỏa thuận đang nằm trong tay Tehran. "Chúng tôi sẵn sàng khi các vị sẵn sàng", ông nói, thêm rằng cả Mỹ và Israel đều không có "thái độ thù địch" gì với người dân Iran. "Chúng tôi chỉ muốn sống trong hòa bình".\nCũng trong bài phát biểu trước quốc hội Israel, ông Trump kêu gọi Tổng thống nước này ân xá cho Thủ tướng Benjamin Netanyahu, người đang phải đối mặt với nhiều vụ kiện tụng liên quan tới cáo buộc tham nhũng.\n"Tôi có một ý tưởng. Thưa ngài Tổng thống (Isaac Herzog), tại sao ngài không ân xá cho ông ấy", ông Trump nói. "Điều này không có trong bài phát biểu, nhưng quý vị biết đấy, tôi quý mến ông ấy và việc ân xá dường như là hoàn toàn hợp lý".\nÔng Netanyahu và vợ Sara bị cáo buộc nhận số đồ xa xỉ trị giá hơn 260.000 USD gồm xì gà, trang sức và rượu champagne từ các tỷ phú muốn được hưởng các lợi ích về chính trị.\n"Xì gà và champagne, ai mà quan tâm cơ chứ", ông Trump nói đùa, thêm rằng Thủ tướng Netanyahu là "một trong những lãnh đạo thời chiến vĩ đại nhất của Israel".\nÔng Trump đến Israel vào thời điểm nhóm Hamas ở Dải Gaza trao trả toàn bộ 20 con tin còn sống cho Tel Aviv như một phần của thỏa thuận hòa bình do ông Trump thúc đẩy. Theo lộ trình hòa bình do Tổng thống Mỹ đề xuất, sau khi Hamas trao trả các con tin còn sống, Israel sẽ bắt đầu thả khoảng 2.000 tù nhân Palestine, cũng như rút lực lượng khỏi Dải Gaza để chuẩn bị cho các bước đàm phán tiếp theo.\nSau khi rời Israel, Tổng thống Mỹ dự kiến đến Ai Cập, nơi ông và Tổng thống Abdel Fattah al-Sisi đồng chủ trì hội nghị thượng đỉnh nhằm chấm dứt chiến sự ở Dải Gaza và thúc đẩy hòa bình Trung Đông. Hơn 20 lãnh đạo thế giới sẽ góp mặt, trong khi Israel và Hamas đều không cử đại diện.\nThanh Tâm(TheoTimes of Israel, AFP)', 'Tổng thống Trump hoan nghênh việc chấm dứt "cơn ác mộng đau đớn" sau hai năm xung đột ở Gaza, ca ngợi đây là chiến thắng cho tất cả.', @cat_id_the_gioi_phan_tich, @user_id_thanhtam, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Áp lực có thể đẩy gia đình gốc Việt ở California đến đường cùng', 'p-luc-co-the-day-gia-dinh-goc-viet-o-california-den-duong-cung', 'https://i1-vnexpress.vnecdn.net/2025/10/13/S1024x683-FWEBP-1760340352-6541-1760340939.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=4XnKRzJQH_GhXkkqnmF5Jw', 'Giới chức San Francisco tuần trước phát hiện 4 thi thể trong căn nhà ở phố Westwood Park, phía tây thành phố. Theo thông báo mới, danh tính 4 người này là Thomas Ocheltree, 57 tuổi, Paula Truong, 52 tuổi, cùng hai con gái là Alexandra Ocheltree, 12 tuổi và Mackenzie Ocheltree, 9 tuổi.\nThi thể ông Ocheltree cùng hai con gái được tìm thấy trên giường với nhiều dấu vết bất thường, như bọt sùi ra từ miệng. Thi thể bà Truong được tìm thấy ở garage trong tư thế treo cổ.\nSự việc làm rúng động dư luận khu vực, bởi nhiều người mô tả bà Truong là một doanh nhân "rất năng động và thành đạt". Tuy nhiên, cảnh sát San Francisco mô tả đây là "hành vi tội phạm đáng ngờ" và đang xem xét khả năng là một vụ "giết người rồi tự sát", bởi có nhiều dấu hiệu cho thấy gia đình bà Truong đã đối mặt với khó khăn tài chính nghiêm trọng.\nTrangSF Gatecho hay dựa trên các dữ liệu công khai, vợ chồng bà Truong gần đây hứng chịu nhiều thất bại trong hoạt động kinh doanh, nhận định đây có thể là nguyên nhân đẩy gia đình đến đường cùng.\nBà Paula Truong di cư từ Việt Nam tới Mỹ, kết hôn với ông Ocheltree năm 2006. Hai người mua căn nhà ba phòng ngủ trên đường Monterey với giá gần 1,14 triệu USD vào năm 2014, trong đó bà Truong là chủ sở hữu chính.\nBà Truong kinh doanh cà phê, sở hữu quán cà phê Red Planet Roastery ở Oakland, đồng thời sáng lập hãng cà phê Orbit Coffee, với ba cơ sở ở Oakland và một cơ sở ở San Jose, năm 2019. Các quán của hãng còn bán thêm bánh donut, bánh mì Việt Nam.\nBà từng nói với truyền thông về lý do mở Orbit Coffee là nhằm "chia sẻ tầm nhìn của mình về cà phê Việt Nam và tạo một nơi mà bà muốn ngồi uống cà phê". Kế toán thuế từng làm việc cho Truong suốt 15 năm mô tả bà là một doanh nhân "rất năng động và thành đạt".\nÔng Ocheltree cũng tham gia một số dự án kinh doanh, đề cập vợ là "đối tác". Một trong số đó là xưởng sửa ôtô Zentrum Motors, vẫn hoạt động, cùng doanh nghiệp thiết kế đồ họa chuyên dành cho đồ uống có cồn Ocheltree Design Branding and Packaging.\nTuy nhiên, rắc rối dường như ập đến với hai vợ chồng kể từ khi đại dịch Covid-19 bùng phát.\nCuối năm 2020, bà Truong mua lại một cửa hàng tiện lợi, bán đồ ăn nhẹ gần nhà với giá 155.000 USD. Bà chuyển đổi quán sang bán rượu vang, bia và một số sản phẩm của Orbit Coffee, nhưng cuối cùng đây là một canh bạc sai lầm.\n"Họ phải liên tục tăng giá bán và mất hết khách địa phương", Almir Zalihic, chủ cũ của cửa hàng tiện lợi, nói.\nTính đến năm 2019, bà Truong đã vay tổng cộng hơn 2,7 triệu USD để khởi nghiệp, trong đó có 500.000 USD vay từ các cá nhân. Do làm ăn thua lỗ, khoản nợ tiếp tục chồng chất, toàn bộ 4 quán Orbit Coffee của bà đã phải đóng cửa năm 2023.\nTháng 2/2024, thành phố thông báo với vợ chồng bà Truong rằng căn nhà của họ bị đưa vào diện thu hồi do quá hạn thanh toán thế chấp. Hai vợ chồng đã cố trì hoãn, song đến tháng 10/2024, căn nhà đã bị bán đấu giá hơn 2 triệu USD cho một công ty tài chính.\nTheoSF Chronicle, việc thu hồi được triển khai sau khi Truong và Ocheltree vay khoản thế chấp thứ hai 2,24 triệu USD cho căn nhà vào tháng 3/2022. Công ty tài chính dường như đã cho vợ chồng bà tiếp tục thuê lại căn nhà.\nTrong một vụ án dân sự, bà Truong đã bị ngân hàng Discover Bank kiện vào tháng 1 với cáo buộc không trả được gần 17.716 USD nợ thẻ tín dụng. Tháng 4, tòa án ở San Francisco phán quyết yêu cầu bà Truong phải trả cho ngân hàng này 18.157 USD.\nNgười phát hiện ra thi thể 4 người là Robert Ocheltree, anh trai của ông Thomas Ocheltree. Ông Robert cho biết đã không nghe ngóng tin tức từ em trai trong 6 ngày, rồi quyết định đến nhà kiểm tra vào ngày 6/10.\nBà Truong hôm đó đã mở cửa, nhưng không mời anh rể vào nhà, nói rằng chồng đang đi chơi golf và làm mất điện thoại. Ông Robert nhìn thấy rèm cửa đóng kín.\nNgày 7/10, Robert nhận được tin nhắn từ bà Truong, đề nghị ông giúp đỡ Ocheltree. "Anh ấy không còn là chính mình nữa, em và các cháu đều rất sợ hãi", Truong nhắn tin cho Robert.\nSáng 8/10, Robert gọi cảnh sát, nhưng các sĩ quan đã rời đi khi gõ cửa nhà bà Truong mà không thấy ai phản hồi. Ông Robert đã quyết định quay lại nhà em trai, dùng kìm đập vỡ kính và đột nhập căn nhà, phát hiện thi thể em trai, em dâu cùng hai cháu lúc 13h30.\nEvan Bloom, hàng xóm, cho biết gia đình Ocheltree và Truong "hoàn toàn bình thường, đầm ấm". Những hàng xóm khác kể hai vợ chồng đều là những người thân thiện, có chút trầm tính.\n"Chúng tôi vô cùng đau khổ. Thomas Ocheltree và những cô con gái xinh đẹp mới là nạn nhân thực sự. Họ không hề liên quan đến thảm kịch này", một người họ hàng của ông Ocheltree nói vớiABC 7.\nĐơn vị điều tra án mạng của Sở Cảnh sát San Francisco đang phụ trách vụ án.\nĐức Trung(TheoSF Chronicle, SF Standard, LA Times)', 'Áp lực nợ nần, kinh doanh thua lỗ có thể là nguyên nhân thảm kịch 4 thành viên gia đình phụ nữ gốc Việt ở California tử vong, theo truyền thông Mỹ.', @cat_id_the_gioi_quan_su, @user_id_uctrung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Mỗi lượng vàng tăng gần 3 triệu đồng', 'moi-luong-vang-tang-gan-3-trieu-dong', 'https://i1-kinhdoanh.vnecdn.net/2025/10/14/07b4fd38583bd5658c2a-176041583-1949-9979-1760416028.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=Nh9W3PFcP11z507WIhqzCQ', 'Sáng 14/10, Công ty Vàng bạc đá quý Sài Gòn (SJC) niêm yếtgiá vàngmiếng tại 144,9 - 146,4 triệu đồng, tăng 2,8 triệu đồng chiều mua vào và 2,3 triệu đồng chiều bán ra so với hôm qua. Các thương hiệu khác cũng nâng giá vàng miếng lên tương ứng. Còn tại Bảo Tín Minh Châu, giá vàng miếng lên 144,9 - 146,9 triệu đồng.\nVới mặt hàng nhẫn trơn, các thương hiệu lớn vẫn rơi vào tình trạng cháy hàng, hạn chế lượng mua hoặc phát giấy hẹn cho khách. Giá vàng nhẫn trơn hiện áp sát giá vàng miếng, thậm chí nhẫn trơn tại nhiều tiệm vàng vượt giá vàng miếng.\nSJC sáng nay niêm yết nhẫn trơn 142,3 - 144,5 triệu đồng. Bảo Tín Minh Châu nâng giá nhẫn trơn lên 143,9 - 146,9 triệu đồng. Tại một số tiệm vàng nhỏ, giá nhẫn trơn lên 148 - 152 triệu đồng một lượng, cao hơn mấy triệu so với vàng miếng SJC.\nTrên thị trường quốc tế, giá vàng giao ngay lập kỷ lục 4.168 USD một ounce. Quy đổi theo tỷ giá bán Vietcombank, giá vàng thế giới tương đương 132,5 triệu đồng mỗi lượng. Chênh lệch giá vàng miếng trong nước và thế giới hiện gần 14 triệu đồng một lượng.\nTrong bối cảnh giá thế giới lên cao, người dân lại xếp hàng mua vào tại các thương hiệu vàng lớn trong khi lượng cung nhỏ giọt. Người dân thực tế hiện rất khó mua vàng miếng. Hầu hết họ phản ánh chật vật khi đăng ký mua vàng trực tuyến qua website SJC, thường xuyên báo hết lượt ngay từ sớm. Các thương hiệu vàng lớn khác cũng "cháy" vàng miếng trong vài ngày gần đây.\nQuỳnh Trang', 'Giá vàng miếng và nhẫn trơn sáng nay tăng gần 3 triệu đồng một lượng, lập kỷ lục mới trên 146 triệu đồng', @cat_id_kinh_doanh, @user_id_vnexpress, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Nobel Kinh tế 2025 làm sáng tỏ vai trò đổi mới công nghệ với tăng trưởng', 'nobel-kinh-te-2025-lam-sang-to-vai-tro-doi-moi-cong-nghe-voi-tang-truong', 'https://i1-kinhdoanh.vnecdn.net/2025/10/14/3-nha-kinh-te-Nobel-jpeg-17603-8924-1693-1760409667.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=70oltV38TgupPQ_oyszSDQ', '200 năm qua, kinh tế thế giới tăng trưởng mạnh mẽ chưa từng có. Nền tảng đến từ dòng chảy đổi mới công nghệ không ngừng. Trong quá trình này, những công nghệ mới liên tục thay thế công nghệ cũ, gọi là "phá hủy sáng tạo".\nVới việc làm sáng tỏ lý do có phát triển kinh tế và điều kiện để duy trì tăng trưởng bền vững, 3 nhà khoa học Joel Mokyr, Peter Howitt và Philippe Aghion đã được Viện Hàn lâm Khoa học Hoàng gia Thụy Điển trao giải Nobel Kinh tế 2025.\n"Công trình của các chủ nhân giải thưởng cho thấy tăng trưởng kinh tế không thể được xem là điều hiển nhiên. Chúng ta cần duy trì những cơ chế cốt lõi của phá hủy sáng tạo để tránh rơi vào tình trạng trì trệ", John Hassler, Chủ tịch Ủy ban Giải Nobel Kinh tế, nhấn mạnh.\nKinh tế bắt đầu phát triển nhờ đổi mới sáng tạo\nTrong phần lớn lịch sử nhân loại, mức sống gần như không thay đổi qua các thế hệ. Đôi lúc, vài phát minh quan trọng xuất hiện giúp mang đến một số cải thiện nhưng tăng trưởng nhìn chung trì trệ.\nThay đổi thật sự đến khi Cách mạng Công nghiệp bắt đầu tại Anh hơn hai thế kỷ trước. Từ đó, tiến bộ khoa học và đổi mới công nghệ diễn ra liên tục, tạo nên vòng xoáy đổi mới không ngừng, thay vì những sự kiện rời rạc.\nĐây chính là nền tảng của tăng trưởng kinh tế ổn định và bền vững, được nhà sử học kinh tế Joel Mokyr - người nhận một nửa giải Nobel Kinh tế 2025 - chỉ ra.\nÔng ví dụ thu nhập gần như không tăng ở Thụy Điển hay Anh từ thế kỷ XIV đến XVIII, dù có những phát minh đáng kể. Nguyên nhân là các ý tưởng mới không tiếp tục được phát triển hay ứng dụng rộng rãi nên không tạo nên cải tiến liên tục.\nNgược lại, từ đầu thế kỷ XIX đến nay, Cách mạng Công nghiệp giúp Anh và Thụy Điển duy trì tăng trưởng ổn định, trung bình khoảng 2% mỗi năm - tương đương thu nhập có thể tăng gấp đôi trong một đời người. Vậy cơ chế nào để khoa học và ứng dụng thực tiễn bổ trợ lẫn nhau, giúp duy trì tăng trưởng dài hạn?\nMokyr chỉ ra việc duy trì tăng trưởng đòi hỏi dòng chảy liên tục của tri thức hữu dụng (useful knowledge), gồm 2 loại: tri thức lý thuyết (propositional knowledge) - giải thích tại sao một hiện tượng tự nhiên lại xảy ra và tri thức thực hành (prescriptive knowledge) - mô tả cách để đạt kết quả mong muốn.\nTrước thế kỷ XVIII, con người chủ yếu dựa vào tri thức thực hành, tức họ biết một thứ có tác dụng, nhưng không hiểu lý do. Còn tri thức lý thuyết lại tách biệt, khiến việc cải tiến dựa trên kiến thức sẵn có khó khăn.\nVề sau, các nhà khoa học đề ra đo lường chính xác, thí nghiệm có kiểm soát và kết quả có thể tái lập, tạo ra vòng phản hồi giữa lý thuyết và thực hành. Từ đó, tri thức hữu dụng tăng nhanh. Ví dụ, cải tiến động cơ hơi nước nhờ hiểu biết về áp suất khí quyển, hay phát hiện vai trò của oxy trong luyện kim giúp nâng chất lượng thép.\n\'Phá hủy sáng tạo\' giúp duy trì tăng trưởng\nKhi đã hiểu lý do kinh tế bắt đầu phát triển, câu hỏi tiếp theo là điều kiện gì để nó duy trì tăng trưởng ổn định lâu dài. Đồng chủ nhân Nobel Kinh tế 2025, hai nhà kinh tế Philippe Aghion và Peter Howitt, đã nghiên cứu cơ chế đằng sau điều này. Năm 1992, họ công bố mô hình toán học phức tạp về "phá hủy sáng tạo" (creative destruction).\nĐây là khái niệm gắn liền với nhà kinh tế Joseph Schumpeter qua cuốn "Chủ nghĩa tư bản, Chủ nghĩa xã hội và Dân chủ" xuất bản năm 1942. Tuy nhiên, Aghion và Peter Howitt đã bổ sung nhiều yếu tố mới.\nHọ chỉ ra doanh nghiệp cạnh tranh nhau bằng đổi mới. Công ty tạo ra công nghệ tiên tiến hơn sẽ vươn lên dẫn đầu, nhưng sau đó lại bị thách thức bởi người đi sau. Quá trình này tạo nên dòng chảy đổi mới liên tục, vừa phá hủy, vừa kiến tạo, gọi là "phá hủy sáng tạo", động lực thật sự của tăng trưởng dài hạn.\nNhững ví dụ điển hình của "phá hủy sáng tạo" có thể kể đến thương mại điện tử làm thay đổi ngành bán lẻ, dịch vụ phát trực tuyến thay thế băng video và DVD, hay quảng cáo trực tuyến khiến quảng cáo trên báo in dần mất chỗ đứng.\nMô hình của hai nhà khoa học cũng xác lập sự cân bằng động giữa các yếu tố: đầu tư R&D, tiết kiệm hộ gia đình, lãi suất và tốc độ tăng trưởng. Đây là mô hình tổng thể đầu tiên mô tả "phá hủy sáng tạo" trong cân bằng vĩ mô.\nTheo đó, nghiên cứu & phát triển (R&D) có thể bị đầu tư thiếu hoặc quá mức, tùy vào tương quan giữa lợi ích xã hội và lợi ích doanh nghiệp. Nếu đổi mới mang lại lợi ích lâu dài cho xã hội hơn doanh nghiệp, cần trợ cấp cho R&D. Ngược lại, cạnh tranh quá gay gắt khiến đổi mới diễn ra quá nhanh, xã hội có thể đầu tư quá mức, gây rủi ro thừa công nghệ và mất cân bằng.\nHay như, tập trung thị trường quá cao hay quá thấp đều cản trở đổi mới. Ví dụ, khi một số công ty trở nên quá lớn, tốc độ tăng trưởng sẽ giảm. Do đó, chính sách chống độc quyền và khuyến khích cạnh tranh lành mạnh rất cần thiết. Ngoài ra, việc hạn chế tự do học thuật hay rào cản với tầng lớp yếu thế cũng có thể khiến "cỗ máy phá hủy sáng tạo" ngừng hoạt động, đưa nhân loại trở lại thời kỳ trì trệ.\nGiá trị thực tiễn của Nobel Kinh tế 2025\nNobel Kinh tế 2025 được trao trong bối cảnh kinh tế toàn cầu có thể đang ở bước ngoặt mới, khi nhiều người kỳ vọng trí tuệ nhân tạo (AI) thúc đẩy làn sóng tăng trưởng tiếp theo. Nhóm tác giả nhận Nobel cho rằng nếu biết tận dụng AI và khoa học mới để tăng cường mối liên kết giữa tri thức lý thuyết và tri thức thực hành, song song áp dụng chính sách quản trị thông minh, dòng chảy đổi mới và phát triển bền vững sẽ được duy trì.\nNhiều năm qua, ông Mokyr luôn được biết đến là người lạc quan về tác động của các đổi mới công nghệ hiện đại. Một thập kỷ trước, nhiều nhà kinh tế cho rằng những phát minh như điện thoại thông minh hay Internet tác động kinh tế nhỏ hơn nhiều so với máy bay hay ôtô.\nÔng Mokyr phản biện, cho rằng nhiều dịch vụ mới có chi phí rất thấp, thậm chí miễn phí, nên lợi ích của chúng không thể hiện rõ trong dữ liệu kinh tế, song giá trị mang lại vô cùng lớn. Trong cuộc phỏng vấn vớiAPnăm 2015, ông ví dụ gọi dịch vụ nghe nhạc trực tuyến Spotify là một đổi mới "đáng kinh ngạc" mà các nhà kinh tế khó có thể đo lường được tác động.\nMokyr kể từng sở hữu hơn 1.000 đĩa CD và trước đó đã dành phần lớn tiền kiếm được thời nghiên cứu sinh để mua đĩa than. Nhưng giờ, ông có thể nghe kho nhạc khổng lồ chỉ với khoản phí nhỏ hàng tháng.\nThừa nhận các phát minh mới thường gây xáo trộn, dẫn đến mất việc làm hoặc giảm thu nhập của người lao động trong ngắn hạn nhưng ông cho rằng đổi mới cũng tạo ra những ngành nghề mới và cơ hội chưa từng có trước đây.\nCông trình nhận Nobel cũng giúp lý giải và chỉ ra rủi ro chiến lược mà châu Âu đang đối mặt nếu tiếp tục tụt lại phía sau Mỹ và Trung Quốc trong các công nghệ của tương lai. Thúc đẩy đổi mới đang trở nên cấp bách khi một nghiên cứu của cựu Chủ tịch Ngân hàng Trung ương châu Âu Mario Draghi chỉ ra năng suất của khu vực này đang ngày càng thua kém Mỹ trong lĩnh vực công nghệ số.\nTheo Aghion, khối này cần đẩy mạnh nghiên cứu và huy động vốn đầu tư mạo hiểm để biến ý tưởng thành doanh nghiệp. "Châu Âu có nền tảng nghiên cứu cơ bản tuyệt vời nhưng cần khai thác trọn vẹn sức mạnh của đổi mới", ông nói.\nÔng đồng thời kêu gọi châu Âu học hỏi Mỹ và Trung Quốc, hai nền kinh tế đã dung hòa được chính sách cạnh tranh và chính sách công nghiệp. Theo ông, châu Âu vì quá chú trọng vào cạnh tranh nên đã gần như bác bỏ mọi hình thức can thiệp hay hỗ trợ của chính sách công nghiệp, dẫn đến tuột hậu công nghệ.\n"Tôi nghĩ đã đến lúc phải thay đổi, tìm ra cách kết hợp hài hòa giữa chính sách công nghiệp và cạnh tranh trong các lĩnh vực như quốc phòng, khí hậu, AI và công nghệ sinh học", ông nói.\nNgoài ra, công trình của 3 nhà khoa học cũng là tri thức kịp thời khi xu hướng phi toàn cầu hóa và các rào cản thuế quan đang tăng. Ông Aghion cho rằng chúng là "trở ngại cho tăng trưởng" vì quy mô thị trường càng lớn thì càng có nhiều cơ hội trao đổi ý tưởng, chuyển giao công nghệ và thúc đẩy cạnh tranh lành mạnh.\n"Bất cứ điều gì hạn chế cởi mở đều là rào cản với tăng trưởng. Tôi thấy đang có những \'đám mây đen\' tích tụ, thúc đẩy xu hướng dựng hàng rào thương mại và hạn chế mở cửa", ông nói.\nPhiên An(Ủy ban Nobel, Reuters, AP)', 'Không chỉ làm rõ vai trò dòng chảy đổi mới công nghệ trong tăng trưởng, công trình đạt Nobel Kinh tế 2025 có giá trị tham khảo rất thời sự.', @cat_id_kinh_doanh_quoc_te, @user_id_phienan, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Trung Quốc bắt đầu thu phí cảng đặc biệt với tàu Mỹ', 'trung-quoc-bat-dau-thu-phi-cang-dac-biet-voi-tau-my', 'https://i1-kinhdoanh.vnecdn.net/2025/10/14/china-port-fee-1760405579-1760-6426-7573-1760406148.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=NFlroTGCiIGH9nyUf001eg', 'Phí này sẽ được thu tại cảng đầu tiên mà tàu cập bến trong hải trình, hoặc thu với 5 hải trình trong vòng một năm, Đài truyền hình Trung ương Trung Quốc (CCTV) cho biết. Mức khởi điểm là 400 nhân dân tệ (56,1 USD) một tấn, tăng lên 640 nhân dân tệ (89,81 USD) từ 17/4/2026 và 1.120 nhân dân tệ (157,16 USD) trong hai năm tiếp theo.\nNếu không nộp phí, các thủ tục xuất nhập khẩu của tàu sẽ bị đình trệ. Tuy nhiên, các tàu do Trung Quốc sản xuất, tàu rỗng vào các xưởng sửa chữa và một số loại khác theo quy định sẽ được miễn phí này.\nTheokế hoạchcông bố từ tháng 4, hôm nay cũng là ngày Mỹ bắt đầu thu phí cảng tàu do Trung Quốc sở hữu và vận hành. Mức phí là 50 USD một tấn, tăng dần lên 140 USD vào 2028.\nKhác với các loại phí dịch vụ thông thường ở cảng, vốn được thu theo quy định lâu nay, loại phí Mỹ và Trung Quốc áp dụng từ ngày 14/10 là khoản đặc biệt. Phí này được giới chức công bố để đáp trả chính sách thương mại của nhau. Vì vậy, lộ trình áp dụng và mức phí hai bên đưa ra gần như tương đương.\nCác nhà phân tích dự báo hãng vận tải biển Trung Quốc - COSCO sẽ chịu ảnh hưởng nặng nhất, khi gánh gần một nửa trong tổng phí ước tính 3,2 tỷ USD năm 2026.\nKế hoạch thu phí với tàu có yếu tố Trung Quốc nhằm giảm quyền lực của nước này trong ngành hàng hải toàn cầu và củng cố ngành đóng tàu Mỹ. Một cuộc điều tra thực hiện dưới thời cựu Tổng thống Joe Biden chỉ ra Trung Quốc áp dụng các chính sách không công bằng nhằm thống trị lĩnh vực hàng hải, logistics và đóng tàu, mở đường cho Mỹ áp phí.\nTuần trước, Bộ Giao thông Trung Quốc cho biết việc áp phí cảng mới để đáp trả, với lộ trình và mức thu tương tự Mỹ. Nhà phân tích Omar Nokta tại Jefferies ước tính 13% tàu dầu và 11% tàu container toàn cầu sẽ bị ảnh hưởng.\n"Việc trả đũa khiến cả hai nền kinh tế rơi vào vòng xoáy thuế hàng hải, đe dọa bóp méo dòng chảy hàng hóa toàn cầu", công ty môi giới tàu biển Xclusiv Shipbrokers nhận định.\nCăng thẳng Mỹ - Trung Quốc gần đây tiếp tục leo thang, sau thời gian lắng xuống khi hai nước đạt được thỏa thuận thương mại. Tổng thống Mỹ Donald Trump ngày 10/10 tuyên bố áp thêm thuế nhập khẩu 100% lên hàng hóa và kiểm soát phần mềm thiết yếu Trung Quốc do nước này mở rộng quy định hạn chế xuất khẩu đất hiếm.\nNgày 12/10, Bộ Thương mại Trung Quốc khẳng định chính sách kiểm soát "hợp pháp" và "tác động rất hạn chế" tới hoạt động sản xuất, cung ứng. Họ đồng thời cáo buộc Mỹ gia tăng các biện pháp kinh tế chống lại nước này kể từ tháng 9. Bộ này cho biết bất chấp thiện chí và sự quan ngại của Trung Quốc, Mỹ vẫn thực hiện các biện pháp hạn chế thương mại theo Mục 301, nhằm vào các ngành hàng hải, logistics và đóng tàu của nền kinh tế thứ hai thế giới.\nHà Thu(theo Reuters)', 'Từ 14/10, Trung Quốc thu phí cảng đặc biệt với các tàu do Mỹ sở hữu, đóng và vận hành vào nước này.', @cat_id_kinh_doanh_doanh_nghiep, @user_id_hathu, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Ai đang gánh chi phí thuế quan của ông Trump?', 'ai-dang-ganh-chi-phi-thue-quan-cua-ong-trump', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/thuequan-1760346700-2344-1760349065.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=P_OZjKiGDxgBMWrByOGxxQ', 'Khi áp thuế đối ứng với hầu hết quốc gia và thuế nhập khẩu lên một số mặt hàng riêng biệt, Tổng thống Mỹ Donald Trump khẳng định các nhà xuất khẩu sẽ phải gánh chi phí để duy trì chỗ đứng tại thị trường tiêu dùng lớn nhất thế giới.\nTuy nhiên, các nghiên cứu, khảo sát và phản hồi từ doanh nghiệp cho thấy tình hình thời gian qua không như vậy. Giáo sư Alberto Cavallo tại Đại học Harvard cho biết phần lớn chi phí dường như do các công ty Mỹ gánh chịu. "Chúng tôi quan sát thấy giá bán lẻ tăng dần và áp lực lạm phát rõ rệt", ông nêu sau khảo sát.\nGiáo sư Cavallo đã cùng các nhà nghiên cứu Paola Llamas và Franco Vasquez theo dõi giá của 359.148 mặt hàng, từ thảm đến cà phê tại các nhà bán lẻ trực tuyến và truyền thống ở Mỹ. Kết quả, giá hàng nhập khẩu tăng 4% kể từ khi ông Trump bắt đầu áp thuế vào đầu tháng 3, trong khi hàng sản xuất trong nước tăng 2%.\nTăng mạnh nhất là những mặt hàng Mỹ không thể tự sản xuất như cà phê, hoặc sản phẩm từ các nước bị đánh thuế nặng. Mức tăng giá này thấp hơn nhiều mức thuế áp dụng, cho thấy nhà nhập khẩu và bán hàng đã tự gánh một phần chi phí.\nTrong khi, chi phí nhập khẩu tính bằng USD (chưa bao gồm thuế) lại cho thấy các nhà xuất khẩu nước ngoài đang nâng giá bán để bù lại việc đồng USD giảm giá so với nội tệ của họ, khiến người mua Mỹ chịu thêm phần chênh lệch.\n"Điều này cho thấy các nhà sản xuất nước ngoài hầu như không hấp thụ thuế quan của Mỹ, phù hợp với các nghiên cứu kinh tế trước đây", theo nhóm nghiên cứu của Viện Nghiên cứu Ngân sách Đại học Yale. Chỉ số giá xuất khẩu của Mỹ cũng phản ánh xu hướng tương tự, với chi phí nhập hàng từ Trung Quốc, Đức, Mexico, Thổ Nhĩ Kỳ và Ấn Độ đều tăng, chỉ Nhật Bản ngoại lệ.\nCác hãng xe châu Âu cố gắng hấp thụ phần lớn chi phí nhưng nhiều doanh nghiệp hàng tiêu dùng như Procter & Gamble, EssilorLuxottica hay Swatch đã tăng giá sản phẩm. Theo thống kê củaReuters, 72% doanh nghiệp tại châu Âu, Trung Đông và châu Phi đã tăng giá kể từ khi Mỹ khởi động áp thuế.\nCác nền tảng thương mại điện tử như Shein và Amazon cũng ghi nhận giá hàng Trung Quốc bán tại Mỹ tăng đáng kể, đặc biệt ở ngành may mặc và điện tử. Thực tế, chính sách "chống cạnh tranh nội bộ" của Trung Quốc đang khuyến khích doanh nghiệp giảm cạnh tranh giá thấp.\nCác chính sách thuế quan mới của ông Trump nâng thuế nhập khẩu của Mỹ trung bình từ 2% lên 17%, hiện mang về cho ngân sách nguồn thu ước khoảng 30 tỷ USD mỗi tháng. Theo các chuyên gia, quá trình thích ứng với mức thuế mới vẫn đang diễn ra.\nCuộc giằng co hấp thụ chi phí thuế quan của nhà xuất khẩu, nhập khẩu và người tiêu dùng sẽ còn kéo dài thêm vài tháng, theo chuyên gia. "Chúng ta không nên kỳ vọng chỉ có một cú sốc giá duy nhất. Các doanh nghiệp đang tìm cách giảm thiểu tác động và phân bổ tăng giá dần theo thời gian", Giáo sư Cavallo nhận định.\nDiễn biến này tạo áp lực lạm phát tại Mỹ. Cục Dự trữ liên bang Mỹ (Fed) đã hạ lãi suất cơ bản tháng trước do lo ngại thị trường lao động yếu đi. Song, các nhà hoạch định chính sách vẫn cảnh giác về nguy cơ lạm phát do thuế quan,\nƯớc tính sơ bộ của Fed chi nhánh Boston cho thấy thuế quan có thể làm tăng lạm phát cơ bản thêm 0,75 điểm phần trăm. Chủ tịch Fed Jerome Powell nói rằng thuế quan đóng góp khoảng 30–40 điểm cơ bản trong mức lạm phát cơ bản (hiện là 2,9%) nhưng tác động này "nhiều khả năng chỉ mang tính tạm thời".\nTheo Viện Kinh tế Quốc tế Peterson, lạm phát trong năm tới sẽ cao hơn khoảng 1 điểm phần trăm so với kịch bản không áp thuế, trước khi giảm trở lại sau đó. Hệ lụy của lạm phát là người tiêu dùng Mỹ vật lộn với giá cả leo thang, dẫn đến nhu cầu hàng hóa nhập khẩu thấp hơn. Đó không phải là tin vui với các nền kinh tế phụ thuộc lớn vào sản xuất - xuất khẩu.\nKhảo sát của S&P Global cho biết đơn hàng xuất khẩu mới toàn cầu đang sụt giảm với tốc độ ngày càng nhanh kể từ tháng 6. Xuất khẩu của Liên minh châu Âu (EU) sang Mỹ đã giảm 4,4% trong tháng 7 so với cùng kỳ 2024. Riêng tại đầu tàu kinh tế Đức, mức giảm lên tới 20,1% trong tháng 8.\nTuần trước, Tổ chức Thương mại Thế giới (WTO) hạ dự báo tăng trưởng khối lượng thương mại hàng hóa toàn cầu năm tới xuống chỉ còn 0,5%, viện dẫn tác động từ các mức thuế mới của Mỹ có thể rõ nét từ cuối 2025.\nNgân hàng ING (Hà Lan) dự báo xuất khẩu hàng hóa của EU sang Mỹ sẽ giảm 17% trong hai năm tới, khiến tăng trưởng GDP của khối giảm khoảng 0,3 điểm phần trăm. Nhà kinh tế Ruben Dewitte của ING nhận định tác động của thuế quan Mỹ vẫn chưa bộc lộ hết. "Chúng tôi cho rằng những ảnh hưởng này sẽ trở nên rõ rệt hơn trong các tháng tới.\nĐáp lại các lo ngại và dự báo kém sáng sủa, ông Stephen Miran - Thống đốc Fed mới được bổ nhiệm theo đề cử của Tổng thống Trump - cho rằng thuế quan không gây lạm phát, chỉ thừa nhận có "những biến động nhỏ trong giá hàng hóa".\nPhát ngôn viên Nhà Trắng nói "người Mỹ có thể trải qua giai đoạn chuyển tiếp do tác động thuế quan" nhưng khẳng định "cuối cùng chi phí sẽ do các nhà xuất khẩu nước ngoài chịu". Vị này cũng cho biết thêm rằng các doanh nghiệp đang đa dạng hóa chuỗi cung ứng và chuyển một phần sản xuất về Mỹ.\nPhiên An(theo Reuters)', 'Các nhà nhập khẩu Mỹ đang là bên chủ lực gánh chi phí phát sinh do thuế quan của ông Trump, chứ không phải nước ngoài, theo khảo sát và nhận định từ chuyên gia.', @cat_id_kinh_doanh_chung_khoan, @user_id_phienan, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Giá vàng thế giới vượt 4.100 USD', 'gia-vang-the-gioi-vuot-4100-usd', 'https://i1-kinhdoanh.vnecdn.net/2025/10/14/gold-2025-10-14T074105-712-176-8905-4400-1760402586.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=mc-iyImF6gjQgSR2GudN2g', 'Chốt phiên giao dịch 13/10,giá vàngthế giới giao ngay tăng gần 93 USD lên 4.109 USD một ounce. Sang phiên sáng 14/10, giá tiếp tục đi lên, hiện giao dịch tại 4.127 USD - cao nhất từ trước đến nay.\nThị trường đi lên khi căng thẳng Mỹ - Trung Quốc leo thang trở lại và nhà đầu tư kỳ vọng Mỹ giảm lãi suất. Cuối tuần trước, Tổng thống Mỹ Donald Trump thông báo áp thêm thuế nhập khẩu 100% lên hàng Trung Quốc, sau khi nước này siết xuất khẩu đất hiếm.\nTừ đầu năm, kim loại quý đã tăng 56% do bất ổn kinh tế - chính trị, kỳ vọng Mỹ giảm lãi suất và lực mua của các ngân hàng trung ương toàn cầu. Vàng thế giới đạt mốc 4.000 USD một ounce vào tuần trước.\n"Giá có thể tiếp tục xu hướng đi lên. Đến cuối năm 2026, giá sẽ hướng đến 5.000 USD", Phillip Streible - chiến lược gia trưởng tại Blue Line Futures cho biết. Lực mua ổn định của các ngân hàng trung ương, nhu cầu của các quỹ ETF vàng, căng thẳng Mỹ - Trung Quốc và khả năng Cục Dự trữ liên bang Mỹ (Fed) tiếp tục hạ lãi suất trong phiên họp tháng này là các yếu tố được đánh giá sẽ hỗ trợ thị trường.\nNhà đầu tư hiện dự báo xác suất Fed giảm lãi tháng này là 97%. Khả năng giảm trong phiên họp tháng 12 thậm chí lên tới 100%. Vàng có xu hướng diễn biến tốt trong môi trường lãi suất thấp..\nCác nhà phân tích tại Bank of America và Societe Generale cũng dự báo giá lên 5.000 USD vào năm sau. Trong khi đó, Standard Chartered cũng điều chỉnh dự báo giá trung bình năm tới lên 4.488 USD.\n"Giá có thể điều chỉnh trong ngắn hạn. Đây là sự điều chỉnh lành mạnh với xu hướng tăng dài hạn", Suki Cooper - Giám đốc nghiên cứu hàng hóa tại Standard Chartered Bank cho biết.\nNgoài vàng, giá bạc cũng lập kỷ lục mới, khi có thời điểm chạm 52,1 USD một ounce phiên 13/10. Giá bạch kim chốt phiên tăng 3,9% lên 1.648 USD, còn palladium tăng hơn 5% lên 1.478 USD.\nHà Thu(theo Reuters)', 'Căng thẳng Mỹ - Trung Quốc leo thang trở lại khiến giá vàng thế giới lần đầu vượt mốc 4.100 USD một ounce.', @cat_id_kinh_doanh_bat_dong_san, @user_id_hathu, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("'Nhựa' từ tre bền hơn nhựa hóa thạch, phân hủy trong 50 ngày", 'nhua-tu-tre-ben-hon-nhua-hoa-thach-phan-huy-trong-50-ngay', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/67a94fe6a310a2ab87b6a08d-17603-3710-2398-1760328588.png?w=680&h=0&q=100&dpr=1&fit=crop&s=qEIUP6CR6A-zz2mi-zPwHA', 'Tương tự ngô và bã mía, tre là nguyên liệu được ứng dụng trong sản xuất nhựa sinh học. Tuy nhiên, nhựa sinh học từ nguyên liệu này thường được phối trộn thêm nhựa thường gốc dầu mỏ, tăng độ bền. Với đặc tính ưa nước, nguyên liệu từ tre không tương thích nhiều với nhựa nguyên sinh (vốn kỵ nước), dẫn tới độ bền của sản phẩm cuối vẫn thấp hơn kỳ vọng.\nMới đây, các nhà khoa học từ Đại học Lâm nghiệp Đông Bắc Trung Quốc đã thành công chế tạo một loại "nhựa" mới từ tre, không cần phối trộn thêm, nhưng bền hơn nhựa gốc dầu mỏ.Sustainability Timesngày 12/10 gọi đây là cuộc cách mạng "biến tre thành nhựa" của giới khoa học Trung Quốc, mở thêm hướng đi cho các giải pháp bền vững thay thế nhựa.\nVật liệu này được gọi là "Bamboo molecular plastics" (BM-plastics). Haipeng Yu, Dawei Zhao và nhóm nghiên cứu phát triển một phương pháp thay đổi cấu trúc vật liệu ở cấp độ phân tử, dựa vào dung môi cồn (ethanol).\nTheo đó, tre được cắt mảnh, tách cellulose và thu hồi lượng gel cấp phân tử. Lượng gel này được ngâm trong dung dịch ethanol 12 giờ, nhằm cắt các liên kết hydro của cellulose, đồng thời kích thích phân tử tái tạo liên kết, "lắp ráp" lại thành một loại vật liệu bền chắc.\nQua thử nghiệm, loại nhựa tre này có độ bền hàng đầu so với các loại nhựa sinh học (PLA, PHA), hay nhựa gốc dầu mỏ (PET, ABS). Thêm vào đó, loại nhựa này chịu được nhiệt trên 180 độ C, với tính năng uốn dẻo, ứng dụng được trong các công đoạn ép phun, đúc khuôn.\nBên cạnh các chỉ số cơ học, nhiệt học vượt trội, loại nhựa mới có thể phân hủy sinh học hoàn toàn. Trong thử nghiệm, một mảnh nhựa tre kích thước 2 x 4 cm, dày 1 mm, phân hủy hoàn toàn trong đất ở nhiệt độ 25 độ C trong 50 ngày.\nTrường hợp được thu gom và tái chế, vật liệu này có thể tái chế vòng kín, tức thành phẩm sau tái chế có chất lượng gần như tương đương vật liệu ban đầu.\nNghiên cứu này mở ra hướng đi mới cho các giải pháp thay thế nhựa ứng dụng trong các ngành công nghiệp, ví dụ xây dựng. Trước đó, tre được pha trộn trong vật liệu nhựa tổng hợp, nhưng không được ứng dụng vào các công trình cơ sở hạ tầng bởi tính bền kém.\nNhóm tác giả cho biết, chi phí sản xuất loại nhựa này ước tính hơn 2.300 USD mỗi tấn, khả thi để nhân rộng ở quy mô công nghiệp. Họ kỳ vọng phương pháp mới có thể chuyển đổi nguồn tre dồi dào thành vật liệu hiệu suất cao, thân thiện môi trường, góp phần giảm ô nhiễm nhựa.\nNỗ lực của giới khoa học Trung Quốc phù hợp với mục tiêu của chính phủ nước này, với định hướng dùng tre thay nhựa, góp phần tiến tới mục tiêu phát thải ròng bằng 0 (Net Zero) vào năm 2060. Họ là quốc gia sản xuất và xuất khẩu sản phẩm tre lớn nhất thế giới, với quy mô hơn 7 triệu ha rừng tre, tương đương diện tích quốc gia Ireland.\nTrung Quốc khởi động kế hoạch ba năm nhằm thúc đẩy ngành công nghiệp tre thay thế nhựa từ năm 2023. Trong đó, với diện tích trồng tre quy mô 666 km 2, huyện An Cát (Chiết Giang) được chọn làm địa phương kiểu mẫu cho mô hình này.\nTại huyện trên, tre đã thay thế nhựa trong nhiều sản phẩm một lần, gồm dao, kéo, bàn chải đánh răng, lược. Tại các căng tin công quyền, khay nhựa cũng được thay bằng tre.\nÔ nhiễm nhựa là vấn nạn toàn cầu. Sản lượng nhựa liên tục tăng theo cấp số nhân kể từ những năm 1950, dự báo đạt 1,2 tỷ tấn vào năm 2060. Tỷ lệ tái chế chưa đến 10% khiến lượng lớn nhựa thải thẳng ra bãi chôn lấp và đại dương, phân mảnh thành các vi nhựa dai dẳng xâm nhập vào hệ sinh thái và chuỗi thức ăn của con người.\nBảo Bảo(theoSustainability Times, Nature, China Daily)', 'Trung QuốcGiới khoa học Trung Quốc đã chế tạo thành công nhựa sinh học hoàn toàn làm từ tre, góp phần vào kế hoạch ba năm "dùng tre thay nhựa" của chính phủ nước này.', @cat_id_kinh_doanh, @user_id_baobao, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Doanh nghiệp bất động sản Việt nhận ba giải thưởng tại APEA 2025', 'doanh-nghiep-bat-dong-san-viet-nhan-ba-giai-thuong-tai-apea-2025', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/CPF08888-JPG-1760362844-3849-1760362905.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=yYYHRnh_58bqrpm7s4wtHw', 'APEA do Enterprise Asia tổ chức, quy tụ doanh nghiệp từ hơn 16 quốc gia khu vực châu Á - Thái Bình Dương. Giải thưởng được đánh giá qua các tiêu chí về tầm nhìn lãnh đạo, đổi mới, văn hóa tổ chức và cam kết phát triển bền vững. Năm nay, chủ đề "Showcasing Future-Ready Enterprises" nhấn mạnh nhóm doanh nghiệp thích ứng nhanh, ứng dụng công nghệ và hướng tới bền vững.\nViệc chiến thắng ba hạng mục giải thưởng không chỉ là sự ghi nhận về tầm nhìn chiến lược củaFilmore Developmentmà còn góp phần mở rộng thương hiệu Việt Nam trên thị trường bất động sản khu vực châu Á - Thái Bình Dương.\nTheo báo cáo tài chính, Filmore Development đạt doanh thu 240% chỉ tiêu nửa đầu năm sau 2,5 tháng triển khai mô hình Agile. Phương thức này giúp rút ngắn quy trình, tăng tính chủ động của các nhóm dự án và đẩy nhanh quyết định nhất có thể. Doanh nghiệp dự kiến, doanh thu năm 2025 tăng 160% so với năm 2024.\nThành tích doanh thu là nền tảng để Filmore Development có mặt trong top doanh nghiệp tăng trưởng nhanh tại APEA. Việc áp dụng thành công mô hình quản trị hiện đại giúp doanh nghiệp phản ứng nhanh với thị trường, tối ưu hóa nguồn lực và đưa ra các quyết định chiến lược linh hoạt.\n"Các mô hình vận hành đặc trưng cùng cấu trúc doanh nghiệp linh hoạt đã phát huy hiệu quả, biến mỗi nhân sự thành những mắt xích sáng tạo trong cỗ máy chung", đại diện Filmore Development chia sẻ.\nTừ góc độ quản trị, định hướng chuyển đổi được thúc đẩy bởi Chủ tịch hội đồng quản trị Nguyễn Tấn Danh, người nhấn mạnh phương châm phát triển dựa trên giá trị sử dụng và trải nghiệm cư dân. Ông muốn từng trải nghiệm phải có sự khác biệt, qua đó, định hình giá trị của thương hiệu và dự án. Tiêu biểu cho triết lý này là dự án The Filmore Da Nang, khu căn hộ hạng sang đã bàn giao tại trung tâm TP Đà Nẵng.\nTheo ông Danh, doanh nghiệp bền vững không chỉ dựa vào tốc độ mở rộng quy mô, mà ở khả năng tạo ra mối liên kết lâu dài giữa con người và không gian sống. Trong đó, con người là chủ thể trung tâm, quyết định các yếu tố từ quy hoạch, thiết kế, vật liệu đến các tiện ích sống của cư dân. Định hướng này giúp lãnh đạo Filmore Development nhận giải thưởng Doanh nhân xuất sắc châu Á.\nGiải thưởng thứ ba - Thương hiệu truyền cảm hứng - tiếp tục khẳng định mối liên hệ giữa kết quả kinh doanh và định hướng phát triển của doanh nghiệp. Filmore Development theo đuổi mô hình vận hành tập trung vào con người, tích hợp công nghệ hiện đại hướng đến cộng đồng thịnh vượng. Doanh nghiệp cũng đề cao việc duy trì chiều sâu văn hóa trong những thiết kế hiện đại, sử dụng công nghệ thông minh phục vụ đúng nhu cầu người dùng, tạo sự kết nối và tương tác trong cộng đồng.\nVới mục tiêu định hình những chuẩn mực khác biệt để cùng cộng đồng kiến tạo tương lai bền vững, Filmore Development phát triển phân khúc cao cấp nhằm tạo ra những không gian sống với kiến trúc hiện đại kết hợp công năng và đề cao tính trải nghiệm. Từ nền tảng nhà ở, doanh nghiệp phát triển hệ sinh thái bền vững, kết nối cộng đồng và góp phần định hình đô thị theo hướng nâng cao chất lượng sống.', 'Filmore Development lập hat-trick tại lễ trao giải Asia Pacific Enterprise Awards (APEA) 2025 ở ba hạng mục gồm doanh nghiệp tăng trưởng, doanh nhân xuất sắc và thương hiệu truyền cảm hứng.', @cat_id_kinh_doanh_quoc_te, @user_id_anhvu, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("Doanh nghiệp Bắc Ninh nhìn vốn liếng 'trôi' theo dòng lũ", 'doanh-nghiep-bac-ninh-nhin-von-lieng-troi-theo-dong-lu', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/CF55636F-2EF7-473E-B603-E49387-2537-5353-1760341656.png?w=680&h=0&q=100&dpr=1&fit=crop&s=9aXekEriPgY6R5gtrZWlMw', 'Bố Hạ là một trong những điểm ngập sâu của Bắc Ninh trong trận lũ lịch sử do hoàn lưu bão số 11 (Matmo) gây ra. Người dân ven sông Thương chưa từng gặp lụt lớn kể từ năm 1986. Đỉnh lũ ghi nhận trên sông Thương và sông Cầu ngày 9/10, đều vượt mức lịch sử.\nThêm vào đó, sự cố đê bao thôn Ngoài, thôn Đụn thuộc xã Tiên Lục, bị tràn rạng sáng 8/10 dẫn đến vỡ khoảng 70 m đê, khiến ngập úng loạt khu dân cư ở các xã Bố Hạ, Tiên Lục, Mỹ Thái và phường Đa Mai.\nTại Bố Hạ, một ngày sau khi nước rút, chị Đỗ Minh Trang, chủ 16 cửa hàng mẹ và bé Khang Baby, chưa hết bàng hoàng sau trận lũ lịch sử. Nước ngập kho tổng 600 m2 khiến toàn bộ chuỗi tê liệt trong 5 ngày, phần lớn hàng hóa bị nhấn chìm trong nước.\n"Tôi mất hết rồi. 30 nhân viên của công ty vật lộn trong lũ 8 giờ mong cứu được hàng hóa, nhưng đành buông xuôi", chị Trang nghẹn ngào.\nChị kể, khi nước bắt đầu tràn vào kho chiều ngày 7/10, chị cùng nhân viên kê các thùng hàng lên cao. Đến khi phát hiện cần phải "chạy hàng", nước đã lên ngang ngực, xe không thể chạy.\nBa chục con người dùng bạt khổ lớn bọc các thùng hàng, nhưng đặc thù của thùng sữa là có nhiều khoảng trống, nhằm giảm sốc khi vận chuyển. Độ rỗng lớn, trọng lượng nhẹ, các thùng sữa được xếp cao lên chừng 3 m thì bị nước lũ quật đổ. Kinh doanh 12 năm ở Bố Hạ, chị chưa từng gặp trận lũ nào như vậy. Trận lũ diễn ra vài ngày sau khi họ nhập thêm hàng vào kho, chuẩn bị khai trương cơ sở thứ 17. Thiệt hại, vì thế càng nặng nề.\nCách Bố Hạ hơn 30 km, phường Đa Mai cũng là nơi chịu cảnh mưa lũ, ngập sâu. Đến chiều 12/10, khu dân cư thuộc tổ dân phố Tiền Đình vẫn chìm trong nước gần 1 m. Anh Nguyễn Minh Tân, Giám đốc Công ty TNHH May Thành Trung, nói nước lũ đã nhấn chìm hàng trăm đầu máy, thiết bị may của doanh nghiệp trong 4 ngày qua.\n"Chúng tôi có hơn 200 máy công nghiệp, một nửa số đó có trọng lượng quá nặng, sức người không thể di chuyển hết trong mưa lũ", anh nói.\nHiện công ty chưa thể ước tính thiệt hại, vì điện vẫn bị cắt để đảm bảo an toàn khi nước ngập sâu. Với số máy kịp được chuyển lên vị trí cao, anh Tân lo tình trạng hấp thụ hơi nước trong nhiều ngày sẽ ảnh hưởng tới khả năng vận hành, giữa bối cảnh chưa rõ khi nào nước rút hết.\nTại các khu công nghiệp, ảnh hưởng được ghi nhận không lớnnhờ doanh nghiệp chuẩn bị sẵn phương án ứng phó. Công ty TNHH Linh Kiện Nhựa S-Dragon Bắc Giang (khu công nghiệp Vân Trung, phường Nếnh) lập đội phòng chống bão từ sau siêu bão Yagi hồi tháng 9 năm ngoái. Anh Lưu Văn Tuấn, Phó giám đốc nhà máy, là một trong số 15 thành viên của đội. Họ đã ăn, ngủ tại nhà máy từ đợt diễn ra cơn bão số 10 (cuối tháng 9).\nNằm ở vị trí gần sông Thương và sông Cầu, nỗi lo lớn nhất của họ là vỡ đê. Do đó, nhiệm vụ thường xuyên của đội trực chiến là kiểm tra mực nước sông hai tiếng một lần, cập nhật hình ảnh lên máy tính và báo cáo lãnh đạo nhà máy ngay khi dự báo tình huống xấu. May mắn là những ngày qua mực nước ngập phía ngoài nhà máy cao nhất chỉ 40 cm, không tràn vào phía trong nhà xưởng.\nCòn Công ty TNHH Crystal Martin Việt Nam (khu công nghiệp Quang Châu, phường Nếnh) cũng chủ động lên kế hoạch sản xuất và tăng ca để tình hình sản xuất không bị gián đoạn. Họ cũng chủ động bố trí kíp trực ngày đêm theo dõi diễn biến thời tiết, đảm bảo an toàn cho nhà xưởng.\nTheo đại diện Ban Quản lý các Khu công nghiệp Bắc Ninh, một số đường nội bộ trong các khu công nghiệp Đình Trám, Vân Trung, Quang Châu, Song Khê - Nội Hoàng bị ngập trong hai ngày (6 và 7/10) do hệ thống tiêu thoát nước bên ngoài khu công nghiệp ùn ứ cục bộ, ảnh hưởng đến di chuyển của công nhân. Đến 8/10, nước rút, các phương tiện lưu thông bình thường.\nDù nhiều doanh nghiệp ít chịu ảnh hưởng bởi có kinh nghiệm ứng phó với bão lũ, nhưng những công ty nhỏ như của chị Trang, anh Tân cần sự trợ giúp để khôi phục sản xuất, kinh doanh sau ngập lụt. Chủ chuỗi bán lẻ Khang Baby đề xuất được giảm thuế và lãi vay ngân hàng, nhằm nhanh chóng phục hồi kinh doanh.\nĐại diện May Thành Trung cũng mong muốn tương tự. Từ trước đến nay công ty của anh Tân chưa từng vay ngân hàng, nưng với thiệt hại hiện tại, anh nói việc miễn tiền thuê đất cùng một khoản vay lãi suất thấp sẽ giúp họ tái đầu tư máy móc, sớm quay lại sản xuất.\nĐể hỗ trợ phục hồi sản xuất sau mưa lũ, Cục Thuế (Bộ Tài chính) hướng dẫn cơ quan quản lý địa phương giảm 30% thuế tiêu thụ đặc biệt, gia hạn nộp thuế tối đa 2 năm cho doanh nghiệp, hộ - cá nhân kinh doanh bị thiệt hại. Doanh nghiệp cũng được giảm một nửa thuế đất phi nông nghiệp nếu đất và nhà trên đất bị thiệt hại 20-50% giá tính thuế, và được miễn khoản thuế này nếu bị thiệt hại trên 50%.\nVới đất thuê hàng năm để sản xuất nông nghiệp, nuôi trồng thủy sản, họ được miễn toàn bộ nếu mức tổn thất trên 40%, và giảm tương ứng nếu thiệt hại dưới mức này. Trường hợp phải ngừng hoạt động để khắc phục hậu quả do ngập lụt, doanh nghiệp được giảm một nửa tiền thuê đất trong thời gian này.', '12 năm kinh doanh tại Bố Hạ, Bắc Ninh, chị Đỗ Minh Trang chưa từng nghĩ kho hàng 600 m2 có ngày chìm trong nước, tài sản tích góp đều cuốn theo dòng lũ.', @cat_id_kinh_doanh_doanh_nghiep, @user_id_thuytruongphuongdung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Đề nghị xem lại việc áp thuế thu nhập với mua bán vàng miếng', 'e-nghi-xem-lai-viec-ap-thue-thu-nhap-voi-mua-ban-vang-mieng', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/202510131618541802-z7112096037-9732-5171-1760354362.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=0B0B7C8pdsSQXitwKkRXjw', 'Chiều 13/10, Ủy ban Thường vụ Quốc hội cho ý kiến dự án Luật Thuế thu nhập cá nhân (sửa đổi). Thay mặt Chính phủ trình bày tờ trình dự án luật, Thứ trưởng Tài chính Cao Anh Tuấn cho biết dự thảo bổ sung quy định về nhóm thu nhập khác thuộc diện chịu thuế, như chuyển nhượng biển số ôtô trúng đấu giá, tài sản số, vàng miếng. Chính phủ quy định chi tiết về các khoản thu nhập khác để đảm bảo phù hợp với thực tiễn.\nLiên quan tới thuế thu nhập cá nhân với vàng miếng, Chủ nhiệm Ủy ban Kinh tế - Tài chính Phan Văn Mãi phản ánh nhiều ý kiến đề nghị cân nhắc cho thỏa đáng. Việc này để tránh bất cập cho người dân chuyển nhượng vàng nhưng không vì mục đích đầu cơ, kinh doanh. Ngoài ra, có ý kiến đề nghị bổ sung việc xác định giới hạn ngưỡng trọng lượng vàng miếng bị đánh thuế thu nhập cá nhân.\nTrước đó, tại Nghị quyết 278, Chính phủ giao Bộ Tài chính, Ngân hàng Nhà nước nghiên cứu quy định thu nhập từ hoạt động giao dịch mua bán vàng thuộc đối tượng chịu thuế thu nhập cá nhân. Việc này để nâng cao tính minh bạch của thị trường, hạn chế đầu cơ vàng.\nSau đó, Bộ Tài chính thống nhất với Ngân hàng Nhà nước, trình Chính phủ đưa vào dự án Luật Thuế thu nhập cá nhân để xem xét thu thuế với các giao dịch vàng miếng của cá nhân. Mức đề xuất ban đầu là thuế suất 0,1% trên giá chuyển nhượng từng lần, dự kiến không áp dụng với vàng nguyên liệu, trang sức mỹ nghệ.\nYêu cầu áp thuế với giao dịch mua bán vàng được Chính phủ đưa ra trong bối cảnh giá vàng biến động liên tục thời gian qua. Gần đây, giá vàng miếng SJC có thời điểm được các thương hiệu bán vượt mức kỷ lục gần 143 triệu đồng một lượng, chênh lệch duy trì khoảng 15-20 triệu.\nNgoài áp thuế với vàng miếng, tại dự thảo, Chính phủ đề xuất giao cơ quan này quy định mức giảm trừ gia cảnh để phù hợp với tình hình kinh tế - xã hội trong từng thời kỳ, thực hiện chủ trương phân cấp, phân quyền.\nTheo ông Phan Văn Mãi, thực tiễn cho thấy mức giảm trừ gia cảnh không nhất thiết được điều chỉnh thường xuyên, liên tục nên không cần giao Chính phủ quy định. Theo đó, đa số ý kiến đề nghị giữ nguyên quy định như hiện hành, tức là việc điều chỉnh mức giảm trừ gia cảnh sẽ do Ủy ban Thường vụ Quốc hội quyết định.\nCũng tại dự thảo luật, Chính phủ điều chỉnh biểu thuế lũy tiến theo hướng giảm số bậc và nới rộng khoảng cách giữa các bậc. Cụ thể, mức thuế tối thiểu 5% tương ứng với thu nhập tính thuế trong tháng là 10 triệu đồng (sau khi giảm trừ gia cảnh, chi phí tính thuế khác). Thuế suất tối đa là 35%, với thu nhập tính thuế trên 100 triệu. Biểu thuế lũy tiến được rút gọn xuống còn 5 bậc.\nTheo Chủ nhiệm Ủy ban Kinh tế - Tài chính, nhiều ý kiến trong cơ quan thẩm tra băn khoăn về tính hợp lý trong phương án điều chỉnh các mức ngưỡng thu nhập và mức thuế suất tương ứng.\nÔng cho rằng phần thu nhập tính thuế còn quá thấp so với mức thu nhập thực tế đã thay đổi tính từ năm 2009 - thời điểm bắt đầu áp dụng Luật Thuế thu nhập cá nhân. Do đó, cơ quan thẩm tra đề nghị cơ quan soạn thảo cân nhắc xác định phần thu nhập tính thuế tăng tương ứng với tốc độ tăng mức lương cơ sở của 2025 so với 2009 hoặc tương ứng tốc độ tăng GDP trên đầu người.\nBên cạnh đó, theo ông Mãi, biểu thuế mới được thiết kế theo 5 bậc với các mức khởi điểm và cuối cùng cao hơn, song vẫn còn một số khoản thu nhập chịu thuế có thể làm tăng gánh nặng thuế so với hiện hành. Ngược lại, nhiều khoản thu nhập khác lại được giảm nghĩa vụ thuế.\n"Có ý kiến đề nghị giữ nguyên biểu thuế 7 bậc với khoảng cách 5% giữa các bậc như hiện nay, đồng thời chỉ điều chỉnh mức thu nhập tính thuế ở từng bậc cho phù hợp thực tế, nhằm bảo đảm quyền lợi của người nộp thuế", ông cho biết thêm.\nDự án Luật Thuế thu nhập cá nhân (sửa đổi) dự kiến trình Quốc hội xem xét, thông qua tại kỳ họp tháng 10.', 'Chính phủ đề xuất áp thuế thu nhập cá nhân khi chuyển nhượng vàng miếng nhưng Ủy ban Kinh tế và Tài chính đề nghị "cân nhắc" bởi nhiều người bán vàng không vì đầu cơ.', @cat_id_kinh_doanh_chung_khoan, @user_id_phuongdung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("'Gánh nặng với hộ kinh doanh sẽ tăng đáng kể khi bỏ thuế khoán'", 'ganh-nang-voi-ho-kinh-doanh-se-tang-dang-ke-khi-bo-thue-khoan', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/202510131618541802-z7112096037-4513-4994-1760348903.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=4DnPF4AMUQIrQHL0lEwQBA', 'Chiều 13/10, Ủy ban Thường vụ Quốc hội họp phiên thứ 50, cho ý kiến về Dự án Luật Quản lý thuế (sửa đổi).\nThừa ủy quyền Chính phủ, Thứ trưởng Tài chính Cao Anh Tuấn trình bày tờ trình cho biết theo dự thảo luật, căn cứ xác định chịu thuế hay không của hộ, cá nhân kinh doanh sẽ dựa vào doanh thu thực tế hàng năm, thay vì thuế khoán như hiện hành.\nVới các trường hợp phải nộp thuế, hộ và cá nhân kinh doanh sẽ tự khai, tính thuế theo từng loại trong kỳ. Chính phủ sẽ quy định chi tiết phương pháp tính thuế giá trị gia tăng (VAT), hồ sơ và thủ tục kê khai và nộp thuế. Chế độ kế toán áp dụng cho nhóm này sẽ được Bộ Tài chính ban hành.\nGóp ý nội dung này, Chủ nhiệm Ủy ban Kinh tế và Tài chính Phan Văn Mãi cho rằng khi áp dụng cơ chế mới, doanh thu kê khai theo hóa đơn sẽ cao hơn đáng kể mức khoán hiện hành. Điều này dẫn tới gia tăng gánh nặng thuế (VAT, thu nhập cá nhân) của các hộ, cá nhân kinh doanh.\nVì vậy, Thường trực Ủy ban Kinh tế và Tài chính đề nghị cơ quan soạn thảo đánh giá tác động sự thay đổi nghĩa vụ thuế. Trường hợp chênh lệch giữa doanh thu kê khai và thuế khoán quá lớn, ông Mãi cho rằng cần xem xét điều chỉnh thuế suất. Việc này để hộ, cá nhân kinh doanh không bị ảnh hưởng lớn, phù hợp tinh thần Nghị quyết 68 và mục tiêu cải thiện đời sống nhân dân.\nViệt Nam hiện có trên 5,2 triệu hộ kinh doanh, tạo ra 8-9 triệu việc làm, tương đương với khối công ty tư nhân. Thực tế, nhiều hộ kinh doanh quen với nộp thuế khoán nên chưa chú trọng ghi chép sổ sách, minh bạch doanh thu. Khi bỏ thuế khoán, họ buộc phải thực hiện chế độ sổ sách kế toán, hóa đơn theo phương pháp kê khai hay doanh nghiệp nhỏ, siêu nhỏ.\nThuế khoán sẽ được bỏ hoàn toàn từ đầu năm 2026. Song theo Nghị định 70, từ 1/6, các hộ kinh doanh có doanh thu trên 1 tỷ đồng mỗi năm thuộc một số ngành nghề (ăn uống, khách sạn, bán lẻ, vận tải hành khách, thẩm mỹ, vui chơi giải trí...) phải chuyển đổi trước. Đại diện Cục Thuế (Bộ Tài chính) từng cho rằng đây là bước chuẩn bị để các hộ kinh doanh lớn dần làm quen với phương thức quản lý mới.\nCũng theo dự án Luật Quản lý thuế (sửa đổi), mã số thuế của cá nhân, hộ kinh doanh là số định danh cá nhân. Chủ tịch Quốc hội Trần Thanh Mẫn đề nghị dự luật cần quy định chặt chẽ tính bảo mật, tránh rò rỉ thông tin cá nhân. Các quy định về hỗ trợ doanh nghiệp vừa và nhỏ trong quá trình chuyển đổi số cũng cần được bổ sung tại dự luật, theo Chủ tịch Quốc hội.\nTại dự luật, Chính phủ cũng đề xuất cơ chế thưởng cho cơ quan thuế trong trường hợp thu ngân sách vượt kế hoạch. Theo lãnh đạo Bộ Tài chính, đây là khoản "thưởng vượt dự toán", không phải cơ chế tài chính đặc thù như trước kia. Tỷ lệ thưởng dự kiến tối đa 10% trên số vượt thu, tương đương khoảng 8.000 tỷ đồng một năm. Khoản này được chi trả tối đa bằng một tháng tiền lương cho cán bộ, công chức thuế. Chính phủ sẽ báo cáo cấp có thẩm quyền cơ chế này trước khi thể chế hóa vào luật.\nDự thảo cũng đề xuất tăng quyền chủ động cho cơ quan thuế trong quản lý dữ liệu, kiểm tra, ấn định thuế và cưỡng chế thi hành.\nVề chính sách khuyến khích hóa đơn điện tử, cơ quan soạn thảo đề xuất trích 0,1% tổng thuế VAT nội địa (khoảng 300 tỷ đồng một năm) để triển khai chương trình "hóa đơn may mắn" và thưởng người tố giác hành vi gian lận hóa đơn. Theo Thứ trưởng Tài chính Cao Anh Tuấn, Bộ Tài chính đã tham khảo kinh nghiệm quốc tế trước khi đưa ra chính sách này.\nChẳng hạn, Đài Loan quay số hóa đơn may mắn 2 tháng một lần với giải thưởng lớn. Hay Trung Quốc, Bồ Đào Nha cũng áp dụng hình thức tương tự để khuyến khích hộ kinh doanh tuân thủ. Song lãnh đạo Bộ Tài chính thừa nhận cần rà soát để đảm bảo đồng bộ với Luật Ngân sách Nhà nước, tránh chồng chéo và khoản này có thể đưa vào phần dự toán tuyên truyền hỗ trợ, thay vì là một khoản riêng.\nLiên quan tới phối hợp quản lý thương mại điện tử, Bộ Tài chính cho biết sẽ phối hợp với Bộ Công Thương để thống nhất quản lý, chống thất thu, rủi ro và đảm bảo quyền lợi người nộp thuế.\nDự án Luật Quản lý thuế (sửa đổi) dự kiến trình Quốc hội xem xét, thông qua tại kỳ họp thứ 10, khai mạc ngày 20/10.', 'Chủ nhiệm Ủy ban Kinh tế và Tài chính Phan Văn Mãi cho rằng doanh thu kê khai theo hóa đơn sẽ cao hơn mức khoán hiện hành, khiến tăng gánh nặng thuế của các hộ, cá nhân kinh doanh.', @cat_id_kinh_doanh_bat_dong_san, @user_id_phuongdung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('3 nhà khoa học thắng giải Nobel Kinh tế 2025', '3-nha-khoa-hoc-thang-giai-nobel-kinh-te-2025', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/3-nha-kinh-te-Nobel-jpeg-17603-9869-9843-1760352431.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=ZH0A9qYvxp5xoDaaFvKwjg', '11h45 sáng 13/10 (khoảng 16h45 giờ Hà Nội), Viện Hàn lâm Khoa học Hoàng gia Thụy Điển công bố giải Nobel Kinh tế học 2025 thuộc về 3 nhà khoa học.\nĐó là Joel Mokyr (79 tuổi), đến từ Đại học Northwestern (Mỹ); Peter Howitt (79 tuổi) của Đại học Brown (Mỹ) và Philippe Aghion (69 tuổi), hiện làm việc tại cả Trường INSEAD (Pháp) và Trường Kinh tế và Khoa học Chính trị London (Anh). Họ được trao giải nhờ nghiên cứu lý giải tăng trưởng kinh tế dựa trên đột phá sáng tạo.\nCông trình được giải của Mokyr nói về "điều kiện cần để duy trì tăng trưởng thông qua tiến bộ công nghệ". Trong khi đó, Aghion và Howitt được vinh danh nhờ "lý thuyết về tăng trưởng bền vững thông qua sự phá hủy một cách sáng tạo".\nViện Hàn lâm Khoa học Hoàng gia Thụy Điển nhận định việc công nghệ liên tục phát triển giúp tạo ra nhiều sản phẩm và phương thức sản xuất mới. Đây là nền tảng cho tăng trưởng bền vững, giúp nâng cao chất lượng cuộc sống cho người dân trên thế giới. Tuy nhiên, tăng trưởng cuối cùng vẫn chững lại, dù thế giới có những đột phá quan trọng.\nJoel Mokyr đã nghiên cứu các tư liệu lịch sử để tìm hiểu nguyên nhân khiến tăng trưởng bền vững trở thành trạng thái bình thường mới. Ông chỉ ra rằng khi quá trình đổi mới liên tục diễn ra, chúng ta không chỉ cần biết nó đang có hiệu quả, mà còn cần có giải thích khoa học rằng vì sao lại như vậy. Yếu tố này không tồn tại trước Cách mạng Công nghiệp, khiến việc tiếp tục phát minh và phát triển trở nên khó khăn. Mokyr cũng nhấn mạnh tầm quan trọng của việc xã hội cởi mở với ý tưởng mới và chấp nhận thay đổi.\nHai nhà kinh tế học Philippe Aghion và Peter Howitt cũng nghiên cứu các cơ chế đằng sau tăng trưởng bền vững. Trong một bài viết năm 1992, họ xây dựng mô hình toán học cho khái niệm "sự phá hủy mang tính sáng tạo" (creative destruction). Theo đó, khi một sản phẩm mới và tốt hơn xuất hiện trên thị trường, các công ty bán sản phẩm cũ sẽ chịu thiệt. Đổi mới đại diện cho sự sáng tạo và mang lại điều mới mẻ, nhưng đồng thời cũng mang tính hủy diệt vì công nghệ cũ trở nên lỗi thời và không thể cạnh tranh.\nDù bằng các cách khác nhau, các nhà khoa học đạt giải cùng chỉ ra sự phá hủy mang tính sáng tạo sẽ tạo ra những xung đột cần được xử lý theo hướng xây dựng. Nếu không, đổi mới sẽ bị cản trở bởi các doanh nghiệp cũ và nhóm lợi ích.\n"Công trình của các nhà khoa học đạt giải cho thấy tăng trưởng kinh tế không thể được coi là điều hiển nhiên. Chúng ta phải duy trì các cơ chế làm nền tảng cho sự phá hủy mang tính sáng tạo, để không quay trở lại tình trạng trì trệ", Chủ tịch Ủy ban Giải thưởng Nobel Kinh tế John Hassler cho biết.\nDanh sách Nobel Kinh tế 10 năm qua:\nAbhijit Banerjee, Esther Duflo và Michael Kremer\nWilliam Nordhaus\nPaul Romer\nKinh tế học khí hậu\nThuyết tăng trưởng nội sinh\nKinh tế là giải thưởng cuối cùng được trao mỗi mùa Nobel hàng năm, sau y học, vật lý, hóa học, văn học và hòa bình. Nobel cho lĩnh vực này không thuộc cơ cấu giải thưởng ban đầu trong di chúc của nhà khoa học Thụy Điển - Alfred Nobel. Giải này được bổ sung năm 1968, nhân kỷ niệm 300 năm thành lập Ngân hàng Trung ương Thụy Điển - Sveriges Riksbank, cũng là đơn vị đóng góp quỹ cho giải thưởng này.\nQuy trình đề cử, chọn lọc và trao giải Nobel Kinh tế tương tự các lĩnh vực khác. Danh tính những người được đề cử và các thông tin liên quan được giữ bí mật trong 50 năm. Nhà khoa học giành giải năm nay được trao huy chương, chứng nhận và 11 triệu kronor Thụy Điển (gần 1,2 triệu USD).\nĐến nay, giải Nobel Kinh tế học đã được trao 57 lần. Người trẻ nhất được xướng tên năm 46 tuổi và cao tuổi nhất là 90. Các nhà khoa học Mỹ hiện thống trị giải thưởng này.\nNăm ngoái, giải thưởng thuộc về3 nhà kinh tế họcngười Mỹ. Đó là Daron Acemoglu, Simon Johnson và James A. Robinson. Họ được trao giải nhờ nghiên cứu về cách các thiết chế xã hội được thành lập và tác động của nhóm này lên sự thịnh vượng.', '3 nhà khoa học đạt Nobel Kinh tế năm nay, nhờ nghiên cứu lý giải tăng trưởng kinh tế dựa trên đột phá sáng tạo.', @cat_id_kinh_doanh, @user_id_hathu, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("'Hợp đồng bảo hiểm rất dày nên nhiều người không thể đọc hết'", 'hop-dong-bao-hiem-rat-day-nen-nhieu-nguoi-khong-the-doc-het', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/202510130837348023-z7110300326-5678-1947-1760327433.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=ZO0D8fDsERKcPHl3E_JQeA', 'Giải trình nội dung này, Thứ trưởng Tài chính Lê Tấn Cận cho hay Bộ Tài chính cũng nhận diện vấn đề tồn tại trong hợp đồng bảo hiểm hiện nay là "phức tạp, khó hiểu, chưa minh bạch". Do đó, cơ quan này đang tăng kiểm tra, giám sát để hạn chế tối đa hành vi lợi dụng cơ chế, đảm bảo quyền lợi của người tham gia.\nTheo ông, các quy định hiện hành yêu cầu doanh nghiệp bảo hiểm khi tư vấn, giới thiệu sản phẩm cho khách hàng phải rõ ràng, ghi âm lưu lại quá trình tư vấn.\nÔng cho biết Bộ Tài chính đã yêu cầu doanh nghiệp bảo hiểm xây dựng bản tài liệu rút gọn của hợp đồng, tóm tắt nội dung chính cần lưu ý trước khi ký với khách hàng. Việc này nhằm đảm bảo mỗi hợp đồng bảo hiểm đều rõ ràng, minh bạch, không gây thiệt thòi cho người dân.\n"Thực tế, có hiện tượng người dân bức xúc khi được tư vấn chưa rõ ràng, ép buộc mua bảo hiểm. Nhưng cũng có trường hợp người dân chưa tìm hiểu rõ, nên không đáp ứng đủ điều kiện để được bồi thường", ông nói, thêm rằng cơ quan này sẽ tăng kiểm tra, giám sát, nhất là với doanh nghiệp có nhiều phản ánh, để phân loại, kiểm soát rủi ro.\nTheo tờ trình của Chính phủ, dự thảo luật bỏ ít nhất 30% điều kiện đầu tư kinh doanh (22 điều kiện), đơn giản 3 thủ tục hành chính (tương đương cắt giảm 31%) so với luật hiện hành. Dự luật cũng bỏ yêu cầu xác nhận của cơ quan quản lý nước ngoài, một số điều kiện hoạt động và tiêu chuẩn của người quản lý, cũng như giảm yêu cầu cấp phép với đại lý, môi giới, dịch vụ phụ trợ bảo hiểm.\nVề thủ tục hành chính, dự thảo chuyển từ đăng ký sang cơ chế thông báo khi doanh nghiệp phi nhân thọ tách nguồn vốn chủ sở hữu và phí bảo hiểm. Dự luật cũng bỏ quy định chấp thuận phương pháp tính phí bảo hiểm xe cơ giới.\nThẩm tra nội dung này, Ủy ban Kinh tế và Tài chính đề nghị chỉ điều chỉnh những nội dung cấp bách, có nguy cơ tạo điểm nghẽn hoặc cản trở sự phát triển của thị trường bảo hiểm. Việc này nhằm bảo đảm tính ổn định và thúc đẩy tăng trưởng kinh tế - xã hội.\nDự thảo Luật sửa đổi, bổ sung một số điều Luật Kinh doanh Bảo hiểm dự kiến trình Quốc hội xem xét, thông qua tại kỳ họp thứ 10, khai mạc ngày 20/10.', 'Đại biểu Quốc hội nêu thực tế hợp đồng bảo hiểm hiện nay thường dày, phức tạp, nhiều người tham gia không thể đọc và hiểu hết nội dung trước khi ký.', @cat_id_kinh_doanh_quoc_te, @user_id_phuongdung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Giá vàng miếng lên gần 144 triệu đồng một lượng', 'gia-vang-mieng-len-gan-144-trieu-dong-mot-luong', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/233a0830-1748841491-1748841503-4660-9652-1760328414.png?w=680&h=0&q=100&dpr=1&fit=crop&s=AzLtop2WMnxH1_O6MLxMeA', 'Sáng 13/10, Công ty Vàng bạc Đá quý Sài Gòn (SJC) niêm yếtgiá vàngmiếng tại 141,6 - 143,6 triệu đồng một lượng, tăng 800.000 đồng so với cuối tuần trước. Một số thương hiệu khác như Doji, PNJ hay Bảo Tín Minh Châu cũng mua bán kim loại quý này ở mức giá tương tự.\nNgân hàng Á Châu (ACB) báo giá vàng miếng thương hiệu riêng tại 142,6 - 143,6 triệu đồng một lượng. Chênh lệch giữa giá mua và bán tại nhà băng này là 1 triệu đồng một lượng, thấp hơn mức 2 triệu đồng tại SJC.\nVới nhẫn trơn, SJC tăng giá 600.000 đồng so với cuối tuần trước, niêm yết tại 137,4 - 140,1 triệu đồng mỗi lượng. Doji, PNJ đều bán ra mặt hàng này trên 141 triệu đồng một lượng. Bảo Tín Minh Châu báo giá 139,2 - 142,2 triệu đồng.\nGiá vàng trong nước tăng theo đà đi lên của thị trường quốc tế. Trong phiên sáng 13/10 (theo giờ Việt Nam), giá quốc tế tăng 33 USD lên 4.050 USD một ounce. Quy đổi theo tỷ giá Vietcombank, kim loại quý này tương đương 128,7 triệu đồng một lượng, thấp hơn trong nước gần 15 triệu đồng.\nTừ 10/10, sau khi cơ chế độc quyền vàng miếng được bãi bỏ, Ngân hàng Nhà nước sẽ cấp hạn mức cho từng doanh nghiệp và ngân hàng theo quy mô vốn, tình hình xuất khẩu, nhập khẩu vàng miếng, nhập khẩu vàng nguyên liệu, tình hình sử dụng nguyên liệu tùy theo mục đích.\nNgân hàng, doanh nghiệp muốn được cấp phép sản xuất vàng miếng phải có giấy phép mua bán, kinh doanh mặt hàng này. Họ cũng phải đáp ứng điều kiện về vốn, với doanh nghiệp là từ 1.000 tỷ đồng trở lên, ngân hàng từ 50.000 tỷ. Dự kiến có khoảng 8 ngân hàng và 3 doanh nghiệp gồm PNJ, DOJI và SJC đáp ứng yêu cầu về vốn điều lệ để sản xuất vàng miếng.\nCùng với đó, thuế suất xuất khẩu vàng trang sức, kỹ nghệ giảm về 0%. Các sản phẩm khác bằng vàng hoặc bạc cũng được giảm thuế suất tương tự.', 'Các thương hiệu bán ra vàng miếng tại 143,6 triệu đồng một lượng, tăng 800.000 đồng so với cuối tuần trước và cùng chiều đi lên với giá thế giới.', @cat_id_kinh_doanh_doanh_nghiep, @user_id_tronghieu, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Chính phủ đặt mục tiêu GDP bình quân đạt 8.500 USD vào 2030', 'chinh-phu-dat-muc-tieu-gdp-binh-quan-dat-8500-usd-vao-2030', 'https://i1-vnexpress.vnecdn.net/2025/10/13/Nguyen-Hoa-Binh2-1760327752-5659-1760330177.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=-gPMmbVIKQS5TXGznQLE-Q', 'Trình bày báo cáo chính trị tại Đại hội đại biểu Đảng bộ Chính phủ lần thứ nhất, sáng 13/10, Phó thủ tướng thường trực Nguyễn Hòa Bình cho biết Việt Nam đặt mục tiêu tới 2030 là nước đang phát triển có công nghiệp hiện đại, thu nhập trung bình cao. Quy mô GDP trong nhóm 30 nền kinh tế hàng đầu thế giới, thứ 3 khu vực ASEAN.\nTheo đó, Chính phủ phấn đấu tốc độ tăng trưởng kinh tế bình quân đạt 10% một năm trở lên trong giai đoạn tới, và GDP bình quân đầu người 8.500 USD vào 2030.\nTheo báo cáo của Chính phủ, năm nay quy mô GDP ước đạt 510 tỷ USD, xếp thứ 32 thế giới và tăng 5 bậc so với cách đây 5 năm.\nGDP bình quân đầu người gấp 1,4 lần - tăng từ 3.552 USD năm 2020 lên hơn 5.000 USD vào cuối năm nay. Như vậy, với mục tiêu đặt ra, tính theo giá hiện hành, GDP bình quân đầu người sẽ tăng khoảng 1,7 lần sau 5 năm nữa. Cùng với đó, tăng trưởng kinh tế năm nay nhiều khả năngtrên 8%, là động lực quan trọng để đạt hai chữ số những năm tới.\nGiai đoạn 2021-2025, thu ngân sách Nhà nước ước đạt 9,6 triệu tỷ đồng - gấp 1,36 lần 5 năm trước đó và vượt mục tiêu đề ra (8,3 triệu tỷ đồng, tăng trên 15,6%). Quy mô thương mại tăng cao qua từng năm, có thể đạt khoảng 850 tỷ USD năm 2025 - cao nhất từ trước đến nay, thuộc nhóm 20 quốc gia dẫn đầu thế giới...\n5 năm tới, Chính phủ xác định xây dựng nền giáo dục quốc dân hiện đại ngang tầm khu vực và thế giới, tập trung phát triển nguồn nhân lực chất lượng cao. Kết cấu hạ tầng đồng bộ, hiện đại, lấy đô thị làm động lực phát triển vùng và đẩy mạnh xây dựng nông thôn mới.\nVề phòng, chống tham nhũng và lãng phí, Phó thủ tướng cho biết Chính phủ kiên quyết không để có "khoảng trống, vùng tối, không để vi phạm nhỏ tích tụ thành sai phạm lớn". Song song đó, Chính phủ sẽ đẩy mạnh chuyển đổi số, từng bước thực hiện "giám sát, kiểm tra trên dữ liệu".\nĐể đạt mục tiêu tăng trưởng kinh tế hai chữ số giai đoạn tới, Chính phủ xác lập mô hình tăng trưởng mới, đẩy mạnh công nghiệp hóa, song song với cơ cấu lại nền kinh tế. Trong đó, khoa học công nghệ, đổi mới sáng tạo và chuyển đổi số làm động lực chính, và thuộc nhóm dẫn đầu trong các nước có thu nhập trung bình cao.\n"Lạm phát sẽ được kiểm soát, bảo đảm nợ công, nợ Chính phủ, nợ nước ngoài quốc gia, bội chi ngân sách trong giới hạn cho phép", Phó thủ tướng nêu, thêm rằng các động lực tăng trưởng mới như chuyển đổi số, chuyển đổi xanh, kinh tế tuần hoàn, các ngành, lĩnh vực mới nổi sẽ được thúc đẩy.\nÔng cho biết phát triển kinh tế nhà nước tiếp tục giữ vai trò chủ đạo, dẫn dắt nền kinh tế, trong đó kinh tế tư nhân là một động lực quan trọng nhất của nền kinh tế quốc gia. Dự án FDI sẽ được thu hút có chọn lọc gắn với chuyển giao công nghệ.\nChính phủ cũng hoàn thành mục tiêu cả nước có 5.000 km đường bộ cao tốc đến 2030 và đưa vào sử dụng toàn tuyến đường bộ ven biển, tuyến đường sắt Lào Cai - Hà Nội - Hải Phòng; đường sắt đô thị tại Hà Nội (100 km) và TP HCM (100 km).\nDự kiến hai nhà máy điện hạt nhân Ninh Thuận 1 và 2 cũng được xây dựng vận hành trong giai đoạn sau 2030 và đầu tư một số nhà máy điện hạt nhân với quy mô phù hợp, công nghệ mới, an toàn.\nVề nguồn nhân lực, Chính phủ yêu cầu tập trung phát triển chất lượng cao theo chuẩn quốc tế, nhất là các ngành mũi nhọn và 11 nhóm ngành công nghệ chiến lược. Các chính sách đột phá phát triển khoa học, công nghệ sẽ được đẩy mạnh giai đoạn tới, nhằm đạt quy mô kinh tế số tối thiểu 30% GDP vào 2030.\nTrước đó, khai mạc Đại hội,Thủ tướng Phạm Minh Chínhđánh giá "đất nước đã đạt những thành tựu quan trọng trên các lĩnh vực", với kết quả năm sau, nhiệm kỳ sau tốt hơn năm trước, nhiệm kỳ trước.\nNhiệm kỳ 2026-2030 theo ông, tình hình thế giới dự báo tiếp tục diễn biến phức tạp, tiềm ẩn nhiều rủi ro, và thách thức nhiều hơn thời cơ. Do đó, Thủ tướng đề nghị các đại biểu thảo luận, phân tích để đưa ra giải pháp đột phá giữ ổn định kinh tế vĩ mô, kiểm soát lạm phát, thúc đẩy tăng trưởng GDP bình quân 10% một năm trở lên trong nhiều năm.\n"Phải chăng cần tập trung hoàn thiện thể chế để giải phóng sức sản xuất, huy động, sử dụng hiệu quả và làm mới các động lực tăng trưởng truyền thống, thúc đẩy mạnh động lực mới như kinh tế xanh, kinh tế số...", Thủ tướng gợi mở.', 'Chính phủ phấn đấu tốc độ tăng trưởng kinh tế đạt 10% một năm trở lên và GDP bình quân đầu người khoảng 8.500 USD vào 2030.', @cat_id_kinh_doanh_chung_khoan, @user_id_sonha, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Mỹ nói Trung Quốc không thông báo việc siết đất hiếm', 'my-noi-trung-quoc-khong-thong-bao-viec-siet-dat-hiem', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/AFP-20250409-2209194904-v3-Hig-2069-1400-1760326465.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=Tb6ZVXytQrzvejj6ue165g', '"Tôi có thể nói với anh rằng chúng tôi không được thông báo. Ngay khi nghe được tin tức từ các nguồn công khai, chúng tôi đã liên lạc với Bắc Kinh, đề nghị sắp xếp một cuộc điện đàm, nhưng họ chưa đồng ý", Greer cho biết trong cuộc phỏng vấn trênFox Newshôm 12/10.\nNgày 9/10, Trung Quốc thông báosiết xuất khẩu đất hiếmlần thứ 2 trong năm nay. Động thái này khiến một ngày sau đó, Tổng thống Mỹ Donald Trump tuyên bố áp thêm thuế nhập khẩu 100% với hàng hóa từ nền kinh tế thứ hai thế giới, và ám chỉ sẽ hủy cuộc gặp Chủ tịch Trung Quốc Tập Cận Bình dự kiến vào cuối tháng này.\nHai ngày sau, Bộ Thương mại Trung Quốc ra thông cáo, cho rằng động thái của Mỹ là "tiêu chuẩn kép", vì chính Washington cũng tăng biện pháp kinh tế chống lại nước này kể từ tháng 9. Họ cũng cho biết trước khi thông báo công khai, họ đã báo cho các nước liên quan thông qua cơ chế đối thoại song phương về kiểm soát xuất khẩu.\nTrên mạng xã hội Truth Social ngày 12/10, Tổng thống Donald Trump thể hiện giọng điệu hòa giải khi viết: "Chủ tịch Tập đáng kính vừa trải qua khoảnh khắc tồi tệ. Ông ấy không muốn nước mình suy thoái, và tôi cũng vậy. Mỹ muốn giúp, chứ không phải làm tổn hại Trung Quốc".\nQuan hệ thương mại Mỹ - Trung liên tục xuống cấp từ khi ông Trump quay lại Nhà Trắng vào đầu năm nay. Hồi tháng 4, hai nước trải qua cuộc chiến áp thuế trả đũa, khiến thuế nhập khẩu của hàng Trung Quốc vào Mỹ có thời điểm lên 145%. Hàng Mỹ sang Trung Quốc cũng bị áp tới 125%.\nĐến tháng 5 căng thẳng hạ nhiệt, khi hai nước đồng ýgiảm thuế cho nhautrong 90 ngày. Trung Quốc hạ mức thuế với hàng Mỹ về 10% từ 125%, còn mức thuế Mỹ cũng hạ xuống còn 30%. Thỏa thuận này được gia hạn hồi tháng 8.\nVới việc căng thẳng hai nước tái bùng phát cuối tuần trước, thị trường tài chính toàn cầu tiếp tục xáo trộn. Chứng khoán Mỹ lao dốc phiên 10/10. Thị trường Trung Quốc sáng nay cũng đang đi xuống.\nDù vậy, Greer tin rằng thị trường tài chính sẽ ổn định trở lại trong tuần này, sau khi tình hình lắng dịu. Ông cũng cho biết cuộc gặp giữa ông Trump và ông Tập bên lề Hội nghị Thượng đỉnh APEC tại Hàn Quốc cuối tháng này vẫn có thể diễn ra.\nHà Thu(theo Reuters)', 'Đại diện Thương mại Mỹ Jamieson Greer cho biết họ không được Trung Quốc báo trước về thay đổi chính sách đất hiếm, ngược với tuyên bố của Bắc Kinh.', @cat_id_kinh_doanh_bat_dong_san, @user_id_hathu, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("Đề xuất token hóa để tích lũy hoặc 'xổ số' nhà ở xã hội", 'e-xuat-token-hoa-de-tich-luy-hoac-xo-so-nha-o-xa-hoi', 'https://i1-vnexpress.vnecdn.net/2025/10/07/andong-1759804135-3602-1759804708.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=lBOHrjcSNp2Xr38zd3T4KQ', 'Trong hội thảo đóng góp ý tưởng các dự án hợp tác công - tư do Ban Nghiên cứu phát triển Kinh tế tư nhân (Ban IV) tổ chức mới đây, doanh nghiệp về blockchain Reetechs Labs đề xuất cho phép thí điểm token hóa nhà ở xã hội.Token hóalà quá trình chuyển đổi quyền sở hữu hoặc giá trị của một tài sản thành các token kỹ thuật số trên blockchain. Những token này đại diện cho một phần hoặc toàn bộ giá trị của tài sản và có thể được giao dịch trên các nền tảngblockchain.\nVới việc token hóa nhà ở xã hội, người dân có thể sở hữu một phần nhỏ (từng token) rồi tích lũy dần cho đến khi sở hữu trọn vẹn. Ví dụ, một căn nhà ở xã hội được quy ra 100 token, người lao động không đủ tiền mua trọn vẹn một căn, có thể mua trước một token mỗi tháng, rồi tích lũy đủ 100 token để nhận nhà.\nNgoài cách tích lũy trên, Reetechs Labs còn gợi ý thêm một cách tiếp cận khác là thực hiện "xổ số" cho những ai sở hữu token nhà ở xã hội. Ví dụ, một căn nhà ở xã hội quy ra 100 token, bán cho 100 người, mỗi người sở hữu một token tương đương một tờ "vé số". Chủ đầu tư sẽ quay số định kỳ để chọn ra người trúng nhà trong số 100 người đã mua token nhà ở xã hội. Hình thức này tương tự cách Ấn Độ triển khai chương trình "xổ số" trúng thưởng để khuyến khích người dân mua vé tàu hỏa chính thống, thay vì vé "chui".\nBình luận vớiVnExpress, bà Trần Thị Kiều Diễm, CEO Orochi Network - công ty công nghệ về blockchain chuyên bảo mật và mật mã, có trụ sở tại TP HCM, cho rằng đề xuất trên có nhiều điểm mới mẻ, nhưng cũng đặt ra các vấn đề pháp lý, kỹ thuật và xã hội cần cân nhắc kỹ.\nƯu điểm của token hóa nằm ở việc "chẻ nhỏ" quyền sở hữu, giúp người ít vốn có thể tham gia. Tuy nhiên, nhà ở xã hội là chính sách an sinh, luật quy định rất chặt như mua, bán phải đăng ký, công chứng; giấy tờ sở hữu là "sổ hồng", "sổ đỏ", không phải token. Hiện token chưa được pháp luật công nhận như giấy tờ sở hữu nhà đất, nên nếu có tranh chấp hoặc bị hack, cơ quan nhà nước khó giải quyết theo token.\nThêm nữa,nhà ở xã hộichỉ được bán lại sau khoảng 5 năm với các điều kiện cụ thể, nên mô hình "mua dần" hay "xổ số trúng nhà" vướng mục tiêu chọn đúng đối tượng và quy trình chuyển nhượng. Chưa kể đến các vấn đề phát sinh khi một căn nhà ở xã hội có quá nhiều người "đồng sở hữu", khó chuyển nhượng.\nCùng quan điểm, ông Nguyễn Hoàng Nam, Tổng giám đốc Công ty cổ phần G-Home - doanh nghiệp chuyên phát triển nhà ở xã hội, nhìn nhận đề xuất token hóa nhà ở xã hội rất khó khả thi. Bởi đây là loại hình nhà ở đặc thù, có nhiều ràng buộc về người thụ hưởng, quy trình mua bán phức tạp.\nÔng nói người mua loại hình này được vay gói tín dụng ưu đãi với lãi suất thấp, việc trả góp từng tháng không khác gì với ý tưởng "trả từng phần nhỏ đến khi sở hữu trọn vẹn" trong khi tính pháp lý được bảo đảm. Nếu tất cả khách hàng đều chọn chia nhỏ tài sản ra token, ông Nam cho rằng rủi ro tài chính sẽ thuộc về chủ đầu tư bởi dòng tiền bị chia quá nhỏ, không đủ để họ xoay vòng đầu tư, hoàn thiện các dự án.\nTương tự với đề xuất "xổ số", ông Nam nói không cần thiết. Bởi với những dự án có lượng hồ sơ đăng ký đông, kéo theo tỷ lệ chọi lớn, chủ đầu tư sẽ ra văn bản quy định cụ thể về quy trình, cách thức thực hiện bốc thăm nhà xã hội. Do đó, người mua không cần phụ thuộc thêm "một lần may rủi khi quay số token".\nDưới góc nhìn của chuyên gia tư vấn Nguyễn An Huy, cố vấn tài chính cấp cao tại Công ty FIDT, đánh giá cao công nghệ blockchain, với khả năng minh bạch hóa và lưu vết mọi giao dịch, có thể hạn chế tình trạng gian lận trong lĩnh vực vốn rất nhạy cảm như nhà ở xã hội. Về phía chủ đầu tư, token hóa còn là một kênh huy động vốn xã hội hóa, giảm gánh nặng phụ thuộc vào ngân hàng.\nTuy nhiên ông Huy cũng thừa nhận những rủi ro và hạn chế không nhỏ. Bản chất của token là dễ mua bán, trao đổi, dẫn đến nguy cơ hình thành một thị trường đầu cơ thứ cấp. Nếu không được kiểm soát, token nhà ở xã hội có thể bị đẩy giá, khiến những đối tượng thực sự cần bị gạt ra. Đặc biệt, mô hình "xổ số" gây nhiều băn khoăn về tính công bằng khi người dân có thể bỏ tiền tích lũy nhiều năm nhưng không chắc được sở hữu nhà, đi ngược mục tiêu an sinh vốn là nền tảng của chính sách nhà ở xã hội.\nNgoài ra, đối tượng hưởng lợi chính là công nhân, người lao động thu nhập thấp - vốn ít tiếp cận công nghệ tài chính - sẽ đối diện với các rủi ro mạo danh, giả mạo hoặc bị lừa đảo tài chính. Song song đó, cơ quan quản lý cũng cần giải quyết các bài toán về vận hành như xây dựng theo tiến độ thế nào khi không thể chắc chắn được tiến độ tích sản vào token của người có thu nhập thấp.\n"Hiện tại với những quy trình đơn giản và thủ công, cũng đã là một thách thức cho việc quản lý đúng đối tượng mua nhà ở xã hội, nếu thêm một thị trường phái sinh như token tham gia vào trong quy trình, mọi thứ có thể còn phức tạp hơn", chuyên gia FIDT nói thêm.\nÔng An Huy cho rằng nếu xem xét thí điểm, cần loại bỏ mô hình "xổ số" vì đi ngược tinh thần công bằng. Với mô hình tích lũy token, nếu có thể chi tiết hơn nữa các đề xuất về khâu vận hành cụ thể, có thể cân nhắc triển khai ở quy mô nhỏ mang tính nghiên cứu thử nghiệm, gắn token với hồ sơ định danh của người mua, hạn chế tuyệt đối việc chuyển nhượng trên thị trường thứ cấp và quản lý thanh toán qua Ngân hàng Chính sách xã hội hoặc một định chế tín dụng được chỉ định. Song song, cơ quan quản lý cần phối hợp xây dựng một hành lang pháp lý thử nghiệm (sandbox) để kiểm soát chặt chẽ.\nTrong khi đó, CEO Orochi Network liên hệ thêm ở nước ngoài, khi token gắn với quyền sở hữu tài sản, cơ quan quản lý thường coi đó làchứng khoán, buộc tuân thủ khung phát hành - lưu ký - phân phối. Ví dụ, Hong Kong có hai thông tư về "chứng khoán được mã hóa", EU có Quy định về thị trường tài sản số (MiCA) nhưng tài sản dạng chứng khoán vẫn theo luật chứng khoán riêng. Thực tế, nhiều dự án phải dùng SPV (Special Purpose Vehicle), quỹ tín thác (trust) hay doanh nghiệp trách nhiệm hữu hạn (LLC) đứng tên nhà đất; nhà đầu tư chỉ cầm "phiếu quyền lợi" gắn vào pháp nhân đó, chứ không trực tiếp đứng tên sổ.\nDo đó, bà Kiều Diễm đề xuất thay vì "bán từng miếng căn hộ", có thể "bán vé chia tiền thuê và lãi của tòa nhà". Về pháp lý, loại "vé" này giốngtrái phiếuhay chứng quyền chia lợi nhuận nên dễ chuẩn hóa theo khung chứng khoán hiện hành, không đụng tới đổi chủ sổ đỏ.\nLãnh đạo Orochi Network lưu ý token phải có whitelist (chỉ ai đạt điều kiện mới mua - bán), giới hạn chuyển nhượng theo thời gian hoặc theo quy định; ví cất giữ token phải có lưu ký, định danh xác thực danh tính (KYC) hay chống rửa tiền (AML) đầy đủ. Dòng tiền gồm tiền thuê và phí vận hành sẽ được kế toán số hóa đưa vào oracle (hệ thống liên lạc giữa blockchain và nguồn dữ liệu) để hệ thống hợp đồng thông minh tự động chia lợi tức, mua lại token, công bố minh bạch.\nĐối với thị trường thứ cấp, bà cho rằng cần áp lock-up (khoảng thời gian khóa sổ khiến nhà đầu tư không rút vốn được), trần sở hữu, tần suất giao dịch. Song song đó, cơ quan quản lý cần chọn quỹ chính sách hay Ngân hàng Chính sách xã hội làm bên mua lại cuối để bình ổn.\nBà cũng cho rằng cần thiết kế sản phẩm theo mô hình "lợi tức thấp - rủi ro thấp" cho người thu nhập thấp như kênhtiết kiệmdài hạn. Đây không phải vé "trúng căn hộ" mà là cách tích lũy minh bạch và đều đặn.\nThực tế theo ông Phạm Đức Toản, Tổng giám đốc Công ty EZ Property, token hóa bất động sản từng "nở rộ" tại Việt Nam giai đoạn 2020-2022, tập trung ở các dự án đất nền, nhà riêng lẻ. Mô hình này cho phép người dân mua theo tỷ lệ phần trăm nhỏ, tăng khả năng tiếp cận cho nhà đầu tư có tài chính vừa và nhỏ.\nTuy nhiên sau giai đoạn phát triển nóng, hình thức này bộc lộ nhiều điểm yếu liên quan đến pháp lý, tính minh bạch cũng như công nghệ, kỹ thuật. Ông Toản cho biết hiện Việt Nam còn thiếu các quy định rõ ràng để đảm bảo an toàn cho mô hình token hóa bất động sản. Điều này khiến cho quy trình chuyển đổi từ token (tài sản số) về giấy chứng nhận quyền sử dụng đất (tài sản thật) có thể gặp nhiều khó khăn và rủi ro.\nQuá trình giao dịch bằng token cũng sẽ phụ thuộc thêm một bên thứ ba là nền tảng blockchain và cơ sở hạ tầng công nghệ. Trường hợp xảy ra sự cố, rủi ro mất mát tài sản hay rò rỉ thông tin bảo mật có thể xảy ra.\nCác chuyên gia cho biết thời gian qua, người mua nhà xã hội nhận nhiều thông tin tích cực khi nguồn cung dự án tăng trong bối cảnh điều kiện thuê, mua được nới lỏng. Đơn cử, điều kiện thuê, mua không còn yêu cầu về cư trú. Người dân cần đáp ứng 2 điều kiện chính gồm chưa có nhà tại nơi có dự án và thu nhập dưới 15 triệu đồng một tháng với cá nhân và tổng 30 triệu đồng cho hộ gia đình. Mới đây, Bộ Xây dựng tiếp tục đề xuất nâng mức thu nhập với cá nhân mua nhà xã hội lên tối đa 20 triệu một tháng, mở rộng thêm khả năng tiếp cận cho người dân.\nCùng đó, Quỹ nhà ở quốc gia là một trong những điểm mới trong Nghị quyết thí điểm về một số cơ chế, chính sách đặc thù phát triển nhà ở xã hội sẽ được Quốc hội biểu quyết thông qua ngày 29/5. Mục đích của quỹ chủ yếu là đầu tư xây dựng, tạo lập nhà ở xã hội để cho thuê. Thay vì bán, việc cho thuê sẽ giúp cho nhiều người có thể tiếp cận loại hình nhà ở này.\nÔng Nguyễn Hoàng Nam nhìn nhận nếu được thông qua, Quỹ nhà ở quốc gia sẽ giúp tăng cường nguồn lực cho phát triển nhà ở xã hội, thu hút thêm các doanh nghiệp tham gia đầu tư xây dựng, tăng nguồn cung. Qua đó, nó sẽ đảm bảo người thu nhập thấp tiếp cận được với nhà ở xã hội có mức giá phù hợp.', 'Doanh nghiệp blockchain Reetechs Labs đề xuất token hóa nhà ở xã hội để người dân mua dần từng phần hoặc "xổ số" để chọn ra người trúng nhà.', @cat_id_kinh_doanh, @user_id_tatatngocdiem, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Đất hiếm - nguồn cơn khiến ông Trump tăng thuế với Trung Quốc', 'at-hiem-nguon-con-khien-ong-trump-tang-thue-voi-trung-quoc', 'https://i1-kinhdoanh.vnecdn.net/2025/10/12/rare-1749621058-3975-174962188-4761-8736-1760237352.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=-AiPjTpMFJb3tLxPrCH6ZA', 'Ngày 9/10, Trung Quốc thông báosiết xuất khẩu đất hiếmlần thứ 2 trong năm nay. Động thái này khiến một ngày sau đó, Tổng thống Mỹ Donald Trump tuyên bố áp thêm thuế nhập khẩu 100% với hàng hóa Trung Quốc, và ám chỉ sẽ hủy cuộc gặp Chủ tịch Trung Quốc Tập Cận Bình dự kiến vào cuối tháng này.\nNgày 12/10, Bộ Thương mại Trung Quốc ra thông cáo, khẳng định chính sách kiểm soát là "hợp pháp" và chỉ "tác động rất hạn chế" lên sản xuất và cung ứng. Bắc Kinh cho rằng đây là bước đi cần thiết, do đất hiếm được ứng dụng trong quân sự và toàn cầu hiện bất ổn, thường xuyên có xung đột. Nước này cũng cáo buộc Mỹ"tiêu chuẩn kép", khi thời gian qua liên tục có các chính sách bất lợi cho doanh nghiệp Trung Quốc.\nThực tế, việc Trung Quốc siết xuất khẩu không phải là điều mới. Nước này đã dần hạn chế bán đất hiếm ra quốc tế trong 2 năm qua. Thông qua hàng loạt chính sách công nghiệp, quốc gia này đã xây dựng vị thế gần như độc quyền với nguồn cung đất hiếm trên toàn cầu.\nHồi tháng 4, nền kinh tế thứ hai thế giới siết xuất khẩu đất hiếm - lần đầu tiên trong năm nay, và đây được coi là đòn đáp trả "thuế đối ứng" mà ông Trump công bố với hầu hết đối tác thương mại. Sau khi hai nước đạt thỏa thuận giảm thuế tại Geneva (Thụy Sĩ) vào tháng 5, quan chức Mỹ từng kỳ vọng Bắc Kinh nới lỏng quy định kiểm soát với vật liệu này, nhưng kết quả chưa khiến họ hài lòng.\nĐất hiếm gồm 17 nguyên tố, là vật liệu thiết yếu trong các sản phẩm như xe điện, động cơ máy bay, máy chụp cộng hưởng từ (MRI) và thiết bị điều trị ung thư. Vật liệu này cũng rất quan trọng với radar quân đội, khi chúng được dùng trong tiêm kích F-35, tàu ngầm, laser, vệ tinh, tên lửa Tomahawk và nhiều loại vũ khí khác, theo báo cáo năm 2025 của Trung tâm Nghiên cứu Chiến lược và Quốc tế (CSIS).\nTuy nhiên, loại khoáng sản này không thực sự hiếm trong tự nhiên, vì các nguyên tố có mặt khắp nơi trong lớp vỏ Trái Đất. Trong báo cáo năm 2024, Cục Khảo sát Địa chất Mỹ (USGS) ước tính có 110 triệu tấn đất hiếm trên thế giới. Trữ lượng lớn nhất ở Trung Quốc, với 44 triệu tấn.\nĐất hiếm thậm chí dồi dào hơn vàng, nhưng việc khai thác, tinh chế rất khó, đòi hỏi các quốc gia cần có năng lực khai mỏ, công nghệ tác chiết và xử lý nguyên liệu thô. Việc này khá tốn kém và gây hại cho môi trường. Trung Quốc hiện sản xuất khoảng 90% lượng đất hiếm tinh chế và nam châm đất hiếm trên thế giới.\nCó 2 loại đất hiếm, chia theo khối lượng nguyên tử, đó là nặng và nhẹ. Đất hiếm nặng khan hiếm hơn, và Mỹ hiện cũng không có năng lực tách chiết sau khai thác.\n"Đến đầu năm nay, chúng ta vẫn phải gửi đất hiếm nặng khai thác ở California sang Trung Quốc để tách chiết", Gracelin Baskaran - Giám đốc Chương trình An ninh Khoáng sản Chiến lược tại CSIS cho biết trênCNN.\nTuy nhiên, việc Mỹ liên tiếp nâng thuế nhập khẩu với Trung Quốc, có thời điểm lên tới 145% hồi tháng 4, đã làm gián đoạn quá trình này. "Bắc Kinh cho thấy họ sẵn sàng xoáy vào sự phụ thuộc của Washington trong công đoạn tách chiết đất hiếm", Baskaran nhận định. Mỹ chỉ có một mỏ đang hoạt động tại California.\nGiới phân tích cho rằng Bắc Kinh đang dùng đất hiếm làm đòn bẩy trong căng thẳng thương mại. Các chính sách mới được đưa ra chỉ vài tuần trước cuộc họp dự kiến giữa lãnh đạo hai nước tại Hội nghị Thượng đỉnh APEC ở Hàn Quốc cuối tháng này.\nĐộng thái hôm 9/10 của Trung Quốc gồm bổ sung 5 nguyên tố là holmium, erbium, thulium, europium, ytterbium cùng nam châm và các vật liệu liên quan vào danh sách cần xin giấy phép xuất khẩu. Như vậy, tổng nguyên tố đất hiếm bị hạn chế đã lên 12. Bắc Kinh cũng đòi hỏi giấy phép khi xuất khẩu công nghệ sản xuất loại khoáng sản này.\nKhách hàng quốc phòng ở nước ngoài không được cấp phép. Đơn xin cấp phép liên quan đến chất bán dẫn tiên tiến cũng được xem xét riêng từng trường hợp. Động thái này được đưa ra chỉ một ngày sau khi các nghị sĩ Mỹ kêu gọi mở rộng lệnh cấm xuất khẩu thiết bị sản xuất chip sang nền kinh tế thứ hai thế giới.\nXuất khẩu đất hiếm của Trung Quốc tăng dần những tháng gần đây, khi Bắc Kinh cấp thêm giấy phép sau khi đạt thỏa thuận với Mỹ và châu Âu. Dù vậy, một số khách hàng vẫn than phiền khó tiếp cận nguồn cung.\nĐây không phải lần đầu tiên trong năm nay ông Trump nổi giận vì Trung Quốc siết đất hiếm. Hồi tháng 6, ông viết trên mạng xã hội Truth Social rằng Trung Quốc vi phạm thỏa thuận khi vẫn giữ các chính sách kiểm soát với 7 nguyên tố đất hiếm và sản phẩm liên quan.\nChính sách của Trung Quốc có thể gây ra tác động lớn, do Mỹ phụ thuộc nhiều vào họ trong lĩnh vực này. Báo cáo của USGS cho thấy giai đoạn 2020-2023, 70% nhập khẩu đất hiếm và hợp chất từ vật liệu này của Mỹ đến từ Trung Quốc.\nVì thế, động thái tuần này của Bắc Kinh được coi là bước leo thang đáng kể trong căng thẳng thương mại giữa hai nền kinh tế lớn nhất thế giới. "Trung Quốc đang ngày càng trở nên kém thân thiện khi gửi thư đến nhiều quốc gia trên thế giới, nói rằng muốn áp kiểm soát xuất khẩu với mọi yếu tố sản xuất liên quan đến đất hiếm", ông Trump viết trên mạng xã hội Truth Social.\nÔng tuyên bố buộc phải đáp trả về mặt tài chính, với tư cách là Tổng thống Mỹ. "Với mỗi nguyên tố mà họ độc quyền, chúng ta sẽ có hai", ông Trump khẳng định.\nHà Thu(theo CNN, Reuters)', 'Căng thẳng thương mại Mỹ - Trung Quốc luôn có điểm nghẽn lớn, là đất hiếm - vật liệu nền kinh tế thứ hai thế giới hiện thống trị chuỗi cung ứng toàn cầu.', @cat_id_kinh_doanh_quoc_te, @user_id_hathu, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Chủ tịch giúp 400 nhân viên thành triệu phú', 'chu-tich-giup-400-nhan-vien-thanh-trieu-phu', 'https://i1-kinhdoanh.vnecdn.net/2025/10/12/jyoti-bansal-1760245118-4624-1760245500.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=oSmDC8f6jNpkr57i9jFirQ', 'Năm 2017, khi AppDynamics chỉ còn vài ngày nữa là lên sàn chứng khoán, Tập đoàn công nghệ Cisco bất ngờ xuất hiện và đề nghị mua lại công ty này với giá 3,7 tỷ USD. Là nhà sáng lập kiêm Chủ tịch, Bansal (48 tuổi) - một doanh nhân công nghệ Mỹ gốc Ấn - vẫn trở nên giàu có dù chọn cách nào. Tuy nhiên, chỉ một trong hai lựa chọn có thể đảm bảo hàng trăm nhân viên của ông cũng đạt được điều đó.\nNgay sau khi thương vụ được chấp thuận, khoảng 400 nhân viên AppDynamics chứng kiến giá trị số cổ phiếu của họ đạt ít nhất 1 triệu USD, người phát ngôn của Bansal cho biết. "Chúng tôi còn có hàng chục nhân viên sở hữu hơn 5 triệu USD. Đó là những khoản tiền thay đổi cuộc đời", Bansal trả lờiCNBCnăm 2024.\nÔng cho biết quyết định bán công ty được đưa ra dựa trên nhiều yếu tố. Đó là mức độ phù hợp giữa phần mềm của AppDynamics và hệ sinh thái Cisco, cùng ảnh hưởng của thương vụ với 1.200 nhân viên của hãng, về văn hóa lẫn tài chính.\nBansal cũng so sánh dự báo vốn hóa sau IPO của AppDynamics với định giá 3,7 tỷ USD mà Cisco đưa ra, để xem phương án nào hiệu quả hơn về mặt tài chính. Ông ước tính để đạt được mức vốn hóa tương đương trên sàn, họ cần "3 đến 4 năm kinh doanh xuất sắc". Điều này đồng nghĩa các nhân viên phải trải qua thêm "3 đến 4 năm rủi ro".\nSau khi bán công ty, Bansal thừa nhận rằng ông từng hối tiếc. Ông nghĩ rằng AppDynamics có thể tiếp tục tăng trưởng và từng cảm thấy mất phương hướng khi không còn startup nào để điều hành. Từ 2020, ông là CEO kiêm đồng sáng lập hai công ty phần mềm Traceable và Harness. Hai doanh nghiệp này đã sáp nhập đầu năm nay, với tổng giá trị 5 tỷ USD.\nDù vậy, Bansal cho rằng quyết định bán vẫn là đúng đắn với thông tin ông có vào thời điểm đó. Thương vụ cũng mang lại giá trị tài chính đáng kể cho chính Bansal, với 14% cổ phần AppDynamics, theo hồ sơ của Ủy ban Chứng khoán Mỹ (SEC).\n"Là nhà sáng lập, quyết định này không chỉ mang lại khoản tiền thay đổi cuộc đời của tôi. Tôi biết mình ở vị trí tốt. Nhưng yếu tố lớn nhất với tôi là nhân viên", ông nói.\nKhông phải nhà sáng lập startup nào cũng nghĩ đến nhân viên khi cân nhắc bán công ty. Vì vậy, quyết định của Bansal là điều đáng chú ý.\nTuy nhiên, Bansal không phải CEO duy nhất biến nhân viên thành triệu phú nhờ các thương vụ mua bán. Năm 1999, sau khi bán Broadcast.com cho Yahoo với giá 5,7 tỷ USD, tỷ phú đầu tư Mark Cuban đã chia thưởng cho các nhân viên, giúp hàng trăm người thành triệu phú.\nCứ mỗi lần bán công ty, Cuban lại làm điều này, bắt đầu từ khi nhượng lại MicroSolutions cho CompuServe năm 1990. Sau này là thương vụ bán cổ phần trong hãng truyền hình HDNet vào 2019 và cổ phần câu lạc bộ bóng rổ Dallas Mavericks năm ngoái.\nJay Chaudhry - tỷ phú sáng lập kiêm CEO công ty an ninh mạng Zscaler cũng từng làm điều này, khi chuyển nhượng công ty đầu tiên của mình là SecureIT cho VeriSign năm 1998. Thương vụ được định giá 70 triệu USD, thanh toán hoàn toàn bằng cổ phiếu VeriSign. Gần 2 năm sau khi thương vụ hoàn tất, cổ phiếu VeriSign tăng vọt. Hơn 70 người trong số 80 nhân viên SecureIT nhận cổ phiếu khi đó trở thành triệu phú.\n"Mọi người rất vui mừng, vì họ chưa bao giờ nghĩ đến số tiền lớn như vậy. Rất nhiều người đã mua nhà, xe mới. Tôi biết có người còn xin nghỉ 6 tháng, thuê một chiếc mobihome (nhà di động) và đi vòng quanh nước Mỹ. Họ có thể làm bất kỳ điều gì mình muốn", ông nói.\nHà Thu(theo CNBC)', 'Khi Jyoti Bansal thực hiện "quyết định khó khăn nhất sự nghiệp" là bán startup phần mềm AppDynamics, anh đã nghĩ đến các nhân viên.', @cat_id_kinh_doanh_doanh_nghiep, @user_id_hathu, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("'Trí tuệ nhân tạo không thể thay thế được kiểm toán viên'", 'tri-tue-nhan-tao-khong-the-thay-the-duoc-kiem-toan-vien', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/Bui-Quoc-Dung-Pho-tong-KTNN-17-7328-5001-1760344721.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=XVSjUDZ-PPunv695bW2qQQ', 'Tại hội thảo quốc tế về nâng cao năng lực kiểm toán ngày 13/10, Phó tổng Kiểm toán Nhà nước Bùi Quốc Dũng nhận định sự phát triển của AI cho phép cơ quan kiểm toán quét, xử lý toàn bộ tập dữ liệu, giảm yếu tố chủ quan trong đánh giá.\nTheo ông, các thuật toán học máy, học sâu giúp chuyển đổi khối lượng lớn văn bản, hồ sơ thành thông tin có thể truy cập, đối chiếu. Việc này giúp kiểm toán viên phát hiện sai phạm và có thể dự báo xu hướng, sai sót... để can thiệp sớm.\nSong Phó tổng Kiểm toán Nhà nước cho rằng AI không thể thay thế con người bởi kinh nghiệm, trực giác và đạo đức nghề nghiệp vẫn là những nhân tố quyết định chất lượng kiểm toán viên. Ông dẫn chứng một báo cáo quyết toán thuế của doanh nghiệp được lập với sự hỗ trợ của các phần mềm chuyên ngành có thể rất "chuẩn mực, đẹp mắt" nên AI khó phát hiện sai sót. Tuy nhiên, kiểm toán viên có thể phát hiện ra những dấu hiệu đáng ngờ nhờ kinh nghiệm và sự nhạy bén.\n"AI là công cụ hỗ trợ, giúp tăng hiệu quả, chứ không thể thay thế hoàn toàn kiểm toán viên", ông Dũng nói. Theo ông, điều này đòi hỏi kiểm toán viên phải có trình độ, năng lực sử dụng công nghệ để xử lý những công việc mà trí tuệ nhân tạo không thể đảm nhiệm.\nThực tế, nhiều quốc gia ứng dụng trí tuệ nhân tạo trong lĩnh vực kiểm toán. Chẳng hạn tại Mỹ, Văn phòng Kiểm toán Chính phủ (GAO) đưa AI vào giám sát tài chính liên bang, y tế và rủi ro ngân hàng.\nỞ Anh, Văn phòng Kiểm toán Quốc gia Vương quốc Anh (NAO) áp dụng AI trong phúc lợi xã hội, y tế, hợp đồng công và xây dựng "playbook" sử dụng trí tuệ nhân tạo an toàn. Còn tại Pakistan, AI từng được sử dụng để phát hiện 128.000 trường hợp "người hưu trí ma" trong chi trả lương hưu.\nÔng Bùi Quốc Dũng cho biết Kiểm toán Nhà nước đang xây dựng nền tảng dữ liệu lớn, kết nối với các bộ ngành, và triển khai các dự án AI phục vụ nghiệp vụ kiểm toán công. Họ đã tạo lập kho dữ liệu hơn 100 triệu bản ghi, kết nối với Bộ Tài chính, Bảo hiểm Xã hội Việt Nam và Ngân hàng Nhà nước.\nViệc ứng dụng công nghệ mới trong bối cảnh khối lượng, độ phức tạp của dữ liệu công vượt khả năng bao quát của phương pháp kiểm toán truyền thống dựa trên chọn mẫu. Chẳng hạn, Bảo hiểm Xã hội Việt Nam mỗi tháng xử lý 17 triệu lượt tham gia bắt buộc, mỗi năm phát hành 96 triệu thẻ bảo hiểm y tế và xử lý hơn 200 triệu lượt khám chữa bệnh. Đến cuối năm ngoái, Cục Thuế có hơn 950.000 doanh nghiệp kê khai điện tử với gần 16 triệu hồ sơ và 150 triệu tờ khai.\n"Đó là những bức tranh dữ liệu khổng lồ, liên tục cập nhật theo thời gian thực. Nếu tiếp tục xử lý bằng cách tiếp cận thủ công truyền thống sẽ có nguy cơ bỏ sót rủi ro hệ thống, giảm độ tin cậy của kết luận kiểm toán", ông nói.\nDù vậy, ông Phạm Huy Thông, Phó cục trưởng Công nghệ thông tin (Kiểm toán Nhà nước) nêu ba thách thức lớn khi triển khai trí tuệ nhân tạo (AI) trong kiểm toán công. Đó là, hạ tầng công nghệ chưa đồng bộ khiến việc ứng dụng còn hạn chế. Việc chia sẻ dữ liệu giữa các bộ ngành còn khó dù có quy chế phối hợp, và chất lượng nhân lực công nghệ thông tin, kiểm toán viên còn thiếu, chưa đồng đều.\nỞ khía cạnh này, ông Phan Ngọc Anh, Phó tổng giám đốc Dịch vụ Kiểm toán Deloitte Việt Nam, cảnh báo các thách thức về bảo mật dữ liệu và thiên lệch thuật toán.\nTrong khi đó, đại diện khối ngân hàng, bà Nguyễn Ngọc Lan Anh, Giám đốc Công nghệ và Nghiệp vụ Ngân hàng Standard Chartered Việt Nam, cho rằng AI không phải giải pháp cho mọi vấn đề. Theo bà, quan trọng nhất là đưa kiểm soát rủi ro vào ngay từ đầu, giống như trang bị "phanh an toàn cho cỗ xe tốc độ cao".', 'Phó tổng Kiểm toán Nhà nước Bùi Quốc Dũng cho rằng trí tuệ nhân tạo (AI) sẽ tái định hình nghề kiểm toán, nhưng không thể thay thế được con người.', @cat_id_kinh_doanh_chung_khoan, @user_id_phuongdung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("TPBank mang loạt trải nghiệm số tới concert 'Em xinh Say Hi'", 'tpbank-mang-loat-trai-nghiem-so-toi-concert-em-xinh-say-hi', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/tpbank-579-jpeg-1760354066-176-3041-4702-1760354310.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=6WXpcAnv0i3ROJDc3oC1_w', 'Đêm concert thứ hai của "Em xinh Say Hi" diễn ra tối 11/10, tại sân vận động quốc gia Mỹ Đình đã thu hút hàng chục nghìn fan hâm mộ. Đồng hành cùng sự kiện với vai trò Nhà tài trợ Kim cương, TPBank xuất hiện nổi bật với thông điệp "App TPBank - Tiện ích đỉnh - Sống đỉnh", mang đến chuỗi hoạt động sáng tạo và gần gũi với cộng đồng người hâm mộ.\nTại sự kiện, TPBank ghi điểm với loạt đặc quyền dành riêng cho khách hàng như: bố trí xe điện đưa đón trong khuôn viên, giúp khán giả dễ dàng di chuyển mà vẫn thoải mái diện trang phục dự concert; giao lưu cùng "Em xinh" LyHan ngay tại gian hàng. Đặc biệt, ngân hàng còn góp phần làm "nóng" không khí bằng việc đưa tiết mục "Vỗ tay", màn trình diễn được fan mong chờ lên sân khấu.\nNgoài ra, TPBank tặng 50.000 đồng cho khách hàng mới mở tài khoản và nhập mã giới thiệu EXSHHN, kèm lời nhắn "ăn uống đầy đủ" tới các fan hâm mộ.\nBên cạnh đêm nhạc, gian hàng trải nghiệm của TPBank cũng trở thành "tọa độ check-in" hot hit thu hút fan hâm mộ. Tại đây, người tham dự có thể trực tiếp khám phá các tiện ích nổi bật của ứng dụng ngân hàng số, tham gia minigame và nhận hàng ngàn phần quà hấp dẫn.\nSau sự kiện, hình ảnh TPBank tiếp tục "phủ sóng" trên các trang mạng xã hội. Nhiều video check-in tại gian hàng lan truyền trên TikTok, Instagram hay Facebook, thu hút hàng trăm nghìn lượt xem và chia sẻ.\nNguyễn Hồng Anh (25 tuổi, Hà Nội), một khán giả có mặt tại concert, cho biết dù đã tham gia nhiều sự kiện âm nhạc trong năm qua nhưng chương trình lần này vẫn mang đến cho cô nhiều cảm xúc đặc biệt với sân khấu được đầu tư hoành tráng, âm nhạc bùng nổ và không khí sôi động lan tỏa khắp khán đài.\n"Sự đồng hành của TPBank giúp trải nghiệm trở nên trọn vẹn hơn, vì khán giả không chỉ được sống trong âm nhạc mà còn có thêm nhiều hoạt động để kết nối, khám phá và mang về những kỷ niệm đáng nhớ," cô chia sẻ.\nTheo báo cáo Social Listening của Monitaz, trong hai tháng 7 và 8, TPBank liên tục dẫn đầu toàn ngành ngân hàng về tỷ lệ cảm xúc tích cực, phần lớn đến từ các nội dung khen ngợi chiến dịch truyền thông sáng tạo và thấu hiểu giới trẻ.\nKhông chỉ dừng ở độ phủ truyền thông, chiến dịch còn mang lại kết quả kinh doanh tích cực. Hàng trăm nghìn người dùng đã tải ứng dụng TPBank, hàng chục nghìn khán giả cài đặt giao diện "Em Xinh" và trải nghiệm thiệp ChatPay với hình ảnh thần tượng. Số lượng thẻ tín dụng mở mới trên ứng dụng tăng gấp 3 lần, còn lượng giao dịch cũng ghi nhận mức tăng mạnh sau sự kiện.\nĐại diện TPBank cho biết, việc đồng hành cùng "Em Xinh Say Hi" là cách ngân hàng kết nối với thế hệ trẻ thông qua những "ngôn ngữ" người trẻ yêu thích như âm nhạc và thời trang.\n"Thông qua chương trình, chúng tôi muốn lan tỏa năng lượng tích cực và tinh thần sống hết mình của người trẻ. Đó cũng là giá trị mà TPBank hướng tới, trở thành một ngân hàng đáp ứng nhu cầu tài chính và đồng hành cùng khách hàng trong những trải nghiệm sống ý nghĩa của kỷ nguyên số", đại diện TPBank chia sẻ.\nChiến dịch kết hợp âm nhạc, lifestyle và công nghệ là một phần trong chiến lược truyền thông dài hạn của TPBank, giúp ngân hàng củng cố hình ảnh trẻ trung, dễ gần, thấu hiểu và tạo dấu ấn mạnh mẽ trong tâm trí người dùng.', 'Gian hàng của TPBank, nhà tài trợ Kim cương của concert "Em xinh Say Hi" đêm 2 tại SVĐ Mỹ Đình thu hút fan hâm mộ với nhiều hoạt động, trải nhiệm số, gắn kết giới trẻ.', @cat_id_kinh_doanh_bat_dong_san, @user_id_minhngoc, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('MB cung cấp bộ giải pháp giúp doanh nghiệp phát triển bền vững', 'mb-cung-cap-bo-giai-phap-giup-doanh-nghiep-phat-trien-ben-vung', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/1-1760354741-7812-1760355141.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=C6beTm7qWps3KYLwQqov_w', 'Ông Nguyễn Hưng, Giám đốc một công ty cơ khí tại Bình Dương chia sẻ, sau nhiều tháng đàm phán, công ty đã ký được một hợp đồng xuất khẩu linh kiện 100 tỷ đồng sang châu Âu, nhưng niềm vui chưa kịp trọn thì nỗi lo dòng tiền ập đến.\n"Đối tác yêu cầu giao hàng trước, thanh toán sau, trong khi vốn lưu động gần như đã dồn hết vào nguyên liệu và nhân công. Chỉ cần chậm một khâu, cả dây chuyền có thể phải dừng lại", ông Hưng chia sẻ.\nSong, doanh nghiệp đã kịp thời tháo gỡ khó khăn, xoay vòng vốn khi sử dụng sản phẩm mua hẳn miễn truy đòi bộ chứng từ L/C nội địa của MB. Đây là một hình thức tài trợ thương mại linh hoạt, cho phép doanh nghiệp nhận tiền ngay sau khi giao hàng, trong khi ngân hàng chịu trách nhiệm thu hồi từ bên mua. Nhờ đó, dòng tiền được xoay vòng nhanh, kế hoạch sản xuất được duy trì liên tục và doanh nghiệp không phải phụ thuộc hoàn toàn vào nguồn vay ngắn hạn.\nTheo PwC, 65-75% cấu trúc vốn của doanh nghiệp Việt vẫn phụ thuộc vào ngắn hạn, khiến họ dễ bị "hụt hơi" khi chi phí vay tăng hoặc chu kỳ thanh toán kéo dài. Trong khi đó, với các tập đoàn, doanh nghiệp lớn và nhà đầu tư FDI, bài toán vốn thường phức tạp hơn khi quy mô đầu tư lớn, chu kỳ dài, tiến độ giải ngân chặt chẽ và yêu cầu tuân thủ tiêu chuẩn môi trường ngày càng cao. Nhiều doanh nghiệp gặp áp lực ngay từ khâu giải ngân, quản trị thanh khoản và kiểm soát dòng tiền, khiến tiến độ dự án hoặc kế hoạch mở rộng sản xuất bị kéo dài.\nGiải pháp tài trợ thương mại nói trên là một phần trong gói sản phẩm "Tài trợ vốn lưu động và tín dụng trung - dài hạn" của MB, được thiết kế để giúp doanh nghiệp duy trì thanh khoản, mở rộng nhà xưởng, đầu tư máy móc mà không phải phụ thuộc hoàn toàn vào vốn ngắn hạn.\n"Vốn không chỉ là dòng tiền, mà là phép thử năng lực quản trị. MB mong muốn trở thành đối tác tài chính chiến lược, giúp doanh nghiệp biến thách thức thành cơ hội tăng trưởng", đại diện MB cho biết.\nKhông dừng lại ở đó, trước sự chuyển dịch của dòng vốn FDI vào Việt Nam, MB tiếp tục mở rộng vai trò đồng hành cùng khối doanh nghiệp khu công nghiệp và nhà đầu tư nước ngoài, nhóm đóng góp ngày càng lớn cho tăng trưởng và định hình chuỗi cung ứng toàn cầu.\nTheo Cục Đầu tư nước ngoài, 9 tháng đầu năm 2025, tổng vốn FDI đăng ký đạt 28,5 tỷ USD, tăng hơn 15% so với cùng kỳ năm trước. Làn sóng mở rộng nhà máy, thuê đất và đầu tư dây chuyền sản xuất xanh đang tạo nên nhu cầu cấp thiết về nguồn vốn linh hoạt, minh bạch và thân thiện với môi trường.\nTrong bối cảnh đó, MB đã thiết kế gói tài trợ thuê đất trong khu công nghiệp, hỗ trợ tối đa 80% giá trị hợp đồng, ân hạn gốc 24 tháng, lãi suất từ 6,5% một năm. Doanh nghiệp có thể giải ngân ngay từ thời điểm ký hợp đồng, rút ngắn thời gian triển khai hạ tầng, giữ nhịp dòng tiền. Cùng với đó là tín dụng trung, dài hạn phục vụ đầu tư máy móc, mở rộng nhà xưởng, số hóa vận hành; và tín dụng xanh ưu tiên các dự án tiết kiệm năng lượng, xử lý nước thải, năng lượng tái tạo.\nBên cạnh các giải pháp dành cho nhóm doanh nghiệp lớn, MB cũng tập trung hỗ trợ khối doanh nghiệp nhỏ và vừa (SME), nhóm chiếm tỷ trọng lớn trong nền kinh tế nhưng thường gặp khó về quản trị tài chính và dòng vốn. Nhiều doanh nghiệp vẫn vận hành thủ công, chi tiêu phân tán, thiếu công cụ kiểm soát ngân sách và đối soát tự động, dẫn đến việc quản lý chi phí, chứng từ và thuế mất nhiều thời gian.\nHướng tới nhóm khách hàng này, MB phát triển thẻ doanh nghiệp đa năng MB Hi BIZ, tích hợp đồng thời hai tính năng ghi nợ và tín dụng trên một công cụ duy nhất. Doanh nghiệp có thể sử dụng thẻ để thanh toán các khoản chi như công tác phí, tiếp khách, mua sắm văn phòng hoặc quảng cáo trực tuyến, đồng thời được hoàn tiền 2,5% khi chi tiêu trên các nền tảng như Google, Meta. Mỗi giao dịch thực hiện bằng thẻ đều tự động sinh chứng từ hợp lệ để khấu trừ VAT và đồng bộ với hệ thống kế toán nội bộ.\nChị Thu Hà, Giám đốc tài chính của một doanh nghiệp quảng cáo tại TP HCM, cho biết công ty có hàng chục khoản chi nhỏ mỗi ngày, từ chạy quảng cáo, thuê freelancer đến hợp tác KOC, nếu thanh toán thủ công thì không thể kiểm soát nổi. Nhưng từ khi dùng thẻ MB Hi BIZ, mọi khoản chi được tổng hợp và đối soát tự động, giúp tiết kiệm đáng kể thời gian và chi phí kế toán cho công ty.\n"Với doanh nghiệp mới thành lập cho đến khi có quy mô, mỗi quyết định đầu tư hay chi tiêu đều ảnh hưởng trực tiếp tới khả năng tồn tại. MB muốn đồng hành từ những bước đầu, giúp họ chuẩn hóa tài chính, quản trị rủi ro và tạo nền tảng bền vững để phát triển", ông Nguyễn Xuân Cường, đại diện phụ trách khối khách hàng doanh nghệp Micro SME của MB chia sẻ.\nNằm trong nhóm "Big 5" ngân hàng lớn tại Việt Nam, MB đóng vai trò cung cấp nguồn vốn cho nền kinh tế, luôn chủ động triển khai những giải pháp tài chính giúp doanh nghiệp thích ứng với quy định và tiêu chuẩn mới. Thông qua các sản phẩm tín dụng xanh, giải pháp vốn linh hoạt và nền tảng tài chính số toàn diện, ngân hàng mong muốn kiến tạo một hệ sinh thái tài chính, đồng hành cùng mọi doanh nghiệp: từ SME, khởi nghiệp đến tập đoàn trong nước và nhà đầu tư FDI phát triển bền vững, xây dựng một nền kinh tế Việt Nam vững vàng trong kỷ nguyên mới.', 'Ngân hàng TMCP Quân đội (MB) phát triển bộ giải pháp tài chính với nhiều quy mô dành cho doanh nghiệp, nhằm tối ưu vốn, gia tăng thanh khoản, chuẩn hóa quản trị, thúc đẩy phát triển bền vững.', @cat_id_kinh_doanh, @user_id_minhngoc, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('MB đồng hành cùng doanh nghiệp đầu tư trong khu công nghiệp', 'mb-dong-hanh-cung-doanh-nghiep-dau-tu-trong-khu-cong-nghiep', 'https://i1-kinhdoanh.vnecdn.net/2025/10/13/z7098207827318-4a2f9b9fda88472-4015-2845-1760342682.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=43DfZpMv_ciBZDtUkx5o9Q', 'Trong tháng 9, Ngân hàng Quân đội (MB) đã phối hợp cùng Trung tâm Xúc tiến Thương mại TP HCM, Liên chi hội Bất động sản Công nghiệp Việt Nam và nền tảng kết nối thúc đẩy đổi mới sáng tạo BambuUP triển khai chương trình đào tạo chuyên sâu và giới hạn "Xây dựng cộng đồng doanh nghiệp, doanh nhân phát triển bền vững TP HCM 2025".\nChương trình đã cung cấp cho gần 150 lãnh đạo doanh nghiệp sản xuất và quản lý tại các khu, cụm công nghiệp trọng điểm phía Nam những kiến thức thực chiến về bốn trụ cột chuyển đổi xanh: ESG, tín chỉ carbon, công nghệ - dữ liệu, nhân sự xanh, cùng hai nội dung trọng tâm: tài chính xanh và chuỗi cung ứng bền vững.\nSong song hoạt động đào tạo, ngân hàng còn giới thiệu sản phẩm "Tài trợ khách hàng thuê đất trong khu công nghiệp", hỗ trợ vốn cho doanh nghiệp đầu tư sản xuất, xây dựng hoặc cho thuê nhà xưởng, với hạn mức tới 80% giá trị đất thuê. Doanh nghiệp được giải ngân ngay từ khi ký hợp đồng thuê đất, hưởng ân hạn gốc đến 24 tháng và lãi suất từ 6,5% một năm. Việc nhận bảo đảm bằng quyền tài sản phát sinh từ hợp đồng thuê đất cùng tài sản gắn liền trên đất giúp doanh nghiệp linh hoạt sử dụng vốn trong suốt vòng đời dự án.\nBà Lê Kim Chi, đại diện Khối Khách hàng Doanh nghiệp MB cho biết, sản phẩm "Tài trợ khách hàng thuê đất trong khu công nghiệp" là "đòn bẩy vốn" giúp doanh nghiệp chủ động cân đối dòng tiền, duy trì sản xuất và mở rộng đầu tư ổn định, liền mạch, đặc biệt đối với thị trường phía Nam khi TP HCM đang đẩy mạnh thu hút vốn FDI vào khu công nghiệp.\nTheo Cục Đầu tư nước ngoài (Bộ Tài chính), 9 tháng đầu năm, Việt Nam ghi nhận tổng vốn FDI đăng ký đạt 28,54 tỷ USD, tăng 15,2% so với cùng kỳ năm trước. Trong đó, TP HCM thu hút 4,4 tỷ USD, vượt 117% kế hoạch cả năm. Riêng các khu chế xuất và khu công nghiệp trên địa bàn hiện quản lý hơn 5.723 dự án còn hiệu lực, với tổng vốn đăng ký 76,8 tỷ USD, trong đó 3.516 dự án FDI chiếm hơn 57 tỷ USD, theo Ban Quản lý các Khu chế xuất và Công nghiệp TP HCM.\nSự gia tăng của dòng vốn FDI cho thấy nhu cầu mở rộng sản xuất tại các khu công nghiệp đang diễn ra mạnh mẽ, đặc biệt tại TP HCM. Cùng với việc mở rộng nhà xưởng, nhiều doanh nghiệp có nhu cầu tiếp cận các nguồn vốn trung - dài hạn để triển khai nhanh dự án, tối ưu chi phí sử dụng đất và hạ tầng.\nĐại diện ngân hàng cho biết, việc triển khai sản phẩm tài trợ thuê đất giúp doanh nghiệp có thêm lựa chọn vốn linh hoạt, giải ngân nhanh trong giai đoạn chuẩn bị mặt bằng, rút ngắn thời gian đưa dự án vào vận hành. Đồng thời, MB kỳ vọng việc kết hợp các gói tài trợ vốn, giải pháp tài chính xanh và nền tảng ngân hàng số sẽ góp phần giúp doanh nghiệp tại các khu công nghiệp nắm bắt cơ hội, mở rộng năng lực sản xuất, phát triển bền vững.', 'Ngân hàng Quân đội (MB) tổ chức đào tạo tài chính xanh, giới thiệu gói tài trợ thuê đất đến 80% giá trị tài sản cho doanh nghiệp tại khu công nghiệp ở TP HCM, hướng tới phát triển bền vững.', @cat_id_kinh_doanh_quoc_te, @user_id_minhngoc, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
("Bà Mai Kiều Liên tiếp tục vào 'Top 100 phụ nữ quyền lực châu Á'", 'ba-mai-kieu-lien-tiep-tuc-vao-top-100-phu-nu-quyen-luc-chau', 'https://i1-kinhdoanh.vnecdn.net/2025/10/12/z7109745074426-f3215a300811368-1368-5375-1760288257.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=FG3mUb6D8v9dtwS42Z_MSg', 'Danh sách "Top 100 Phụ nữ quyền lực nhất châu Á" năm 2025 (Fortune Most Powerful Women Asia 2025) do Tạp chíFortunecông bố trong số tháng 10, vinh danh những nữ lãnh đạo có ảnh hưởng lớn trong việc định hình doanh nghiệp, thúc đẩy đổi mới, truyền cảm hứng cho thế hệ kế tiếp.\nFortuneđánh giá các dấu ấn của bà Mai Kiều Liên tại Vinamilk thể hiện qua tư tưởng đổi mới sáng tạo, khả năng hoạch định chiến lược đúng đắn, xây dựng văn hóa doanh nghiệp, gắn kết người lao động. Với đặc thù công ty dinh dưỡng, bà xây dựng triết lý kinh doanh "vì con người", "lấy chất lượng sản phẩm làm đầu". Qua đó, nữ CEO đưa công ty phát triển, tạo tác động kinh tế, đóng góp cho phát triển cộng đồng và tác động xã hội.\nNăm nay, các nữ lãnh đạo, doanh nhân tiêu biểu được vinh danh đến từ 14 thị trường ở nhiều lĩnh vực, gồm: Trung Quốc, Ấn Độ, Nhật Bản, Hàn Quốc, Australia, Singapore, Malaysia, Thái Lan, Indonesia, Philippines, Hong Kong (Trung Quốc), Đài Loan, Macao (Trung Quốc) và Việt Nam.\nDanh sách xây dựng dựa trên 5 tiêu chí: quy mô và sự phát triển của doanh nghiệp; tầm nhìn chiến lược; thúc đẩy sự đổi mới, tạo ra đột phá; tầm ảnh hưởng đối với lĩnh vực kinh doanh và nền kinh tế; danh tiếng và tác động xã hội.\n"Fortune Most Powerful Women Asia 2025 tôn vinh sự đa dạng và sức ảnh hưởng của phụ nữ trong khu vực. Những gương mặt được vinh danh có chiều sâu tri thức, sự đa dạng và sức sáng tạo", bà Ang Khoon Fong, Tổng giám đốcFortune Asia, chia sẻ.\nVới gần 50 năm làm việc tại Vinamilk, trong đó có 33 năm làm Tổng giám đốc, bà Mai Kiều Liên luôn kiên định với quan điểm "thực phẩm vào cơ thể thì không có cơ hội sửa sai". Bằng tinh thần này, bà đưa thương hiệu sữa Việt tiến lên vị trí 36 trong danh sách 50 công ty sữa có doanh thu cao nhất thế giới vào năm 2021 (Plimsoll, Anh); đồng thời là Thương hiệu sữa tiềm năng nhất toàn cầu (Brand Finance, Anh) vào tháng 8 năm nay.\nNgoài thị trường nội địa, hãng xuất khẩu đến 65 quốc gia và vùng lãnh thổ. Lũy kế 6 tháng đầu năm, doanh thu hợp nhất của Vinamilk đạt 29.710 tỷ đồng, hoàn thành 46,1% kế hoạch doanh thu năm. Trong đó, nguồn thu thuần từ thị trường nội địa đạt 23.624 tỷ đồng và từ nước ngoài đạt 6.035 tỷ đồng.\nNhìn lại hành trình gần nửa thế kỷ, nữ CEO cho biết năm 1976 ngành sữa Việt Nam gần như chưa định hình, trong khi tỷ lệ trẻ em suy dinh dưỡng ở mức cao. Trong bối cảnh này, ban lãnh đạo công ty cùng bà đặt mục tiêu: tạo ra những sản phẩm giúp mọi trẻ em đều có cơ hội tiếp cận nguồn dinh dưỡng chất lượng.\nĐể có những bước đi dài hạn, năm 1990, Vinamilk tiên phong thực hiện "cuộc cách mạng trắng", xây dựng và phát triển vùng nguyên liệu chăn nuôi bò sữa trong nước. Các vùng nguyên liệu khi đó cũng là tiền đề để xây dựng ngành chăn nuôi bò sữa quy mô, hiện đại ngày nay, xóa bỏ định kiến "Việt Nam không thể phát triển đàn bò sữa".\nSong song kinh doanh, Vinamilk triển khai nhiều chương trình dài hơi, góp phần nâng cao thể trạng cho các thế hệ người Việt. "Quỹ sữa Vươn cao Việt Nam" triển khai liên tục suốt 18 năm qua, với gần 43 triệu hộp sữa được trao cho hơn 550.000 trẻ em khó khăn khắp cả nước. Từ năm 2016, chương trình "Sữa học đường" đến với các trường học, góp phần cải thiện tình trạng dinh dưỡng cho trẻ mẫu giáo và tiểu học.\nNăm 2023, hãng công bố cam kết và lộ trình tiến đến mục tiêu Net Zero 2050. Vinamilk là doanh nghiệp sữa đầu tiên tại Việt Nam đã có ba đơn vị (gồm hai nhà máy và một trang trại) đạt chứng nhận quốc tế về trung hòa carbon (PAS 2060:2014). Vinamilk cũng là doanh nghiệp sữa đầu tiên của Việt Nam tham gia vào Sáng kiến toàn cầu của ngành sữa về Net Zero - Pathways to Dairy Net Zero. Trước đó, đơn vị nhiều năm triển khai "Quỹ 1 triệu cây xanh cho Việt Nam".\nTrước danh hiệu "Top 100 Phụ nữ quyền lực nhất châu Á" của Fortune, bà Mai Kiều Liên nhận nhiều danh hiệu: Huân chương Độc lập hạng Ba (2022), Anh hùng lao động thời kỳ đổi mới (2005), Huân chương Lao động hạng Nhất (2006), Nhì (2001). Năm 2025, bà nhận danh hiệu Top 60 Cá nhân tiêu biểu trong sự nghiệp 50 năm xây dựng, bảo vệ và phát triển TP HCM.\nNikkeivàForbes Asianhiều lần đưa bà Mai Kiều Liên vào danh sách những nữ doanh nhân ảnh hưởng nhất châu Á, có nhiều đóng góp xuất sắc cho nền kinh tế Việt Nam và khu vực. Bà cũng là nữ doanh nhân duy nhất đượcForbes Việt Namvinh danh "Thành tựu trọn đời".', 'Bà Mai Kiều Liên, CEO Vinamilk là nữ doanh nhân Việt Nam duy nhất hai năm liên tiếp vào "Top 100 Phụ nữ quyền lực nhất châu Á" của Fortune.', @cat_id_kinh_doanh_doanh_nghiep, @user_id_hoangan, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Niềm tin tiêu dùng của người Mỹ thấp nhất 5 tháng', 'niem-tin-tieu-dung-cua-nguoi-my-thap-nhat-5-thang', 'https://i1-kinhdoanh.vnecdn.net/2025/10/12/us-consumer-1760262289-1760262-3980-3845-1760262462.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=_ndMahJV1pyiGQj4LZm1Ng', 'Khảo sát công bố ngày 10/10 của Đại học Michigan cho thấy chỉ số niềm tin tiêu dùng của Mỹ trong tháng này chỉ là 55 điểm, tương đương tháng 9. Đây cũng là số liệu thấp nhất kể từ tháng 5 và chỉ nhỉnh hơn thời điểm đầu năm nay, khi Tổng thống Mỹ Donald Trump công bố hàng loạt chính sách áp thuế nhập khẩu.\n"Các vấn đề tài chính cá nhân, như giá cao và triển vọng việc làm yếu vẫn là mối lo hàng đầu của người tiêu dùng. Họ không kỳ vọng những điều này được cải thiện đáng kể", Joanne Hsu - Giám đốc phụ trách khảo sát cho biết trong thông cáo.\nTrong khi đó, việc chính phủ Mỹ đóng cửa 12 ngày qua không khiến người dân lo ngại về nền kinh tế, dù hơn một triệu nhân viên liên bang đã phải tạm nghỉ làm. "Các cuộc phỏng vấn cho thấy hầu như không có bằng chứng nào cho thấy việc chính phủ đóng cửa tác động đến quan điểm của người dân về nền kinh tế", Hsu cho biết.\nSau vài tháng hồi phục, niềm tin tiêu dùng của người Mỹ lại quay đầu giảm, do giá cả tăng cao và thị trường lao động yếu đi. Khảo sát tuần này cho thấy "sự suy giảm trong kỳ vọng về tài chính cá nhân cũng như khả năng mua sắm hàng hóa lâu bền".\nDù vậy, vài năm qua, niềm tin tiêu dùng thường không phản ánh chính xác về chi tiêu. Hè năm 2022, khi lạm phát Mỹ lên cao nhất 40 năm, niềm tin tiêu dùng cũng xuống đáy, nhưng người Mỹ vẫn chi tiêu mạnh tay trong năm đó.\nXu hướng này vẫn tiếp tục. Niềm tin năm nay yếu, nhưng người dân chưa giảm chi. Số liệu từ Bộ Thương mại Mỹ cho thấy doanh số bán lẻ tháng 8 đã tăng 3 tháng liên tiếp.\nCác nhà kinh tế học cho rằng chi tiêu phụ thuộc chủ yếu vào sức khỏe của thị trường lao động. Thị trường này gần đây đi xuống, nhưng vẫn khá vững chắc, với tỷ lệ thất nghiệp duy trì ở mức thấp. Miễn là hoạt động sa thải không tăng mạnh, người Mỹ nhiều khả năng vẫn sẽ chi tiêu.\nTuy vậy, việc chính phủ đóng cửa đã khiến hàng loạt số liệu kinh tế không được công bố, trong đó có báo cáo việc làm tháng 9. Các báo cáo của doanh nghiệp tư nhân và Fed chi nhánh khu vực cho thấy thị trường việc làm tháng trước tiếp tục chậm lại.\nHà Thu(theo CNN, Reuters)', 'Lo ngại về giá cả và việc làm khiến niềm tin của người tiêu dùng Mỹ tiếp tục đi xuống trong tháng 10.', @cat_id_kinh_doanh_chung_khoan, @user_id_hathu, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured) VALUES 
('Giảm thuế 30%, gia hạn nộp 2 năm cho doanh nghiệp bị thiệt hại ngập lụt', 'giam-thue-30-gia-han-nop-2-nam-cho-doanh-nghiep-bi-thiet-hai-ngap-lut', 'https://i1-kinhdoanh.vnecdn.net/2025/10/12/Ngap-lut-Thai-Nguyen-Giang-Huy-5741-1165-1760256752.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=F5YMg2kIG9MgCc65Hhi-Xg', 'Cục Thuế (Bộ Tài chính) vừa hướng dẫn các địa phương về giảm, gia hạn nộp thuế, tiền thuê đất với các doanh nghiệp, hộ và cá nhân kinh doanh bị ảnh hưởng bởi bão số 10 và 11 (bão Bualoi và Matmo).\nTheo đó, doanh nghiệp được miễn toàn bộ thuế đất phi nông nghiệp nếu đất và nhà trên đất bị thiệt hại hơn 50% giá tính thuế. Trường hợp bị thiệt hại khoảng 20-50%, họ được giảm một nửa thuế này.\nDoanh nghiệp cũng được miễn toàn bộ tiền thuê đất hàng năm để sản xuất nông nghiệp, nuôi trồng thủy sản khi mức tổn thất 40% trở lên. Mức giảm thuế tương ứng nếu thiệt hại dưới 40%. Trường hợp phải ngừng hoạt động để khắc phục hậu quả do ngập lụt gây ra, doanh nghiệp được giảm một nửa tiền thuê đất trong thời gian này. Ngoài ra, họ cũng được gia hạn nộp thuế tối đa 2 năm.\nVề thuế tiêu thụ đặc biệt, doanh nghiệp, hộ và cá nhân kinh doanhbị thiệt hại trực tiếp được giảm tối đa 30% thuế phải nộp năm nay. Số này không được vượt quá giá trị bị tổn thất sau bồi thường (nếu có).\nDoanh nghiệp chịu ảnh hưởng cũng được xét miễn, giảm thuế tài nguyên. Hộ và cá nhân kinh doanh cũng được giảm tương ứng thuế này với mức tổn thất tài nguyên và được hoàn trả vào kỳ sau trong trường hợp đã nộp.\nHộ, cá nhân kinh doanh còn được gia hạn nộp thuế giá trị gia tăng (thuế VAT), thu nhập cá nhân đến hết ngày 31/12. Với trường hợp bị thiệt hại nặng do bão lụt, thời gian gia hạn có thể kéo dài tối đa 2 năm kể từ ngày hết hạn nộp thuế. Họ không bị phạt, tính tiền chậm nộp trong thời gian được gia hạn.\nCác doanh nghiệp, hộ, cá nhân kinh doanh gửi hồ sơ đề nghị giảm thuế đến cơ quan thuế quản lý trực tiếp. Sau đó, trong 30-40 ngày, cơ quan thuế sẽ có quyết định miễn, giảm hoặc thông báo lý do không đủ điều kiện.\nChưa đầy một tháng, Việt Nam hứng chịu ba cơn bão lớn gồm bão số 9 (Ragasa) đổ bộ ngày 25/9, bão số 10 (Bualoi) 29/9 và bão số 11 (Matmo) vào 6/10. Các cơn bão này có cường độ mạnh, diễn biến phức tạp, gây thiệt hại nghiêm trọng tại nhiều tỉnh, thành khu vực Bắc Bộ và Bắc Trung Bộ.\nTrước đó, Thứ trưởng Nông nghiệp và Phát triển nông thôn Nguyễn Hoàng Hiệp thông tin từ đầu năm đến nay, Việt Nam xảy ra 20 loại hình thiên tai. Đến 9/10, thiên tai trên cả nước đã làm 238 người chết và mất tích, 367 người bị thương, 258.000 ngôi nhà bị hư hỏng, tốc mái, và hơn 500.000 ha lúa, hoa màu bị ngập úng, với tổng thiệt hại ước tính hơn33.500 tỷ đồng.\nRiêng đợt thiên tai sau bão số 11 được đánh giá là một trong những đợt nghiêm trọng nhất, khi xảy ra tình trạng lũ chồng lũ, sạt lở đất trên diện rộng, gây thiệt hại nặng nề về người, tài sản và hạ tầng thiết yếu. Nhiều doanh nghiệp tại Thái Nguyên - một trong 4 địa phương phía bắc chịu ảnh hưởng nặng nề do ngập lụt lần này, bịthiệt hại hàng tỷ đồng.\nTính đến sáng 10/10, bão số 11 đã khiến 18 người chết và mất tích, hơn 225.245 nhà bị ngập.', 'Doanh nghiệp, hộ kinh doanh được giảm 30% thuế tiêu thụ đặc biệt, gia hạn nộp thuế tối đa 2 năm nếu bị thiệt hại do mưa bão, lũ lụt.', @cat_id_kinh_doanh_bat_dong_san, @user_id_phuongdung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0);
