INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured, is_realtime, live_start_time, live_end_time, review_note) VALUES
('Miền Trung mưa lớn từ trưa nay', 'mien-trung-mua-lon-tu-trua-nay', 'https://i1-vnexpress.vnecdn.net/2025/10/22/Anh-chup-Man-hinh-2025-10-22-l-7224-3538-1761088437.png?w=680&h=0&q=100&dpr=1&fit=crop&s=kFM54UnQVzsaHaXLwK1uog', 'Trung tâm Dự báo Khí tượng Thủy văn quốc gia cho biết lúc 6h hôm nay, tâm bão Fengshen cách Đà Nẵng khoảng 280 km về phía đông đông bắc, sức gió mạnh nhất 102 km/h, cấp 10, giật cấp 12 và đang theo hướng tây tây nam với tốc độ 10 km/h.\nTrong 24 giờ tới, do tương tác với không khí lạnh từ phương bắc tràn xuống, bão sẽ suy yếu thành áp thấp nhiệt đới trước khi vào bờ. Đến 4h ngày mai, áp thấp nhiệt đới trên vùng ven biển Huế - Quảng Ngãi, sức gió mạnh nhất cấp 7, giật cấp 9 và theo hướng tây tây nam với tốc độ 10-15 km/h.\nKhi vào đất liền Đà Nẵng, áp thấp nhiệt đới tiếp tục suy yếu thành vùng áp thấp trên khu vực Nam Lào.\nĐài khí tượng Nhật Bản cho biết bão đang đạt cực đại 90 km/h, những giờ tới giảm còn 83 km/h, vào bờ giảm dưới cấp bão. Đài Hong Kong cho rằng bão đang mạnh 110 km/h, khi ở vùng biển ngoài khơi Đà Nẵng còn 65 km/h.\nẢnh hưởng của bão, vùng biển phía tây của bắc Biển Đông (gồm đặc khu Hoàng Sa) gió mạnh cấp 7-10, giật cấp 12, sóng biển cao 3-7 m. Vùng biển Quảng Trị - Quảng Ngãi (gồm đặc khu Cồn Cỏ, Lý Sơn, Cù Lao Chàm) gió mạnh cấp 6-8, giật cấp 10, sóng biển cao 3-5 m, biển động mạnh. Toàn bộ tàu, thuyền hoạt động trong các vùng nguy hiểm này có khả năng chịu tác động.\nVen biển các tỉnh thành Quảng Trị - Đà Nẵng nước dâng do bão cao 0,4-0,8 m, kết hợp với triều cường có thể gây ngập vùng trũng, sạt lở bờ biển. Từ chiều nay, đất liền ven biển Quảng Trị - Đà Nẵng gió mạnh dần lên cấp 6-7, giật cấp 8-9.\nGhi nhận thực tế tại Đà Nẵng, sáng nay trời âm u, mưa rào vài nơi. Khu vực ven biển có gió giật nhẹ. Khu vực đô thị có gió rít từng cơn. Hầu hết các xã phường đã thông báo cho học sinh nghỉ học đề phòng mưa lớn. Thành phố yêu cầu hoàn thành sơ tán dân khỏi các vùng thấp trũng, vùng có nguy cơ sạt lở trước 17h ngày 22/10; các đơn vị chức năng phải bảo đảm đầy đủ lương thực, trang thiết bị tại điểm sơ tán, không để người dân thiếu đói, rét.\nTại TP Huế, sáng nay trung tâm thành phố mưa nhỏ rải rác, trời se lạnh, thỉnh thoảng xuất hiện những đợt gió giật khiến cây cối rung lắc. Tại miền núi, khu vực vườn quốc gia Bạch Mã mưa lớn. Nước từ thượng nguồn đổ về sông Hương làm ngập tuyến đường đi bộ ven sông, mấp mé Đập Đá ngăn cách sông Hương và sông Như Ý.\nTrung tâm Dự báo Khí tượng Thủy văn quốc gia cho biết hoàn lưu bão, không khí lạnh, nhiễu động gió đông kết hợp với hiệu ứng địa hình sẽ gây ra đợt mưa rất lớn ở Hà Tĩnh - Quảng Ngãi trong các ngày 22-27/10, trong đó mưa cường độ lớn tập trung từ trưa nay đến hết ngày mai.\nTổng lượng mưa phổ biến trong đợt này ở Hà Tĩnh, bắc Quảng Trị, Quảng Ngãi 200-400 mm, cục bộ trên 500 mm; nam Quảng Trị - Đà Nẵng mưa 500-700 mm, cục bộ trên 900 mm, nguy cơ cao mưa cường suất trên 200 mm trong ba giờ.\nMưa lớn miền Trung còn có khả năng kéo dài đến hết tháng 10, nguy cơ cao xảy ra lũ quét và sạt lở đất ở vùng núi, ngập lụt vùng trũng thấp và đô thị. Cơ quan khí tượng lưu ý các địa phương vận hành an toàn các hồ chứa thủy điện, thủy lợi trước, trong và sau bão, sẵn sàng phương án ứng phó với kịch bản lũ trên các sông vượt báo động ba, mức nguy hiểm nhất.\nCụ thể, đỉnh lũ sông Kiến Giang, Thạch Hãn (Quảng Trị); sông Bồ, Hương (Huế); sông Vu Gia Thu Bồn (Đà Nẵng); sông Trà Khúc, Vệ (Quảng Ngãi) và các sông nhỏ lên báo động ba và trên báo động ba. Sông Gianh (Quảng Trị), Sê San (Quảng Ngãi) lên báo động hai.\nNgày 20/10, Thủ tướng Phạm Minh Chính đã ban hành công điện yêu cầu các bộ, ngành và địa phương, đặc biệt Hà Tĩnh - Đăk Lăk, khẩn trương triển khai biện pháp phòng, chống với tinh thần chủ động cao nhất.\nThủ tướng nhấn mạnh không được chủ quan, phải lường trước các tình huống xấu để bảo đảm an toàn tính mạng, tài sản của người dân. Các địa phương ven biển được yêu cầu rà soát, quản lý chặt chẽ tàu thuyền, hướng dẫn tránh trú an toàn; đồng thời chủ động sơ tán dân ở khu vực có nguy cơ sạt lở, ngập lụt, bảo đảm an toàn hồ đập, đê điều, vận hành hiệu quả hệ thống thủy điện, thủy lợi.\nCác bộ Nông nghiệp và Môi trường, Xây dựng, Công Thương, Quốc phòng và Công an được giao phối hợp chặt chẽ, sẵn sàng lực lượng, phương tiện ứng cứu, cứu hộ khi có tình huống xấu xảy ra.\n"Đặt an toàn tính mạng người dân lên trên hết, không để bị động, bất ngờ trước bão lũ", Thủ tướng chỉ đạo.\nFengshen là cơn bão thứ 12 xuất hiện trên Biển Đông trong năm nay. Trước đó 11 cơn bão và 4 áp thấp nhiệt đới đã xuất hiện ở vùng biển này, trong đó các cơnWutip,Wipha,Kajiki,Nongfa,Ragasa,Bualoi,Matmoảnh hưởng trực tiếp hoặc gây mưa lũ lớn cho miền Bắc và Trung.', 'Bão Fengshen còn cách đất liền Trung Trung Bộ hơn 200 km, song hoàn lưu trước bão kết hợp với không khí lạnh, gió đông và yếu tố địa hình gây mưa lớn từ trưa nay.', @cat_id_thoi_su, @user_id_giachinhvothanhnguyenong, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0, 0, NULL, NULL, NULL);


INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured, is_realtime, live_start_time, live_end_time, review_note) VALUES
('Hôm nay Quốc hội xem xét sửa ba luật về giáo dục', 'hom-nay-quoc-hoi-xem-xet-sua-ba-luat-ve-giao-duc', 'https://i1-vnexpress.vnecdn.net/2025/10/22/202411201137445300-z6050966923-3825-4067-1761089821.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=jonVLAImdCqnTH0eNR6_ww', 'VớiLuật Giáo dục, dự thảo tập trung hoàn thiện hệ thống giáo dục quốc dân theo hướng mở, linh hoạt và liên thông, đáp ứng yêu cầu học tập suốt đời. Luật bổ sung mô hình "trung học nghề" để học sinh sau trung học cơ sở có thêm lựa chọn học văn hóa gắn với nghề, tạo hành lang pháp lý cho phân luồng thực chất.\nDự thảo cũng mở rộng phổ cập mầm non cho trẻ 3-5 tuổi, giáo dục bắt buộc đến hết trung học cơ sở, đồng thời thể chế hóa mạnh mẽ chuyển đổi số, đổi mới sáng tạo, như quy định về văn bằng điện tử, ứng dụng trí tuệ nhân tạo và cơ sở dữ liệu quốc gia về giáo dục.\nLuật sửa đổi hướng tới phân cấp, phân quyền, tăng tính tự chủ trong quản lý giáo dục. Nhiều thẩm quyền của Chính phủ được giao cho Bộ Giáo dục và Đào tạo, UBND tỉnh và các cơ sở giáo dục, giúp bộ máy phù hợp với mô hình chính quyền hai cấp. Bộ giữ vai trò định hướng, giám sát, còn địa phương và nhà trường chủ động tổ chức dạy học, biên soạn tài liệu, xác nhận kết quả và cấp bằng tốt nghiệp.\nĐối vớiLuật Giáo dục đại học, cơ quan soạn thảo nhấn mạnh việc sửa đổi nhằm tái định vị vai trò của đại học trong hệ sinh thái khoa học, công nghệ và đổi mới sáng tạo quốc gia. Đại học không chỉ là nơi đào tạo mà còn là trung tâm nghiên cứu, sáng tạo và chuyển giao tri thức, gắn kết chặt chẽ với doanh nghiệp, địa phương và cộng đồng. Dự thảo khẳng định cơ sở giáo dục đại học là chủ thể trung tâm của hệ sinh thái đổi mới sáng tạo, được khuyến khích huy động nguồn lực xã hội, liên kết đào tạo và nghiên cứu trong và ngoài nước.\nCơ chế quản trị đại học được thiết kế lại theo hướng tinh gọn, hiện đại. Nhiều mô hình tổ chức phức tạp như đại học hai cấp, hội đồng trường không còn phù hợp sẽ được điều chỉnh để thống nhất, rõ ràng về thẩm quyền. Quyền tự chủ của nhà trường đi đôi với trách nhiệm giải trình, đồng thời thể chế hóa vai trò lãnh đạo của Đảng trong cơ sở giáo dục phù hợp với đặc thù chính trị Việt Nam.\nVề tài chính, cơ quan soạn thảo chuyển mạnh sang cơ chế đặt hàng, giao nhiệm vụ theo kết quả đầu ra thay cho cấp phát hành chính, tạo động lực cạnh tranh lành mạnh, khuyến khích nâng cao chất lượng. Các trường công lập được huy động, sử dụng linh hoạt nguồn lực ngoài ngân sách, mở rộng hợp tác trong và ngoài nước, đi đôi với chính sách ưu đãi về đất đai, thuế và tín dụng cho giáo dục.\nChính phủ cho biết lần sửa đổiLuật Giáo dục nghề nghiệpnày sẽ tái cấu trúc toàn diện hệ thống giáo dục nghề nghiệp theo hướng mở, linh hoạt, liên thông, hiện đại và hội nhập quốc tế. Dự thảo bổ sung mô hình "trung học nghề" cho phép học sinh sau trung học cơ sở học song song văn hóa và nghề nghiệp, mở rộng cơ hội học tập, giúp phân luồng hiệu quả và đáp ứng nhu cầu xã hội.\nLuật sửa đổi cũng đổi mới mạnh mẽ cơ chế quản lý và đảm bảo chất lượng. Các cơ sở giáo dục nghề được tự chủ toàn diện về chuyên môn, tài chính và quản trị nội bộ, đồng thời chịu trách nhiệm giải trình trước Nhà nước và xã hội. Bộ Giáo dục và Đào tạo ban hành khung tiêu chuẩn, quy trình, công cụ kiểm định; cơ sở tự đánh giá và công khai chất lượng đào tạo. Hệ thống kiểm định được tổ chức độc lập, minh bạch, có thể hợp tác quốc tế để bảo đảm chất lượng thực chất và cạnh tranh công bằng.\nDự luật bổ sung chính sách khuyến khích hợp tác công - tư và xã hội hóa nhưng vẫn bảo đảm công bằng, không thương mại hóa giáo dục. Nhà nước có chính sách hỗ trợ người học yếu thế, học sinh dân tộc thiểu số, người khuyết tật và ngành nghề đặc thù quan trọng với quốc gia.\nBộ trưởng Nguyễn Kim Sơn nói việc sửa đồng thời ba luật là "một cuộc cải cách thể chế" để đáp ứng yêu cầu mới, tiếp tục đổi mới mạnh mẽ hơn. Ba luật hiện hành điều chỉnh các cấp học và loại hình khác nhau nhưng có nhiều điểm giao thoa như phân luồng sau THCS, THPT, liên thông giữa các bậc học, chuẩn đầu ra hay khung trình độ quốc gia. Nếu sửa riêng rẽ từng luật dễ dẫn đến mâu thuẫn, chồng chéo.\nViệc sửa đổi đồng bộ lần này nhằm cụ thể hóa chủ trương phân luồng học sinh từ sau THCS và THPT, xây dựng hệ thống giáo dục mở, linh hoạt, tạo điều kiện cho người học học tập suốt đời, liên thông giữa phổ thông, nghề nghiệp và đại học.\nNgoài ba luật về giáo dục, ngày 22/10, Quốc hội cũng nghe Chính phủ trình dự án Luật Hàng không dân dụng Việt Nam sửa đổi và Luật Viên chức sửa đổi. Sau đó đại biểu Quốc hội thảo luận tại tổ về các dự thảo luật này.', 'Sáng 22/10, Bộ trưởng Giáo dục và Đào tạo Nguyễn Kim Sơn trình Quốc hội ba dự thảo luật sửa đổi gồm Giáo dục, Giáo dục đại học và Giáo dục nghề nghiệp.', @cat_id_thoi_su_chinh_tri, @user_id_sonha, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0, 0, NULL, NULL, NULL);


INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured, is_realtime, live_start_time, live_end_time, review_note) VALUES
('Người đưa củ sắn dây thành thương hiệu tỷ đồng', 'nguoi-dua-cu-san-day-thanh-thuong-hieu-ty-dong', 'https://i1-vnexpress.vnecdn.net/2025/10/21/z7139458816941-e45c57e8a9c34ee-9062-5754-1761019245.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=7FZAtsOvvyX4z_AbN3Ptjg', 'Ông Bùi Văn Thành, 56 tuổi, sinh ra trong gia đình thuần nông ở vùng Thượng Quận, thị xã Kinh Môn cũ (nay là phường Trần Liễu, TP Hải Phòng), nơi vốn có truyền thống trồng sắn dây. Học hết phổ thông, ngoài công việc đồng áng, ông Thành thường mang củ sắn dây mang đi bán cho các cơ sở làm bột ở Hải Phòng, Quảng Ninh, Hưng Yên.\nTiện công đi lại, ông đứng ra kết nối, giúp các xưởng tiêu thụ sản phẩm. "Họ làm nhiều không bán hết, tôi tiện xe thì chở giúp, không lấy công. Họ bán được bột thì tôi cũng tiêu thụ được củ sắn dây", ông giải thích.\nNhững chuyến hàng rong ruổi khắp miền Bắc giúp ông hiểu sâu hơn về giá trị thực của sắn dây. Một lần sau khi chở hàng tạ bột từ Hưng Yên về Quảng Ninh giữa trời mưa rét, ông Thành bị vợ trách vì quá vất vả. Chính cuộc tranh luận đó đã làm ông nảy ra ý tưởng tự làm bột để bán.\nThời điểm ấy, hầu như nhà nào cũng làm vài cân bột sắn dây dùng dần. Nhưng khi kinh tế khấm khá, người dân dần mua bột chế biến sẵn cho tiện và ông Thành nhận ra cơ hội từ đó. Từ vài chục kg bột sấy thủ công trong căn bếp nhỏ, ông bắt đầu làm bài bản hơn.\nKhông ai chỉ dẫn, ông Thành mày mò học hỏi từ những xưởng lớn mà mình cung cấp nguyên liệu. "Tôi may mắn được đi nhiều nơi, gặp hàng nghìn người làm nghề. Thấy ai có cách hay, tôi ghi nhớ, thử nghiệm và chọn ra công thức tốt nhất", ông chia sẻ.\nTheo Đông y, bột sắn dây vị ngọt, tính mát, giúp thanh nhiệt, giải độc, tốt cho tiêu hóa, tim mạch và nội tiết tố nữ. Cây sắn dây được trồng từ tháng 3, thu hoạch vào tháng 1 năm sau, khi củ tích tụ nhiều tinh bột nhất. Bột sắn phải sản xuất trong ba tháng kể từ khi thu hoạch mới đảm bảo chất lượng.\nNhững năm đầu khởi nghiệp, do chưa có nước máy, vợ chồng ông phải chở téc đi mua nước sạch về lọc bột. "Nước quan trọng không kém gì củ sắn. Nước sạch thì bột mới trắng, đóng bánh chuẩn", ông giải thích. Để hong khô bột, ông dùng bếp than tổ ong, sau chuyển sang sấy bằng bóng đèn công suất lớn, rồi cải tiến thành buồng sấy bằng dây mayso quây tôn, vừa tiết kiệm, vừa hiệu quả.\nNhững cải tiến được ông thực hiện tỉ mỉ suốt nhiều năm, giúp hoàn thiện quy trình sản xuất, đảm bảo chất lượng, vệ sinh, năng suất. Ông bắt đầu nghĩ đến việc xây dựng thương hiệu cho sản phẩm. Ban đầu lấy tên "Bột sắn dây Kinh Môn" vì đó là đặc sản làng quê mình, nhưng in xong bao bì ông lại thấy "sai sai". Tên chung ai cũng dùng được, nếu ai làm kém chất lượng, cả làng sẽ bị mang tiếng.\nCuối cùng, ông bỏ toàn bộ bao bì cũ, mất trắng 20 triệu đồng, số tiền bằng cả vốn nhập nguyên liệu một năm và đăng ký thương hiệu \'\'Sắn dây Thành Nhàn\'\' ghép từ tên hai vợ chồng. Năm 2020, ông thành lập Hợp tác xã Nông sản sạch Thành Nhàn, đầu tư gần 1.500 m2 nhà xưởng cùng hệ thống máy nghiền, tách bã, sấy, đóng gói khép kín. Mỗi năm, hợp tác xã sản xuất khoảng 100 tấn bột sắn dây đạt tiêu chuẩn vệ sinh an toàn thực phẩm.\n"Làm sắn dây không chỉ là trồng và xay. Muốn bán được, phải chuẩn hóa quy trình, tạo niềm tin bằng chất lượng", ông nói. Không ít lần, ông từ chối những đơn hàng xuất khẩu lớn vì không muốn pha trộn hay nhập hàng kém chất lượng, bởi chỉ một mẻ không đạt cũng có thể mất uy tín bao năm gây dựng.\nĐể đảm bảo nguồn nguyên liệu ổn định, ông cùng các xã viên liên kết với hàng trăm nông dân trồng hơn 25 ha sắn dây theo hướng VietGAP, kiểm soát nguồn nước, đất và thời gian thu hoạch. Mỗi năm, hợp tác xã tiêu thụ hơn 500 tấn củ tươi, cam kết bao tiêu toàn bộ, không để nông dân chịu thiệt.\nNhận ra "làm tốt mà không biết bán thì cũng bằng không", ông Thành tự học marketing, lập Fanpage, đăng video giới thiệu quy trình sản xuất. Hợp tác xã tham gia các hội chợ xúc tiến thương mại, liên kết với doanh nghiệp phân phối và chuỗi thực phẩm sạch ở Hà Nội, Hải Phòng, Quảng Ninh.\nNhờ đó sản phẩm bột sắn dây Thành Nhàn nhanh chóng có chỗ đứng trên thị trường. Năm 2022, hợp tác đạt doanh thu 10 tỷ đồng, lợi nhuận hơn 1,8 tỷ; năm 2023 doanh thu 12,5 tỷ, lãi 2,4 tỷ; năm 2024 gần 15 tỷ đồng, lãi ròng hơn 3 tỷ đồng. Mỗi vụ sản xuất, hợp tác xã tạo việc làm ổn định cho hơn 20 lao động với thu nhập trung bình 9 triệu đồng/tháng.\nHiện bột sắn dây Thành Nhàn đạt chứng nhận OCOP 4 sao, được nhiều đơn vị đặt hàng dài hạn và liên tục được đánh giá cao tại các hội chợ nông sản miền Bắc. Sản phẩm không chỉ hiện diện trong các cửa hàng thực phẩm sạch mà còn xuất hiện trong giỏ quà biếu cao cấp dịp lễ Tết.\nTháng 10/2025, ông Bùi Văn Thành được vinh danh là Nông dân Việt Nam xuất sắc, ghi nhận hành trình hơn 20 năm gắn bó và sáng tạo cùng cây sắn dây quê hương. "Củ sắn dây từng bị coi là thứ bình thường, nhưng nếu làm tử tế có thể nuôi sống cả làng", ông nói.\nÔng Nguyễn Đức Minh, Chủ tịch Hội Nông dân phường Trần Liễu, nhận xét: "Ông Thành là người tiên phong xây dựng thương hiệu nông sản địa phương, mạnh dạn đầu tư máy móc, áp dụng kỹ thuật mới, góp phần nâng tầm giá trị cây sắn dây của vùng Kinh Môn".', 'Hải PhòngTừ cây sắn dây quen thuộc của quê hương, ông Bùi Văn Thành đã gây dựng thành sản phẩm OCOP 4 sao, doanh thu hàng tỷ đồng mỗi năm.', @cat_id_thoi_su_giao_thong, @user_id_letan, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0, 0, NULL, NULL, NULL);


INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured, is_realtime, live_start_time, live_end_time, review_note) VALUES
('Bọc kín ôtô bằng vải bạt tránh ngập lụt', 'boc-kin-oto-bang-vai-bat-tranh-ngap-lut', 'https://i1-vnexpress.vnecdn.net/2025/10/21/VNE3061-JPG-1761061100-4055-1761061342.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=CjqDhLuNmozRNjJK6MkxSg', 'Chiều 21/10, anh Trần Thanh Nguyên, 34 tuổi, mua tấm bạt kích cỡ lớn nhất về trải ra giữa phòng khách ngôi nhà ba tầng số 107 đường Mẹ Nhu, phường Thanh Khê, sau đó lùi chiếc BMW 320i mới mua vào chính giữa.\nHai người trong gia đình phụ anh Nguyên trùm kín chiếc xe trong tấm bạt. Ở nóc xe, nơi các đầu bạt được túm lại, anh dùng dây rút buộc kín. Phía trước đầu xe cũng được túm chặt.\nAnh Nguyên tự gọi phương pháp này là "gói bánh". Chiếc BMW được bọc kín trong bạt, chỗ nào có khả năng bị hở thì được gia cố thêm băng keo. Trường hợp nước tràn vào nhà, xe có thể tự nổi lên.\nHồi đầu tháng 10, chứng kiến một số hộ dân vùng lũ Thái Nguyên dùng tấm bạt bọc kín giữ xe an toàn, anh Nguyên quyết định áp dụng cho xế hộp trị giá hơn hai tỷ đồng. Tưởng đơn giản, nhưng tấm bạt to và cứng, ba người phải cẩn thận từng công đoạn, bọc từng góc của chiếc xe để đảm bảo không có nước lọt vào. Ba người mất gần một giờ mới "gói xong" chiếc xe.\nSau khi chia sẻ lên mạng xã hội, nhiều người đồng tình với phương pháp của anh Nguyên, số khác cho rằng nên đưa xe gửi ở những chỗ cao ráo, bọc kín để trong nhà vẫn không an toàn. Anh Nguyên giải thích Đà Nẵng thiếu nhiều bãi đỗ xe. Đợt mưa này dự báo lên đến 900 mm, ngập lụt đô thị có thể xảy ra ở nhiều nơi nên không biết nơi nào mới an toàn.\n"Tôi chuẩn bị sẵn nhiều tấm hợp kim, có gioăng cao su để khi nước lên sẽ gia cố thêm ngoài cổng, có thể ngăn được nước cao 1,4 m. Thiên tai ngày càng khó lường, càng chủ động sẽ càng giảm được thiệt hại", anh nói thêm.\nĐợt mưa tháng 10/2022, anh Nguyên để ôtô ngoài đường, bị ngập nặng, phải bán rẻ. Vì thế đợt mưa lũ này, anh chủ động phòng tránh sớm. Ngoài dùng bạt bọc ôtô, anh cũng áp dụng với cây đàn piano. Chi phí mua bạt không nhiều (500.000 đồng tấm bạt lớn), người dân vùng thấp trũng có thể tham khảo cách này để bảo vệ tài sản, anh Nguyên gợi ý.\nTrong khi đó trên nhiều tuyến phố Đà Nẵng, nhiều người chọn cách đưa ôtô lên vỉa hè hoặc đỗ dưới lòng đường để tránh ngập lụt. Trên đường Điện Biên Phủ, nơi cao ráo ở khu vực trung tâm, ôtô đậu kín hai bên. Những khu vực thấp trũng như đường Hàm Nghi, Hải Phòng, Quang Trung... lượng xe đậu đỗ ít hơn, do người dân đã biết khu vực này nước có thể đổ dồn về trước khi thoát ra biển.\nTạiTP Huế, công viên Trung tâm hành chính ở phường Vỹ Dạ nằm trên đồi cao trở thành điểm đỗ ôtô tránh lũ cho hàng chục người dân. Ôtô đậu sát nhau trên đường lót đá granite vốn dành cho người đi bộ.\n"Thấy nước sông Hương dâng cao, lại nghe dự báo mưa lớn kéo dài có thể gây lũ vượt báo động ba, tôi chủ động đưa ôtô ra đây đỗ trước", anh Nguyễn Văn Quang, 39 tuổi, ở phường Vỹ Dạ nói.\nTheo anh Quang, khi mưa lớn kéo dài, đường Hoàng Lanh, Vũ Thắng gần các khu chung cư Vicoland, Aranya, Xuân Phú thường bị ngập. Những năm trước, lũ từ sông Như Ý tràn vào, nhiều ôtô bị ngâm nước, hư hỏng.\nNgoài công viên Trung tâm hành chính công, nhiều tuyến đường cốt nền cao như Điện Biên Phủ, Phan Bội Châu, dốc cầu chợ Dinh và cầu vượt Thủy Dương đều kín ôtô tránh lũ. Trong kinh thành Huế, các đường Lương Ngọc Quyến, Xuân 68 cũng trở thành điểm đỗ xe an toàn.\nAnh Nguyễn Văn Minh, 35 tuổi, phường Phú Xuân, đưa ôtô đến đậu ở khu Eo Bầu, đường Xuân 68, sau khi nghe dự báo sông Hương lên báo động ba. "Nhờ di chuyển xe đến chỗ cao trong các đợt lũ năm 2020 và 2022 tôi tránh được thiệt hại hàng chục triệu đồng", anh nói.\nTối 21/10, theo Trung tâm Dự báo Khí tượng Thủy văn quốc gia, tâm bão Fengshen cách Đà Nẵng khoảng 390 km, gió mạnh cấp 9-10, giật cấp 12, di chuyển hướng tây tây nam. Dự báo đến 19h ngày 22/10, tâm bão ở vùng biển Huế - Quảng Ngãi, sức gió giảm còn cấp 8 rồi suy yếu thành áp thấp nhiệt đới, đi vào Đà Nẵng và tan ở Nam Lào.\nKhu vực từ Quảng Trị đến Quảng Ngãi sẽ mưa rất to trong các ngày 22-27/10, riêng Huế và Đà Nẵng mưa phổ biến 500-700 mm, có nơi trên 900 mm. Lũ các sông Hương, Bồ, Vu Gia - Thu Bồn, Trà Khúc... có thể lên báo động ba, nguy cơ ngập sâu, sạt lở, lũ quét rất cao.\nÔng Mai Văn Khiêm, Giám đốc Trung tâm Dự báo Khí tượng Thủy văn quốc gia, khuyến cáo người dân vùng núi theo dõi dấu hiệu sạt lở, sẵn sàng sơ tán; vùng trũng kê cao tài sản, chủ động ứng phó ngập lụt; vùng đô thị tránh để xe, hàng hóa ở khu vực thấp.', 'Học kinh nghiệm từ người dân vùng lũ Thái Nguyên, anh Thanh Nguyên ở Đà Nẵng dùng tấm bạt 6x12 m bọc kín chiếc BMW 320i đặt trong phòng khách.', @cat_id_thoi_su, @user_id_nguyenongvothanh, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0, 0, NULL, NULL, NULL);


INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured, is_realtime, live_start_time, live_end_time, review_note) VALUES
('Trưởng thôn bị gấu hoang dã tấn công', 'truong-thon-bi-gau-hoang-da-tan-cong', 'https://i1-vnexpress.vnecdn.net/2025/10/21/25f7cc7b42a2cffc96b3-176105584-2332-6860-1761055993.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=KAGtah3xfaNYhSlpAERGeA', 'Khoảng 18h30 ngày 21/10, một số người dân phát hiện ông Nhương, người dân tộc Cơ Tu, nằm bên đường, trên người có nhiều vết thương nghi do động vật hoang dã cắn nên đã khiêng về thôn sơ cứu.\n"Ông Nhương cho biết bị gấu hoang tấn công trên đường đi làm rẫy về", thượng tá Bhnướch Đèn, Trưởng Công an xã Hùng Sơn, thông tin.\nTheo thượng tá Đèn, công an xã đã phối hợp với y tế địa phương đưa ông Nhương đến Trạm Y tế Ch’Ơm sơ cứu. Nạn nhân bị rách môi dưới và một vết thương dài ở cổ, may mắn không trúng động mạch. Hiện ông tỉnh táo, nhưng không thể nói, cần được chuyển đến cơ sở y tế tuyến trên.\nCông an xã Hùng Sơn đã liên lạc với Trung tâm Y tế khu vực Tây Giang xin điều xe cấp cứu lên thôn A Tu 1 để đón nạn nhân về tiếp tục điều trị. Quãng đường di chuyển mất gần hai giờ đi ôtô.\nSau sự việc, nhà chức trách tuyên truyền người dân không đến gần khu rừng giáp biên giới Lào, nơi ông Pơloong Nhương bị gấu tấn công. Trước năm 2020, khu vực Tây Giang từng xảy ra trường hợp tương tự.', 'Đà NẵngÔng Pơloong Nhương, 48 tuổi, Trưởng thôn A Tu 1, xã Hùng Sơn trên đường đi làm rẫy về bị con gấu ở rừng gần biên giới Lào tấn công vùng mặt, cổ.', @cat_id_thoi_su_chinh_tri, @user_id_nguyenong, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0, 0, NULL, NULL, NULL);


INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured, is_realtime, live_start_time, live_end_time, review_note) VALUES
('Người học lái xe có thể được xác thực điện tử để chống gian lận', 'nguoi-hoc-lai-xe-co-the-duoc-xac-thuc-dien-tu-de-chong-gian-lan', 'https://i1-vnexpress.vnecdn.net/2025/10/21/2-5974-1761021978.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=626iWfGBk8cZ0ScgW3M02w', 'Bộ Xây dựng đang lấy ý kiến dự thảo văn bản sửa đổi Nghị định 160/2024 quy định về hoạt động đào tạo và sát hạch giấy phép lái xe.\nDự thảo bổ sung điều kiện đối với cơ sở đào tạo lái xe là có hệ thống ứng dụng định danh và xác thực điện tử để bảo đảm việc nhận diện, xác thực chính xác học viên từ khi đăng ký đến khi sát hạch lái xe, nhằm xây dựng chuyển đổi số trong hoạt động đào tạo lái xe.\nTheo ông Lương Duyên Thống, Trưởng phòng Quản lý Vận tải phương tiện và người lái (Cục Đường bộ Việt Nam), hiện người học khi đăng ký và sát hạch có thể sử dụng nhiều loại giấy tờ, dễ xảy ra nhầm lẫn trong quá trình khai hoặc nhập dữ liệu, khi tra cứu có thể ra thông tin người khác. Việc này khiến dữ liệu đào tạo và sát hạch, cấp giấy phép lái xe không được đồng bộ.\nViệc số hóa ngay từ giai đoạn đăng ký học lái xe giúp đưa vào hệ thống "số liệu sạch", là cơ sở xây dựng số hóa giai đoạn đào tạo lái xe đến kết thúc đào tạo cấp giấy xác nhận, giúp đồng bộ dữ liệu của học viên. Trong quá trình đào tạo hoặc sát hạch, học viên có thể đi qua hệ thống nhận diện hình ảnh, giúp cơ quan quản lý phát hiện gian lận như không đủ giờ học, học hộ, thi hộ...\nƯớc tính chi phí đầu tư của bộ thiết bị định danh và xác thực điện tử trên thị trường khoảng 15 triệu đồng. Cả nước hiện có hơn 370 cơ sở đào tạo lái xe, tổng chi phí đầu tư khoảng 5,5 tỷ đồng.\nDự thảo nghị định còn quy định đơn giản hóa thủ tục hành chính trong lĩnh vực đào tạo lái xe, như giảm 30% thời gian giải quyết thủ tục hành chính, giảm 30% chi phí, 100% thủ tục hành chính lĩnh vực đào tạo được thông suốt. Dự thảo sẽ được Bộ Xây dựng trình Chính phủ ban hành trong tháng 12.', 'Bộ Xây dựng đề xuất lắp hệ thống ứng dụng định danh và xác thực điện tử tại các cơ sở đào tạo, sát hạch lái xe để nhận diện học viên, tránh gian lận và đồng bộ cơ sở dữ liệu cá nhân.', @cat_id_thoi_su_giao_thong, @user_id_oanloan, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0, 0, NULL, NULL, NULL);


INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured, is_realtime, live_start_time, live_end_time, review_note) VALUES
('Drone quân sự Mỹ trục trặc vì trời nóng, hơi nước biển', 'drone-quan-su-my-truc-trac-vi-troi-nong-hoi-nuoc-bien', 'https://i1-vnexpress.vnecdn.net/2025/10/21/5563187178137268905a-drone-My-4456-4808-1761039785.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=6YUd0rRH-BYnyO0pKqgEAA', '"Một số mẫu thiết bị bay không người lái (drone) từng thử nghiệm tại nơi khác đã gặp trục trặc khi hoạt động tại Hawaii. Nhiệt độ bên ngoài là 38 độ C, không khí có độ mặn cao. Những mẫu drone đó không tồn tại được trong môi trường như vậy", thượng sĩ Shaun Curry, thành viên Sư đoàn Bộ binh số 25 lục quân Mỹ, cho biết tại hội nghị thường niên của quân chủng này hồi tuần trước.\nSư đoàn 25, đóng quân tại căn cứ Schofield ở Hawaii, nhận lô thiết bị nói trên theo khuôn khổ chương trình Chuyển đổi Thực địa, sáng kiến nhằm đơn giản hóa quy trình thử nghiệm và cải tiến vũ khí mới, trong đó các binh sĩ trực tiếp tham gia và đóng góp ý kiến. Thông tin từ quá trình này sẽ được áp dụng vào kế hoạch tác chiến và xây dựng chiến thuật.\nThượng sĩ Curry cho biết khi nhà phát triển sẽ cử kỹ thuật viên đến hiện trường để xử lý những chiếc drone gặp sự cố. "Họ sửa chữa tại chỗ những thiết bị gặp trục trặc. Sau đó, họ sẽ cung cấp biến thể mới đã được nâng cấp", quân nhân Mỹ cho hay.\nBinh sĩ lục quân Mỹ từng gặp những tình huống tương tự khi sử dụng drone ở khu vực Ấn Độ Dương - Thái Bình Dương. Một số buổi diễn tập được tổ chức trong mưa bão, gió mạnh hoặc thời tiết ẩm ướt, khiến tầm bay và thời gian hoạt động của drone bị suy giảm. Ngoài ra, một số hòn đảo không có vị trí phù hợp để binh sĩ phóng drone.\nPhương tiện không người lái đã trở thành một trong những loại vũ khí chủ chốt trên chiến trường hiện nay. Tuy nhiên, các quan chức và chỉ huy Mỹ nhiều lần thừa nhận nước này đang tụt hậu so với đối thủ.\nTrung tướng Charles Costanza, chỉ huy Quân đoàn 5 quân đội Mỹ, cho biết lực lượng này đã nghiên cứu về máy bay không người lái (UAV) và drone, cũng như các biện pháp đối phó chúng trong gần 10 năm qua nhưng điều này "vẫn chưa đủ nhanh".\nKhi được hỏi liệu Mỹ có cần UAV tầm xa tương tự mẫu Geran của Nga và Shahed do Iran phát triển hay không, tướng Costanza đáp rằng "tôi nghĩ là có".\nNguyễn Tiến(TheoAFP, AP, Business Insider)', 'Binh sĩ Mỹ cho biết một số nguyên mẫu drone, UAV gặp trục trặc vì trời nóng và hơi muối khi thử nghiệm tại Hawaii.', @cat_id_the_gioi, @user_id_nguyentien, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0, 0, NULL, NULL, NULL);


INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured, is_realtime, live_start_time, live_end_time, review_note) VALUES
("Phu nhân Tổng Bí thư trao tặng 'Tủ sách tiếng Việt' cho thư viện Phần Lan", 'phu-nhan-tong-bi-thu-trao-tang-tu-sach-tieng-viet-cho-thu-vien-phan-lan', 'https://i1-vnexpress.vnecdn.net/2025/10/22/phu-nhan-ngo-phuong-ly-jpeg-17-4613-7791-1761091170.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=u6j1pAFfbMZBzgUVceFs-Q', 'Bà Ngô Phương Ly cùng phu nhân Tổng thống Phần Lan Suzanne Innes-Stubb hôm 21/10 tham quan các khu vực trưng bày, không gian đọc, khu "Thiên đường sách" (Book Heaven), chiếu phim, triển lãm nghệ thuật, hội thảo..., theoTTXVN.\nGiới thiệu về quá trình hình thành, kiến trúc cũng như các hoạt động nổi bật của Thư viện Oodi, Giám đốc thư viện Anna-Maria Soininvaara nhấn mạnh đây là một trong những công trình văn hóa hiện đại bậc nhất của Phần Lan, được xem là biểu tượng của "nền tri thức mở" và không gian sáng tạo cộng đồng của người dân Helsinki.\nPhu nhân Tổng Bí thư chia sẻ Thư viện Oodi là mô hình độc đáo để Việt Nam tham khảo. Bà đặc biệt ấn tượng với chính sách và hệ thống giáo dục của Phần Lan, nơi sách và tri thức không chỉ được lưu giữ mà còn lan tỏa, khơi gợi sáng tạo và thúc đẩy phát triển cộng đồng.\nBà Ngô Phương Ly thay mặt đoàn Việt Nam trao tặng Thư viện Oodi "Tủ sách tiếng Việt" gồm hơn 120 đầu sách chọn lọc, gần 160 quyển sách tiếng Việt và song ngữ theo 4 nhóm chủ đề chính: sách văn học - nghệ thuật; sách lịch sử - văn hóa Việt Nam; sách Việt Nam đương đại - hội nhập và phát triển; và sách dành cho thiếu nhi và luyện học tiếng Việt.\n"Tủ sách tiếng Việt" được bố trí tại khu vực "Thiên đường sách" ở tầng 3 của thư viện, cùng các bộ sách tiếng nước ngoài khác. Đây sẽ là góc đọc tiếng Việt đầu tiên trong hệ thống thư viện công cộng Helsinki, mở cửa miễn phí cho công chúng và cộng đồng người Việt Nam tại Phần Lan.\nBà Ngô Phương Ly nhấn mạnh việc Thư viện Oodi dành không gian trang trọng để tiếp nhận "Tủ sách tiếng Việt" là minh chứng sinh động cho sự cởi mở, tôn trọng và trân quý đa dạng văn hóa của đất nước Phần Lan. Bà mong muốn trong tương lai sẽ có thêm nhiều tủ sách và chương trình giao lưu, trao đổi văn hóa được tổ chức không chỉ tại Phần Lan mà còn nhiều quốc gia khác trên thế giới, nơi có người Việt sinh sống.\nPhu nhân Tổng thống Phần Lan cảm ơn món quà của phu nhân Tổng Bí thư, nhấn mạnh tủ sách có ý nghĩa rất đặc biệt, góp phần làm cầu nối tăng cường sự hiểu biết và tình hữu nghị giữa nhân dân hai nước.\nGiám đốc Anna-Maria Soininvaara khẳng định Thư viện Oodi sẽ giới thiệu và duy trì "Tủ sách tiếng Việt" như một phần của không gian đa ngôn ngữ tại thư viện.\nTổng Bí thư Tô Lâm và phu nhân đang trong chuyến thăm chính thức Phần Lan ngày 20-22/10. Hai nước hôm 21/10 ra Tuyên bố chung về nâng cấp quan hệ ngoại giao lên Đối tác Chiến lược, khẳng định các nguyên tắc, đề ra những định hướng lớn để đẩy mạnh và tăng cường hợp tác.', 'Phu nhân Tổng Bí thư, bà Ngô Phương Ly, thăm Thư viện Trung tâm Oodi tại thủ đô Helsinki và trao tặng "Tủ sách tiếng Việt".', @cat_id_the_gioi_tu_lieu, @user_id_huyenle, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0, 0, NULL, NULL, NULL);


INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured, is_realtime, live_start_time, live_end_time, review_note) VALUES
('Bảo tàng Louvre mất lượng trang sức hơn 100 triệu USD', 'bao-tang-louvre-mat-luong-trang-suc-hon-100-trieu-usd', 'https://i1-vnexpress.vnecdn.net/2025/10/22/5563187178137269004a-Louvre-17-4602-9842-1761090486.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=hxZsQHE6cRaL726hX9CX9A', '"Người phụ trách bảo tàng Louvre ước tính thiệt hại lên tới 88 triệu euro (khoảng 102 triệu USD). Tuy nhiên, di sản lịch sử của Pháp chịu tổn thất lớn hơn", công tố viên Paris Laure Beccuau ngày 21/10 thông báo. "Nhóm cướp sẽ không thể hưởng trọn món lợi này nếu chúng có ý tưởng tồi tệ về việc nấu chảy những món trang sức lấy được".\nCác điều tra viên đang truy tìm thủ phạm vụ trộm ngày 19/10, dựa trên suy luận rằng nhóm tội phạm có tổ chức dùng thang nâng trên xe tải để đột nhập bảo tàng Louvre, sau đó đánh rơi một vương miện nạm kim cương khi tìm đường tẩu thoát. Những người này mặc áo phản quang để ngụy trang thành công nhân xây dựng.\nTrong số các món trang sức mà nhóm này lấy đi có một chiếc vòng cổ với ngọc lục bảo và kim cương mà Hoàng đế Napoleon tặng cho vợ ông là Marie-Louise, cùng một vương miện của Hoàng hậu Eugenie được đính gần 2.000 viên kim cương.\nTheo công tố viên Beccuau, có 4 người liên quan tới vụ trộm và chuyên gia đang phân tích dấu vân tay tìm thấy ở hiện trường. Các thám tử đang xem xét kỹ lưỡng video trích xuất từ camera quanh bảo tàng và những tuyến đường chính để truy vết nhóm cướp đã dùng xe tay ga để chạy trốn.\nLaurence des Cars, giám đốc bảo tàng Louvre từ năm 2021, dự kiến ra điều trần trước Ủy ban Văn hóa của thượng viện Pháp trong ngày 22/10.\nVụ trộm khơi lại tranh cãi về tình trạng thiếu an ninh tại các bảo tàng Pháp. Đánh giá của Cơ quan Kiểm toán Tối cao Pháp trong giai đoạn 2019-2024 cho rằng việc nâng cấp an ninh tại bảo tàng Louvre "bị chậm trễ kéo dài", chỉ 1/4 số cửa ra vào có camera giám sát.\nTrong khi đó, bảo tàng Louvre cho biết các tủ trưng bày trang sức không phải loại dễ bị phá vỡ, chúng được lắp đặt vào năm 2019 và thể hiện cải thiện đáng kể về mặt an ninh.\nVào tháng 9, một nhóm tội phạm đột nhập bảo tàng lịch sử tự nhiên Paris, lấy đi những thỏi vàng trị giá 1,5 triệu USD. Giới chức Pháp ngày 21/10 cho biết một phụ nữ Trung Quốc 24 tuổi bị bắt tại Barcelona, Tây Ban Nha khi cố gắng tìm cách tẩu tán gần một kg vàng đã nung chảy.\nCũng trong tháng trước, những tên trộm lấy đi hai chiếc đĩa và một chiếc bình trị giá khoảng 7,6 triệu USD tại một bảo tàng ở trung tâm thành phố Limoges, miền trung nước Pháp.\nNguyễn Tiến(TheoAFP, AP, Reuters)', 'Công tố viên Pháp cho biết nhóm cướp lấy đi lượng trang sức trị giá hơn 100 triệu USD tại bảo tàng Louvre trong vòng 7 phút.', @cat_id_the_gioi_phan_tich, @user_id_nguyentien, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0, 0, NULL, NULL, NULL);


INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured, is_realtime, live_start_time, live_end_time, review_note) VALUES
('Xe bồn phát nổ, ít nhất 35 người hôi xăng thiệt mạng', 'xe-bon-phat-no-it-nhat-35-nguoi-hoi-xang-thiet-mang', 'https://i1-vnexpress.vnecdn.net/2025/10/22/no-xe-bon-jpeg-1761088132-2585-1761088709.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=DEfp71VRza8ZkkXB-9ivsA', '"Xe bồn chở nhiên liệu bị lật và bắt đầu rò rỉ. Mọi người đổ xô đến hứng xăng. Chiếc xe phát nổ và họ bị bỏng", Ibrahim Hussaini, điều phối viên địa phương của cơ quan ứng phó khẩn cấp quốc gia, hôm 21/10 cho hay.\nVụ nổ trên tuyến xa lộ Bida-Agaie ở bang Niger, miền trung đất nước, khiến ít nhất 35 người thiệt mạng và khoảng 40 người bị thương.\nCảnh sát đã mở cuộc điều tra để xác định danh tính tài xế, chủ sở hữu xe bồn và nguyên nhân tai nạn.\nThống đốc bang Niger, Umaru Bago, bày tỏ rất đau lòng khi chứng kiến cảnh người dân bất chấp nguy hiểm, cố thu gom xăng dầu từ những chiếc xe bồn bị lật. "Thêm một sự việc đau lòng, khó khăn và bi thảm đối với người dân và chính quyền bang", ông Bago nói.\nTổng thống Nigeria Bola Tinubu đã chấm dứt trợ cấp nhiên liệu và kiểm soát tiền tệ sau khi nhậm chức vào tháng 5/2023, nhằm phục hồi nền kinh tế. Những thay đổi này gây ra cuộc khủng hoảng chi phí sinh hoạt ở quốc gia đông dân nhất châu Phi, khiến người dân chấp nhận nguy hiểm để có được xăng.\nTai nạn xe bồn cũng thường xảy ra ở Nigeria, nơi đường sá xuống cấp, tài xế lái xe quá tốc độ và người dân luôn cố thu gom nhiên liệu sau những tai nạn như vậy. Hồi tháng 1, ít nhất 98 người thiệt mạng tại bang Niger khi người dân cố chuyển xăng từ xe bồn gặp nạn sang xe tải.\nSau thảm kịch đó, Tổng thống Tinubu phát động chiến dịch quốc gia nhằm nâng cao nhận thức của cộng đồng về rủi ro và tác động đối với môi trường trong việc thu gom nhiên liệu từ xe bồn gặp nạn.\nHuyền Lê(TheoAFP,AP, Express Tribune)', 'NigeriaXe bồn phát nổ ở bang Niger khi đám đông đang hôi xăng, khiến ít nhất 35 người thiệt mạng và hàng chục người bị thương.', @cat_id_the_gioi, @user_id_huyenle, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0, 0, NULL, NULL, NULL);


INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured, is_realtime, live_start_time, live_end_time, review_note) VALUES
('Bữa trưa căng thẳng giữa ông Trump và ông Zelensky', 'bua-trua-cang-thang-giua-ong-trump-va-ong-zelensky', 'https://i1-vnexpress.vnecdn.net/2025/10/21/AFP-20251017-797R2DW-v2-HighRe-9441-7274-1761033389.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=bckIe5Cuz57g9n0B6KbOCQ', 'Tổng thống Ukraine Volodymyr Zelensky ngày 17/10 tới Nhà Trắng gặp Tổng thống Mỹ Donald Trump để thảo luận về việc Washington cung cấp cho Kiev những vũ khí tầm xa và uy lực, như tên lửa hành trình Tomahawk. Ông Zelensky mang nhiều kỳ vọng, bởi ông Trump trước đó nhiều lần thể hiện phẫn nộ với Tổng thống Nga Vladimir Putin và sẵn sàng tăng ủng hộ Ukraine.\nCuộc gặp diễn ra tại Phòng Nội các, dưới hình thức ăn trưa làm việc. Hai tổng thống ngồi đối diện nhau, phát biểu và trả lời một số câu hỏi từ truyền thông rồi bắt đầu họp kín. Nhưng trái với những lời tán dương dành cho nhau lúc đầu, bầu không khí dần trở nên căng thẳng.\nCác nguồn thạo tin cho biết hai bên xuất hiện nhiều bất đồng, và sự kiện có những lúc giống như "cuộc cãi vã".\nNhững dấu hiệu về một cuộc gặp không suôn sẻ dường như đã xuất hiện từ trước đó. Tổng thống Trump ngày 16/10 thông báo có cuộc điện đàm "rất hiệu quả" với người đồng cấp Nga Vladimir Putin và hai lãnh đạo nhất trí gặp thượng đỉnh tại Hungary. Khi trả lời truyền thông tại Phòng Nội các, Tổng thống Trump cho rằng "ông Putin muốn đạt thỏa thuận".\n"Tôi nghĩ Tổng thống Zelensky cũng muốn kết thúc chiến sự và ông ấy phải làm điều đó bây giờ", ông Trump nói.\nCâu hỏi của phóng viên ngay sau đó khiến ông Zelensky lộ vẻ bất ngờ.\n"Ông sẵn sàng đưa ra những nhượng bộ nào để chấm dứt cuộc chiến với Nga? Ukraine có từ bỏ ý định gia nhập NATO không? Người Mỹ đã mệt mỏi với những cuộc chiến ở nước ngoài và như Tổng thống Trump vừa nói, chúng tôi cũng cần Tomahawk", nữ phóng viên hỏi.\n"Câu hỏi dành cho tôi à?", ông Zelensky đáp lại, rồi nói rằng các bên cần ngồi lại và đối thoại, tiếp đó là một lệnh ngừng bắn. Tổng thống Ukraine cho rằng Nga không muốn hòa bình và sẽ trao đổi với Tổng thống Trump về cách buộc ông chủ Điện Kremlin trở lại bàn đàm phán.\nTrong cuộc gặp, ông Trump được cho là đã "lặp lại gần như nguyên văn nhiều luận điểm của ông Putin", ngay cả khi chúng trái ngược với những tuyên bố gần đây của ông chủ Nhà Trắng về một nước Nga "đang suy yếu về kinh tế", theo các nguồn thạo tin.\n"\'Nếu muốn, Tổng thống Putin có thể hủy diệt các ông\'", một nguồn tin thuật lại lời của ông Trump.\n"Đặc phái viên Mỹ về Trung Đông Steve Witkoff lúc này tham gia trao đổi, nói \'Nga đặt mục tiêu kiểm soát toàn bộ vùng Donbass\'. Dường như phía Mỹ đang thử thách phái đoàn Ukraine và xem họ có thể chấp thuận điều gì", nguồn tin kể.\nDonbass, gồm hai tỉnh miền đông Donetsk và Lugansk, là hai trong số 4 tỉnh mà Nga tuyên bố sáp nhập sau khi mở chiến dịch hồi năm 2022, dù chưa hoàn toàn kiểm soát tỉnh nào.\n"Từ bỏ Donbass là không thể chấp nhận đối với người dân Ukraine, và ông Putin hiểu rõ điều này", chủ tịch ủy ban đối ngoại quốc hội Ukraine Oleksandr Merezhko nói.\nÔng Zelensky đã chuẩn bị các bản đồ và mọi thứ liên quan, cố gắng giải thích tình hình, nhưng ông Trump tỏ ra không quan tâm.\n"Ông ấy nói kiểu \'không, nghe này, các ông không thể giành lại bất kỳ lãnh thổ nào đâu... Chúng tôi cũng không thể làm gì để cứu các ông. Ukraine nên cho ngoại giao một cơ hội nữa\'", nguồn tin cho biết thêm.\nCó thời điểm, Tổng thống Mỹ gạt các bản đồ chiến sự sang một bên, thể hiện rằng ông "phát chán" khi phải nhìn chúng hết lần này đến lần khác.\n"Tôi còn không biết cái lằn ranh đỏ này ở đâu. Tôi chưa từng đến đó", ông Trump nói.\nTổng thống Zelensky không đồng ý việc Ukraine từ bỏ những khu vực mà Nga chưa kiểm soát. Sau cùng, vào cuối cuộc gặp, Tổng thống Trump chấp thuận quan điểm này.\n"\'Được rồi, hãy thử chấm dứt chiến sự với chiến tuyến hiện tại\'", nguồn tin dẫn lại lời ông Trump.\nTổng thống Trump còn nêu rõ với người đồng cấp Zelensky một cách "trực diện và thẳng thắn" rằng Mỹ lúc này chưa thể cung cấp những tên lửa tầm xa mà Ukraine mong muốn. Một quan chức Mỹ nói ông Trump có ấn tượng Ukraine đang tìm cách leo thang và kéo dài xung đột.\nCuộc gặp kết thúc sau 2,5 giờ. "Tôi nghĩ chúng ta đã xong việc. Hãy chờ xem chuyện gì xảy ra tuần sau", ông Trump nói, ý nhắc đến kế hoạch gặp thượng đỉnh với ông Putin.\nMột quan chức châu Âu nói cuộc gặp ngày 17/10 "không đến mức thảm họa" như cuộc đấu khẩu gay gắt giữa ông Trump và ông Zelensky tại cuộc gặp tại Nhà Trắng hồi đầu năm. Tuy nhiên, Tổng thống Ukraine vẫn ra về với vẻ mặt thất vọng.\nCác nguồn thạo tin đều chưa rõ điều gì đã khiến ông Trump thay đổi quan điểm, dù ông chủ Nhà Trắng suốt nhiều tuần trước đó đã bày tỏ phẫn nộ với ông Putin vì tiến trình hòa đàm bế tắc. Họ cho rằng cuộc điện đàm giữa lãnh đạo Mỹ - Nga ngày 16/10 dường như "có ảnh hưởng rất lớn".\n"Cuộc gặp này là thất bại với phía Ukraine. Nó không quá tệ, chỉ tiếc là diễn ra không đúng thời điểm và các kỳ vọng đã bị thổi phồng", hai chuyên gia chính sách đối ngoại đảng Cộng hòa nói vớiPolitico. Họ thêm rằng các quan chức Cộng hòa ủng hộ Ukraine trước đó đã kêu gọi ông Zelensky hủy chuyến thăm, bởi sự chú ý ở Washington lúc này tập trung vào vấn đề chính phủ đóng cửa và Trung Đông.\nTrả lời báo giới sau cuộc gặp, ông Zelensky cho biết các thông điệp mà người đồng cấp Mỹ đưa ra "mập mờ".\nKhi được hỏi liệu ông có tin tưởng sẽ nhận được tên lửa Tomahawk hay không, Tổng thống Zelensky chỉ mỉm cười gượng và đáp "tôi thực tế thôi", đồng thời thêm rằng sự do dự của ông Trump chỉ phản ánh "lập trường của Mỹ ở thời điểm hiện tại".\nNhư Tâm(TheoFT, Politico, Washington Post)', 'Cuộc ăn trưa làm việc ở Nhà Trắng trở nên căng thẳng, nhiều lần "như cãi vã" sau khi Tổng thống Trump yêu cầu ông Zelensky nhượng một phần lãnh thổ cho Nga để chấm dứt chiến sự.', @cat_id_the_gioi_tu_lieu, @user_id_nhutam, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0, 0, NULL, NULL, NULL);


INSERT INTO news (title, slug, image_url, content, summary, category_id, author_id, published, status, created_at, updated_at, view_count, published_at, featured, is_realtime, live_start_time, live_end_time, review_note) VALUES
("Làn sóng xe máy điện khiến Toronto 'không kịp trở tay'", 'lan-song-xe-may-dien-khien-toronto-khong-kip-tro-tay', 'https://i1-vnexpress.vnecdn.net/2025/10/20/e4e98b88f5d9ecc2272d2a6bd8cfbc-9476-3769-1760946282.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=kD5LxWWUrLyuuQnI16ILdA', 'Ngồi dưới mưa lạnh trên phố Queen Street, Mezanur Rahman đang chờ thông báo trên điện thoại. Anh hy vọng kiếm được ít nhất 100 USD trong ca làm việc giao đồ ăn dài 12 tiếng để gửi tiền về nhà ở Bangladesh.\nRahman giao hàng bằng xe máy điện, phương tiện đã trở thành biểu tượng cho hoạt động giao đồ ăn ở Toronto. Tài xế dùng xe máy điện vì nhiều lý do: rẻ, không cần bằng lái, có thể len lỏi qua đường phố thường xuyên tắc nghẽn, thậm chí đi vào làn xe đạp, leo lên vỉa hè.\nCác loại xe điện hạng nhẹ như xe máy điện, scooter điện đang tràn ngập đường phố Toronto, trở thành nét đặc trưng của cảnh quan trung tâm thành phố, trong làn sóng được gọi là "giao thông vi mô".\nTuy nhiên, sự xuất hiện ngày càng nhiều của xe điện khiến nhiều người bất bình, cho rằng chúng là nguyên nhân khiến số vụ tai nạn tăng cao. Với số khác, đó là tiện ích hàng ngày, phương tiện đi làm và là cách thức di chuyển hiện đại.\nTheoToronto Star, công chúng, gồm cả các tài xế giao đồ ăn, cho rằng các cấp chính quyền đều bị bất ngờ trước sự xuất hiện của những phương tiện mà họ chưa biết cách quản lý.\nNhiều người lo ngại về rủi ro an toàn giao thông. Từ năm 2020 đến 2024, số ca nhập viện liên quan đến xe máy điện hàng năm ở St. Michael, một trong những bệnh viện quan trọng nhất Toronto, đã tăng 240%.\nCác tài xế cho biết các nhà quản lý nền tảng giao hàng là một phần nguyên nhân gây ra tình trạng này, khi họ áp dụng chính sách phạt những người người giao hàng chậm, ưu tiên an toàn hơn tốc độ.\n"Có những lúc bạn phải chạy nhanh vào giờ cao điểm mới kiếm được tiền. Một số người chẳng quan tâm đến bất cứ điều gì, họ vượt đèn đỏ, phớt lờ biển báo vì vội giao đồ ăn", Rahman nói. Các công ty giao hàng cho biết đã thực hiện nhiều biện pháp để giảm thiểu hành vi lái xe ẩu.\nTrong khi đó, người đi xe đạp phàn nàn rằng không gian an toàn hiếm hoi trong thành phố dành cho họ đang bị xe điện chiếm dụng. Michael Longfield, lãnh đạo tổ chức thúc đẩy đạp xe Cycle Toronto, mô tả làn đường dành cho xe đạp trong thành phố đang "ngày càng hỗn loạn".\n"Những chiếc xe điện lao vun vút qua người đạp xe trở thành cảnh thường nhật. Tôi từng bị ngã vì một chiếc xe máy điện", ông Longfield cho biết. "Tôi coi đây là một thất bại ở các cấp trong quản lý các loại xe được phép nhập khẩu". Hầu hết xe máy điện ở Toronto được nhập khẩu từ châu Á, hoặc lắp ráp tại Canada với các linh kiện nhập khẩu.\nGiới chức Toronto đã cố gắng cấm một số loại xe điện vì lý do an toàn, trong đó có scooter điện.\n"Scooter điện không phù hợp với cấu trúc đường phố Toronto. Bánh xe nhỏ gây nhiều vấn đề, dễ kẹt vào đường ray xe điện. Chúng di chuyển nhanh, không ổn định", Barbara, lãnh đạo dịch vụ giao thông Toronto, nói trong tháng này.\nNhưng các nhà lập pháp và cảnh sát địa phương gần như không thể làm gì để loại bỏ scooter điện khỏi đường phố. Cảnh sát không có thẩm quyền tịch thu các phương tiện này, trong khi hoạt động mua bán, lái chúng trong khuôn viên tư nhân vẫn hợp pháp. Số ca nhập viện liên quan đến scooter điện đã tăng vọt 600% từ năm 2020 đến 2024.\nĐức Trung(TheoToronto Star, CBC)', 'CanadaXe máy điện, xe đạp điện ngày càng phổ biến ở Toronto, trong khi chính quyền thành phố chưa kịp đề ra công cụ pháp lý để kiểm soát mức độ an toàn.', @cat_id_the_gioi_phan_tich, @user_id_uctrung, 1, 'PUBLISHED', NOW(), NOW(), 1, NOW(), 0, 0, NULL, NULL, NULL);
